  .reset                            (reset),
  .fifo_data_out                    (fifo_data_out),
  .fifo_empty                       (fifo_empty),
  .worker_status                    (worker_status));

Counter #(.BITSIZE_value(32), .BITSIZE_driver_lines(1)) StartCounter (
/*AUTOINST*/
// Outputs
.value             (StartCounter_value), // Templated
// Inputs
.clock              (clock),                 // Templated
