NP_functionality.VERILOG_PROVIDED:
generate
genvar o1;
 for (o1=0; o1<NUM_ELEM_out1; o1=o1+1)
 begin : vec_expr
 assign out1[o1*BITSIZE_out1+BITSIZE_out1-1:o1*BITSIZE_out1] = in1[o1*BITSIZE_in1+BITSIZE_in1-1:o1*BITSIZE_in1];
 end
endgenerate

NP_functionality.VHDL_PROVIDED:
begin\nprocess(in1)
begin
   for index in 0 to NUM_ELEM_out1-1 loop
       out1((index+1)*BITSIZE_out1-1 downto index*BITSIZE_out1) <= std_logic_vector(resize_signed(signed(in1((index+1)*BITSIZE_in1-1 downto index*BITSIZE_in1)), BITSIZE_out1));
   end loop;
end process;

NP_functionality.VERILOG_PROVIDED:
generate
genvar o1;
 for (o1=0; o1<NUM_ELEM_out1; o1=o1+1)
 begin : vec_expr
 assign out1[o1*BITSIZE_out1+BITSIZE_out1-1:o1*BITSIZE_out1] = in1[o1*BITSIZE_in1+BITSIZE_in1-1:o1*BITSIZE_in1];
 end
endgenerate

NP_functionality.VHDL_PROVIDED:
begin\nprocess(in1)
begin
   for index in 0 to NUM_ELEM_out1-1 loop
       out1((index+1)*BITSIZE_out1-1 downto index*BITSIZE_out1) <= std_logic_vector(resize(unsigned(in1((index+1)*BITSIZE_in1-1 downto index*BITSIZE_in1)), BITSIZE_out1));
   end loop;
end process;

constant zeroes : std_logic_vector(in1'range) := (others => '0');
begin
 out1 <= resize_signed(in2, BITSIZE_out1) when (in1 /= zeroes) else resize_signed(in3, BITSIZE_out1);

constant zeroes : std_logic_vector(in1'range) := (others => '0');
begin
 out1 <= resize(in2, BITSIZE_out1) when (in1 /= zeroes) else resize(in3, BITSIZE_out1);

NP_functionality.VERILOG_PROVIDED:
parameter required_iterations = BITSIZE_in1 > PIPE_PARAMETER ? BITSIZE_in1 : PIPE_PARAMETER;
parameter STEPS = ((required_iterations)/(PIPE_PARAMETER+1)) + (((required_iterations)%(PIPE_PARAMETER+1)) ? 1 : 0);
parameter num_length = STEPS * (PIPE_PARAMETER+1);
parameter denom_length = BITSIZE_in2+1;
parameter temp_length = num_length+denom_length;
parameter PIPE_PARAMETER_T = PIPE_PARAMETER > temp_length-denom_length ? temp_length-denom_length : PIPE_PARAMETER;
parameter quot_length = temp_length - denom_length + 1;
parameter xquot_length = quot_length < BITSIZE_out1 ? quot_length : BITSIZE_out1;

wire [BITSIZE_in1-1:0] xnum;
wire [BITSIZE_in2-1:0] xdenom;

function [BITSIZE_out1-1:0] UMOD;
  input [BITSIZE_in1-1:0] fxnum;
  input [BITSIZE_in2-1:0] fxdenom;
  reg [quot_length-1:0] quot;
  integer column, j;
  reg term;
  reg [temp_length-1:0] temp;
  reg signed [denom_length:0] sum;
begin
  temp = {temp_length{1'b0}};
  temp[num_length-1:0] = fxnum;
  sum = {denom_length+1{1'b0}};

  quot = {quot_length{1'b0}};
  for(j = temp_length-denom_length; j >= 0; j = j - 1)
  begin
    sum = $signed({1'b0, temp[j +: denom_length]})-$signed({2'b00, fxdenom});
    quot[j] = ~sum[denom_length];
    for(column = 0; column < denom_length; column = column + 1)
    begin
      term = ((quot[j] & sum[column])) | ((~quot[j] & temp[column+j]));
      temp[column+j] = term;
    end
  end
  UMOD = {BITSIZE_out1{1'b0}};
  UMOD[xquot_length-1:0] = temp[xquot_length-1:0];
end
endfunction

generate
  if(PIPE_PARAMETER_T>0)
  begin
    reg signed [BITSIZE_out1-1:0] frem;
    reg [temp_length-1:0] temp[PIPE_PARAMETER_T-1:0];
    reg [BITSIZE_in2-1:0] xdenom_arr[PIPE_PARAMETER_T-1:0];
    reg [quot_length-1:0] quot[PIPE_PARAMETER_T-1:0];
    reg in1_sign[PIPE_PARAMETER_T-1:0];

    always @(temp[0] or quot[0] or in1_sign[0] or xdenom_arr[0])
    begin : last_block
      reg [temp_length-1:0] t_temp;
      reg signed [denom_length:0] sum;
      reg [quot_length-1:0] t_quot;
      integer j, st;
      // let's start computing the output
      t_temp = temp[0];
      t_quot = quot[0];
      sum = {denom_length+1{1'b0}};
      j=0;
      for(st = STEPS-1; st >=0 ; st = st - 1)
      begin
        sum = $signed({1'b0, t_temp[(j+st) +: denom_length]})-$signed({2'b00, xdenom_arr[0]});
        t_quot[st+j] = ~sum[denom_length];
        t_temp[st+j+:denom_length] = t_quot[st+j] ? sum[denom_length-1:0] :t_temp[st+j+:denom_length];
      end
      frem = in1_sign[0] ? -t_temp : t_temp;
    end
    always @(posedge clock)
    begin : intermediate_block
      reg [temp_length-1:0] t_temp;
      reg signed [denom_length:0] sum;
      reg [quot_length-1:0] t_quot;
      integer j, st, pp;
      /// intermediate steps
      for(pp = 1; pp < PIPE_PARAMETER_T; pp = pp + 1)
      begin
        t_temp = temp[pp];
        t_quot = quot[pp];
        sum = {denom_length+1{1'b0}};
        j=pp*STEPS;
        for(st = STEPS-1; st >=0 ; st = st - 1)
        begin
          sum = $signed({1'b0, t_temp[(j+st) +: denom_length]})-$signed({2'b00, xdenom_arr[pp]});
          t_quot[st+j] = ~sum[denom_length];
          t_temp[st+j+:denom_length] = t_quot[st+j] ? sum[denom_length-1:0] :t_temp[st+j+:denom_length];
        end
        quot[pp-1] <= t_quot;
        temp[pp-1] <= t_temp;
        xdenom_arr[pp-1] <= xdenom_arr[pp]; 
        in1_sign[pp-1] <= in1_sign[pp]; 
      end

      /// first stage initialization
      t_temp = {temp_length{1'b0}};
      t_temp[num_length-1:0] = xnum;
      t_quot = {quot_length{1'b0}};
      sum = {denom_length+1{1'b0}};
      j=-STEPS+((PIPE_PARAMETER_T+1)*STEPS);
      for(st=STEPS+(temp_length-denom_length-((PIPE_PARAMETER_T+1)*STEPS)); st >=0 ; st = st - 1)
      begin
        sum = $signed({1'b0, t_temp[(j+st) +: denom_length]})-$signed({2'b00, xdenom});
        t_quot[st+j] = ~sum[denom_length];
        t_temp[st+j+:denom_length] = t_quot[st+j] ? sum[denom_length-1:0] :t_temp[st+j+:denom_length];
      end
      quot[PIPE_PARAMETER_T-1] <= t_quot;
      temp[PIPE_PARAMETER_T-1] <= t_temp;
      xdenom_arr[PIPE_PARAMETER_T-1] <= xdenom;
      in1_sign[PIPE_PARAMETER_T-1] <= in1[BITSIZE_in1-1];
    end
    assign out1 = frem;
  end
  else
  begin
    wire signed [BITSIZE_out1-1:0] frem;
    assign frem = UMOD(xnum, xdenom);
    assign out1 = in1[BITSIZE_in1-1] ? -frem : frem;
  end
endgenerate

assign xnum = in1 >= $signed({BITSIZE_in1{1'b0}}) ? in1 : -in1;
assign xdenom = in2 >= $signed({BITSIZE_in2{1'b0}}) ? in2 : -in2;

NP_functionality.VHDL_PROVIDED:
function integer_ternary_operator(cond : boolean; op1 : integer; op2 : integer) return integer is
  begin
    if cond then
      return op1;
    else
      return op2;
    end if;
    end integer_ternary_operator;
  constant required_iterations : integer := integer_ternary_operator(BITSIZE_in1 > PIPE_PARAMETER, BITSIZE_in1, PIPE_PARAMETER);
  constant STEPS            : integer := (required_iterations/(PIPE_PARAMETER+1)) + integer_ternary_operator(((required_iterations)mod(PIPE_PARAMETER+1) /= 0), 1, 0);
  constant num_length       : integer := STEPS * (PIPE_PARAMETER+1);
  constant denom_length     : integer := BITSIZE_in2+1;
  constant temp_length      : integer := num_length+denom_length;
  constant PIPE_PARAMETER_T : integer := integer_ternary_operator(PIPE_PARAMETER > temp_length-denom_length, temp_length-denom_length, PIPE_PARAMETER);
  constant quot_length      : integer := temp_length - denom_length + 1;
  constant xquot_length     : integer := integer_ternary_operator(quot_length < BITSIZE_out1, quot_length, BITSIZE_out1);

  signal xnum   : std_logic_vector(BITSIZE_in1-1 downto 0);
  signal xdenom : std_logic_vector(BITSIZE_in2-1 downto 0);

  function UMOD(xnum, xdenom : std_logic_vector) return signed is
  variable quot : std_logic_vector(quot_length-1 downto 0);
  variable column : integer;
  variable j : integer;
  variable term : std_logic;
  variable temp : std_logic_vector(temp_length-1 downto 0);
  variable sum  : signed(denom_length downto 0);
  variable ret_UMOD : signed(BITSIZE_out1-1 downto 0);
  begin
    temp := (others => '0');
    temp(num_length-1 downto 0) := xnum;
    sum := (others => '0');

    quot := (others => '0');
    for j in temp_length-denom_length downto 0 loop
      sum := signed(resize(unsigned(temp(j + denom_length - 1 downto j)), denom_length+1)) - signed(resize(unsigned(xdenom), denom_length+1));
      quot(j) := not sum(denom_length);
      for column in 0 to denom_length-1 loop
        term := (quot(j) and sum(column)) or ((not quot(j) and temp(column+j)));
        temp(column+j) := term;
      end loop;
    end loop;
    ret_UMOD := (others => '0');
    ret_UMOD(xquot_length-1 downto 0) := signed(temp(xquot_length-1 downto 0));
    return ret_UMOD;
  end function;
  begin

  cond: if(PIPE_PARAMETER > 0) generate
    signal frem : signed(BITSIZE_out1-1 downto 0);
    type temp_type is array (PIPE_PARAMETER_T-1 downto 0) of std_logic_vector(temp_length-1 downto 0);
    signal temp : temp_type;
    type xdenom_arr_type is array (PIPE_PARAMETER_T-1 downto 0) of std_logic_vector(BITSIZE_in2-1 downto 0);
    signal xdenom_arr : xdenom_arr_type;
    type quot_type is array (PIPE_PARAMETER_T-1 downto 0) of std_logic_vector(quot_length-1 downto 0);
    signal quot : quot_type;
    type in1_sign_type is array (PIPE_PARAMETER_T-1 downto 0) of std_logic;
    signal in1_sign : in1_sign_type;


  begin
    last_block : process(temp(0), quot(0), in1_sign(0), xdenom_arr(0))
      variable t_temp : std_logic_vector(temp_length-1 downto 0);
      variable sum : signed(denom_length downto 0);
      variable t_quot : std_logic_vector(quot_length-1 downto 0);
      variable j : integer;
    begin

      t_temp := temp(0);
      t_quot := quot(0);
      sum := (others => '0');
      j := 0;
      for st in STEPS-1 downto 0 loop
        sum := signed(resize(unsigned(t_temp(j + st + denom_length -1 downto j + st)), denom_length+1)) - signed(resize(unsigned(xdenom_arr(0)), denom_length+1));
        t_quot(st+j) := not sum(denom_length);
        if(t_quot(st+j) /= '0') then
          t_temp(st+j+denom_length-1 downto + st+j) := std_logic_vector(sum(denom_length-1 downto 0));
        else
          t_temp(st+j+denom_length-1 downto + st+j) := t_temp(st+j+denom_length-1 downto st+j);
        end if;
      end loop;
      if(in1_sign(0) /= '0') then
        frem <= resize(-signed(t_temp), BITSIZE_out1);
      else
        frem <= resize(signed(t_temp), BITSIZE_out1);
      end if;
    end process;
    intermediate_block : process(clock)
      variable t_temp : std_logic_vector(temp_length-1 downto 0);
      variable sum : signed(denom_length downto 0);
      variable t_quot : std_logic_vector(quot_length-1 downto 0);
      variable j : integer;
    begin
      if(clock'event and clock = '1') then
         for pp in 1 to PIPE_PARAMETER_T-1 loop
           t_temp := temp(pp);
           t_quot := quot(pp);
           sum := (others => '0');
           j := pp*STEPS;
           for st in STEPS-1 downto 0 loop
             sum := signed(resize(unsigned(t_temp(j+st + denom_length - 1 downto j+st)), denom_length+1)) - signed(resize(unsigned(xdenom_arr(pp)), denom_length+1));
             t_quot(st+j) := not sum(denom_length);
             if(t_quot(st+j) /= '0') then
               t_temp(st+j+denom_length-1 downto st+j) := std_logic_vector(sum(denom_length-1 downto 0));
             else
               t_temp(st+j+denom_length-1 downto st+j) := t_temp(st+j+denom_length-1 downto st+j);
             end if;
           end loop;
           quot(pp-1) <= t_quot;
           temp(pp-1) <= t_temp;
           xdenom_arr(pp-1) <= xdenom_arr(pp);
           in1_sign(pp-1) <= in1_sign(pp);
         end loop;
         --- first stage initialization
         t_temp := (others => '0');
         t_temp(num_length-1 downto 0) := std_logic_vector(resize(unsigned(xnum), num_length));
         t_quot := (others => '0');
         sum := (others => '0');
         j := - STEPS + ((PIPE_PARAMETER_T+1)*STEPS);
         for st in STEPS+(temp_length-denom_length-((PIPE_PARAMETER_T+1)*STEPS)) downto 0 loop
           sum := signed(resize(unsigned(t_temp(j+st+denom_length-1 downto j+st)), denom_length+1)) - signed(resize(unsigned(xdenom), denom_length+1));
           t_quot(st+j) := not sum(denom_length);
           if(t_quot(st+j) /= '0') then
              t_temp(st+j+denom_length-1 downto st+j) := std_logic_vector(sum(denom_length-1 downto 0));
           else
              t_temp(st+j+denom_length-1 downto st+j) := t_temp(st+j+denom_length-1 downto st+j);
           end if;
         end loop;
         quot(PIPE_PARAMETER_T-1) <= t_quot;
         temp(PIPE_PARAMETER_T-1) <= t_temp;
         xdenom_arr(PIPE_PARAMETER_T-1) <= xdenom;
         in1_sign(PIPE_PARAMETER_T-1) <= in1(BITSIZE_in1-1);
      end if;
    end process;
    out1 <= frem;
  end generate cond;
  cond2: if(PIPE_PARAMETER = 0) generate
    signal frem : signed(BITSIZE_out1-1 downto 0);
  begin
    frem <= UMOD(xnum, xdenom);
    out1 <= -frem when (in1(BITSIZE_in1-1) /= '0') else frem;
  end generate cond2;

  xnum <= std_logic_vector(in1) when (in1 >= 0) else std_logic_vector(-in1);
  xdenom <= std_logic_vector(in2) when (in2 >= 0) else std_logic_vector(-in2);

NP_functionality.VERILOG_PROVIDED:
parameter required_iterations = BITSIZE_in1 > PIPE_PARAMETER ? BITSIZE_in1 : PIPE_PARAMETER;
parameter STEPS = ((required_iterations)/(PIPE_PARAMETER+1)) + (((required_iterations)%(PIPE_PARAMETER+1)) ? 1 : 0);
parameter num_length = STEPS * (PIPE_PARAMETER+1);
parameter denom_length = BITSIZE_in2+1;
parameter temp_length = num_length+denom_length;
parameter PIPE_PARAMETER_T = PIPE_PARAMETER > temp_length-denom_length ? temp_length-denom_length : PIPE_PARAMETER;
parameter quot_length = temp_length - denom_length + 1;
parameter xquot_length = quot_length < BITSIZE_out1 ? quot_length : BITSIZE_out1;

wire [BITSIZE_in1-1:0] xnum;
wire [BITSIZE_in2-1:0] xdenom;

function [BITSIZE_out1-1:0] UMOD;
  input [BITSIZE_in1-1:0] fxnum;
  input [BITSIZE_in2-1:0] fxdenom;
  reg [quot_length-1:0] quot;
  integer column, j;
  reg term;
  reg [temp_length-1:0] temp;
  reg signed [denom_length:0] sum;
begin
  temp = {temp_length{1'b0}};
  temp[num_length-1:0] = fxnum;
  sum = {denom_length+1{1'b0}};

  quot = {quot_length{1'b0}};
  for(j = temp_length-denom_length; j >= 0; j = j - 1)
  begin
    sum = $signed({1'b0, temp[j +: denom_length]})-$signed({2'b00, fxdenom});
    quot[j] = ~sum[denom_length];
    for(column = 0; column < denom_length; column = column + 1)
    begin
      term = ((quot[j] & sum[column])) | ((~quot[j] & temp[column+j]));
      temp[column+j] = term;
    end
  end
  UMOD = {BITSIZE_out1{1'b0}};
  UMOD[xquot_length-1:0] = temp[xquot_length-1:0];
end
endfunction

generate
  if(PIPE_PARAMETER_T>0)
  begin
    reg [BITSIZE_out1-1:0] frem;
    reg [temp_length-1:0] temp[PIPE_PARAMETER_T-1:0];
    reg [BITSIZE_in2-1:0] xdenom_arr[PIPE_PARAMETER_T-1:0];
    reg [quot_length-1:0] quot[PIPE_PARAMETER_T-1:0];

    always @(temp[0] or quot[0] or xdenom_arr[0])
    begin : last_block
      reg [temp_length-1:0] t_temp;
      reg signed [denom_length:0] sum;
      reg [quot_length-1:0] t_quot;
      integer j, st;

      // let's start computing the output
      t_temp = temp[0];
      t_quot = quot[0];
      sum = {denom_length+1{1'b0}};
      j=0;
      for(st = STEPS-1; st >=0 ; st = st - 1)
      begin
        sum = $signed({1'b0, t_temp[(j+st) +: denom_length]})-$signed({2'b00, xdenom_arr[0]});
        t_quot[st+j] = ~sum[denom_length];
        t_temp[st+j+:denom_length] = t_quot[st+j] ? sum[denom_length-1:0] :t_temp[st+j+:denom_length];
      end
      frem = t_temp;
    end
    always @(posedge clock)
    begin : intermediate_block
      reg [temp_length-1:0] t_temp;
      reg signed [denom_length:0] sum;
      reg [quot_length-1:0] t_quot;
      integer j, st, pp;
      /// intermediate steps
      for(pp = 1; pp < PIPE_PARAMETER_T; pp = pp + 1)
      begin
        t_temp = temp[pp];
        t_quot = quot[pp];
        sum = {denom_length+1{1'b0}};
        j=pp*STEPS;
        for(st = STEPS-1; st >=0 ; st = st - 1)
        begin
          sum = $signed({1'b0, t_temp[(j+st) +: denom_length]})-$signed({2'b00, xdenom_arr[pp]});
          t_quot[st+j] = ~sum[denom_length];
          t_temp[st+j+:denom_length] = t_quot[st+j] ? sum[denom_length-1:0] :t_temp[st+j+:denom_length];
        end
        quot[pp-1] <= t_quot;
        temp[pp-1] <= t_temp;
        xdenom_arr[pp-1] <= xdenom_arr[pp]; 
      end

      /// first stage initialization
      t_temp = {temp_length{1'b0}};
      t_temp[num_length-1:0] = xnum;
      t_quot = {quot_length{1'b0}};
      sum = {denom_length+1{1'b0}};
      j=-STEPS+((PIPE_PARAMETER_T+1)*STEPS);
      for(st=STEPS+(temp_length-denom_length-((PIPE_PARAMETER_T+1)*STEPS)); st >=0 ; st = st - 1)
      begin
        sum = $signed({1'b0, t_temp[(j+st) +: denom_length]})-$signed({2'b00, xdenom});
        t_quot[st+j] = ~sum[denom_length];
        t_temp[st+j+:denom_length] = t_quot[st+j] ? sum[denom_length-1:0] :t_temp[st+j+:denom_length];
      end
      quot[PIPE_PARAMETER_T-1] <= t_quot;
      temp[PIPE_PARAMETER_T-1] <= t_temp;
      xdenom_arr[PIPE_PARAMETER_T-1] <= xdenom;
    end
    assign out1 = frem;
  end
  else
  begin
    wire [BITSIZE_out1-1:0] frem;
    assign frem = UMOD(xnum, xdenom);
    assign out1 = frem;
  end
endgenerate

assign xnum = in1;
assign xdenom = in2;

`ifndef _SIM_HAVE_CLOG2
  function integer log2;
     input integer value;
     integer temp_value;
    begin
      temp_value = value-1;
      for (log2=0; temp_value>0; log2=log2+1)
        temp_value = temp_value>>1;
    end
  endfunction
`endif
`ifdef _SIM_HAVE_CLOG2
  parameter arg2_bitsize = $clog2(PRECISION);
`else
  parameter arg2_bitsize = log2(PRECISION);
`endif
generate
  if(BITSIZE_in2 > arg2_bitsize)
    assign out1 = in1 <<< in2[arg2_bitsize-1:0];
  else
    assign out1 = in1 <<< in2;
endgenerate

NP_functionality.VHDL_PROVIDED:
begin
  process(in1, in2)
  begin
    out1 <= shift_left(resize_signed(in1, BITSIZE_out1), to_integer(unsigned(in2) rem PRECISION));
  end process;

NP_functionality.VERILOG_PROVIDED:
`ifndef _SIM_HAVE_CLOG2
  function integer log2;
     input integer value;
     integer temp_value;
    begin
      temp_value = value-1;
      for (log2=0; temp_value>0; log2=log2+1)
        temp_value = temp_value>>1;
    end
  endfunction
`endif
`ifdef _SIM_HAVE_CLOG2
  parameter arg2_bitsize = $clog2(PRECISION);
`else
  parameter arg2_bitsize = log2(PRECISION);
`endif
generate
  if(BITSIZE_in2 > arg2_bitsize)
    assign out1 = in1 << in2[arg2_bitsize-1:0];
  else
    assign out1 = in1 << in2;
endgenerate

NP_functionality.VHDL_PROVIDED:
begin
  process(in1, in2)
  begin
    out1 <= shift_left(resize(in1, BITSIZE_out1), to_integer(unsigned(in2) rem PRECISION));
  end process;

begin
  process(in1, in2)
     begin
    out1 <= (others => '0');
     if (signed(in1) < signed(in2)) then
      out1(0) <= '1';
    else
      out1(0) <= '0';
    end if;
end process
;

NP_functionality.VERILOG_PROVIDED:
`ifndef _SIM_HAVE_CLOG2
  function integer log2;
     input integer value;
     integer temp_value;
    begin
      temp_value = value-1;
      for (log2=0; temp_value>0; log2=log2+1)
        temp_value = temp_value>>1;
    end
  endfunction
`endif
`ifdef _SIM_HAVE_CLOG2
  parameter arg2_bitsize = $clog2(PRECISION);
`else
  parameter arg2_bitsize = log2(PRECISION);
`endif
parameter marg2_bitsize = arg2_bitsize < BITSIZE_in2 ? arg2_bitsize : BITSIZE_in2;
assign out1 = (in1 << (in2[marg2_bitsize-1:0]))|(in1 >> (PRECISION-(in2[marg2_bitsize-1:0])));

generate
  if(PIPE_PARAMETER==1)
  begin
    reg signed [BITSIZE_out1-1:0] out1_reg;
    assign out1 = out1_reg;
    always @(posedge clock)
    begin
      out1_reg <= in1 * in2;
    end
  end
  else if(PIPE_PARAMETER>1)
  begin
    reg signed [BITSIZE_in1-1:0] in1_in;
    reg signed [BITSIZE_in2-1:0] in2_in;
    wire signed [BITSIZE_out1-1:0] mult_res;
    reg signed [BITSIZE_out1-1:0] mul [PIPE_PARAMETER-2:0];
    integer i;
    assign mult_res = in1_in * in2_in;
    always @(posedge clock)
    begin
      in1_in <= in1;
      in2_in <= in2;
      mul[PIPE_PARAMETER-2] <= mult_res;
      for (i=0; i<PIPE_PARAMETER-2; i=i+1)
        mul[i] <= mul[i+1];
    end
    assign out1 = mul[0];
  end
  else
  begin
    assign out1 = in1 * in2;
  end
  endgenerate

NP_functionality.VHDL_PROVIDED:
  signal in1_int : signed (BITSIZE_in1-1 downto 0);
  signal in2_int : signed (BITSIZE_in2-1 downto 0);
begin
  cond: if(PIPE_PARAMETER=0) generate
    out1 <= signed(resize_signed(in1 * in2, BITSIZE_out1));
  end generate cond;
  cond1 : if(PIPE_PARAMETER=1) generate
    out1 <= resize_signed(in1_int * in2_int, BITSIZE_out1);
    process(clock)
    begin
      if clock'event and clock = '1' then
        in1_int <= resize(in1, BITSIZE_in1);
        in2_int <= resize(in2, BITSIZE_in2);
      end if;
    end process;
  end generate cond1;
  cond2 : if(PIPE_PARAMETER >1) generate
    type levels_of_registers is array (PIPE_PARAMETER-2 downto 0) of signed(BITSIZE_out1-1 downto 0);
    signal out1_int : levels_of_registers;
  begin
    process(clock)
    begin
      if clock'event and clock = '1' then
        in1_int <= in1;
        in2_int <= in2;
        out1_int(PIPE_PARAMETER-2) <= signed(resize_signed(signed(in1_int * in2_int), BITSIZE_out1));
        for i in 0 to PIPE_PARAMETER-3 loop
          out1_int (i) <= out1_int (i+1);
        end loop;
      end if;
    end process;
    out1 <= out1_int (0);
  end generate cond2;

NP_functionality.VERILOG_PROVIDED:
generate
  if(PIPE_PARAMETER==1)
  begin
    reg signed [BITSIZE_out1-1:0] out1_reg;
    assign out1 = out1_reg;
    always @(posedge clock)
    begin
      out1_reg <= in1 * in2;
    end
  end
  else if(PIPE_PARAMETER>1)
  begin
    reg [BITSIZE_in1-1:0] in1_in;
    reg [BITSIZE_in2-1:0] in2_in;
