  end
endgenerate

NP_functionality.VHDL_PROVIDED:
  signal in1_int : unsigned (BITSIZE_in1-1 downto 0);
  signal in2_int : unsigned (BITSIZE_in2-1 downto 0);
begin
  cond: if(PIPE_PARAMETER=0) generate
    out1 <= resize(in1 * in2, BITSIZE_out1);
  end generate cond;
  cond1 : if(PIPE_PARAMETER=1) generate
    out1 <= resize(in1_int * in2_int, BITSIZE_out1);
    process(clock)
    begin
      if clock'event and clock = '1' then
        in1_int <= in1;
        in2_int <= in2;
      end if;
    end process;
  end generate cond1;
  cond2 : if(PIPE_PARAMETER >1) generate
    type levels_of_registers is array (PIPE_PARAMETER-1 downto 0) of unsigned (BITSIZE_out1-1 downto 0);
    signal out1_int : levels_of_registers;
  begin
    out1 <= out1_int (0);
    process(clock)
    begin
      if clock'event and clock = '1' then
        in1_int <= in1;
        in2_int <= in2;
        out1_int(PIPE_PARAMETER-2) <= resize(in1_int * in2_int, BITSIZE_out1);
        for i in 0 to PIPE_PARAMETER-3 loop
          out1_int (i) <= out1_int (i+1);
        end loop;
      end if;
    end process;
  end generate cond2;

NP_functionality.VERILOG_PROVIDED:
wire signed [BITSIZE_in3-1:0] tmp_mult;
widen_mult_expr_FU #(.BITSIZE_in1(BITSIZE_in1), .BITSIZE_in2(BITSIZE_in2), .BITSIZE_out1(BITSIZE_in3), .PIPE_PARAMETER(PIPE_PARAMETER)) m1 (.out1(tmp_mult), .clock(clock), .in1(in1), .in2(in2));
generate
 if(PIPE_PARAMETER>0)
 begin
 reg signed [BITSIZE_in3-1:0] tmp_in3 [PIPE_PARAMETER-1:0];
 integer i;
 always @(posedge clock)
 begin
 tmp_in3[0] <= in3;
 for (i=1; i<=PIPE_PARAMETER-1; i=i+1)
 tmp_in3[i] <= tmp_in3[i-1];
 end
 assign out1 = tmp_mult + tmp_in3[PIPE_PARAMETER-1];
 end
 else
 begin
 wire signed [BITSIZE_out1-1:0] tmp1;
 assign tmp1 = tmp_mult + in3;
 assign out1 = tmp1;
 end
endgenerate

