`ifndef _SIM_HAVE_CLOG2
  function integer log2;
     input integer value;
     integer temp_value;
    begin
      temp_value = value-1;
      for (log2=0; temp_value>0; log2=log2+1)
        temp_value = temp_value>>1;
    end
  endfunction
`endif
`ifdef _SIM_HAVE_CLOG2
  parameter arg2_bitsize = $clog2(PRECISION);
`else
  parameter arg2_bitsize = log2(PRECISION);
`endif
parameter marg2_bitsize = arg2_bitsize < BITSIZE_in2 ? arg2_bitsize : BITSIZE_in2;
assign out1 = (in1 >> (in2[marg2_bitsize-1:0]))|(in1 <<(PRECISION-(in2[marg2_bitsize-1:0])));

reg[7:0] cleaned_in0;
wire [7:0] in0;
wire[BITSIZE_in1-1:0] shifted_s;

assign in0 = {in9, in8, in7, in6, in5, in4, in3, in2};
generate
genvar i0;
for (i0=0; i0<8; i0=i0+1)
begin : L0
      always @(*)
      begin
         if (in0[i0] == 1'b1)
            cleaned_in0[i0] = 1'b1;
         else
            cleaned_in0[i0] = 1'b0;
      end
  end
endgenerate
assign shifted_s = in1 >> cleaned_in0;
assign out1[0] = shifted_s[0];
generate
 if(BITSIZE_out1 > 1)
   assign out1[BITSIZE_out1-1:1] = 0;
endgenerate

NP_functionality.VHDL_PROVIDED:
  signal in0 : std_logic_vector(7 downto 0);
  signal shifted_s : unsigned(in1'range) := (others => '0');
begin
  in0(0) <= in2;
  in0(1) <= in3;
  in0(2) <= in4;
  in0(3) <= in5;
  in0(4) <= in6;
  in0(5) <= in7;
  in0(6) <= in8;
  in0(7) <= in9;
  out1 <= std_logic_vector(resize(to_unsigned(1, BITSIZE_out1), BITSIZE_out1)) when (shifted_s(0) = '1') else (others => '0');
  process(in0, in1)
  begin
    shifted_s <= shift_right(unsigned(in1), to_integer(unsigned(in0)));
  end process;

begin
  process(in1, in2)
  begin
    out1 <= shift_right(in1, to_integer(unsigned(in2)))(0);
  end process;

begin
  process(in1, in2)
  begin
    out1 <= shift_right(unsigned(in1), to_integer(unsigned(in2)))(0);
  end process;

