assign xdenom = in2 >= $signed({BITSIZE_in2{1'b0}}) ? in2 : -in2;

parameter required_iterations = BITSIZE_in1 > PIPE_PARAMETER ? BITSIZE_in1 : PIPE_PARAMETER;
parameter STEPS = ((required_iterations)/(PIPE_PARAMETER+1)) + (((required_iterations)%(PIPE_PARAMETER+1)) ? 1 : 0);
parameter num_length = STEPS * (PIPE_PARAMETER+1);
parameter denom_length = BITSIZE_in2+1;
parameter temp_length = num_length+denom_length;
parameter PIPE_PARAMETER_T = PIPE_PARAMETER > temp_length-denom_length ? temp_length-denom_length : PIPE_PARAMETER;
parameter quot_length = temp_length - denom_length + 1;
parameter xquot_length = quot_length < BITSIZE_out1 ? quot_length : BITSIZE_out1;

wire [BITSIZE_in1-1:0] xnum;
wire [BITSIZE_in2-1:0] xdenom;

function [BITSIZE_out1-1:0] UDIV;
  input [BITSIZE_in1-1:0] fxnum;
  input [BITSIZE_in2-1:0] fxdenom;
  reg [quot_length-1:0] quot;
  integer column, j;
  reg term;
  reg [temp_length-1:0] temp;
  reg signed [denom_length:0] sum;
begin
  temp = {temp_length{1'b0}};
  temp[num_length-1:0] = fxnum;
  sum = {denom_length+1{1'b0}};

  quot = {quot_length{1'b0}};
  for(j = temp_length-denom_length; j >= 0; j = j - 1)
  begin
    sum = $signed({1'b0, temp[j +: denom_length]})-$signed({2'b00, fxdenom});
    quot[j] = ~sum[denom_length];
    for(column = 0; column < denom_length; column = column + 1)
    begin
      term = ((quot[j] & sum[column])) | ((~quot[j] & temp[column+j]));
      temp[column+j] = term;
    end
  end
  UDIV = {BITSIZE_out1{1'b0}};
  UDIV[xquot_length-1:0] = quot;
end
endfunction

generate
  if(PIPE_PARAMETER_T>0)
  begin
    reg [BITSIZE_out1-1:0] fquot;
    reg [temp_length-1:0] temp[PIPE_PARAMETER_T-1:0];
    reg [BITSIZE_in2-1:0] xdenom_arr[PIPE_PARAMETER_T-1:0];
    reg [quot_length-1:0] quot[PIPE_PARAMETER_T-1:0];

    always @(temp[0] or quot[0] or xdenom_arr[0])
    begin : last_block
      reg [temp_length-1:0] t_temp;
      reg signed [denom_length:0] sum;
      reg [quot_length-1:0] t_quot;
      integer j, st;
      // let's start computing the output
      t_temp = temp[0];
      t_quot = quot[0];
      sum = {denom_length+1{1'b0}};
      j=0;
      for(st = STEPS-1; st >=0 ; st = st - 1)
      begin
        sum = $signed({1'b0, t_temp[(j+st) +: denom_length]})-$signed({2'b00, xdenom_arr[0]});
        t_quot[st+j] = ~sum[denom_length];
        t_temp[st+j+:denom_length] = t_quot[st+j] ? sum[denom_length-1:0] :t_temp[st+j+:denom_length];
      end
      fquot = t_quot;
    end
    always @(posedge clock)
    begin : intermediate_block
      reg [temp_length-1:0] t_temp;
      reg signed [denom_length:0] sum;
      reg [quot_length-1:0] t_quot;
      integer j, st, pp;
      /// intermediate steps
      for(pp = 1; pp < PIPE_PARAMETER_T; pp = pp + 1)
      begin
        t_temp = temp[pp];
        t_quot = quot[pp];
        sum = {denom_length+1{1'b0}};
        j=pp*STEPS;
        for(st = STEPS-1; st >=0 ; st = st - 1)
        begin
          sum = $signed({1'b0, t_temp[(j+st) +: denom_length]})-$signed({2'b00, xdenom_arr[pp]});
          t_quot[st+j] = ~sum[denom_length];
          t_temp[st+j+:denom_length] = t_quot[st+j] ? sum[denom_length-1:0] :t_temp[st+j+:denom_length];
        end
        quot[pp-1] <= t_quot;
        temp[pp-1] <= t_temp;
        xdenom_arr[pp-1] <= xdenom_arr[pp];
      end

      /// first stage initialization
      t_temp = {temp_length{1'b0}};
      t_temp[num_length-1:0] = xnum;
      t_quot = {quot_length{1'b0}};
      sum = {denom_length+1{1'b0}};
      j=-STEPS+((PIPE_PARAMETER_T+1)*STEPS);
      for(st=STEPS+(temp_length-denom_length-((PIPE_PARAMETER_T+1)*STEPS)); st >=0 ; st = st - 1)
      begin
        sum = $signed({1'b0, t_temp[(j+st) +: denom_length]})-$signed({2'b00, xdenom});
        t_quot[st+j] = ~sum[denom_length];
        t_temp[st+j+:denom_length] = t_quot[st+j] ? sum[denom_length-1:0] :t_temp[st+j+:denom_length];
      end
      quot[PIPE_PARAMETER_T-1] <= t_quot;
      temp[PIPE_PARAMETER_T-1] <= t_temp;
      xdenom_arr[PIPE_PARAMETER_T-1] <= xdenom;
    end
    assign out1 = fquot;
  end
  else
  begin
    wire [BITSIZE_out1-1:0] fquot;
    assign fquot = UDIV(xnum, xdenom);
    assign out1 = fquot;
  end
endgenerate

assign xnum = in1;
assign xdenom = in2;

NP_functionality.VHDL_PROVIDED:
signal resized_in1 : signed (BITSIZE_out1-1 downto 0);
 signal resized_in2 : signed (BITSIZE_out1-1 downto 0);
 begin
 process(in1,in2,resized_in1,resized_in2)
 begin
 if(BITSIZE_out1 <= BITSIZE_in1) then
 resized_in1 <= in1(BITSIZE_out1-1 downto 0);
 else
 resized_in1 <= resize(in1, BITSIZE_out1);
 end if;
 if(BITSIZE_out1 < BITSIZE_in2) then
 resized_in2 <= in2(BITSIZE_out1-1 downto 0);
 else
 resized_in2 <= resize(in2, BITSIZE_out1);
 end if;
 out1 <= resized_in1 + resized_in2;
 end process;

signal resized_in1 : unsigned(BITSIZE_out1-1 downto 0);
 signal resized_in2 : unsigned(BITSIZE_out1-1 downto 0);
 begin
 process(in1,in2,resized_in1,resized_in2)
 begin
 if(BITSIZE_out1 <= BITSIZE_in1) then
 resized_in1 <= in1(BITSIZE_out1-1 downto 0);
 else
 resized_in1 <= resize(in1, BITSIZE_out1);
 end if;
 if(BITSIZE_out1 < BITSIZE_in2) then
 resized_in2 <= in2(BITSIZE_out1-1 downto 0);
 else
 resized_in2 <= resize(in2, BITSIZE_out1);
 end if;
 out1 <= resized_in1 + resized_in2;
 end process;

NP_functionality.VERILOG_PROVIDED:
wire signed [BITSIZE_out1-1:0] add_tmp;
wire signed [BITSIZE_out1-1:0] mask_max;
wire signed [BITSIZE_out1-1:0] mask_min;
wire cond1, cond2;
assign add_tmp = in1 + in2;
assign mask_min = {1'b1, {BITSIZE_out1-1{1'b0}}};
assign mask_max = {1'b0, {BITSIZE_out1-1{1'b1}}};
assign cond1 = (!in1[BITSIZE_in1-1])&(!in2[BITSIZE_in2-1])&add_tmp[BITSIZE_out1-1];
assign cond2 = in1[BITSIZE_in1-1]&in2[BITSIZE_in2-1]&(!add_tmp[BITSIZE_out1-1]);
assign out1 = cond2 ? mask_min : (cond1 ? mask_max : add_tmp);

wire signed [BITSIZE_out1:0] add_tmp;
assign add_tmp = in1 + in2;
assign out1 = add_tmp[BITSIZE_out1] ? {BITSIZE_out1{1'b1}} : add_tmp[BITSIZE_out1-1:0];

wire [1:0] in1_tail;
wire [1:0] in2_tail;
wire [1:0] in3_tail;
wire [1:0] alu_decode;
wire signed [BITSIZE_in1+1:0] ext_in10;
wire signed [BITSIZE_in2+1:0] ext_in20;
wire signed [BITSIZE_in3+1:0] ext_in30;
wire signed [BITSIZE_out1+1:0] ext_in1;
wire signed [BITSIZE_out1+1:0] ext_in2;
wire signed [BITSIZE_out1+1:0] ext_in3;
wire signed [2*(BITSIZE_out1+2)-1:0] cs_vector;
wire signed [BITSIZE_out1+1:0] c_vector;
wire signed [BITSIZE_out1+1:0] s_vector;
function [1:0] c5to2;
  input [4:0] value;
begin
  case (value)
    /* plus plus */
    5'b00000 : c5to2 = 2'b00;
    5'b00001 : c5to2 = 2'b01;
    5'b00010 : c5to2 = 2'b01;
    5'b00011 : c5to2 = 2'b10;
    5'b00100 : c5to2 = 2'b01;
    5'b00101 : c5to2 = 2'b10;
    5'b00110 : c5to2 = 2'b10;
    5'b00111 : c5to2 = 2'b11;
    /* plus minus */
    5'b01000 : c5to2 = 2'b01;
    5'b01001 : c5to2 = 2'b00;
    5'b01010 : c5to2 = 2'b10;
    5'b01011 : c5to2 = 2'b01;
    5'b01100 : c5to2 = 2'b10;
    5'b01101 : c5to2 = 2'b01;
    5'b01110 : c5to2 = 2'b11;
    5'b01111 : c5to2 = 2'b10;
    /* minus plus */
    5'b10000 : c5to2 = 2'b01;
    5'b10001 : c5to2 = 2'b10;
    5'b10010 : c5to2 = 2'b00;
    5'b10011 : c5to2 = 2'b01;
    5'b10100 : c5to2 = 2'b10;
    5'b10101 : c5to2 = 2'b11;
    5'b10110 : c5to2 = 2'b01;
    5'b10111 : c5to2 = 2'b10;
    /* minus minus */
    5'b11000 : c5to2 = 2'b10;
    5'b11001 : c5to2 = 2'b01;
    5'b11010 : c5to2 = 2'b01;
    5'b11011 : c5to2 = 2'b00;
    5'b11100 : c5to2 = 2'b11;
    5'b11101 : c5to2 = 2'b10;
    5'b11110 : c5to2 = 2'b10;
    5'b11111 : c5to2 = 2'b01;
    default  : c5to2 = 2'bxx;
  endcase
end
endfunction
function [1:0] alu_decode_fun;
  input [3:0] value;
begin
  case (value)
    4'b0001 : alu_decode_fun = 2'b00;/*sel_ternary_plus_expr||sel_plus_expr*/
    4'b0010 : alu_decode_fun = 2'b01;/*sel_ternary_pm_expr*/
    4'b0100 : alu_decode_fun = 2'b10;/*sel_ternary_mp_expr*/
    4'b1000 : alu_decode_fun = 2'b11;/*sel_ternary_mm_expr*/
    default : alu_decode_fun = 2'bxx;
  endcase
end
endfunction
function [1:0] in1_tail_fun;
  input [3:0] value;
begin
  case (value)
    4'b0001 : in1_tail_fun = 2'b00;/*sel_ternary_plus_expr||sel_plus_expr*/
    4'b0010 : in1_tail_fun = 2'b00;/*sel_ternary_pm_expr*/
    4'b0100 : in1_tail_fun = 2'b00;/*sel_ternary_mp_expr||sel_minus_expr*/
    4'b1000 : in1_tail_fun = 2'b11;/*sel_ternary_mm_expr*/
    default : in1_tail_fun = 2'bxx;
  endcase
end
endfunction
function [1:0] in2_tail_fun;
  input [3:0] value;
begin
  case (value)
    4'b0001 : in2_tail_fun = 2'b00;/*sel_ternary_plus_expr||sel_plus_expr*/
    4'b0010 : in2_tail_fun = 2'b10;/*sel_ternary_pm_expr*/
    4'b0100 : in2_tail_fun = 2'b01;/*sel_ternary_mp_expr||sel_minus_expr*/
    4'b1000 : in2_tail_fun = 2'b00;/*sel_ternary_mm_expr*/
    default : in2_tail_fun = 2'bxx;
  endcase
end
endfunction
function [1:0] in3_tail_fun;
  input [3:0] value;
begin
  case (value)
    4'b0001 : in3_tail_fun = 2'b00;/*sel_ternary_plus_expr||sel_plus_expr*/
    4'b0010 : in3_tail_fun = 2'b01;/*sel_ternary_pm_expr*/
    4'b0100 : in3_tail_fun = 2'b10;/*sel_ternary_mp_expr||sel_minus_expr*/
    4'b1000 : in3_tail_fun = 2'b00;/*sel_ternary_mm_expr*/
    default : in3_tail_fun = 2'bxx;
  endcase
end
endfunction

// 0001 a + b + c a=00 b=00 c=00
// 0010 a + b - c a=00 b=10 c=01
// 0100 a - b + c a=00 b=01 c=10
// 1000 a - b - c a=11 b=00 c=00
assign alu_decode = alu_decode_fun({sel_ternary_mm_expr,sel_ternary_mp_expr||sel_minus_expr,sel_ternary_pm_expr,sel_ternary_plus_expr||sel_plus_expr});
assign in1_tail = in1_tail_fun({sel_ternary_mm_expr,sel_ternary_mp_expr||sel_minus_expr,sel_ternary_pm_expr,sel_ternary_plus_expr||sel_plus_expr});
assign in2_tail = in2_tail_fun({sel_ternary_mm_expr,sel_ternary_mp_expr||sel_minus_expr,sel_ternary_pm_expr,sel_ternary_plus_expr||sel_plus_expr});
assign in3_tail = in3_tail_fun({sel_ternary_mm_expr,sel_ternary_mp_expr||sel_minus_expr,sel_ternary_pm_expr,sel_ternary_plus_expr||sel_plus_expr});
assign ext_in10 = {in1, in1_tail};
assign ext_in20 = {in2, in2_tail};
assign ext_in30 = {in3, in3_tail};
assign ext_in1 = ext_in10;
assign ext_in2 = ext_in20;
assign ext_in3 = ext_in30;

generate
genvar index_cs;
for (index_cs=0; index_cs<(BITSIZE_out1+2); index_cs=index_cs+1)
  begin : cs_loop
    assign {cs_vector[2*index_cs+1],cs_vector[2*index_cs]} = c5to2({alu_decode,ext_in1[index_cs],ext_in2[index_cs],ext_in3[index_cs]});
  end
endgenerate

assign c_vector[0] = 1'b0;
generate
genvar index_c;
for (index_c=0; index_c<BITSIZE_out1+1; index_c=index_c+1)
  begin : c_loop
    assign c_vector[index_c+1] = cs_vector[2*index_c+1];
  end
endgenerate

generate
genvar index_s;
for (index_s=0; index_s<BITSIZE_out1+2; index_s=index_s+1)
  begin : s_loop
    assign s_vector[index_s] = cs_vector[2*index_s];
  end
endgenerate
assign out1 = (c_vector+s_vector)>>2;

