NP_functionality.VHDL_PROVIDED:
begin
  process(in1)
  begin
    if(BITSIZE_out1 > BITSIZE_in1) then
      out1 <= resize_signed(in1, BITSIZE_out1);
    else
      out1 <= in1 (BITSIZE_out1-1 downto 0);
    end if;
  end process;

begin
   process(in1,in2)
   begin
     if(BITSIZE_in1 > BITSIZE_in2) then
        out1 <= std_logic_vector(resize(unsigned(in1 or std_logic_vector(resize(unsigned(in2), BITSIZE_in1))), BITSIZE_out1));
     else
        out1 <= std_logic_vector(resize(unsigned(std_logic_vector(resize(unsigned(in1), BITSIZE_in2)) or in2), BITSIZE_out1));
     end if;
   end process;

NP_functionality.VERILOG_PROVIDED:
wire signed [BITSIZE_out1-1:0] sub_tmp;
wire signed [BITSIZE_out1-1:0] mask_max;
wire signed [BITSIZE_out1-1:0] mask_min;
wire cond1, cond2;
assign sub_tmp = in1 - in2;
assign mask_min = {1'b1, {BITSIZE_out1-1{1'b0}}};
assign mask_max = {1'b0, {BITSIZE_out1-1{1'b1}}};
assign cond1 = (!in1[BITSIZE_in1-1])&in2[BITSIZE_in2-1]&sub_tmp[BITSIZE_out1-1];
assign cond2 = in1[BITSIZE_in1-1]&(!in2[BITSIZE_in2-1])&(!sub_tmp[BITSIZE_out1-1]);
assign out1 = cond2 ? mask_min : (cond1 ? mask_max : sub_tmp);

wire signed [BITSIZE_out1:0] sub_tmp;
assign sub_tmp = in1 - in2;
assign out1 = sub_tmp[BITSIZE_out1] ? {BITSIZE_out1{1'b0}} : sub_tmp[BITSIZE_out1-1:0];

NP_functionality.VHDL_PROVIDED:
signal xs1 : unsigned(in1'range) := (others => 'X');
signal xs2 : unsigned(in2'range) := (others => 'X');
begin
  out1 <= (others => 'X') when (not((in1 >= in2) or (in1 <= in2))) else std_logic_vector(resize(to_unsigned(1, BITSIZE_out1), BITSIZE_out1)) when (in1 /= in2) else (others => '0');

signal xs1 : unsigned(in1'range) := (others => 'X');
signal xs2 : unsigned(in2'range) := (others => 'X');
begin
  out1 <= (others => 'X') when (not((in1 >= in2) or (in1 <= in2))) else std_logic_vector(resize(to_unsigned(1, BITSIZE_out1), BITSIZE_out1)) when (in1 /= in2) else (others => '0');

NP_functionality.VERILOG_PROVIDED:
reg done_port;
// synthesis translate_off
always @(posedge clock)
  if(start_port == 1'b1)
  begin
    $write("%c",c[7:0]);
  end
// synthesis translate_on
always @(posedge clock) done_port <= start_port;
assign return_port=c;

NP_functionality.VHDL_PROVIDED:
begin
  process(clock)
begin
    if(clock'event and clock = '1') then
      -- synthesis translate_off
      if(start_port = '1') then
        write(output, "" & (character'val(to_integer(c))));
      end if;
    -- synthesis translate_on
    done_port <= start_port;
    return_port <= resize(c,32);
  end if;
end process;
