    assign Mout_Wdata_ram[(i+1)*BITSIZE_Mout_Wdata_ram-1:i*BITSIZE_Mout_Wdata_ram] = delayed_sel_STORE[i] ? delayed_in1[(i+1)*BITSIZE_in1-1:i*BITSIZE_in1] : Min_Wdata_ram[(i+1)*BITSIZE_Min_Wdata_ram-1:i*BITSIZE_Min_Wdata_ram];
end
endgenerate
generate
  for (i=0; i<max_n_rw; i=i+1)
  begin : L3
    assign Mout_data_ram_size[(i+1)*BITSIZE_Mout_data_ram_size-1:i*BITSIZE_Mout_data_ram_size] = ((i < PORTSIZE_sel_LOAD && delayed_sel_LOAD[i]) || (i < PORTSIZE_sel_STORE && delayed_sel_STORE[i])) ? (delayed_in3[(i+1)*BITSIZE_in3-1:i*BITSIZE_in3]) : Min_data_ram_size[(i+1)*BITSIZE_Min_data_ram_size-1:i*BITSIZE_Min_data_ram_size];
  end
  endgenerate

parameter max_n_writes = PORTSIZE_sel_STORE > PORTSIZE_Mout_we_ram ? PORTSIZE_sel_STORE : PORTSIZE_Mout_we_ram;
parameter max_n_reads = PORTSIZE_sel_LOAD > PORTSIZE_Mout_oe_ram ? PORTSIZE_sel_STORE : PORTSIZE_Mout_oe_ram;
parameter max_n_rw = max_n_writes > max_n_reads ? max_n_writes : max_n_reads;
wire  [(PORTSIZE_in2*BITSIZE_in2)-1:0] tmp_addr;
reg [PORTSIZE_sel_LOAD-1:0] delayed_sel_LOAD 1INIT_ZERO_VALUE;
reg [PORTSIZE_sel_STORE-1:0] delayed_sel_STORE 1INIT_ZERO_VALUE;
reg [PORTSIZE_in1*BITSIZE_in1-1:0] delayed_in1 1INIT_ZERO_VALUE;
reg [PORTSIZE_in2*BITSIZE_in2-1:0] delayed_in2 1INIT_ZERO_VALUE;
reg [PORTSIZE_in3*BITSIZE_in3-1:0] delayed_in3 1INIT_ZERO_VALUE;
reg [PORTSIZE_out1*BITSIZE_out1-1:0] delayed_out1 1INIT_ZERO_VALUE;

always @(posedge clock)
  delayed_sel_LOAD <= sel_LOAD & in4;
always @(posedge clock)
  delayed_sel_STORE <= sel_STORE & in4;
always @(posedge clock)
  delayed_in1 <= in1;
always @(posedge clock)
  delayed_in2 <= in2;
always @(posedge clock)
  delayed_in3 <= in3;
assign tmp_addr = delayed_in2;
generate
genvar i;
  for (i=0; i<max_n_rw; i=i+1)
  begin : L0
    assign Mout_addr_ram[(i+1)*BITSIZE_Mout_addr_ram-1:i*BITSIZE_Mout_addr_ram] = ((i < PORTSIZE_sel_LOAD && delayed_sel_LOAD[i]) || (i < PORTSIZE_sel_STORE && delayed_sel_STORE[i])) ? (tmp_addr[(i+1)*BITSIZE_in2-1:i*BITSIZE_in2]) : Min_addr_ram[(i+1)*BITSIZE_Min_addr_ram-1:i*BITSIZE_Min_addr_ram];
  end
  endgenerate
assign Mout_oe_ram = delayed_sel_LOAD | Min_oe_ram;
assign Mout_we_ram = delayed_sel_STORE | Min_we_ram;
generate
  for (i=0; i<max_n_reads; i=i+1)
  begin : L1
    always @(posedge clock)
      delayed_out1[(i+1)*BITSIZE_out1-1:i*BITSIZE_out1] <= M_Rdata_ram[i*BITSIZE_M_Rdata_ram+BITSIZE_out1-1:i*BITSIZE_M_Rdata_ram];
    assign out1[(i+1)*BITSIZE_out1-1:i*BITSIZE_out1] = delayed_out1[(i+1)*BITSIZE_out1-1:i*BITSIZE_out1];
end
endgenerate
generate
  for (i=0; i<max_n_rw; i=i+1)
  begin : L2
    assign Mout_Wdata_ram[(i+1)*BITSIZE_Mout_Wdata_ram-1:i*BITSIZE_Mout_Wdata_ram] = delayed_sel_STORE[i] ? delayed_in1[(i+1)*BITSIZE_in1-1:i*BITSIZE_in1] : Min_Wdata_ram[(i+1)*BITSIZE_Min_Wdata_ram-1:i*BITSIZE_Min_Wdata_ram];
end
endgenerate
generate
  for (i=0; i<max_n_rw; i=i+1)
  begin : L3
    assign Mout_data_ram_size[(i+1)*BITSIZE_Mout_data_ram_size-1:i*BITSIZE_Mout_data_ram_size] = ((i < PORTSIZE_sel_LOAD && delayed_sel_LOAD[i]) || (i < PORTSIZE_sel_STORE && delayed_sel_STORE[i])) ? (delayed_in3[(i+1)*BITSIZE_in3-1:i*BITSIZE_in3]) : Min_data_ram_size[(i+1)*BITSIZE_Min_data_ram_size-1:i*BITSIZE_Min_data_ram_size];
  end
  endgenerate

`ifndef _SIM_HAVE_CLOG2
  function integer log2;
     input integer value;
     integer temp_value;
    begin
      temp_value = value-1;
      for (log2=0; temp_value>0; log2=log2+1)
        temp_value = temp_value>>1;
    end
  endfunction
`endif
parameter n_bytes = n_elements*(BRAM_BITSIZE/8);
parameter n_byte_on_databus = BRAM_BITSIZE/8;
parameter n_mem_elements = n_bytes/(n_byte_on_databus) + (n_bytes%(n_byte_on_databus) == 0 ? 0 : 1);
parameter nbit_addr = BITSIZE_in2 > BITSIZE_proxy_in2 ? BITSIZE_in2 : BITSIZE_proxy_in2;

`ifdef _SIM_HAVE_CLOG2
  parameter nbit_read_addr = n_elements == 1 ? 1 : $clog2(n_elements);
  parameter nbits_byte_offset = n_byte_on_databus==1 ? 0 : $clog2(n_byte_on_databus);
`else
  parameter nbit_read_addr = n_elements == 1 ? 1 : log2(n_elements);
  parameter nbits_byte_offset = n_byte_on_databus==1 ? 0 : log2(n_byte_on_databus);
`endif

wire [nbit_read_addr-1:0] memory_addr_a;

wire bram_write;
wire [data_size-1:0] dout_a;
wire [nbit_addr-1:0] relative_addr;
wire [nbit_addr-1:0] tmp_addr;
wire [data_size-1:0] din_a;
reg [data_size-1:0] memory [0:n_elements-1] /* synthesis syn_ramstyle = "no_rw_check" */;

initial
begin
  $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
end

assign din_a = (sel_STORE & in4) ? in1 : proxy_in1;
assign bram_write = (sel_STORE & in4) || proxy_sel_STORE;

generate if(READ_ONLY_MEMORY==0)
  always @(posedge clock)
  begin
    if (bram_write)
    begin
      memory[memory_addr_a] <= din_a;
    end
  end
endgenerate

assign dout_a = memory[memory_addr_a];
assign tmp_addr = (in4 & (sel_LOAD||sel_STORE)) ? in2 : proxy_in2;
generate
  if(USE_SPARSE_MEMORY==1)
    assign relative_addr = tmp_addr[nbit_addr-1:0];
  else
    assign relative_addr = tmp_addr-address_space_begin[nbit_addr-1:0];
endgenerate

generate
  if (n_elements==1)
    assign memory_addr_a = {nbit_read_addr{1'b0}};
  else
    assign memory_addr_a = relative_addr[nbit_read_addr+nbits_byte_offset-1:nbits_byte_offset];
endgenerate

assign out1 = dout_a;
assign proxy_out1 = dout_a;
assign Sout_Rdata_ram =Sin_Rdata_ram;
assign Sout_DataRdy = Sin_DataRdy;
// Add assertion here
// psl default clock = (posedge clock);
// psl ERROR_SDS_data_ram_size: assert never {(BRAM_BITSIZE != in3 && ((sel_LOAD & in4) || (sel_STORE & in4))) || (BRAM_BITSIZE != proxy_in3 && (proxy_sel_LOAD || proxy_sel_STORE))};
// psl ERROR_SDS_alignment: assert never {(((in2-address_space_begin) %(BRAM_BITSIZE/8) != 0) && ((sel_LOAD & in4) ||( sel_STORE & in4))) || (((proxy_in2-address_space_begin) %(BRAM_BITSIZE/8) != 0) && (proxy_sel_LOAD || proxy_sel_STORE))};

NP_functionality.VHDL_PROVIDED:
  function integer_ternary_operator(cond : boolean; op1 : integer; op2 : integer) return integer is
  begin
    if cond then
      return op1;
    else
      return op2;
    end if;
  end integer_ternary_operator;

  constant n_bytes           : integer := n_elements*(BRAM_BITSIZE/8);
  constant n_byte_on_databus : integer := BRAM_BITSIZE/8;
  constant n_mem_elements    : integer := n_bytes/(n_byte_on_databus) + integer_ternary_operator((n_bytes mod n_byte_on_databus) = 0, 0, 1);
  constant nbit_addr         : integer := integer_ternary_operator(BITSIZE_in2 > BITSIZE_proxy_in2, BITSIZE_in2, BITSIZE_proxy_in2);
  constant nbit_read_addr    : integer := integer_ternary_operator(n_elements = 1, 1, integer(ceil(log2(real(n_elements)))));
  constant nbits_byte_offset : integer := integer_ternary_operator(n_byte_on_databus = 1, 0, integer(ceil(log2(real(n_byte_on_databus)))));

  signal memory_addr_a : std_logic_vector(nbit_read_addr-1 downto 0);

  signal bram_write : std_logic;
  signal dout_a : std_logic_vector(data_size-1 downto 0);
  signal relative_addr : std_logic_vector(nbit_addr-1 downto 0);
  signal tmp_addr : std_logic_vector(nbit_addr-1 downto 0);
  signal din_a : std_logic_vector(data_size-1 downto 0);

  type mem_type is array (n_elements-1 downto 0) of std_logic_vector(data_size-1 downto 0);

  impure function InitMemFromFile (MemFileName : in string) return mem_type is
    FILE memfile : text open READ_MODE is MemFileName;
    variable MemFileLine : line;
    variable mem : mem_type;
    begin
       for i in 0 to n_elements-1 loop
        readline(memfile, MemFileLine);
        read(MemFileLine, mem(i));
      end loop;
      return mem;
  end function;

  signal mem : mem_type := InitMemFromFile(MEMORY_INIT_file);

begin

  din_a <= in1 when ((in4 and sel_STORE) = '1') else std_logic_vector(resize(unsigned(proxy_in1), data_size));
  bram_write <= (sel_STORE and in4) or proxy_sel_STORE;

  L1_single_write : process(clock)
  begin
    if (clock'event and clock='1') then
      if(READ_ONLY_MEMORY=0) then
        if(bram_write='1') then
-- synthesis translate_off
          if (to_integer(unsigned(memory_addr_a)) < n_mem_elements) then
-- synthesis translate_on
            mem(to_integer(unsigned(memory_addr_a))) <= din_a;
-- synthesis translate_off
          end if;
-- synthesis translate_on
        end if;
       end if;
    end if;
  end process;

  dout_a <= mem(to_integer(unsigned(memory_addr_a))) when (to_integer(unsigned(memory_addr_a)) < n_elements) else (others => 'X');
  tmp_addr <= in2 when ((in4 and (sel_LOAD or sel_STORE)) = '1') else proxy_in2;

  L2 : process(relative_addr)
  begin
    if(n_elements=1) then
      memory_addr_a <= (others => '0');
    else
      memory_addr_a <= relative_addr(nbit_read_addr+nbits_byte_offset-1 downto nbits_byte_offset);
    end if;
  end process;

  L3 : process(tmp_addr)
  begin
      if(USE_SPARSE_MEMORY=1) then
        relative_addr <= tmp_addr(nbit_addr-1 downto 0);
      else
        relative_addr <= std_logic_vector(resize(unsigned(tmp_addr), nbit_addr) - to_unsigned(address_space_begin, nbit_addr));
      end if;
  end process;

  out1 <= dout_a;
  proxy_out1 <= std_logic_vector(resize(unsigned(dout_a), BITSIZE_proxy_out1));
  Sout_Rdata_ram <= Sin_Rdata_ram;
  Sout_DataRdy <= Sin_DataRdy;

