  always @(posedge clock)
  begin
    if(bram_write[0])
      memory[memory_addr_a_0] <= din_value_aggregated[data_size*0+:data_size];
    if(bram_write[1])
      memory[memory_addr_a_1] <= din_value_aggregated[data_size*1+:data_size];
  end
endgenerate

