NP_functionality.VHDL_PROVIDED:
begin
  process(in1)
  begin
    for index in 0 to NUM_ELEM_out1-1 loop
      if(BITSIZE_out1 > BITSIZE_in1) then
        out1((index+1)*BITSIZE_out1-1 downto index*BITSIZE_out1) <= std_logic_vector(resize(unsigned(in2), BITSIZE_out1)) when (o1 = in3) else std_logic_vector(resize(unsigned(in1((index+1)*BITSIZE_in1-1 downto index*BITSIZE_in1)), BITSIZE_out1));
      else
        out1((index+1)*BITSIZE_out1-1 downto index*BITSIZE_out1) <= in2(BITSIZE_out1-1+index*BITSIZE_in2 downto index*BITSIZE_in2) when (o1 = in3) else in1(BITSIZE_out1-1+index*BITSIZE_in1 downto index*BITSIZE_in1);
      end if;
    end loop;
  end process;

NP_functionality.VERILOG_PROVIDED:
generate
genvar o1;
  for (o1=0; o1<NUM_ELEM_out1; o1=o1+1)
  begin  : vec_expr
    if (BITSIZE_out1 <= BITSIZE_in1)
    begin
      assign out1[o1*BITSIZE_out1+BITSIZE_out1-1:o1*BITSIZE_out1] = o1 == in3 ? in2 : in1[o1*BITSIZE_in1+BITSIZE_in1-1:o1*BITSIZE_in1];
    end
    else
    begin
      assign out1[o1*BITSIZE_out1+BITSIZE_out1-1:o1*BITSIZE_out1] = o1 == in3 ? in2 :  {{(BITSIZE_out1-BITSIZE_in1){in1[o1*BITSIZE_in1+BITSIZE_in1-1]}},in1[o1*BITSIZE_in1+BITSIZE_in1-1:o1*BITSIZE_in1]};
    end
  end
endgenerate

