// psl ERROR_requested_size: assert never {BITSIZE_out1<in3 && (sel_LOAD)};
// psl ERROR_proxy_requested_size: assert never {BITSIZE_proxy_out1<proxy_in3 && (proxy_sel_LOAD)};
// psl ERROR_STORE_LOAD: assert never {sel_STORE && sel_LOAD};
// psl ERROR_proxy_STORE_proxy_LOAD: assert never {proxy_sel_STORE && proxy_sel_LOAD};

NP_functionality.VHDL_PROVIDED:
function integer_ternary_operator(cond : boolean; op1 : integer; op2 : integer) return integer is
   begin
      if cond then
         return op1;
      else
         return op2;
      end if;
   end integer_ternary_operator;

function CONV(po2 : std_logic_vector(2*n_byte_on_databus-1 downto 0)) return std_logic_vector is
  variable int_po2 : integer;
  begin
      int_po2 := to_integer(unsigned(po2));
      case int_po2 is
         when 1 =>
            return std_logic_vector(shift_left(to_unsigned(1,2*n_byte_on_databus),1) -1);
         when 2 =>
            return std_logic_vector(shift_left(to_unsigned(1,2*n_byte_on_databus),2) -1);
         when 4 =>
            return std_logic_vector(shift_left(to_unsigned(1,2*n_byte_on_databus),4) -1);
         when 8 =>
            return std_logic_vector(shift_left(to_unsigned(1,2*n_byte_on_databus),8) -1);
         when 16 =>
            return std_logic_vector(shift_left(to_unsigned(1,2*n_byte_on_databus),16) -1);
         when 32 =>
            return std_logic_vector(shift_left(to_unsigned(1,2*n_byte_on_databus),32) -1);
         when others =>
            return std_logic_vector(to_signed(-1,2*n_byte_on_databus));
      end case;
   end CONV;

   constant nbit_addr                     : integer := BITSIZE_S_addr_ram;
   constant nbits_address_space_rangesize : integer := integer(ceil(log2(real(address_space_rangesize))));
   constant nbits_byte_offset             : integer := integer_ternary_operator(n_byte_on_databus = 1, 1, integer(ceil(log2(real(n_byte_on_databus)))));
   constant memory_bitsize                : integer := 2 * BRAM_BITSIZE;
   constant address_space_begin_u         : unsigned(nbit_addr-1 downto 0) := to_unsigned(address_space_begin, nbit_addr);

   signal tmp_addr                       : std_logic_vector(BITSIZE_in2-1 downto 0);
   signal conv_in                        : std_logic_vector(n_byte_on_databus*2-1 downto 0);
   signal conv_out                       : std_logic_vector(n_byte_on_databus*2-1 downto 0);
   signal cs                             : std_logic;
   signal oe_ram_cs                      : std_logic;
   signal we_ram_cs                      : std_logic;
   signal relative_addr                  : std_logic_vector(nbit_addr-1 downto 0);
   signal S_Wdata_ram_int                : std_logic_vector(2*BRAM_BITSIZE-1 downto 0);
   signal out1_shifted                   : std_logic_vector(2*BRAM_BITSIZE-1 downto 0);
   signal dout                           : std_logic_vector(2*BRAM_BITSIZE-1 downto 0);
   signal byte_offset                    : std_logic_vector(nbits_byte_offset-1 downto 0);

   signal we_ram_cs_delayed              : std_logic;
   signal oe_ram_cs_delayed              : std_logic;
   signal oe_ram_cs_delayed_registered   : std_logic;
   signal oe_ram_cs_delayed_registered1  : std_logic;
   signal delayed_byte_offset            : std_logic_vector(nbits_byte_offset-1 downto 0);
   signal delayed_byte_offset_registered : std_logic_vector(nbits_byte_offset-1 downto 0);
   signal delayed_byte_offset_registered1 : std_logic_vector(nbits_byte_offset-1 downto 0);
begin
   tmp_addr <= std_logic_vector(resize(unsigned(proxy_in2), BITSIZE_in2)) when ((proxy_sel_LOAD or proxy_sel_STORE) = '1') else in2;

   dout(BRAM_BITSIZE-1 downto 0) <= dout_a;
   dout(2*BRAM_BITSIZE-1 downto BRAM_BITSIZE) <= dout_b;

   L3 : process(S_addr_ram)
   begin
      if(PRIVATE_MEMORY = 0 and USE_SPARSE_MEMORY = 0) then
         if(to_integer(unsigned(S_addr_ram)) >= (address_space_begin)) and (to_integer(unsigned(S_addr_ram)) < (address_space_begin+address_space_rangesize)) then
            cs <= '1';
         else
            cs <= '0';
         end if;
      else
         if(PRIVATE_MEMORY=0) then
            if S_addr_ram(nbit_addr-1 downto nbits_address_space_rangesize) = std_logic_vector(address_space_begin_u(nbit_addr-1 downto nbits_address_space_rangesize)) then
               cs <= '1';
            else
               cs <= '0';
            end if;
         else
            cs <= '0';
         end if;
      end if;
   end process;

   oe_ram_cs <= S_oe_ram and cs;
   we_ram_cs <= S_we_ram and cs;

   L6 : process(sel_STORE, proxy_sel_STORE, sel_LOAD, proxy_sel_LOAD, tmp_addr, S_addr_ram)
   begin
      if(PRIVATE_MEMORY=0 and USE_SPARSE_MEMORY=0) then
         if(sel_STORE = '1' or sel_LOAD = '1' or proxy_sel_STORE = '1' or proxy_sel_LOAD = '1') then
            relative_addr <= std_logic_vector(unsigned(tmp_addr)-(to_unsigned(address_space_begin, nbit_addr)));
         else
            relative_addr <= std_logic_vector(unsigned(S_addr_ram) - (to_unsigned(address_space_begin, nbit_addr)));
         end if;
      else
         if(PRIVATE_MEMORY=0) then
            if(sel_STORE = '1' or sel_LOAD = '1' or proxy_sel_STORE = '1' or proxy_sel_LOAD = '1') then
               relative_addr <= std_logic_vector(resize(unsigned(tmp_addr(nbits_address_space_rangesize-1 downto 0)), nbit_addr));
            else
               relative_addr <= std_logic_vector(resize(unsigned(S_addr_ram(nbits_address_space_rangesize-1 downto 0)), nbit_addr));
            end if;
         else
            if(USE_SPARSE_MEMORY=1) then
               relative_addr <= std_logic_vector(resize(unsigned(tmp_addr(nbits_address_space_rangesize-1 downto 0)), nbit_addr));
            else
               relative_addr <= std_logic_vector(unsigned(tmp_addr) - to_unsigned(address_space_begin, nbit_addr));
            end if;
         end if;
      end if;
   end process;

   L7_A : process(relative_addr)
   begin
      if (n_elements=1) then
         memory_addr_a <= (others => '0');
      elsif (n_byte_on_databus=1) then
         memory_addr_a <= relative_addr(nbit_read_addr-1 downto 0);
      else
         memory_addr_a <= relative_addr(nbit_read_addr+nbits_byte_offset-1 downto nbits_byte_offset);
      end if;
   end process;

   L7_B : process(relative_addr)
   begin
      if (n_bytes <= BRAM_BITSIZE/8) then
         memory_addr_b <= (others => '0');
      elsif (n_byte_on_databus=1) then
         memory_addr_b <= std_logic_vector(unsigned(relative_addr(nbit_read_addr-1 downto 0)) + to_unsigned(1,nbit_read_addr));
      else
         memory_addr_b <= std_logic_vector(unsigned(relative_addr(nbit_read_addr+nbits_byte_offset-1 downto nbits_byte_offset)) + to_unsigned(1,nbit_read_addr));
      end if;
   end process;

   L8 : process(relative_addr)
   begin
      if (n_byte_on_databus=1) then
         byte_offset <= (others => '0');
      else
         byte_offset <= relative_addr(nbits_byte_offset-1 downto 0);
      end if;
   end process;

   be_conv_in : process(proxy_sel_STORE, proxy_in3, sel_STORE, in3, S_data_ram_size)
   begin
      if(PRIVATE_MEMORY=0) then
         if(proxy_sel_STORE /= '0') then
            conv_in <= std_logic_vector(resize(unsigned(proxy_in3(BITSIZE_proxy_in3-1 downto 3)), 2*n_byte_on_databus));
         else
            if(sel_STORE /= '0') then
               conv_in <= std_logic_vector(resize(unsigned(in3(BITSIZE_in3-1 downto 3)), 2*n_byte_on_databus));
            else
               conv_in <= std_logic_vector(resize(unsigned(S_data_ram_size(BITSIZE_S_data_ram_size-1 downto 3)), 2*n_byte_on_databus));
            end if;
         end if;
      else
         if(proxy_sel_STORE /= '0') then
            conv_in <= std_logic_vector(resize(unsigned(proxy_in3(BITSIZE_proxy_in3-1 downto 3)), 2*n_byte_on_databus));
         else
            conv_in <= std_logic_vector(resize(unsigned(in3(BITSIZE_in3-1 downto 3)), 2*n_byte_on_databus));
         end if;
      end  if;
   end process;

   conv_out <= CONV(conv_in);
   be <= std_logic_vector(shift_left(unsigned(conv_out), to_integer(unsigned(byte_offset))));


   L13 : process(S_Wdata_ram)
   begin
      if (BITSIZE_S_Wdata_ram < 2*BRAM_BITSIZE) then
         S_Wdata_ram_int(2*BRAM_BITSIZE-1 downto BITSIZE_S_Wdata_ram) <= (others => '0');
         S_Wdata_ram_int(BITSIZE_S_Wdata_ram-1 downto 0) <= S_Wdata_ram(BITSIZE_S_Wdata_ram-1 downto 0);
      else
         S_Wdata_ram_int <= S_Wdata_ram(2*BRAM_BITSIZE-1 downto 0);
      end if;
   end process;

   L14 : process(proxy_sel_STORE, proxy_in1, byte_offset, sel_STORE, in1, S_Wdata_ram_int)
   begin
      if(PRIVATE_MEMORY=0) then
         if(proxy_sel_STORE /= '0') then
            din_value_aggregated <= std_logic_vector(shift_left(resize(unsigned(proxy_in1(BITSIZE_proxy_in1-1 downto 0)), 2*BRAM_BITSIZE), to_integer(unsigned(byte_offset(nbits_byte_offset-1 downto 0)))*8));
         else
            if(sel_STORE /= '0') then
               din_value_aggregated <= std_logic_vector(shift_left(resize(unsigned((in1(BITSIZE_in1-1 downto 0))), 2*BRAM_BITSIZE), to_integer(unsigned(byte_offset(nbits_byte_offset-1 downto 0)))*8));
            else
               din_value_aggregated <= std_logic_vector(shift_left(resize(unsigned(S_Wdata_ram_int), 2*BRAM_BITSIZE), to_integer(unsigned(byte_offset(nbits_byte_offset-1 downto 0)))*8));
            end if;
         end if;
      else
         if(proxy_sel_STORE /= '0') then
            din_value_aggregated <= std_logic_vector(shift_left(resize(unsigned(proxy_in1(BITSIZE_proxy_in1-1 downto 0)), 2*BRAM_BITSIZE), to_integer(unsigned(byte_offset(nbits_byte_offset-1 downto 0)))*8));
         else
            din_value_aggregated <= std_logic_vector(shift_left(resize(unsigned(in1(BITSIZE_in1-1 downto 0)), 2*BRAM_BITSIZE), to_integer(unsigned(byte_offset(nbits_byte_offset-1 downto 0)))*8));
         end if;
      end if;
   end process;


   out1_shifted <= std_logic_vector(shift_right(unsigned(dout), to_integer(unsigned(delayed_byte_offset(nbits_byte_offset-1 downto 0)))*8));

   out1 <= std_logic_vector(resize(unsigned(out1_shifted(2*BRAM_BITSIZE-1 downto 0)), BITSIZE_out1));
   proxy_out1 <= std_logic_vector(resize(unsigned(out1_shifted(2*BRAM_BITSIZE-1 downto 0)), BITSIZE_proxy_out1));

   L16 : process(reset, clock)
   begin
      if(1RESET_VALUE) then
         oe_ram_cs_delayed <= '0';
         if(HIGH_LATENCY /= 0) then
            oe_ram_cs_delayed_registered <= '0';
         end if;
         if(HIGH_LATENCY = 2) then
            oe_ram_cs_delayed_registered1 <= '0';
         end if;
      else
         if (clock'event and clock = '1') then
            if(HIGH_LATENCY = 0) then
               if(BUS_PIPELINED /= 0) then
                  oe_ram_cs_delayed <= oe_ram_cs;
               else
                  oe_ram_cs_delayed <= oe_ram_cs and (not oe_ram_cs_delayed);
               end if;
            elsif(HIGH_LATENCY = 1) then
               if(BUS_PIPELINED /= 0) then
                  oe_ram_cs_delayed_registered <= oe_ram_cs;
               else
                  oe_ram_cs_delayed_registered <= oe_ram_cs and ((not oe_ram_cs_delayed_registered and not oe_ram_cs_delayed));
               end if;
               oe_ram_cs_delayed <= oe_ram_cs_delayed_registered;
            else
               if(BUS_PIPELINED /= 0) then
                  oe_ram_cs_delayed_registered1 <= oe_ram_cs;
               else
                  oe_ram_cs_delayed_registered1 <= oe_ram_cs and ((not oe_ram_cs_delayed_registered1 and not oe_ram_cs_delayed_registered and not oe_ram_cs_delayed));
               end if;
               oe_ram_cs_delayed_registered <= oe_ram_cs_delayed_registered1;
               oe_ram_cs_delayed <= oe_ram_cs_delayed_registered;
            end if;
         end if;
      end if;
   end process;

   process(clock)
   begin
      if (clock'event and clock='1') then
         if(HIGH_LATENCY = 0) then
            delayed_byte_offset <= byte_offset;
         elsif(HIGH_LATENCY = 1) then
            delayed_byte_offset_registered <= byte_offset;
            delayed_byte_offset <= delayed_byte_offset_registered;
         else
            delayed_byte_offset_registered1 <= byte_offset;
            delayed_byte_offset_registered <= delayed_byte_offset_registered1;
            delayed_byte_offset <= delayed_byte_offset_registered;
         end if;
      end if;
   end process;

   process(clock, reset)
   begin
      if(1RESET_VALUE) then
         we_ram_cs_delayed <= '0';
      else
         if (clock'event and clock = '1') then
            we_ram_cs_delayed <= we_ram_cs and not we_ram_cs_delayed;
         end if;
      end if;
   end process;

   L18 : process(Sin_Rdata_ram, out1_shifted, oe_ram_cs_delayed)
   begin
      if(PRIVATE_MEMORY=1) then
         Sout_Rdata_ram(BITSIZE_Sout_Rdata_ram-1 downto 0) <= Sin_Rdata_ram(BITSIZE_Sin_Rdata_ram-1 downto 0);
      else
         if (BITSIZE_Sout_Rdata_ram <= 2*BRAM_BITSIZE) then
            if(oe_ram_cs_delayed /= '0') then
               Sout_Rdata_ram <= out1_shifted(BITSIZE_Sout_Rdata_ram-1 downto 0);
            else
               Sout_Rdata_ram <= Sin_Rdata_ram;
            end if;
         else
            if(oe_ram_cs_delayed /= '0') then
               Sout_Rdata_ram <= std_logic_vector(resize(unsigned(out1_shifted), BITSIZE_Sout_Rdata_ram));
            else
               Sout_Rdata_ram <= Sin_Rdata_ram;
            end if;
         end if;
      end if;
   end process;

   L19 : process(oe_ram_cs_delayed, Sin_DataRdy, we_ram_cs_delayed)
   begin
      if(PRIVATE_MEMORY=0) then
         Sout_DataRdy <= oe_ram_cs_delayed or Sin_DataRdy or we_ram_cs_delayed;
      else
         Sout_DataRdy <= Sin_DataRdy;
      end if;
   end process;

   bram_write <= sel_STORE or proxy_sel_STORE or we_ram_cs;

NP_functionality.VERILOG_PROVIDED:
`ifndef _SIM_HAVE_CLOG2
  function integer log2;
     input integer value;
     integer temp_value;
    begin
      temp_value = value-1;
      for (log2=0; temp_value>0; log2=log2+1)
        temp_value = temp_value>>1;
    end
  endfunction
`endif
`ifdef _SIM_HAVE_CLOG2
  localparam nbit_addr = BITSIZE_S_addr_ram/*n_bytes ==  1 ? 1 : $clog2(n_bytes)*/;
  localparam nbits_byte_offset = n_byte_on_databus==1 ? 1 : $clog2(n_byte_on_databus);
  localparam nbits_address_space_rangesize = $clog2(address_space_rangesize);
`else
  localparam nbit_addr = BITSIZE_S_addr_ram/*n_bytes ==  1 ? 1 : log2(n_bytes)*/;
  localparam nbits_address_space_rangesize = log2(address_space_rangesize);
  localparam nbits_byte_offset = n_byte_on_databus==1 ? 1 : log2(n_byte_on_databus);
`endif
 localparam memory_bitsize = 2*BRAM_BITSIZE;

function [n_byte_on_databus*max_n_writes-1:0] CONV;
  input [n_byte_on_databus*max_n_writes-1:0] po2;
begin
  case (po2)
    1:CONV=(1<<1)-1;
    2:CONV=(1<<2)-1;
    4:CONV=(1<<4)-1;
    8:CONV=(1<<8)-1;
    16:CONV=(1<<16)-1;
    32:CONV=(1<<32)-1;
    default:CONV=-1;
  endcase
end
endfunction

wire [(PORTSIZE_in2*BITSIZE_in2)+(-1):0] tmp_addr;
wire [n_byte_on_databus*max_n_writes-1:0] conv_in;
wire [n_byte_on_databus*max_n_writes-1:0] conv_out;
wire [PORTSIZE_S_addr_ram-1:0] cs;
wire [PORTSIZE_S_oe_ram-1:0] oe_ram_cs;
wire [PORTSIZE_S_we_ram-1:0] we_ram_cs;
wire [nbit_addr*max_n_rw-1:0] relative_addr;
wire [memory_bitsize*max_n_writes-1:0] din_value_aggregated;
wire [memory_bitsize*PORTSIZE_S_Wdata_ram-1:0] S_Wdata_ram_int;
wire [memory_bitsize*max_n_reads-1:0] out1_shifted;
wire [memory_bitsize*max_n_reads-1:0] dout;
wire [nbits_byte_offset*max_n_rw-1:0] byte_offset;
wire [n_byte_on_databus*max_n_writes-1:0] be;

reg [PORTSIZE_S_we_ram-1:0] we_ram_cs_delayed;
reg [PORTSIZE_S_oe_ram-1:0] oe_ram_cs_delayed;
reg [PORTSIZE_S_oe_ram-1:0] oe_ram_cs_delayed_registered;
reg [PORTSIZE_S_oe_ram-1:0] oe_ram_cs_delayed_registered1;
reg [max_n_reads-1:0] delayed_swapped_bit;
reg [max_n_reads-1:0] delayed_swapped_bit_registered;
reg [max_n_reads-1:0] delayed_swapped_bit_registered1;
reg [nbits_byte_offset*max_n_reads-1:0] delayed_byte_offset;
reg [nbits_byte_offset*max_n_reads-1:0] delayed_byte_offset_registered;
reg [nbits_byte_offset*max_n_reads-1:0] delayed_byte_offset_registered1;

generate
genvar ind2;
for (ind2=0; ind2<PORTSIZE_in2; ind2=ind2+1)
  begin : Lind2
    assign tmp_addr[(ind2+1)*BITSIZE_in2-1:ind2*BITSIZE_in2] = (proxy_sel_LOAD[ind2]||proxy_sel_STORE[ind2]) ? proxy_in2[(ind2+1)*BITSIZE_proxy_in2-1:ind2*BITSIZE_proxy_in2] : in2[(ind2+1)*BITSIZE_in2-1:ind2*BITSIZE_in2];
  end
endgenerate

generate
genvar i2;
  for (i2=0;i2<max_n_reads;i2=i2+1)
  begin : L_copy
      assign dout[(memory_bitsize/2)+memory_bitsize*i2-1:memory_bitsize*i2] = delayed_swapped_bit[i2] ? dout_a[(memory_bitsize/2)*(i2+1)-1:(memory_bitsize/2)*i2] : dout_b[(memory_bitsize/2)*(i2+1)-1:(memory_bitsize/2)*i2];
      assign dout[memory_bitsize*(i2+1)-1:memory_bitsize*i2+(memory_bitsize/2)] = delayed_swapped_bit[i2] ? dout_b[(memory_bitsize/2)*(i2+1)-1:(memory_bitsize/2)*i2] : dout_a[(memory_bitsize/2)*(i2+1)-1:(memory_bitsize/2)*i2];
      always @(posedge clock)
      begin
        if(HIGH_LATENCY == 0)
          delayed_swapped_bit[i2] <= !relative_addr[nbits_byte_offset+i2*nbit_addr-1];
        else if(HIGH_LATENCY == 1)
        begin
          delayed_swapped_bit_registered[i2] <= !relative_addr[nbits_byte_offset+i2*nbit_addr-1];
          delayed_swapped_bit[i2] <= delayed_swapped_bit_registered[i2];
        end
        else
        begin
          delayed_swapped_bit_registered1[i2] <= !relative_addr[nbits_byte_offset+i2*nbit_addr-1];
          delayed_swapped_bit_registered[i2] <= delayed_swapped_bit_registered1[i2];
          delayed_swapped_bit[i2] <= delayed_swapped_bit_registered[i2];
        end
      end
  end
endgenerate

generate
genvar i3;
  for (i3=0; i3<PORTSIZE_S_addr_ram; i3=i3+1)
  begin : L3
    if(PRIVATE_MEMORY==0 && USE_SPARSE_MEMORY==0)
      assign cs[i3] = (S_addr_ram[(i3+1)*BITSIZE_S_addr_ram-1:i3*BITSIZE_S_addr_ram] >= (address_space_begin)) && (S_addr_ram[(i3+1)*BITSIZE_S_addr_ram-1:i3*BITSIZE_S_addr_ram] < (address_space_begin+address_space_rangesize));
    else if(PRIVATE_MEMORY==0 && nbits_address_space_rangesize < 32)
      assign cs[i3] = S_addr_ram[(i3+1)*BITSIZE_S_addr_ram-1:i3*BITSIZE_S_addr_ram+nbits_address_space_rangesize] == address_space_begin[((nbit_addr-1) < 32 ? (nbit_addr-1) : 31):nbits_address_space_rangesize];
    else
      assign cs[i3] = 1'b0;
  end
endgenerate

generate
genvar i4;
  for (i4=0; i4<PORTSIZE_S_oe_ram; i4=i4+1)
  begin : L4
    assign oe_ram_cs[i4] = S_oe_ram[i4] & cs[i4];
  end
endgenerate

generate
genvar i5;
  for (i5=0; i5<PORTSIZE_S_we_ram; i5=i5+1)
  begin : L5
    assign we_ram_cs[i5] = S_we_ram[i5] & cs[i5];
  end
endgenerate

generate
genvar i6;
  for (i6=0; i6<max_n_rw; i6=i6+1)
  begin : L6
    if(PRIVATE_MEMORY==0 && USE_SPARSE_MEMORY==0 && i6< PORTSIZE_S_addr_ram)
      assign relative_addr[(i6+1)*nbit_addr-1:i6*nbit_addr] = ((i6 < max_n_writes && (sel_STORE[i6]==1'b1 || proxy_sel_STORE[i6]==1'b1)) || (i6 < max_n_reads && (sel_LOAD[i6]==1'b1 || proxy_sel_LOAD[i6]==1'b1))) ? tmp_addr[(i6+1)*BITSIZE_in2-1:i6*BITSIZE_in2]-address_space_begin: S_addr_ram[(i6+1)*BITSIZE_S_addr_ram-1:i6*BITSIZE_S_addr_ram]-address_space_begin;
    else if(PRIVATE_MEMORY==0 && i6< PORTSIZE_S_addr_ram)
      assign relative_addr[(i6)*nbit_addr+nbits_address_space_rangesize-1:i6*nbit_addr] = ((i6 < max_n_writes && (sel_STORE[i6]==1'b1 || proxy_sel_STORE[i6]==1'b1)) || (i6 < max_n_reads && (sel_LOAD[i6]==1'b1 || proxy_sel_LOAD[i6]==1'b1))) ? tmp_addr[(i6)*BITSIZE_in2+nbits_address_space_rangesize-1:i6*BITSIZE_in2] : S_addr_ram[(i6)*BITSIZE_S_addr_ram+nbits_address_space_rangesize-1:i6*BITSIZE_S_addr_ram];
    else if(USE_SPARSE_MEMORY==1)
      assign relative_addr[(i6)*nbit_addr+nbits_address_space_rangesize-1:i6*nbit_addr] = tmp_addr[(i6)*BITSIZE_in2+nbits_address_space_rangesize-1:i6*BITSIZE_in2];
    else
      assign relative_addr[(i6+1)*nbit_addr-1:i6*nbit_addr] = tmp_addr[(i6+1)*BITSIZE_in2-1:i6*BITSIZE_in2]-address_space_begin;
  end
endgenerate

generate
genvar i7;
  for (i7=0; i7<max_n_rw; i7=i7+1)
  begin : L7_A
    if (n_elements==1)
      assign memory_addr_a[(i7+1)*nbit_read_addr-1:i7*nbit_read_addr] = {nbit_read_addr{1'b0}};
    else
      assign memory_addr_a[(i7+1)*nbit_read_addr-1:i7*nbit_read_addr] = !relative_addr[nbits_byte_offset+i7*nbit_addr-1] ? relative_addr[nbit_read_addr+nbits_byte_offset-1+i7*nbit_addr:nbits_byte_offset+i7*nbit_addr] : (relative_addr[nbit_read_addr+nbits_byte_offset-1+i7*nbit_addr:nbits_byte_offset+i7*nbit_addr-1]+ 1'b1) >> 1;
  end
endgenerate

generate
  for (i7=0; i7<max_n_rw; i7=i7+1)
  begin : L7_B
    if (n_elements==1)
      assign memory_addr_b[(i7+1)*nbit_read_addr-1:i7*nbit_read_addr] = {nbit_read_addr{1'b0}};
    else
      assign memory_addr_b[(i7+1)*nbit_read_addr-1:i7*nbit_read_addr] = !relative_addr[nbits_byte_offset+i7*nbit_addr-1] ? (relative_addr[nbit_read_addr+nbits_byte_offset-1+i7*nbit_addr:nbits_byte_offset+i7*nbit_addr-1] + 1'b1) >> 1 : relative_addr[nbit_read_addr+nbits_byte_offset-1+i7*nbit_addr:nbits_byte_offset+i7*nbit_addr];
  end
endgenerate

generate
genvar i8;
  for (i8=0; i8<max_n_rw; i8=i8+1)
  begin : L8
    if (n_byte_on_databus==2)
      assign byte_offset[(i8+1)*nbits_byte_offset-1:i8*nbits_byte_offset] = {nbits_byte_offset{1'b0}};
    else
      assign byte_offset[(i8+1)*nbits_byte_offset-1:i8*nbits_byte_offset] = {1'b0, relative_addr[nbits_byte_offset+i8*nbit_addr-2:i8*nbit_addr]};
  end
endgenerate

generate
genvar i9, i10;
  for (i9=0; i9<max_n_writes; i9=i9+1)
  begin : byte_enable
    if(PRIVATE_MEMORY==0 && i9 < PORTSIZE_S_data_ram_size)
    begin
      assign conv_in[(i9+1)*n_byte_on_databus-1:i9*n_byte_on_databus] = proxy_sel_STORE[i9] ? proxy_in3[BITSIZE_proxy_in3+BITSIZE_proxy_in3*i9-1:3+BITSIZE_proxy_in3*i9] : (sel_STORE[i9] ? in3[BITSIZE_in3+BITSIZE_in3*i9-1:3+BITSIZE_in3*i9] : S_data_ram_size[BITSIZE_S_data_ram_size+BITSIZE_S_data_ram_size*i9-1:3+BITSIZE_S_data_ram_size*i9]);
      assign conv_out[(i9+1)*n_byte_on_databus-1:i9*n_byte_on_databus] = CONV(conv_in[(i9+1)*n_byte_on_databus-1:i9*n_byte_on_databus]);
      assign be[(i9+1)*n_byte_on_databus-1:i9*n_byte_on_databus] = conv_out[(i9+1)*n_byte_on_databus-1:i9*n_byte_on_databus] << byte_offset[(i9+1)*nbits_byte_offset-1:i9*nbits_byte_offset];
    end
    else
    begin
      assign conv_in[(i9+1)*n_byte_on_databus-1:i9*n_byte_on_databus] = proxy_sel_STORE[i9] ? proxy_in3[BITSIZE_proxy_in3+BITSIZE_proxy_in3*i9-1:3+BITSIZE_proxy_in3*i9] : in3[BITSIZE_in3+BITSIZE_in3*i9-1:3+BITSIZE_in3*i9];
      assign conv_out[(i9+1)*n_byte_on_databus-1:i9*n_byte_on_databus] = CONV(conv_in[(i9+1)*n_byte_on_databus-1:i9*n_byte_on_databus]);
      assign be[(i9+1)*n_byte_on_databus-1:i9*n_byte_on_databus] = conv_out[(i9+1)*n_byte_on_databus-1:i9*n_byte_on_databus] << byte_offset[(i9+1)*nbits_byte_offset-1:i9*nbits_byte_offset];
    end
  end
endgenerate

generate
  for (i9=0; i9<max_n_writes; i9=i9+1)
  begin : L9_swapped
    for (i10=0; i10<n_byte_on_databus/2; i10=i10+1)
    begin  : byte_enable_swapped
      assign be_swapped[i10+i9*n_byte_on_databus] = !relative_addr[nbits_byte_offset+i9*nbit_addr-1] ? be[i10+i9*n_byte_on_databus] : be[i10+i9*n_byte_on_databus+n_byte_on_databus/2];
      assign be_swapped[i10+i9*n_byte_on_databus+n_byte_on_databus/2] =  !relative_addr[nbits_byte_offset+i9*nbit_addr-1] ? be[i10+i9*n_byte_on_databus+n_byte_on_databus/2] : be[i10+i9*n_byte_on_databus];
    end
  end
endgenerate

generate
genvar i13;
  for (i13=0; i13<PORTSIZE_S_Wdata_ram; i13=i13+1)
  begin : L13
    if (BITSIZE_S_Wdata_ram < memory_bitsize)
      assign S_Wdata_ram_int[memory_bitsize*(i13+1)-1:memory_bitsize*i13] = {{memory_bitsize-BITSIZE_S_Wdata_ram{1'b0}}, S_Wdata_ram[(i13+1)*BITSIZE_S_Wdata_ram-1:BITSIZE_S_Wdata_ram*i13]};
    else
      assign S_Wdata_ram_int[memory_bitsize*(i13+1)-1:memory_bitsize*i13] = S_Wdata_ram[memory_bitsize+BITSIZE_S_Wdata_ram*i13-1:BITSIZE_S_Wdata_ram*i13];
  end
endgenerate

generate
genvar i14;
  for (i14=0; i14<max_n_writes; i14=i14+1)
  begin : L14
    if(PRIVATE_MEMORY==0 && i14 < PORTSIZE_S_Wdata_ram)
      assign din_value_aggregated[(i14+1)*memory_bitsize-1:i14*memory_bitsize] = proxy_sel_STORE[i14] ? proxy_in1[(i14+1)*BITSIZE_proxy_in1-1:i14*BITSIZE_proxy_in1] << byte_offset[(i14+1)*nbits_byte_offset-1:i14*nbits_byte_offset]*8 : (sel_STORE[i14] ? in1[(i14+1)*BITSIZE_in1-1:i14*BITSIZE_in1] << byte_offset[(i14+1)*nbits_byte_offset-1:i14*nbits_byte_offset]*8 : S_Wdata_ram_int[memory_bitsize*(i14+1)-1:memory_bitsize*i14] << byte_offset[(i14+1)*nbits_byte_offset-1:i14*nbits_byte_offset]*8);
    else
      assign din_value_aggregated[(i14+1)*memory_bitsize-1:i14*memory_bitsize] = proxy_sel_STORE[i14] ? proxy_in1[(i14+1)*BITSIZE_proxy_in1-1:i14*BITSIZE_proxy_in1] << byte_offset[(i14+1)*nbits_byte_offset-1:i14*nbits_byte_offset]*8 : in1[(i14+1)*BITSIZE_in1-1:i14*BITSIZE_in1] << byte_offset[(i14+1)*nbits_byte_offset-1:i14*nbits_byte_offset]*8;
  end
endgenerate

generate
  for (i14=0; i14<max_n_writes; i14=i14+1)
  begin : L14_swapped
    assign din_value_aggregated_swapped[(i14)*memory_bitsize+memory_bitsize/2-1:i14*memory_bitsize] = !relative_addr[nbits_byte_offset+i14*nbit_addr-1] ? din_value_aggregated[(i14)*memory_bitsize+memory_bitsize/2-1:i14*memory_bitsize] : din_value_aggregated[(i14+1)*memory_bitsize-1:i14*memory_bitsize+memory_bitsize/2];
    assign din_value_aggregated_swapped[(i14+1)*memory_bitsize-1:i14*memory_bitsize+memory_bitsize/2] = !relative_addr[nbits_byte_offset+i14*nbit_addr-1] ?  din_value_aggregated[(i14+1)*memory_bitsize-1:i14*memory_bitsize+memory_bitsize/2] : din_value_aggregated[(i14)*memory_bitsize+memory_bitsize/2-1:i14*memory_bitsize];
  end
endgenerate

generate
genvar i15;
  for (i15=0; i15<max_n_reads; i15=i15+1)
  begin : L15
    assign out1_shifted[(i15+1)*memory_bitsize-1:i15*memory_bitsize] = dout[(i15+1)*memory_bitsize-1:i15*memory_bitsize] >> delayed_byte_offset[(i15+1)*nbits_byte_offset-1:i15*nbits_byte_offset]*8;
  end
endgenerate

generate
genvar i20;
  for (i20=0; i20<max_n_reads; i20=i20+1)
  begin : L20
    assign out1[(i20+1)*BITSIZE_out1-1:i20*BITSIZE_out1] = out1_shifted[i20*memory_bitsize+BITSIZE_out1-1:i20*memory_bitsize];
    assign proxy_out1[(i20+1)*BITSIZE_proxy_out1-1:i20*BITSIZE_proxy_out1] = out1_shifted[i20*memory_bitsize+BITSIZE_proxy_out1-1:i20*memory_bitsize];
  end
endgenerate

generate
genvar i16;
  for (i16=0; i16<PORTSIZE_S_oe_ram; i16=i16+1)
  begin : L16
    always @(posedge clock 1RESET_EDGE)
    begin
      if(1RESET_VALUE)
        begin
          oe_ram_cs_delayed[i16] <= 1'b0;
          if(HIGH_LATENCY != 0) oe_ram_cs_delayed_registered[i16] <= 1'b0;
          if(HIGH_LATENCY == 2) oe_ram_cs_delayed_registered1[i16] <= 1'b0;
        end
      else
        if(HIGH_LATENCY == 0)
        begin
          oe_ram_cs_delayed[i16] <= oe_ram_cs[i16] & (!oe_ram_cs_delayed[i16] | BUS_PIPELINED);
        end
        else if(HIGH_LATENCY == 1)
        begin
          oe_ram_cs_delayed_registered[i16] <= oe_ram_cs[i16] & ((!oe_ram_cs_delayed_registered[i16] & !oe_ram_cs_delayed[i16]) | BUS_PIPELINED);
          oe_ram_cs_delayed[i16] <= oe_ram_cs_delayed_registered[i16];
        end
        else
        begin
          oe_ram_cs_delayed_registered1[i16] <= oe_ram_cs[i16] & ((!oe_ram_cs_delayed_registered1[i16] & !oe_ram_cs_delayed_registered[i16] & !oe_ram_cs_delayed[i16]) | BUS_PIPELINED);
          oe_ram_cs_delayed_registered[i16] <= oe_ram_cs_delayed_registered1[i16];
          oe_ram_cs_delayed[i16] <= oe_ram_cs_delayed_registered[i16];
        end
      end
    end
endgenerate

always @(posedge clock)
begin
  if(HIGH_LATENCY == 0)
    delayed_byte_offset <= byte_offset[nbits_byte_offset*max_n_reads-1:0];
  else if(HIGH_LATENCY == 1)
  begin
    delayed_byte_offset_registered <= byte_offset[nbits_byte_offset*max_n_reads-1:0];
    delayed_byte_offset <= delayed_byte_offset_registered;
  end
  else
  begin
    delayed_byte_offset_registered1 <= byte_offset[nbits_byte_offset*max_n_reads-1:0];
    delayed_byte_offset_registered <= delayed_byte_offset_registered1;
    delayed_byte_offset <= delayed_byte_offset_registered;
  end
end


generate
genvar i17;
  for (i17=0; i17<PORTSIZE_S_we_ram; i17=i17+1)
  begin : L17
    always @(posedge clock 1RESET_EDGE)
    begin
      if(1RESET_VALUE)
        we_ram_cs_delayed[i17] <= 1'b0;
      else
        we_ram_cs_delayed[i17] <= we_ram_cs[i17] & !we_ram_cs_delayed[i17];
    end
  end
endgenerate

generate
genvar i18;
  for (i18=0; i18<PORTSIZE_Sout_Rdata_ram; i18=i18+1)
  begin : L18
    if(PRIVATE_MEMORY==1)
      assign Sout_Rdata_ram[(i18+1)*BITSIZE_Sout_Rdata_ram-1:i18*BITSIZE_Sout_Rdata_ram] = Sin_Rdata_ram[(i18+1)*BITSIZE_Sin_Rdata_ram-1:i18*BITSIZE_Sin_Rdata_ram];
    else if (BITSIZE_Sout_Rdata_ram <= memory_bitsize)
      assign Sout_Rdata_ram[(i18+1)*BITSIZE_Sout_Rdata_ram-1:i18*BITSIZE_Sout_Rdata_ram] = oe_ram_cs_delayed[i18] ? out1_shifted[BITSIZE_Sout_Rdata_ram+i18*memory_bitsize-1:i18*memory_bitsize] : Sin_Rdata_ram[(i18+1)*BITSIZE_Sin_Rdata_ram-1:i18*BITSIZE_Sin_Rdata_ram];
    else
      assign Sout_Rdata_ram[(i18+1)*BITSIZE_Sout_Rdata_ram-1:i18*BITSIZE_Sout_Rdata_ram] = oe_ram_cs_delayed[i18] ? {{BITSIZE_S_Wdata_ram-memory_bitsize{1'b0}}, out1_shifted[(i18+1)*memory_bitsize-1:i18*memory_bitsize]} : Sin_Rdata_ram[(i18+1)*BITSIZE_Sin_Rdata_ram-1:i18*BITSIZE_Sin_Rdata_ram];
  end
endgenerate

generate
genvar i19;
  for (i19=0; i19<PORTSIZE_Sout_DataRdy; i19=i19+1)
  begin : L19
    if(PRIVATE_MEMORY==0)
      assign Sout_DataRdy[i19] = (i19 < PORTSIZE_S_oe_ram && oe_ram_cs_delayed[i19]) | Sin_DataRdy[i19] | (i19 < PORTSIZE_S_we_ram && we_ram_cs_delayed[i19]);
    else
      assign Sout_DataRdy[i19] = Sin_DataRdy[i19];
  end
endgenerate

generate
genvar i21;
  for (i21=0; i21<PORTSIZE_bram_write; i21=i21+1)
  begin : L21
    if(i21 < PORTSIZE_S_we_ram)
      assign bram_write[i21] = (sel_STORE[i21] || proxy_sel_STORE[i21] || we_ram_cs[i21]);
    else
      assign bram_write[i21] = (sel_STORE[i21] || proxy_sel_STORE[i21]);
  end
  endgenerate

NP_functionality.VHDL_PROVIDED:
function integer_ternary_operator(cond : boolean; op1 : integer; op2 : integer) return integer is
   begin
      if cond then
         return op1;
      else
         return op2;
      end if;
   end integer_ternary_operator;

function CONV(po2 : std_logic_vector(n_byte_on_databus-1 downto 0)) return std_logic_vector is
  variable int_po2 : integer;
  begin
      int_po2 := to_integer(unsigned(po2));
      case int_po2 is
         when 1 =>
            return std_logic_vector(shift_left(to_unsigned(1,n_byte_on_databus),1) -1);
         when 2 =>
            return std_logic_vector(shift_left(to_unsigned(1,n_byte_on_databus),2) -1);
         when 4 =>
            return std_logic_vector(shift_left(to_unsigned(1,n_byte_on_databus),4) -1);
         when 8 =>
            return std_logic_vector(shift_left(to_unsigned(1,n_byte_on_databus),8) -1);
         when 16 =>
            return std_logic_vector(shift_left(to_unsigned(1,n_byte_on_databus),16) -1);
         when 32 =>
            return std_logic_vector(shift_left(to_unsigned(1,n_byte_on_databus),32) -1);
         when others =>
            return std_logic_vector(to_signed(-1,n_byte_on_databus));
      end case;
   end CONV;

   constant nbit_addr                     : integer := BITSIZE_S_addr_ram;
   constant nbits_address_space_rangesize : integer := integer(ceil(log2(real(address_space_rangesize))));
   constant nbits_byte_offset             : integer := integer_ternary_operator(n_byte_on_databus = 1, 1, integer(ceil(log2(real(n_byte_on_databus)))));
   constant memory_bitsize                : integer := 2 * BRAM_BITSIZE;
   constant address_space_begin_u         : unsigned(nbit_addr-1 downto 0) := to_unsigned(address_space_begin, nbit_addr);

   signal tmp_addr                       : std_logic_vector((PORTSIZE_in2*BITSIZE_in2)+(-1) downto 0);
   signal conv_in                        : std_logic_vector(n_byte_on_databus*max_n_writes-1 downto 0);
   signal conv_out                       : std_logic_vector(n_byte_on_databus*max_n_writes-1 downto 0);
   signal cs                             : std_logic_vector(PORTSIZE_S_addr_ram-1 downto 0);
   signal oe_ram_cs                      : std_logic_vector(PORTSIZE_S_oe_ram-1 downto 0);
   signal we_ram_cs                      : std_logic_vector(PORTSIZE_S_we_ram-1 downto 0);
   signal relative_addr                  : std_logic_vector(nbit_addr*max_n_rw-1 downto 0);
   signal din_value_aggregated           : std_logic_vector(memory_bitsize*max_n_writes-1 downto 0);
   signal S_Wdata_ram_int                : std_logic_vector(memory_bitsize*PORTSIZE_S_Wdata_ram-1 downto 0);
   signal out1_shifted                   : std_logic_vector(memory_bitsize*max_n_reads-1 downto 0);
   signal dout                           : std_logic_vector(memory_bitsize*max_n_reads-1 downto 0);
   signal byte_offset                    : std_logic_vector(nbits_byte_offset*max_n_rw-1 downto 0);
   signal be                             : std_logic_vector(n_byte_on_databus*max_n_writes-1 downto 0);

   signal we_ram_cs_delayed              : std_logic_vector(PORTSIZE_S_we_ram-1 downto 0);
   signal oe_ram_cs_delayed              : std_logic_vector(PORTSIZE_S_oe_ram-1 downto 0);
   signal oe_ram_cs_delayed_registered   : std_logic_vector(PORTSIZE_S_oe_ram-1 downto 0);
   signal oe_ram_cs_delayed_registered1   : std_logic_vector(PORTSIZE_S_oe_ram-1 downto 0);
   signal delayed_swapped_bit            : std_logic_vector(max_n_reads-1 downto 0);
   signal delayed_swapped_bit_registered : std_logic_vector(max_n_reads-1 downto 0);
   signal delayed_swapped_bit_registered1 : std_logic_vector(max_n_reads-1 downto 0);
   signal delayed_byte_offset            : std_logic_vector(nbits_byte_offset*max_n_reads-1 downto 0);
   signal delayed_byte_offset_registered : std_logic_vector(nbits_byte_offset*max_n_reads-1 downto 0);
   signal delayed_byte_offset_registered1 : std_logic_vector(nbits_byte_offset*max_n_reads-1 downto 0);
begin
   process(proxy_sel_LOAD, proxy_sel_STORE, proxy_in2, in2)
   begin
      for ind2 in 0 to PORTSIZE_in2-1 loop
         if(proxy_sel_LOAD(ind2) = '1' or proxy_sel_STORE(ind2) = '1') then
            tmp_addr((ind2+1)*BITSIZE_in2-1 downto ind2*BITSIZE_in2) <= proxy_in2((ind2+1)*BITSIZE_proxy_in2-1  downto ind2*BITSIZE_proxy_in2);
         else
            tmp_addr((ind2+1)*BITSIZE_in2-1 downto ind2*BITSIZE_in2) <= in2((ind2+1)*BITSIZE_in2-1 downto ind2*BITSIZE_in2);
         end if;
      end loop;
   end process;

   L_delayed_swapped_bit : process(clock)
   begin
      if (clock'event and clock='1') then
         for i2 in 0 to max_n_reads-1 loop
            if(HIGH_LATENCY = 0) then
               delayed_swapped_bit(i2) <= not relative_addr(nbits_byte_offset+i2*nbit_addr-1);
            elsif(HIGH_LATENCY = 1) then
               delayed_swapped_bit_registered(i2) <= not relative_addr(nbits_byte_offset+i2*nbit_addr-1);
               delayed_swapped_bit(i2) <= delayed_swapped_bit_registered(i2);
            else
               delayed_swapped_bit_registered1(i2) <= not relative_addr(nbits_byte_offset+i2*nbit_addr-1);
               delayed_swapped_bit_registered(i2) <= delayed_swapped_bit_registered1(i2);
               delayed_swapped_bit(i2) <= delayed_swapped_bit_registered(i2);
            end if;
         end loop;
      end if;
   end process;

   L_dout : process(dout_a, dout_b, delayed_swapped_bit)
   begin
      for i2 in 0 to max_n_reads-1 loop
         if(delayed_swapped_bit(i2) /= '0') then
            dout((memory_bitsize/2)+memory_bitsize*i2-1 downto memory_bitsize*i2) <= dout_a((memory_bitsize/2)*(i2+1)-1 downto (memory_bitsize/2)*i2);
         else
            dout((memory_bitsize/2)+memory_bitsize*i2-1 downto memory_bitsize*i2) <= dout_b((memory_bitsize/2)*(i2+1)-1 downto (memory_bitsize/2)*i2);
         end if;
         if(delayed_swapped_bit(i2) /= '0') then
            dout(memory_bitsize*(i2+1)-1 downto memory_bitsize*i2+(memory_bitsize/2)) <= dout_b((memory_bitsize/2)*(i2+1)-1 downto (memory_bitsize/2)*i2);
         else
            dout(memory_bitsize*(i2+1)-1 downto memory_bitsize*i2+(memory_bitsize/2)) <= dout_a((memory_bitsize/2)*(i2+1)-1 downto (memory_bitsize/2)*i2);
         end if;
      end loop;
   end process;

   L3 : process(S_addr_ram)
   begin
      for i3 in 0 to PORTSIZE_S_addr_ram-1 loop
         if(PRIVATE_MEMORY = 0 and USE_SPARSE_MEMORY = 0) then
            if(to_integer(unsigned(S_addr_ram((i3+1)*BITSIZE_S_addr_ram-1 downto i3*BITSIZE_S_addr_ram))) >= (address_space_begin)) and (to_integer(unsigned(S_addr_ram((i3+1)*BITSIZE_S_addr_ram-1 downto i3*BITSIZE_S_addr_ram))) < (address_space_begin+address_space_rangesize)) then
               cs(i3) <= '1';
            else
               cs(i3) <= '0';
            end if;
         else
            if(PRIVATE_MEMORY=0) then
               if S_addr_ram((i3+1)*BITSIZE_S_addr_ram-1 downto i3*BITSIZE_S_addr_ram+nbits_address_space_rangesize) = std_logic_vector(address_space_begin_u(nbit_addr-1 downto nbits_address_space_rangesize)) then
                  cs(i3) <= '1';
               else
                  cs(i3) <= '0';
               end if;
            else
               cs(i3) <= '0';
            end if;
         end if;
      end loop;
   end process;

   L4 : process(S_oe_ram, cs)
   begin
      for i4 in 0 to PORTSIZE_S_oe_ram-1 loop
         oe_ram_cs(i4) <= S_oe_ram(i4) and cs(i4);
      end loop;
   end process;

   L5 : process(S_we_ram, cs)
   begin
      for i5 in 0 to PORTSIZE_S_we_ram-1 loop
         we_ram_cs(i5) <= S_we_ram(i5) and cs(i5);
      end loop;
   end process;

   L6 : process(sel_STORE, proxy_sel_STORE, sel_LOAD, proxy_sel_LOAD, tmp_addr, S_addr_ram)
   begin
      for i6 in 0 to max_n_rw-1 loop
         if(PRIVATE_MEMORY=0 and USE_SPARSE_MEMORY=0 and i6< PORTSIZE_S_addr_ram) then
            if((i6 < max_n_writes and (sel_STORE(i6) = '1'  or proxy_sel_STORE(i6) = '1')) or (i6 < max_n_reads and (sel_LOAD(i6) = '1' or proxy_sel_LOAD(i6) = '1'))) then
               relative_addr((i6+1)*nbit_addr-1 downto i6*nbit_addr) <= std_logic_vector(unsigned(tmp_addr((i6+1)*BITSIZE_in2-1 downto i6*BITSIZE_in2))-(to_unsigned(address_space_begin, nbit_addr)));
            else
               relative_addr((i6+1)*nbit_addr-1 downto i6*nbit_addr) <= std_logic_vector(unsigned(S_addr_ram((i6+1)*BITSIZE_S_addr_ram-1 downto i6*BITSIZE_S_addr_ram)) - (to_unsigned(address_space_begin, nbit_addr)));
            end if;
         else
            if(PRIVATE_MEMORY=0 and i6< PORTSIZE_S_addr_ram) then
               if((i6 < max_n_writes and (sel_STORE(i6) = '1' or proxy_sel_STORE(i6) ='1' )) or (i6 < max_n_reads and (sel_LOAD(i6)='1' or proxy_sel_LOAD(i6)='1'))) then
                  relative_addr((i6)*nbit_addr+nbits_address_space_rangesize-1 downto i6*nbit_addr) <= tmp_addr((i6)*BITSIZE_in2+nbits_address_space_rangesize-1 downto i6*BITSIZE_in2);
                  relative_addr((i6+1)*nbit_addr-1 downto (i6)*nbit_addr+nbits_address_space_rangesize) <= (others => '0');
               else
                  relative_addr((i6)*nbit_addr+nbits_address_space_rangesize-1 downto i6*nbit_addr) <= S_addr_ram((i6)*BITSIZE_S_addr_ram+nbits_address_space_rangesize-1 downto i6*BITSIZE_S_addr_ram);
                  relative_addr((i6+1)*nbit_addr-1 downto (i6)*nbit_addr+nbits_address_space_rangesize) <= (others => '0');
               end if;
            else
               if(USE_SPARSE_MEMORY=1) then
                  relative_addr((i6)*nbit_addr+nbits_address_space_rangesize-1 downto i6*nbit_addr) <= tmp_addr((i6)*BITSIZE_in2+nbits_address_space_rangesize-1 downto i6*BITSIZE_in2);
                  relative_addr((i6+1)*nbit_addr-1 downto (i6)*nbit_addr+nbits_address_space_rangesize) <= (others => '0');
               else
                  relative_addr((i6+1)*nbit_addr-1 downto i6*nbit_addr) <= std_logic_vector(unsigned(tmp_addr((i6+1)*BITSIZE_in2-1 downto i6*BITSIZE_in2)) - to_unsigned(address_space_begin, nbit_addr));
               end if;
            end if;
         end if;
      end loop;
   end process;

   L7_A : process(relative_addr)
   begin
      for i7 in 0 to max_n_rw-1 loop
         if (n_elements=1) then
             memory_addr_a((i7+1)*nbit_read_addr-1 downto i7*nbit_read_addr) <= (others => '0');
         else
            if(relative_addr(nbits_byte_offset+i7*nbit_addr-1) = '0') then
               memory_addr_a((i7+1)*nbit_read_addr-1 downto i7*nbit_read_addr) <= relative_addr(nbit_read_addr+nbits_byte_offset-1+i7*nbit_addr downto nbits_byte_offset+i7*nbit_addr);
            else
               memory_addr_a((i7+1)*nbit_read_addr-1 downto i7*nbit_read_addr) <= std_logic_vector(resize(shift_right(unsigned(relative_addr(nbit_read_addr+nbits_byte_offset-1+i7*nbit_addr downto nbits_byte_offset+i7*nbit_addr-1)) + 1, 1), nbit_read_addr));
               end if;
         end if;
      end loop;
   end process;

   L7_B : process(relative_addr)
   begin
      for i7 in 0 to max_n_rw-1 loop
         if (n_elements=1) then
            memory_addr_b((i7+1)*nbit_read_addr-1 downto i7*nbit_read_addr) <= (others => '0');
         else
            if(relative_addr(nbits_byte_offset+i7*nbit_addr-1) = '0') then
               memory_addr_b((i7+1)*nbit_read_addr-1 downto i7*nbit_read_addr) <= std_logic_vector(resize(shift_right(unsigned(relative_addr(nbit_read_addr+nbits_byte_offset-1+i7*nbit_addr downto nbits_byte_offset+i7*nbit_addr-1)) + 1, 1), nbit_read_addr));
            else
               memory_addr_b((i7+1)*nbit_read_addr-1 downto i7*nbit_read_addr) <= relative_addr(nbit_read_addr+nbits_byte_offset-1+i7*nbit_addr downto nbits_byte_offset+i7*nbit_addr);
            end if;
         end if;
      end loop;
   end process;

   L8 : process(relative_addr)
   begin
      for i8 in 0 to max_n_rw-1 loop
         if (n_byte_on_databus=2) then
            byte_offset((i8+1)*nbits_byte_offset-1 downto i8*nbits_byte_offset) <= (others => '0');
         else
            byte_offset((i8+1)*nbits_byte_offset-1 downto i8*nbits_byte_offset) <= '0' & relative_addr(nbits_byte_offset+i8*nbit_addr-2 downto i8*nbit_addr);
         end if;
      end loop;
   end process;

   be_conv_in : process(proxy_sel_STORE, proxy_in3, sel_STORE, in3, S_data_ram_size)
   begin
      for i9 in 0 to max_n_writes-1 loop
         if(PRIVATE_MEMORY=0 and i9 < PORTSIZE_S_data_ram_size) then
            if(proxy_sel_STORE(i9) /= '0') then
               conv_in((i9+1)*n_byte_on_databus-1 downto i9*n_byte_on_databus) <= std_logic_vector(resize(unsigned(proxy_in3(BITSIZE_proxy_in3+BITSIZE_proxy_in3*i9-1 downto 3+BITSIZE_proxy_in3*i9)), n_byte_on_databus));
            else
               if(sel_STORE(i9) /= '0') then
                  conv_in((i9+1)*n_byte_on_databus-1 downto i9*n_byte_on_databus) <= std_logic_vector(resize(unsigned(in3(BITSIZE_in3+BITSIZE_in3*i9-1 downto 3+BITSIZE_in3*i9)), n_byte_on_databus));
               else
                  conv_in((i9+1)*n_byte_on_databus-1 downto i9*n_byte_on_databus) <= std_logic_vector(resize(unsigned(S_data_ram_size(BITSIZE_S_data_ram_size+BITSIZE_S_data_ram_size*i9-1 downto 3+BITSIZE_S_data_ram_size*i9)), n_byte_on_databus));
               end if;
            end if;
         else
            if(proxy_sel_STORE(i9) /= '0') then
               conv_in((i9+1)*n_byte_on_databus-1 downto i9*n_byte_on_databus) <= std_logic_vector(resize(unsigned(proxy_in3(BITSIZE_proxy_in3+BITSIZE_proxy_in3*i9-1 downto 3+BITSIZE_proxy_in3*i9)), n_byte_on_databus));
            else
               conv_in((i9+1)*n_byte_on_databus-1 downto i9*n_byte_on_databus) <= std_logic_vector(resize(unsigned(in3(BITSIZE_in3+BITSIZE_in3*i9-1 downto 3+BITSIZE_in3*i9)), n_byte_on_databus));
            end if;
         end  if;
      end loop;
   end process;

   be_conv_out : process(conv_in)
   begin
      for i9 in 0 to max_n_writes-1 loop
         if(PRIVATE_MEMORY=0 and i9 < PORTSIZE_S_data_ram_size) then
            conv_out((i9+1)*n_byte_on_databus-1 downto i9*n_byte_on_databus) <= std_logic_vector(
               resize(
                  unsigned(
                     CONV(
                        conv_in((i9+1)*n_byte_on_databus-1 downto i9*n_byte_on_databus)
                     )
                  ),
                  n_byte_on_databus
               )
            );
         else
            conv_out((i9+1)*n_byte_on_databus-1 downto i9*n_byte_on_databus) <= std_logic_vector(
               resize(
                  unsigned(
                     CONV(
                        conv_in((i9+1)*n_byte_on_databus-1 downto i9*n_byte_on_databus)
                     )
                  ),
                  n_byte_on_databus
               )
            );
         end  if;
      end loop;
   end process;

   byte_enable : process(conv_out, byte_offset)
   begin
      for i9 in 0 to max_n_writes-1 loop
         if(PRIVATE_MEMORY=0 and i9 < PORTSIZE_S_data_ram_size) then
            be((i9+1)*n_byte_on_databus-1 downto i9*n_byte_on_databus) <= std_logic_vector(shift_left(unsigned(conv_out((i9+1)*n_byte_on_databus-1 downto i9*n_byte_on_databus)), to_integer(unsigned(byte_offset((i9+1)*nbits_byte_offset-1 downto i9*nbits_byte_offset)))));
         else
            be((i9+1)*n_byte_on_databus-1 downto i9*n_byte_on_databus) <= std_logic_vector(shift_left(unsigned(conv_out((i9+1)*n_byte_on_databus-1 downto i9*n_byte_on_databus)), to_integer(unsigned(byte_offset((i9+1)*nbits_byte_offset-1 downto i9*nbits_byte_offset)))));
         end  if;
      end loop;
   end process;

   byte_enable_swapped : process(relative_addr, be)
   begin
      for i9 in 0 to max_n_writes-1 loop
         for i10 in 0 to n_byte_on_databus/2 - 1 loop
            if(relative_addr(nbits_byte_offset+i9*nbit_addr-1) = '0') then
               be_swapped(i10+i9*n_byte_on_databus) <= be(i10+i9*n_byte_on_databus);
               be_swapped(i10+i9*n_byte_on_databus+n_byte_on_databus/2) <= be(i10+i9*n_byte_on_databus+n_byte_on_databus/2);
            else
               be_swapped(i10+i9*n_byte_on_databus) <= be(i10+i9*n_byte_on_databus+n_byte_on_databus/2);
               be_swapped(i10+i9*n_byte_on_databus+n_byte_on_databus/2) <= be(i10+i9*n_byte_on_databus);
            end if;
         end loop;
      end loop;
   end process;

   L13 : process(S_Wdata_ram)
   begin
      for i13 in 0 to PORTSIZE_S_Wdata_ram-1 loop
         if (BITSIZE_S_Wdata_ram < memory_bitsize) then
            S_Wdata_ram_int(memory_bitsize*(i13+1)-1 downto memory_bitsize*i13 + BITSIZE_S_Wdata_ram) <= (others => '0');
            S_Wdata_ram_int(memory_bitsize*i13 + BITSIZE_S_Wdata_ram - 1 downto memory_bitsize*i13) <= S_Wdata_ram((i13+1)*BITSIZE_S_Wdata_ram-1 downto BITSIZE_S_Wdata_ram*i13);
         else
            S_Wdata_ram_int(memory_bitsize*(i13+1)-1 downto memory_bitsize*i13) <= S_Wdata_ram(memory_bitsize+BITSIZE_S_Wdata_ram*i13-1 downto BITSIZE_S_Wdata_ram*i13);
         end if;
      end loop;
   end process;

   L14 : process(proxy_sel_STORE, proxy_in1, byte_offset, sel_STORE, in1, S_Wdata_ram_int)
   begin
      for i14 in 0 to max_n_writes-1 loop
         if(PRIVATE_MEMORY=0 and i14 < PORTSIZE_S_Wdata_ram) then
            if(proxy_sel_STORE(i14) /= '0') then
               din_value_aggregated((i14+1)*memory_bitsize-1 downto i14*memory_bitsize) <= std_logic_vector(shift_left(resize(unsigned(proxy_in1((i14+1)*BITSIZE_proxy_in1-1 downto i14*BITSIZE_proxy_in1)), memory_bitsize), to_integer(unsigned(byte_offset((i14+1)*nbits_byte_offset-1 downto i14*nbits_byte_offset)))*8));
            else
               if(sel_STORE(i14) /= '0') then
                  din_value_aggregated((i14+1)*memory_bitsize-1 downto i14*memory_bitsize) <= std_logic_vector(shift_left(resize(unsigned((in1((i14+1)*BITSIZE_in1-1 downto i14*BITSIZE_in1))), memory_bitsize), to_integer(unsigned(byte_offset((i14+1)*nbits_byte_offset-1 downto i14*nbits_byte_offset)))*8));
               else
                  din_value_aggregated((i14+1)*memory_bitsize-1 downto i14*memory_bitsize) <= std_logic_vector(shift_left(resize(unsigned(S_Wdata_ram_int(memory_bitsize*(i14+1)-1 downto memory_bitsize*i14)), memory_bitsize), to_integer(unsigned(byte_offset((i14+1)*nbits_byte_offset-1 downto i14*nbits_byte_offset)))*8));
               end if;
             end if;
         else
            if(proxy_sel_STORE(i14) /= '0') then
               din_value_aggregated((i14+1)*memory_bitsize-1 downto i14*memory_bitsize) <= std_logic_vector(shift_left(resize(unsigned(proxy_in1((i14+1)*BITSIZE_proxy_in1-1 downto i14*BITSIZE_proxy_in1)), memory_bitsize), to_integer(unsigned(byte_offset((i14+1)*nbits_byte_offset-1 downto i14*nbits_byte_offset)))*8));
            else
               din_value_aggregated((i14+1)*memory_bitsize-1 downto i14*memory_bitsize) <= std_logic_vector(shift_left(resize(unsigned(in1((i14+1)*BITSIZE_in1-1 downto i14*BITSIZE_in1)), memory_bitsize), to_integer(unsigned(byte_offset((i14+1)*nbits_byte_offset-1 downto i14*nbits_byte_offset)))*8));
            end if;
         end if;
      end loop;
   end process;

   L14_swapped : process(relative_addr, din_value_aggregated)
   begin
      for i14 in 0 to max_n_writes-1 loop
         if(relative_addr(nbits_byte_offset+i14*nbit_addr-1) = '0') then
            din_value_aggregated_swapped((i14)*memory_bitsize+memory_bitsize/2-1 downto i14*memory_bitsize) <= din_value_aggregated((i14)*memory_bitsize+memory_bitsize/2-1 downto i14*memory_bitsize);
         else
            din_value_aggregated_swapped((i14)*memory_bitsize+memory_bitsize/2-1 downto i14*memory_bitsize) <= din_value_aggregated((i14+1)*memory_bitsize-1 downto i14*memory_bitsize+memory_bitsize/2);
         end if;
         if(relative_addr(nbits_byte_offset+i14*nbit_addr-1) = '0') then
            din_value_aggregated_swapped((i14+1)*memory_bitsize-1 downto i14*memory_bitsize+memory_bitsize/2) <= din_value_aggregated((i14+1)*memory_bitsize-1 downto i14*memory_bitsize+memory_bitsize/2);
         else
            din_value_aggregated_swapped((i14+1)*memory_bitsize-1 downto i14*memory_bitsize+memory_bitsize/2) <= din_value_aggregated((i14)*memory_bitsize+memory_bitsize/2-1 downto i14*memory_bitsize);
         end if;
      end loop;
   end process;

   L15 : process(dout, delayed_byte_offset)
   begin
      for i15 in 0 to max_n_reads-1 loop
         out1_shifted((i15+1)*memory_bitsize-1 downto i15*memory_bitsize) <= std_logic_vector(shift_right(resize(unsigned(dout((i15+1)*memory_bitsize-1 downto i15*memory_bitsize)), memory_bitsize), to_integer(unsigned(delayed_byte_offset((i15+1)*nbits_byte_offset-1 downto i15*nbits_byte_offset)))*8));
      end loop;
   end process;

   L20 : process(out1_shifted)
   begin
      for i20 in 0 to max_n_reads-1 loop
         out1((i20+1)*BITSIZE_out1-1 downto i20*BITSIZE_out1) <= out1_shifted(i20*memory_bitsize+BITSIZE_out1-1 downto i20*memory_bitsize);
         proxy_out1((i20+1)*BITSIZE_proxy_out1-1 downto i20*BITSIZE_proxy_out1) <= out1_shifted(i20*memory_bitsize+BITSIZE_proxy_out1-1 downto i20*memory_bitsize);
      end loop;
   end process;

   L16 : process(reset, clock)
   begin
      if(1RESET_VALUE) then
         for i16 in 0 to PORTSIZE_S_oe_ram - 1 loop
            oe_ram_cs_delayed(i16) <= '0';
            if(HIGH_LATENCY /= 0) then
               oe_ram_cs_delayed_registered(i16) <= '0';
            end if;
            if(HIGH_LATENCY = 2) then
               oe_ram_cs_delayed_registered1(i16) <= '0';
            end if;
         end loop;
      elsif (clock'event and clock = '1') then
         for i16 in 0 to PORTSIZE_S_oe_ram - 1 loop
            if(HIGH_LATENCY = 0) then
               if(BUS_PIPELINED /= 0) then
                  oe_ram_cs_delayed(i16) <= oe_ram_cs(i16);
               else
                  oe_ram_cs_delayed(i16) <= oe_ram_cs(i16) and (not oe_ram_cs_delayed(i16));
               end if;
            elsif(HIGH_LATENCY = 1) then
               if(BUS_PIPELINED /= 0) then
                  oe_ram_cs_delayed_registered(i16) <= oe_ram_cs(i16);
               else
                  oe_ram_cs_delayed_registered(i16) <= oe_ram_cs(i16) and ((not oe_ram_cs_delayed_registered(i16) and not oe_ram_cs_delayed(i16)));
                end if;
                oe_ram_cs_delayed(i16) <= oe_ram_cs_delayed_registered(i16);
            else
               if(BUS_PIPELINED /= 0) then
                  oe_ram_cs_delayed_registered1(i16) <= oe_ram_cs(i16);
               else
                  oe_ram_cs_delayed_registered1(i16) <= oe_ram_cs(i16) and ((not oe_ram_cs_delayed_registered1(i16) and not oe_ram_cs_delayed_registered(i16) and not oe_ram_cs_delayed(i16)));
               end if;
               oe_ram_cs_delayed_registered(i16) <= oe_ram_cs_delayed_registered1(i16);
               oe_ram_cs_delayed(i16) <= oe_ram_cs_delayed_registered(i16);
            end if;
         end loop;
      end if;
   end process;

   process(clock)
   begin
      if (clock'event and clock='1') then
         if(HIGH_LATENCY = 0) then
            delayed_byte_offset <= byte_offset(nbits_byte_offset*max_n_reads-1 downto 0);
         elsif(HIGH_LATENCY = 1) then
            delayed_byte_offset_registered <= byte_offset(nbits_byte_offset*max_n_reads-1 downto 0);
            delayed_byte_offset <= delayed_byte_offset_registered;
         else
            delayed_byte_offset_registered1 <= byte_offset(nbits_byte_offset*max_n_reads-1 downto 0);
            delayed_byte_offset_registered <= delayed_byte_offset_registered1;
            delayed_byte_offset <= delayed_byte_offset_registered;
         end if;
      end if;
   end process;

   process(clock, reset)
   begin
      if(1RESET_VALUE) then
         for i17 in 0 to PORTSIZE_S_we_ram-1 loop
            we_ram_cs_delayed(i17) <= '0';
         end loop;
      elsif (clock'event and clock = '1') then
         for i17 in 0 to PORTSIZE_S_we_ram-1 loop
               we_ram_cs_delayed(i17) <= we_ram_cs(i17) and not we_ram_cs_delayed(i17);
         end loop;
      end if;
   end process;

   L18 : process(Sin_Rdata_ram, out1_shifted, oe_ram_cs_delayed)
   begin
      for i18 in 0 to PORTSIZE_Sout_Rdata_ram-1 loop
         if(PRIVATE_MEMORY=1) then
             Sout_Rdata_ram((i18+1)*BITSIZE_Sout_Rdata_ram-1 downto i18*BITSIZE_Sout_Rdata_ram) <= Sin_Rdata_ram((i18+1)*BITSIZE_Sin_Rdata_ram-1 downto i18*BITSIZE_Sin_Rdata_ram);
         else
            if (BITSIZE_Sout_Rdata_ram <= memory_bitsize) then
               if(oe_ram_cs_delayed(i18) /= '0') then
                  Sout_Rdata_ram((i18+1)*BITSIZE_Sout_Rdata_ram-1 downto i18*BITSIZE_Sout_Rdata_ram) <= out1_shifted(BITSIZE_Sout_Rdata_ram+i18*memory_bitsize-1 downto i18*memory_bitsize);
               else
                  Sout_Rdata_ram((i18+1)*BITSIZE_Sout_Rdata_ram-1 downto i18*BITSIZE_Sout_Rdata_ram) <= Sin_Rdata_ram((i18+1)*BITSIZE_Sin_Rdata_ram-1 downto i18*BITSIZE_Sin_Rdata_ram);
               end if;
            else
               if(oe_ram_cs_delayed(i18) /= '0') then
                  Sout_Rdata_ram((i18+1)*BITSIZE_Sout_Rdata_ram-1 downto i18*BITSIZE_Sout_Rdata_ram) <= std_logic_vector(resize(unsigned(out1_shifted((i18+1)*memory_bitsize-1 downto i18*memory_bitsize)), BITSIZE_Sout_Rdata_ram));
               else
                  Sout_Rdata_ram((i18+1)*BITSIZE_Sout_Rdata_ram-1 downto i18*BITSIZE_Sout_Rdata_ram) <= Sin_Rdata_ram((i18+1)*BITSIZE_Sin_Rdata_ram-1 downto i18*BITSIZE_Sin_Rdata_ram);
               end if;
            end if;
         end if;
      end loop;
   end process;

   L19 : process(oe_ram_cs_delayed, Sin_DataRdy, we_ram_cs_delayed)
   begin
      for i19 in 0 to PORTSIZE_Sout_DataRdy-1 loop
         if(PRIVATE_MEMORY=0) then
            if((i19 < PORTSIZE_S_oe_ram and oe_ram_cs_delayed(i19) /= '0') or (Sin_DataRdy(i19) /= '0') or (i19 < PORTSIZE_S_we_ram and we_ram_cs_delayed(i19) /= '0')) then
               Sout_DataRdy(i19) <= '1';
            else
               Sout_DataRdy(i19) <= '0';
            end if;
         else
            Sout_DataRdy(i19) <= Sin_DataRdy(i19);
         end if;
      end loop;
   end process;

   L21 : process(sel_STORE, proxy_sel_STORE, we_ram_cs)
   begin
      for i21 in 0 to PORTSIZE_bram_write-1 loop
         if(i21 < PORTSIZE_S_we_ram) then
            bram_write(i21) <= (sel_STORE(i21) or proxy_sel_STORE(i21) or we_ram_cs(i21));
         else
            bram_write(i21) <= (sel_STORE(i21) or proxy_sel_STORE(i21));
         end if;
      end loop;
   end process;

NP_functionality.VERILOG_PROVIDED:
wire [n_byte_on_databus-1:0] we_a;
wire [n_byte_on_databus-1:0] we_b;

wire [n_byte_on_databus-1:0] we_a_temp;
wire [n_byte_on_databus-1:0] we_b_temp;
wire bram_write_temp;
wire [BITSIZE_din_value_aggregated-1:0] din_value_aggregated_temp;
wire [BITSIZE_memory_addr_a-1:0] memory_addr_a_temp;
wire [BITSIZE_memory_addr_b-1:0] memory_addr_b_temp;


wire [BRAM_BITSIZE-1:0] din_a_temp;
wire [BRAM_BITSIZE-1:0] din_b_temp;
reg [BITSIZE_dout_a-1:0] dout_a;
reg [BITSIZE_dout_a-1:0] dout_a_registered;
reg [BITSIZE_dout_b-1:0] dout_b;
reg [BITSIZE_dout_b-1:0] dout_b_registered;
reg [BRAM_BITSIZE-1:0] memory [0:n_elements-1] /* synthesis syn_ramstyle = "no_rw_check" */;

initial
begin
  $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
end

generate
  if(HIGH_LATENCY==2)
  begin
    reg [n_byte_on_databus-1:0] we_a_reg;
    reg [n_byte_on_databus-1:0] we_b_reg;
    reg bram_write_reg;
    reg [BITSIZE_din_value_aggregated-1:0] din_value_aggregated_reg;
    reg [BITSIZE_memory_addr_a-1:0] memory_addr_a_reg;
    reg [BITSIZE_memory_addr_b-1:0] memory_addr_b_reg;
    always @ (posedge clock)
    begin
       memory_addr_a_reg <= memory_addr_a;
       memory_addr_b_reg <= memory_addr_b;
       we_a_reg <= we_a;
       we_b_reg <= we_b;
       bram_write_reg <= bram_write;
       din_value_aggregated_reg <= din_value_aggregated;
    end
    assign we_a_temp = we_a_reg;
    assign we_b_temp = we_b_reg;
    assign memory_addr_a_temp = memory_addr_a_reg;
    assign memory_addr_b_temp = memory_addr_b_reg;
    assign bram_write_temp = bram_write_reg;
    assign din_value_aggregated_temp = din_value_aggregated_reg;
  end
  else
  begin
    assign we_a_temp = we_a;
    assign we_b_temp = we_b;
    assign memory_addr_a_temp = memory_addr_a;
    assign memory_addr_b_temp = memory_addr_b;
    assign bram_write_temp = bram_write;
    assign din_value_aggregated_temp = din_value_aggregated;
  end
endgenerate


generate
genvar i0_a;
  for (i0_a=0; i0_a<n_byte_on_databus; i0_a=i0_a+1)
  begin  : din_a_computation1
    assign din_a_temp[(i0_a+1)*8-1:i0_a*8] = we_a_temp[i0_a] ? din_value_aggregated_temp[(i0_a+1)*8-1:i0_a*8] : memory[memory_addr_a_temp][(i0_a+1)*8-1:i0_a*8];
  end
endgenerate

generate
genvar i0_b;
  for (i0_b=0; i0_b<n_byte_on_databus && n_bytes > BRAM_BITSIZE/8; i0_b=i0_b+1)
  begin  : din_b_computation1
    assign din_b_temp[(i0_b+1)*8-1:i0_b*8] = we_b_temp[i0_b] ? din_value_aggregated_temp[(i0_b+n_byte_on_databus+1)*8-1:(i0_b+n_byte_on_databus)*8] : memory[memory_addr_b_temp][(i0_b+1)*8-1:i0_b*8];
  end
endgenerate

always @(posedge clock)
begin
  if(READ_ONLY_MEMORY==0)
  begin
    if (bram_write_temp)
    begin
      memory[memory_addr_a_temp] <= din_a_temp;
    end
  end
  if(HIGH_LATENCY==0)
  begin
    dout_a <= memory[memory_addr_a_temp];
  end
  else
  begin
    dout_a_registered <= memory[memory_addr_a_temp];
    dout_a <= dout_a_registered;
  end
end

generate
  if (n_bytes > BRAM_BITSIZE/8)
  begin
    always @(posedge clock)
    begin
      if(READ_ONLY_MEMORY==0)
      begin
        if (bram_write_temp)
        begin
          memory[memory_addr_b_temp] <= din_b_temp;
        end
      end
      if(HIGH_LATENCY==0)
      begin
        dout_b <= memory[memory_addr_b_temp];
      end
      else
      begin
        dout_b_registered <= memory[memory_addr_b_temp];
        dout_b <= dout_b_registered;
      end
    end
  end
endgenerate

generate
genvar i2_a;
  for (i2_a=0; i2_a<n_byte_on_databus; i2_a=i2_a+1)
  begin  : write_enable_a
    assign we_a[i2_a] = (bram_write) && be[i2_a];
  end
endgenerate

generate
genvar i2_b;
  for (i2_b=0; i2_b<n_byte_on_databus; i2_b=i2_b+1)
  begin  : write_enable_b
    assign we_b[i2_b] = (bram_write) && be[i2_b+n_byte_on_databus];
  end
  endgenerate

wire [nbit_read_addr-1:0] memory_addr_a_0;
wire [nbit_read_addr-1:0] memory_addr_a_1;
wire [(n_byte_on_databus/2)*max_n_writes-1:0] we_a;
wire [(n_byte_on_databus/2)*max_n_writes-1:0] we_a_temp;
wire [(PORTSIZE_memory_addr_a*BITSIZE_memory_addr_a)+(-1):0] memory_addr_a_temp;
wire [PORTSIZE_bram_write-1:0] bram_write_temp;
wire [(PORTSIZE_din_value_aggregated_swapped*BITSIZE_din_value_aggregated_swapped)+(-1):0] din_value_aggregated_swapped_temp;

wire [BRAM_BITSIZE-1:0] din_a_0_temp;
wire [BRAM_BITSIZE-1:0] din_a_1_temp;
reg [BRAM_BITSIZE-1:0] dout_a_0;
reg [BRAM_BITSIZE-1:0] dout_a_1;
reg [BRAM_BITSIZE-1:0] dout_a_0_registered;
reg [BRAM_BITSIZE-1:0] dout_a_1_registered;

integer index1, index2;
reg [BRAM_BITSIZE-1:0] memory [0:n_elements-1] /* synthesis syn_ramstyle = "no_rw_check" */;

initial
begin
  $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
end

generate
  if(HIGH_LATENCY==2)
  begin
    reg [(n_byte_on_databus/2)*max_n_writes-1:0] we_a_reg;
    reg [PORTSIZE_bram_write-1:0] bram_write_reg;
    reg [(PORTSIZE_din_value_aggregated_swapped*BITSIZE_din_value_aggregated_swapped)+(-1):0] din_value_aggregated_swapped_reg;
    reg [(PORTSIZE_memory_addr_a*BITSIZE_memory_addr_a)+(-1):0] memory_addr_a_reg;
    always @ (posedge clock)
    begin
       we_a_reg <= we_a;
       memory_addr_a_reg <= memory_addr_a;
       bram_write_reg <= bram_write;
       din_value_aggregated_swapped_reg <= din_value_aggregated_swapped;
    end
    assign we_a_temp = we_a_reg;
    assign memory_addr_a_temp = memory_addr_a_reg;
    assign bram_write_temp = bram_write_reg;
    assign din_value_aggregated_swapped_temp = din_value_aggregated_swapped_reg;
  end
  else
  begin
    assign we_a_temp = we_a;
    assign memory_addr_a_temp = memory_addr_a;
    assign bram_write_temp = bram_write;
    assign din_value_aggregated_swapped_temp = din_value_aggregated_swapped;
  end
endgenerate


assign memory_addr_a_0 = memory_addr_a_temp[nbit_read_addr-1:0];
assign memory_addr_a_1 = memory_addr_a_temp[nbit_read_addr*2-1:nbit_read_addr];
assign dout_a[BRAM_BITSIZE*(0+1)-1:BRAM_BITSIZE*0] = dout_a_0;
assign dout_a[BRAM_BITSIZE*(1+1)-1:BRAM_BITSIZE*1] = dout_a_1;
generate
genvar i0_0;
  for (i0_0=0; i0_0<n_byte_on_databus/2; i0_0=i0_0+1)
  begin  : din_0_computation1
    assign din_a_0_temp[(i0_0+1)*8-1:i0_0*8] = we_a_temp[i0_0] ? din_value_aggregated_swapped_temp[(i0_0+1)*8+memory_offset-1:i0_0*8+memory_offset] : memory[memory_addr_a_0][(i0_0+1)*8-1:i0_0*8];
  end
endgenerate

generate
genvar i0_1;
  for (i0_1=0; i0_1<n_byte_on_databus/2; i0_1=i0_1+1)
  begin  : din_1_computation1
    assign din_a_1_temp[(i0_1+1)*8-1:i0_1*8] = we_a_temp[i0_1+n_byte_on_databus/2] ? din_value_aggregated_swapped_temp[2*BRAM_BITSIZE+(i0_1+1)*8+memory_offset-1:2*BRAM_BITSIZE+i0_1*8+memory_offset] : memory[memory_addr_a_1][(i0_1+1)*8-1:i0_1*8];
  end
endgenerate

generate
  if (n_elements==1)
  begin : single_element
    always @(posedge clock)
    begin
      if(HIGH_LATENCY == 0)
        dout_a_0  <= memory[memory_addr_a_temp[nbit_read_addr*0+:nbit_read_addr]];
      else
      begin
        dout_a_0_registered  <= memory[memory_addr_a_temp[nbit_read_addr*0+:nbit_read_addr]];
        dout_a_0  <= dout_a_0_registered;
      end
      if(HIGH_LATENCY == 0)
        dout_a_1  <= memory[memory_addr_a_temp[nbit_read_addr*1+:nbit_read_addr]];
      else
      begin
        dout_a_1_registered  <= memory[memory_addr_a_temp[nbit_read_addr*1+:nbit_read_addr]];
        dout_a_1  <= dout_a_1_registered;
      end
      if(READ_ONLY_MEMORY==0)
      begin
      for (index2=0; index2<max_n_writes; index2=index2+1)
      begin : L12_single_write
          for (index1=0; index1<n_byte_on_databus/2; index1=index1+1)
          begin : L11_single_write
            if(we_a_temp[index1+n_byte_on_databus/2*index2])
              memory[memory_addr_a_temp[nbit_read_addr*index2+:nbit_read_addr]][index1*8+:8] <= din_value_aggregated_swapped_temp[2*BRAM_BITSIZE*index2+index1*8+memory_offset+:8];
          end
        end
      end
    end
  end
  else
  begin
    always @(posedge clock)
    begin
      if(READ_ONLY_MEMORY==0)
      begin
        if (bram_write_temp[0])
        begin
          memory[memory_addr_a_0] <= din_a_0_temp;
        end
      end
      if(HIGH_LATENCY == 0)
        dout_a_0 <= memory[memory_addr_a_0];
      else
      begin
        dout_a_0_registered <= memory[memory_addr_a_0];
        dout_a_0 <= dout_a_0_registered;
      end
    end

    always @(posedge clock)
    begin
      if(READ_ONLY_MEMORY==0)
      begin
        if (bram_write_temp[1])
        begin
          memory[memory_addr_a_1] <= din_a_1_temp;
        end
      end
      if(HIGH_LATENCY == 0)
        dout_a_1 <= memory[memory_addr_a_1];
      else
      begin
        dout_a_1_registered <= memory[memory_addr_a_1];
        dout_a_1 <= dout_a_1_registered;
      end
    end
  end
endgenerate

generate
genvar i11, i12;
  for (i12=0; i12<max_n_writes; i12=i12+1)
  begin : L12
    for (i11=0; i11<n_byte_on_databus/2; i11=i11+1)
    begin  : write_enable_a
      assign we_a[i11+(n_byte_on_databus/2)*i12] = bram_write[i12] && be_swapped[i11+n_byte_on_databus*i12+n_byte_on_databus_offset];
    end
  end
endgenerate
// Add assertion here
// psl default clock = (posedge clock);
// psl ERROR_conflicting_writes: assert never {bram_write[0] && bram_write[1] && memory_addr_a[nbit_read_addr*0+:nbit_read_addr] == memory_addr_a[nbit_read_addr*1+:nbit_read_addr] && (we_a[n_byte_on_databus/2-1:0]&we_a[n_byte_on_databus/2+n_byte_on_databus/2-1:n_byte_on_databus/2]) != 0};

NP_functionality.VHDL_PROVIDED:
signal memory_addr_a_0 : std_logic_vector(nbit_read_addr-1 downto 0);
signal memory_addr_a_1  :  std_logic_vector(nbit_read_addr-1 downto 0);
signal we_a  :  std_logic_vector((n_byte_on_databus/2)*max_n_writes-1 downto 0);
signal din_a_0  :  std_logic_vector(BRAM_BITSIZE-1 downto 0);
signal din_a_1  :  std_logic_vector(BRAM_BITSIZE-1 downto 0);
signal dout_a_0  :  std_logic_vector(BRAM_BITSIZE-1 downto 0);
signal dout_a_1  :  std_logic_vector(BRAM_BITSIZE-1 downto 0);
signal dout_a_0_registered  :  std_logic_vector(BRAM_BITSIZE-1 downto 0);
signal dout_a_1_registered  :  std_logic_vector(BRAM_BITSIZE-1 downto 0);
signal we_a_temp : std_logic_vector((n_byte_on_databus/2)*max_n_writes-1 downto 0);
signal memory_addr_a_temp : std_logic_vector((PORTSIZE_memory_addr_a*BITSIZE_memory_addr_a)+(-1) downto 0);
signal bram_write_temp : std_logic_vector(PORTSIZE_bram_write-1 downto 0);
signal din_value_aggregated_swapped_temp : std_logic_vector((PORTSIZE_din_value_aggregated_swapped*BITSIZE_din_value_aggregated_swapped)+(-1) downto 0);

type mem_type is array (n_elements-1 downto 0) of std_logic_vector(BRAM_BITSIZE-1 downto 0);

impure function InitMemFromFile (MemFileName : in string) return mem_type is
  FILE memfile  :  text open READ_MODE is MemFileName;
  variable MemFileLine  :  line;
  variable mem  :  mem_type;
  begin
     for i in 0 to n_elements-1 loop
      readline(memfile, MemFileLine);
      read(MemFileLine, mem(i));
    end loop;
    return mem;
end function;

signal memory : mem_type := InitMemFromFile(MEMORY_INIT_file);
begin
memory_addr_a_0 <= memory_addr_a_temp(nbit_read_addr-1 downto 0);
memory_addr_a_1 <= memory_addr_a_temp(nbit_read_addr*2-1 downto nbit_read_addr);
dout_a(BRAM_BITSIZE*(0+1)-1 downto BRAM_BITSIZE*0) <= dout_a_0;
dout_a(BRAM_BITSIZE*(1+1)-1 downto BRAM_BITSIZE*1) <= dout_a_1;
input_registration_2 : if HIGH_LATENCY=2 generate
  signal we_a_reg : std_logic_vector((n_byte_on_databus/2)*max_n_writes-1 downto 0);
  signal memory_addr_a_reg : std_logic_vector((PORTSIZE_memory_addr_a*BITSIZE_memory_addr_a)+(-1) downto 0);
  signal bram_write_reg : std_logic_vector(PORTSIZE_bram_write-1 downto 0);
  signal din_value_aggregated_swapped_reg : std_logic_vector((PORTSIZE_din_value_aggregated_swapped*BITSIZE_din_value_aggregated_swapped)+(-1) downto 0);
begin
  in_reg : process(clock)
  begin
    if(clock'event and clock = '1') then
      we_a_reg <= we_a;
      memory_addr_a_reg <= memory_addr_a;
      bram_write_reg <= bram_write;
      din_value_aggregated_swapped_reg <= din_value_aggregated_swapped;
    end if;
  end process;
  we_a_temp <= we_a_reg;
  memory_addr_a_temp <= memory_addr_a_reg;
  bram_write_temp <= bram_write_reg;
  din_value_aggregated_swapped_temp <= din_value_aggregated_swapped_reg;
end generate input_registration_2 ;
input_registration_01 : if HIGH_LATENCY < 2 generate
begin
  we_a_temp <= we_a;
  memory_addr_a_temp <= memory_addr_a;
  bram_write_temp <= bram_write;
  din_value_aggregated_swapped_temp <= din_value_aggregated_swapped;
end generate input_registration_01 ;

din_0_computation : process(we_a_temp, din_value_aggregated_swapped_temp, memory, memory_addr_a_0)
begin
   for i0_0 in 0 to n_byte_on_databus/2-1 loop
      if(we_a_temp(i0_0) = '1') then
         din_a_0((i0_0+1)*8-1 downto i0_0*8) <= din_value_aggregated_swapped_temp((i0_0+1)*8+memory_offset-1 downto i0_0*8+memory_offset);
      else
-- synthesis translate_off
         if (to_integer(unsigned(memory_addr_a_0)) >= n_elements) then
            din_a_0((i0_0+1)*8-1 downto i0_0*8) <= (others => 'X');
         else
-- synthesis translate_on
            din_a_0((i0_0+1)*8-1 downto i0_0*8) <= memory(to_integer(unsigned(memory_addr_a_0)))((i0_0+1)*8-1 downto i0_0*8);
-- synthesis translate_off
         end if;
-- synthesis translate_on
     end if;
   end loop;
end process;

din_1_computation : process(we_a_temp, din_value_aggregated_swapped_temp, memory, memory_addr_a_1)
begin
   for i0_1 in 0 to n_byte_on_databus/2-1 loop
      if(we_a_temp(i0_1+n_byte_on_databus/2) = '1') then
         din_a_1((i0_1+1)*8-1 downto i0_1*8) <= din_value_aggregated_swapped_temp(2*BRAM_BITSIZE+(i0_1+1)*8+memory_offset-1 downto 2*BRAM_BITSIZE+i0_1*8+memory_offset);
      else
-- synthesis translate_off
         if (to_integer(unsigned(memory_addr_a_1)) >= n_elements) then
            din_a_1((i0_1+1)*8-1 downto i0_1*8) <= (others => 'X');
         else
-- synthesis translate_on
            din_a_1((i0_1+1)*8-1 downto i0_1*8) <= memory(to_integer(unsigned(memory_addr_a_1)))((i0_1+1)*8-1 downto i0_1*8);
-- synthesis translate_off
         end if;
-- synthesis translate_on
      end if;
   end loop;
end process;

single_element : process(clock)
begin
   if(n_elements = 1) then
      if(clock'event and clock = '1') then
         if(HIGH_LATENCY = 0) then
            dout_a_0  <= memory(to_integer(unsigned(memory_addr_a_temp(nbit_read_addr-1 downto 0))));
         else
            dout_a_0_registered  <= memory(to_integer(unsigned(memory_addr_a_temp(nbit_read_addr-1 downto 0))));
            dout_a_0  <= dout_a_0_registered;
         end if;
         if(HIGH_LATENCY = 0) then
            dout_a_1  <= memory(to_integer(unsigned(memory_addr_a_temp(2*nbit_read_addr-1 downto nbit_read_addr))));
         else
            dout_a_1_registered  <= memory(to_integer(unsigned(memory_addr_a_temp(2*nbit_read_addr-1 downto nbit_read_addr))));
            dout_a_1  <= dout_a_1_registered;
         end if;
         if(READ_ONLY_MEMORY = 0) then
           for index2 in 0 to max_n_writes-1 loop
              for index1 in 0 to n_byte_on_databus/2-1 loop
                 if(we_a_temp(index1+n_byte_on_databus/2*index2)= '1') then
                    memory(to_integer(unsigned(memory_addr_a_temp(nbit_read_addr*(index2+1)-1 downto nbit_read_addr*index2))))((index1+1)*8-1 downto index1*8) <= din_value_aggregated_swapped_temp(2*BRAM_BITSIZE*index2+index1*8+memory_offset+7 downto 2*BRAM_BITSIZE*index2+index1*8+memory_offset);
                 end if;
              end loop;
           end loop;
         end if;
      end if;
   else
      if(clock'event and clock = '1') then
         if(READ_ONLY_MEMORY = 0) then
           if (bram_write_temp(0) = '1') then
-- synthesis translate_off
              if (to_integer(unsigned(memory_addr_a_0)) < n_elements) then
-- synthesis translate_on
                 memory(to_integer(unsigned(memory_addr_a_0))) <= din_a_0;
-- synthesis translate_off
              end if;
-- synthesis translate_on
           end if;
         end if;
         if(HIGH_LATENCY = 0) then
-- synthesis translate_off
            if (to_integer(unsigned(memory_addr_a_0)) >= n_elements) then
               dout_a_0 <= (others => 'X');
            else
-- synthesis translate_on
               dout_a_0 <= memory(to_integer(unsigned(memory_addr_a_0)));
-- synthesis translate_off
            end if;
-- synthesis translate_on
         else
-- synthesis translate_off
            if (to_integer(unsigned(memory_addr_a_0)) >= n_elements) then
               dout_a_0_registered <= (others => 'X');
            else
-- synthesis translate_on
               dout_a_0_registered <= memory(to_integer(unsigned(memory_addr_a_0)));
-- synthesis translate_off
            end if;
-- synthesis translate_on
            dout_a_0 <= dout_a_0_registered;
         end if;
         if(READ_ONLY_MEMORY = 0) then
           if (bram_write_temp(1) = '1') then
-- synthesis translate_off
             if (to_integer(unsigned(memory_addr_a_1)) < n_elements) then
-- synthesis translate_on
               memory(to_integer(unsigned(memory_addr_a_1))) <= din_a_1;
-- synthesis translate_off
             end if;
-- synthesis translate_on
           end if;
         end if;
         if(HIGH_LATENCY = 0) then
-- synthesis translate_off
            if (to_integer(unsigned(memory_addr_a_1)) >= n_elements) then
               dout_a_1 <= (others => 'X');
            else
-- synthesis translate_on
               dout_a_1 <= memory(to_integer(unsigned(memory_addr_a_1)));
-- synthesis translate_off
            end if;
-- synthesis translate_on
         else
-- synthesis translate_off
            if (to_integer(unsigned(memory_addr_a_1)) >= n_elements) then
               dout_a_1_registered <= (others => 'X');
            else
-- synthesis translate_on
               dout_a_1_registered <= memory(to_integer(unsigned(memory_addr_a_1)));
-- synthesis translate_off
            end if;
-- synthesis translate_on
            dout_a_1 <= dout_a_1_registered;
         end if;
      end if;
   end if;
end process;

L12 : process(bram_write, be_swapped)
begin
   for i12 in 0 to max_n_writes-1 loop
      for i11 in 0 to (n_byte_on_databus/2)-1 loop
         we_a(i11+(n_byte_on_databus/2)*i12) <= bram_write(i12) and be_swapped(i11+n_byte_on_databus*i12+n_byte_on_databus_offset);
      end loop;
   end loop;
end process;
-- Add assertion here
-- psl default clock is (rising_edge(clock));
-- psl ERROR_conflicting_writes : assert never {bram_write(0) /= '0' and bram_write(1) /= '0' and (memory_addr_a(nbit_read_addr-1 downto 0) = memory_addr_a(2*nbit_read_addr-1 downto nbit_read_addr)) and to_integer(unsigned(we_a(n_byte_on_databus/2-1 downto 0))) /= 0 and to_integer(unsigned(we_a(n_byte_on_databus/2+n_byte_on_databus/2-1 downto n_byte_on_databus/2))) /= 0};

NP_functionality.VERILOG_PROVIDED:
reg bram_write1;
reg [BITSIZE_memory_addr_a-1:0] memory_addr_a1;
reg [BITSIZE_memory_addr_b-1:0] memory_addr_b1;
reg [BITSIZE_be-1:0] be1;
reg [BITSIZE_din_value_aggregated-1:0] din_value_aggregated1;

generate
  if(HIGH_LATENCY==2)
  begin
    always @ (posedge clock)
    begin
       memory_addr_a1 <= memory_addr_a;
       memory_addr_b1 <= memory_addr_b;
       bram_write1 <= bram_write;
       be1 <= be;
       din_value_aggregated1 <= din_value_aggregated;
    end
  end
endgenerate

generate
if(BRAM_BITSIZE == 8)
begin
  reg [(n_byte_on_databus)*8-1:0] dout_a_tmp;
  reg [(n_byte_on_databus)*8-1:0] dout_b_tmp;
  reg [(n_byte_on_databus)*8-1:0] dout_a_registered;
  reg [(n_byte_on_databus)*8-1:0] dout_b_registered;
  reg [(n_byte_on_databus)*8-1:0] memory [0:n_elements-1]/* synthesis syn_ramstyle = "no_rw_check" */ ;
  assign dout_a = dout_a_tmp;
  assign dout_b = dout_b_tmp;
  initial
  begin
    $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
  end


  if(n_elements == 1)
  begin
    always @(posedge clock)
    begin
      if(READ_ONLY_MEMORY==0)
      begin
        if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
        begin
          if (bram_write && be[0])
            memory[memory_addr_a] <= din_value_aggregated[BRAM_BITSIZE-1:0];
        end
        else
        begin
          if (bram_write1 && be1[0])
            memory[memory_addr_a1] <= din_value_aggregated1[BRAM_BITSIZE-1:0];
        end
      end
      if(HIGH_LATENCY == 0)
        dout_a_tmp <= memory[memory_addr_a];
      else if(HIGH_LATENCY == 1)
      begin
        dout_a_registered <= memory[memory_addr_a];
        dout_a_tmp <= dout_a_registered;
      end
      else
      begin
        dout_a_registered <= memory[memory_addr_a1];
        dout_a_tmp <= dout_a_registered;
      end
      if(READ_ONLY_MEMORY==0)
      begin
        if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
        begin
          if (bram_write && be[1])
            memory[memory_addr_b] <= din_value_aggregated[2*BRAM_BITSIZE-1:BRAM_BITSIZE];
        end
        else
        begin
          if (bram_write1 && be1[1])
            memory[memory_addr_b1] <= din_value_aggregated1[2*BRAM_BITSIZE-1:BRAM_BITSIZE];
        end
      end
      if(HIGH_LATENCY == 0)
        dout_b_tmp <= memory[memory_addr_b];
      else if(HIGH_LATENCY == 1)
      begin
        dout_b_registered <= memory[memory_addr_b];
        dout_b_tmp <= dout_b_registered;
      end
      else
      begin
        dout_b_registered <= memory[memory_addr_b1];
        dout_b_tmp <= dout_b_registered;
      end
    end
  end
  else
  begin
    always @(posedge clock)
    begin
      if(READ_ONLY_MEMORY==0)
      begin
        if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
        begin
          if (bram_write && be[0])
            memory[memory_addr_a] <= din_value_aggregated[BRAM_BITSIZE-1:0];
        end
        else
        begin
          if (bram_write1 && be1[0])
            memory[memory_addr_a1] <= din_value_aggregated1[BRAM_BITSIZE-1:0];
        end
      end
      if(HIGH_LATENCY == 0)
        dout_a_tmp <= memory[memory_addr_a];
      else if(HIGH_LATENCY == 1)
      begin
        dout_a_registered <= memory[memory_addr_a];
        dout_a_tmp <= dout_a_registered;
      end
      else
      begin
        dout_a_registered <= memory[memory_addr_a1];
        dout_a_tmp <= dout_a_registered;
      end
    end
    always @(posedge clock)
    begin
      if(READ_ONLY_MEMORY==0)
      begin
        if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
        begin
          if (bram_write && be[1])
            memory[memory_addr_b] <= din_value_aggregated[2*BRAM_BITSIZE-1:BRAM_BITSIZE];
        end
        else
        begin
          if (bram_write1 && be1[1])
            memory[memory_addr_b1] <= din_value_aggregated1[2*BRAM_BITSIZE-1:BRAM_BITSIZE];
        end
      end
      if(HIGH_LATENCY == 0)
        dout_b_tmp <= memory[memory_addr_b];
      else if(HIGH_LATENCY == 1)
      begin
        dout_b_registered <= memory[memory_addr_b];
        dout_b_tmp <= dout_b_registered;
      end
      else
      begin
        dout_b_registered <= memory[memory_addr_b1];
        dout_b_tmp <= dout_b_registered;
      end
    end
  end
end
else
begin
  if(n_elements == 1)
  begin
    reg [(n_byte_on_databus)*8-1:0] dout_a_tmp;
    reg [(n_byte_on_databus)*8-1:0] dout_b_tmp;
    reg [(n_byte_on_databus)*8-1:0] dout_a_registered;
    reg [(n_byte_on_databus)*8-1:0] dout_b_registered;
    reg [(n_byte_on_databus)*8-1:0] memory [0:n_elements-1]/* synthesis syn_ramstyle = "no_rw_check" */ ;
    assign dout_a = dout_a_tmp;
    assign dout_b = dout_b_tmp;
    initial
    begin
      $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
    end
    always @(posedge clock)
    begin
      if(READ_ONLY_MEMORY==0)
      begin
        if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
        begin
          if (bram_write)
          begin : L11_write
            integer i11;
            for (i11=0; i11<n_byte_on_databus; i11=i11+1)
            begin
              if(be[i11])
                memory[memory_addr_a][i11*8+:8] <= din_value_aggregated[i11*8+:8];
            end
          end
        end
        else
        begin
          if (bram_write1)
          begin : L11_write1
            integer i11;
            for (i11=0; i11<n_byte_on_databus; i11=i11+1)
            begin
              if(be1[i11])
                memory[memory_addr_a1][i11*8+:8] <= din_value_aggregated1[i11*8+:8];
            end
          end
        end
      end
      if(HIGH_LATENCY == 0)
        dout_a_tmp <= memory[memory_addr_a];
      else if(HIGH_LATENCY == 1)
      begin
        dout_a_registered <= memory[memory_addr_a];
        dout_a_tmp <= dout_a_registered;
      end
      else
      begin
        dout_a_registered <= memory[memory_addr_a1];
        dout_a_tmp <= dout_a_registered;
      end
      if(READ_ONLY_MEMORY==0)
      begin
        if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
        begin
          if (bram_write && n_bytes > BRAM_BITSIZE/8)
          begin : L22_write
            integer i22;
            for (i22=0; i22<n_byte_on_databus; i22=i22+1)
            begin
              if(be[i22+n_byte_on_databus])
                memory[memory_addr_b][i22*8+:8] <= din_value_aggregated[(i22+n_byte_on_databus)*8+:8];
            end
          end
        end
        else
        begin
          if (bram_write1 && n_bytes > BRAM_BITSIZE/8)
          begin : L22_write1
            integer i22;
            for (i22=0; i22<n_byte_on_databus; i22=i22+1)
            begin
              if(be1[i22+n_byte_on_databus])
                memory[memory_addr_b1][i22*8+:8] <= din_value_aggregated1[(i22+n_byte_on_databus)*8+:8];
            end
          end
        end
      end
      if(HIGH_LATENCY == 0)
        dout_b_tmp <= memory[memory_addr_b];
      else if(HIGH_LATENCY == 1)
      begin
        dout_b_registered <= memory[memory_addr_b];
        dout_b_tmp <= dout_b_registered;
      end
      else
      begin
        dout_b_registered <= memory[memory_addr_b1];
        dout_b_tmp <= dout_b_registered;
      end
    end
  end
  else
  begin
    BRAM_MEMORY_SV_CORE #(.BITSIZE_memory_addr_a(BITSIZE_memory_addr_a),
  .BITSIZE_memory_addr_b(BITSIZE_memory_addr_b),
  .BITSIZE_din_value_aggregated(BITSIZE_din_value_aggregated),
  .BITSIZE_be(BITSIZE_be),
  .BITSIZE_dout_a(BITSIZE_dout_a),
  .BITSIZE_dout_b(BITSIZE_dout_b),
  .MEMORY_INIT_file(MEMORY_INIT_file),
  .BRAM_BITSIZE(BRAM_BITSIZE),
  .nbit_read_addr(nbit_read_addr),
  .n_byte_on_databus(n_byte_on_databus),
  .n_elements(n_elements),
  .n_bytes(n_bytes),
  .READ_ONLY_MEMORY(READ_ONLY_MEMORY),
  .HIGH_LATENCY(HIGH_LATENCY)) BRAM_MEMORY_CORE_instance (.clock(clock),
  .bram_write(bram_write),
  .memory_addr_a(memory_addr_a),
  .memory_addr_b(memory_addr_b),
  .din_value_aggregated(din_value_aggregated),
  .be(be),
  .dout_a(dout_a),
  .dout_b(dout_b));
  end
end
endgenerate

NP_functionality.SYSTEM_VERILOG_PROVIDED:
reg [BITSIZE_dout_a-1:0] dout_a;
reg [BITSIZE_dout_a-1:0] dout_a_registered;
reg [BITSIZE_dout_b-1:0] dout_b;
reg [BITSIZE_dout_b-1:0] dout_b_registered;
reg [BITSIZE_memory_addr_a-1:0] memory_addr_a1;
reg [BITSIZE_memory_addr_b-1:0] memory_addr_b1;
reg bram_write1;
reg [BITSIZE_be-1:0] be1;
reg [BITSIZE_din_value_aggregated-1:0] din_value_aggregated1;
(* syn_ramstyle = "no_rw_check" *) reg [(BRAM_BITSIZE/8)-1:0][7:0] memory [0:n_elements-1];

initial
begin
  //dout_a=0;  dout_b=0;
  $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
end

generate
  if(HIGH_LATENCY==2)
  begin
    always @ (posedge clock)
    begin
       memory_addr_a1 <= memory_addr_a;
       memory_addr_b1 <= memory_addr_b;
       bram_write1 <= bram_write;
       be1 <= be;
       din_value_aggregated1 <= din_value_aggregated;
    end
  end
endgenerate

generate
  if(n_byte_on_databus==1)
  begin
    always @ (posedge clock)
    begin // Port A
      if(READ_ONLY_MEMORY==0)
      begin
        if(HIGH_LATENCY==0 || HIGH_LATENCY==1)
        begin
          if (bram_write)
          begin
            if(be[0])
              memory[memory_addr_a][0] <= din_value_aggregated[(0+1)*8-1:0*8];
          end
        end
        else
        begin
          if (bram_write1)
          begin
            if(be1[0])
              memory[memory_addr_a1][0] <= din_value_aggregated1[(0+1)*8-1:0*8];
          end
        end
      end
      if(HIGH_LATENCY==0)
      begin
        dout_a <= memory[memory_addr_a];
      end
