            begin
              if(be1[0+n_byte_on_databus])
                memory[memory_addr_b1][0] <= din_value_aggregated1[(0+1+n_byte_on_databus)*8-1:(0+n_byte_on_databus)*8];
              if(be1[1+n_byte_on_databus])
                memory[memory_addr_b1][1] <= din_value_aggregated1[(1+1+n_byte_on_databus)*8-1:(1+n_byte_on_databus)*8];
              if(be1[2+n_byte_on_databus])
                memory[memory_addr_b1][2] <= din_value_aggregated1[(2+1+n_byte_on_databus)*8-1:(2+n_byte_on_databus)*8];
              if(be1[3+n_byte_on_databus])
                memory[memory_addr_b1][3] <= din_value_aggregated1[(3+1+n_byte_on_databus)*8-1:(3+n_byte_on_databus)*8];
              if(be1[4+n_byte_on_databus])
                memory[memory_addr_b1][4] <= din_value_aggregated1[(4+1+n_byte_on_databus)*8-1:(4+n_byte_on_databus)*8];
              if(be1[5+n_byte_on_databus])
                memory[memory_addr_b1][5] <= din_value_aggregated1[(5+1+n_byte_on_databus)*8-1:(5+n_byte_on_databus)*8];
              if(be1[6+n_byte_on_databus])
                memory[memory_addr_b1][6] <= din_value_aggregated1[(6+1+n_byte_on_databus)*8-1:(6+n_byte_on_databus)*8];
              if(be1[7+n_byte_on_databus])
                memory[memory_addr_b1][7] <= din_value_aggregated1[(7+1+n_byte_on_databus)*8-1:(7+n_byte_on_databus)*8];
            end
          end
        end
        if(HIGH_LATENCY==0)
        begin
          dout_b <= memory[memory_addr_b];
        end
        else if(HIGH_LATENCY==1)
        begin
          dout_b_registered <= memory[memory_addr_b];
          dout_b <= dout_b_registered;
        end
        else
        begin
          dout_b_registered <= memory[memory_addr_b1];
          dout_b <= dout_b_registered;
        end
      end
    end
  else if (n_bytes > BRAM_BITSIZE/8 && n_byte_on_databus==16)
    begin
      always @ (posedge clock)
      begin // Port B
        if(READ_ONLY_MEMORY==0)
        begin
          if(HIGH_LATENCY==0 || HIGH_LATENCY==1)
          begin
            if (bram_write)
            begin
              if(be[0+n_byte_on_databus])
                memory[memory_addr_b][0] <= din_value_aggregated[(0+1+n_byte_on_databus)*8-1:(0+n_byte_on_databus)*8];
              if(be[1+n_byte_on_databus])
                memory[memory_addr_b][1] <= din_value_aggregated[(1+1+n_byte_on_databus)*8-1:(1+n_byte_on_databus)*8];
              if(be[2+n_byte_on_databus])
                memory[memory_addr_b][2] <= din_value_aggregated[(2+1+n_byte_on_databus)*8-1:(2+n_byte_on_databus)*8];
              if(be[3+n_byte_on_databus])
                memory[memory_addr_b][3] <= din_value_aggregated[(3+1+n_byte_on_databus)*8-1:(3+n_byte_on_databus)*8];
              if(be[4+n_byte_on_databus])
                memory[memory_addr_b][4] <= din_value_aggregated[(4+1+n_byte_on_databus)*8-1:(4+n_byte_on_databus)*8];
              if(be[5+n_byte_on_databus])
                memory[memory_addr_b][5] <= din_value_aggregated[(5+1+n_byte_on_databus)*8-1:(5+n_byte_on_databus)*8];
              if(be[6+n_byte_on_databus])
                memory[memory_addr_b][6] <= din_value_aggregated[(6+1+n_byte_on_databus)*8-1:(6+n_byte_on_databus)*8];
              if(be[7+n_byte_on_databus])
                memory[memory_addr_b][7] <= din_value_aggregated[(7+1+n_byte_on_databus)*8-1:(7+n_byte_on_databus)*8];
              if(be[8+n_byte_on_databus])
                memory[memory_addr_b][8] <= din_value_aggregated[(8+1+n_byte_on_databus)*8-1:(8+n_byte_on_databus)*8];
              if(be[9+n_byte_on_databus])
                memory[memory_addr_b][9] <= din_value_aggregated[(9+1+n_byte_on_databus)*8-1:(9+n_byte_on_databus)*8];
              if(be[10+n_byte_on_databus])
                memory[memory_addr_b][10] <= din_value_aggregated[(10+1+n_byte_on_databus)*8-1:(10+n_byte_on_databus)*8];
              if(be[11+n_byte_on_databus])
                memory[memory_addr_b][11] <= din_value_aggregated[(11+1+n_byte_on_databus)*8-1:(11+n_byte_on_databus)*8];
              if(be[12+n_byte_on_databus])
                memory[memory_addr_b][12] <= din_value_aggregated[(12+1+n_byte_on_databus)*8-1:(12+n_byte_on_databus)*8];
              if(be[13+n_byte_on_databus])
                memory[memory_addr_b][13] <= din_value_aggregated[(13+1+n_byte_on_databus)*8-1:(13+n_byte_on_databus)*8];
              if(be[14+n_byte_on_databus])
                memory[memory_addr_b][14] <= din_value_aggregated[(14+1+n_byte_on_databus)*8-1:(14+n_byte_on_databus)*8];
              if(be[15+n_byte_on_databus])
                memory[memory_addr_b][15] <= din_value_aggregated[(15+1+n_byte_on_databus)*8-1:(15+n_byte_on_databus)*8];
            end
          end
          else
          begin
            if (bram_write1)
            begin
              if(be1[0+n_byte_on_databus])
                memory[memory_addr_b1][0] <= din_value_aggregated1[(0+1+n_byte_on_databus)*8-1:(0+n_byte_on_databus)*8];
              if(be1[1+n_byte_on_databus])
                memory[memory_addr_b1][1] <= din_value_aggregated1[(1+1+n_byte_on_databus)*8-1:(1+n_byte_on_databus)*8];
              if(be1[2+n_byte_on_databus])
                memory[memory_addr_b1][2] <= din_value_aggregated1[(2+1+n_byte_on_databus)*8-1:(2+n_byte_on_databus)*8];
              if(be1[3+n_byte_on_databus])
                memory[memory_addr_b1][3] <= din_value_aggregated1[(3+1+n_byte_on_databus)*8-1:(3+n_byte_on_databus)*8];
              if(be1[4+n_byte_on_databus])
                memory[memory_addr_b1][4] <= din_value_aggregated1[(4+1+n_byte_on_databus)*8-1:(4+n_byte_on_databus)*8];
              if(be1[5+n_byte_on_databus])
                memory[memory_addr_b1][5] <= din_value_aggregated1[(5+1+n_byte_on_databus)*8-1:(5+n_byte_on_databus)*8];
              if(be1[6+n_byte_on_databus])
                memory[memory_addr_b1][6] <= din_value_aggregated1[(6+1+n_byte_on_databus)*8-1:(6+n_byte_on_databus)*8];
              if(be1[7+n_byte_on_databus])
                memory[memory_addr_b1][7] <= din_value_aggregated1[(7+1+n_byte_on_databus)*8-1:(7+n_byte_on_databus)*8];
              if(be1[8+n_byte_on_databus])
                memory[memory_addr_b1][8] <= din_value_aggregated1[(8+1+n_byte_on_databus)*8-1:(8+n_byte_on_databus)*8];
              if(be1[9+n_byte_on_databus])
                memory[memory_addr_b1][9] <= din_value_aggregated1[(9+1+n_byte_on_databus)*8-1:(9+n_byte_on_databus)*8];
              if(be1[10+n_byte_on_databus])
                memory[memory_addr_b1][10] <= din_value_aggregated1[(10+1+n_byte_on_databus)*8-1:(10+n_byte_on_databus)*8];
              if(be1[11+n_byte_on_databus])
                memory[memory_addr_b1][11] <= din_value_aggregated1[(11+1+n_byte_on_databus)*8-1:(11+n_byte_on_databus)*8];
              if(be1[12+n_byte_on_databus])
                memory[memory_addr_b1][12] <= din_value_aggregated1[(12+1+n_byte_on_databus)*8-1:(12+n_byte_on_databus)*8];
              if(be1[13+n_byte_on_databus])
                memory[memory_addr_b1][13] <= din_value_aggregated1[(13+1+n_byte_on_databus)*8-1:(13+n_byte_on_databus)*8];
              if(be1[14+n_byte_on_databus])
                memory[memory_addr_b1][14] <= din_value_aggregated1[(14+1+n_byte_on_databus)*8-1:(14+n_byte_on_databus)*8];
              if(be1[15+n_byte_on_databus])
                memory[memory_addr_b1][15] <= din_value_aggregated1[(15+1+n_byte_on_databus)*8-1:(15+n_byte_on_databus)*8];
            end
          end
        end
        if(HIGH_LATENCY==0)
        begin
          dout_b <= memory[memory_addr_b];
        end
        else if(HIGH_LATENCY==1)
        begin
          dout_b_registered <= memory[memory_addr_b];
          dout_b <= dout_b_registered;
        end
        else
        begin
          dout_b_registered <= memory[memory_addr_b1];
          dout_b <= dout_b_registered;
        end
      end
    end
  else
    always @(clock) dout_b = {BRAM_BITSIZE{1'b0}};
endgenerate

NP_functionality.VERILOG_PROVIDED:
reg [PORTSIZE_bram_write-1:0] bram_write1;
reg [(PORTSIZE_memory_addr_a*BITSIZE_memory_addr_a)+(-1):0] memory_addr_a1;
reg [(PORTSIZE_din_value_aggregated_swapped*BITSIZE_din_value_aggregated_swapped)+(-1):0] din_value_aggregated_swapped1;
reg [(PORTSIZE_be_swapped*BITSIZE_be_swapped)+(-1):0] be_swapped1;

generate
  if(HIGH_LATENCY==2)
  begin
    always @ (posedge clock)
    begin
       memory_addr_a1 <= memory_addr_a;
       bram_write1 <= bram_write;
       be_swapped1 <= be_swapped;
       din_value_aggregated_swapped1 <= din_value_aggregated_swapped;
    end
  end
endgenerate


generate
if(BRAM_BITSIZE == 8)
begin
  reg [(n_byte_on_databus/2)*8-1:0] dout_a_tmp;
  reg [(n_byte_on_databus/2)*8-1:0] dout_b_tmp;
  reg [(n_byte_on_databus/2)*8-1:0] dout_a_registered;
  reg [(n_byte_on_databus/2)*8-1:0] dout_b_registered;
  reg [(n_byte_on_databus/2)*8-1:0] memory [0:n_elements-1]/* synthesis syn_ramstyle = "no_rw_check" */ ;
  assign dout_a[BRAM_BITSIZE-1:0] = dout_a_tmp;
  assign dout_a[2*BRAM_BITSIZE-1:BRAM_BITSIZE] = dout_b_tmp;
  initial
  begin
    $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
  end
  if(n_elements == 1)
  begin
    always @(posedge clock)
    begin
      if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
      begin
        if (bram_write[0] && be_swapped[n_byte_on_databus_offset])
          memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]] <= din_value_aggregated_swapped[(n_byte_on_databus/2)*8+memory_offset-1:memory_offset];
      end
      else
      begin
        if (bram_write1[0] && be_swapped1[n_byte_on_databus_offset])
          memory[memory_addr_a1[BITSIZE_memory_addr_a-1:0]] <= din_value_aggregated_swapped1[(n_byte_on_databus/2)*8+memory_offset-1:memory_offset];
      end
      if(HIGH_LATENCY == 0)
        dout_a_tmp <= memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]];
      else if(HIGH_LATENCY == 1)
      begin
        dout_a_registered <= memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]];
        dout_a_tmp <= dout_a_registered;
      end
      else
      begin
        dout_a_registered <= memory[memory_addr_a1[BITSIZE_memory_addr_a-1:0]];
        dout_a_tmp <= dout_a_registered;
      end
      if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
      begin
        if (bram_write[1] && be_swapped[n_byte_on_databus+n_byte_on_databus_offset])
          memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]] <= din_value_aggregated_swapped[2*BRAM_BITSIZE+(n_byte_on_databus/2)*8+memory_offset-1:2*BRAM_BITSIZE+memory_offset];
      end
      else
      begin
        if (bram_write1[1] && be_swapped1[n_byte_on_databus+n_byte_on_databus_offset])
          memory[memory_addr_a1[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]] <= din_value_aggregated_swapped1[2*BRAM_BITSIZE+(n_byte_on_databus/2)*8+memory_offset-1:2*BRAM_BITSIZE+memory_offset];
      end
      if(HIGH_LATENCY == 0)
        dout_b_tmp <= memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
      else if(HIGH_LATENCY == 1)
      begin
        dout_b_registered <= memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
        dout_b_tmp <= dout_b_registered;
      end
      else
      begin
        dout_b_registered <= memory[memory_addr_a1[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
        dout_b_tmp <= dout_b_registered;
      end
    end
  end
  else
  begin
    always @(posedge clock)
    begin
      if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
      begin
        if (bram_write[0] && be_swapped[n_byte_on_databus_offset])
          memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]] <= din_value_aggregated_swapped[(n_byte_on_databus/2)*8+memory_offset-1:memory_offset];
      end
      else
      begin
        if (bram_write1[0] && be_swapped1[n_byte_on_databus_offset])
          memory[memory_addr_a1[BITSIZE_memory_addr_a-1:0]] <= din_value_aggregated_swapped1[(n_byte_on_databus/2)*8+memory_offset-1:memory_offset];
      end
      if(HIGH_LATENCY == 0)
        dout_a_tmp <= memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]];
      else if(HIGH_LATENCY == 1)
      begin
        dout_a_registered <= memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]];
        dout_a_tmp <= dout_a_registered;
      end
      else
      begin
        dout_a_registered <= memory[memory_addr_a1[BITSIZE_memory_addr_a-1:0]];
        dout_a_tmp <= dout_a_registered;
      end
    end
    always @(posedge clock)
    begin
      if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
      begin
        if (bram_write[1] && be_swapped[n_byte_on_databus+n_byte_on_databus_offset])
          memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]] <= din_value_aggregated_swapped[2*BRAM_BITSIZE+(n_byte_on_databus/2)*8+memory_offset-1:2*BRAM_BITSIZE+memory_offset];
      end
      else
      begin
        if (bram_write1[1] && be_swapped1[n_byte_on_databus+n_byte_on_databus_offset])
          memory[memory_addr_a1[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]] <= din_value_aggregated_swapped1[2*BRAM_BITSIZE+(n_byte_on_databus/2)*8+memory_offset-1:2*BRAM_BITSIZE+memory_offset];
      end
      if(HIGH_LATENCY == 0)
        dout_b_tmp <= memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
      else if(HIGH_LATENCY == 1)
      begin
        dout_b_registered <= memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
        dout_b_tmp <= dout_b_registered;
      end
      else
      begin
        dout_b_registered <= memory[memory_addr_a1[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
        dout_b_tmp <= dout_b_registered;
      end
    end
  end
end
else
begin
  if(n_elements == 1)
  begin
    reg [(n_byte_on_databus/2)*8-1:0] dout_a_tmp;
    reg [(n_byte_on_databus/2)*8-1:0] dout_b_tmp;
    reg [(n_byte_on_databus/2)*8-1:0] dout_a_registered;
    reg [(n_byte_on_databus/2)*8-1:0] dout_b_registered;
    reg [(n_byte_on_databus/2)*8-1:0] memory [0:n_elements-1]/* synthesis syn_ramstyle = "no_rw_check" */ ;
    assign dout_a[BRAM_BITSIZE-1:0] = dout_a_tmp;
    assign dout_a[2*BRAM_BITSIZE-1:BRAM_BITSIZE] = dout_b_tmp;
    initial
    begin
      $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
    end
    always @(posedge clock)
    begin
      if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
      begin
        if (bram_write[0])
        begin : L11_write
          integer i11;
          for (i11=0; i11<n_byte_on_databus/2; i11=i11+1)
          begin
            if(be_swapped[i11+n_byte_on_databus_offset])
              memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]][i11*8+:8] <= din_value_aggregated_swapped[i11*8+memory_offset+:8];
          end
        end
      end
      else
      begin
        if (bram_write1[0])
        begin : L11_write1
          integer i11;
          for (i11=0; i11<n_byte_on_databus/2; i11=i11+1)
          begin
            if(be_swapped1[i11+n_byte_on_databus_offset])
              memory[memory_addr_a1[BITSIZE_memory_addr_a-1:0]][i11*8+:8] <= din_value_aggregated_swapped1[i11*8+memory_offset+:8];
          end
        end
      end
      if(HIGH_LATENCY == 0)
        dout_a_tmp <= memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]];
      else if(HIGH_LATENCY == 1)
      begin
        dout_a_registered <= memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]];
        dout_a_tmp <= dout_a_registered;
      end
      else
      begin
        dout_a_registered <= memory[memory_addr_a1[BITSIZE_memory_addr_a-1:0]];
        dout_a_tmp <= dout_a_registered;
      end
      if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
      begin
        if (bram_write[1])
        begin : L22_write
          integer i22;
          for (i22=0; i22<n_byte_on_databus/2; i22=i22+1)
          begin
            if(be_swapped[i22+n_byte_on_databus+n_byte_on_databus_offset])
              memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]][i22*8+:8] <= din_value_aggregated_swapped[2*BRAM_BITSIZE+memory_offset+i22*8+:8];
          end
        end
      end
      else
      begin
        if (bram_write1[1])
        begin : L22_write1
          integer i22;
          for (i22=0; i22<n_byte_on_databus/2; i22=i22+1)
          begin
            if(be_swapped1[i22+n_byte_on_databus+n_byte_on_databus_offset])
              memory[memory_addr_a1[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]][i22*8+:8] <= din_value_aggregated_swapped1[2*BRAM_BITSIZE+memory_offset+i22*8+:8];
          end
        end
      end
      if(HIGH_LATENCY == 0)
        dout_b_tmp <= memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
      else if(HIGH_LATENCY == 1)
      begin
        dout_b_registered <= memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
        dout_b_tmp <= dout_b_registered;
      end
      else
      begin
        dout_b_registered <= memory[memory_addr_a1[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
        dout_b_tmp <= dout_b_registered;
      end
    end
  end
  else
    BRAM_MEMORY_NN_SV_CORE #(.PORTSIZE_bram_write(PORTSIZE_bram_write),
  .BITSIZE_bram_write(BITSIZE_bram_write),
  .BITSIZE_dout_a(BITSIZE_dout_a),
  .PORTSIZE_dout_a(PORTSIZE_dout_a),
  .BITSIZE_memory_addr_a(BITSIZE_memory_addr_a),
  .PORTSIZE_memory_addr_a(PORTSIZE_memory_addr_a),
  .BITSIZE_din_value_aggregated_swapped(BITSIZE_din_value_aggregated_swapped),
  .PORTSIZE_din_value_aggregated_swapped(PORTSIZE_din_value_aggregated_swapped),
  .BITSIZE_be_swapped(BITSIZE_be_swapped),
  .PORTSIZE_be_swapped(PORTSIZE_be_swapped),
  .MEMORY_INIT_file(MEMORY_INIT_file),
  .BRAM_BITSIZE(BRAM_BITSIZE),
  .nbit_read_addr(nbit_read_addr),
  .n_byte_on_databus(n_byte_on_databus),
  .n_elements(n_elements),
  .max_n_reads(max_n_reads),
  .max_n_writes(max_n_writes),
  .memory_offset(memory_offset),
  .n_byte_on_databus_offset(n_byte_on_databus_offset),
  .READ_ONLY_MEMORY(READ_ONLY_MEMORY),
  .HIGH_LATENCY(HIGH_LATENCY)) BRAM_MEMORY_NN_CORE_instance_a(.clock(clock),
  .bram_write(bram_write),
  .memory_addr_a(memory_addr_a),
  .din_value_aggregated_swapped(din_value_aggregated_swapped),
  .be_swapped(be_swapped),
  .dout_a(dout_a));

end
endgenerate

NP_functionality.SYSTEM_VERILOG_PROVIDED:
wire [(n_byte_on_databus/2)*max_n_writes-1:0] we_a;
reg [PORTSIZE_dout_a*BITSIZE_dout_a-1:0] dout_a;
reg [PORTSIZE_dout_a*BITSIZE_dout_a-1:0] dout_a_registered;
reg [(PORTSIZE_memory_addr_a*BITSIZE_memory_addr_a)+(-1):0] memory_addr_a1;
reg [(PORTSIZE_din_value_aggregated_swapped*BITSIZE_din_value_aggregated_swapped)+(-1):0] din_value_aggregated_swapped1;
reg [(n_byte_on_databus/2)*max_n_writes-1:0] we_a1;

(* syn_ramstyle = "no_rw_check" *) reg [(BRAM_BITSIZE/8)-1:0][7:0] memory_a [0:n_elements-1];
initial
begin
  $readmemb(MEMORY_INIT_file, memory_a, 0, n_elements-1);
end

generate
  if(HIGH_LATENCY==2)
  begin
    always @ (posedge clock)
    begin
       memory_addr_a1 <= memory_addr_a;
       we_a1 <= we_a;
       din_value_aggregated_swapped1 <= din_value_aggregated_swapped;
    end
  end
endgenerate

generate
  if(n_byte_on_databus==2)
  begin
    always @(posedge clock)
    begin
      if(HIGH_LATENCY==0 || HIGH_LATENCY==1)
      begin
        if(we_a[0+n_byte_on_databus/2*0])
          memory_a[memory_addr_a[nbit_read_addr*(0+1)-1:nbit_read_addr*0]][0] <= din_value_aggregated_swapped[2*BRAM_BITSIZE*0+(0+1)*8+memory_offset-1:2*BRAM_BITSIZE*0+0*8+memory_offset];
      end
      else
