NP_functionality.VHDL_PROVIDED:
function integer_ternary_operator(cond : boolean; op1 : integer; op2 : integer) return integer is
   begin
      if cond then
         return op1;
      else
         return op2;
      end if;
   end integer_ternary_operator;
constant n_bytes           : integer := (n_elements*data_size)/8;
constant memory_bitsize    : integer := 2*BRAM_BITSIZE;
constant n_byte_on_databus : integer := memory_bitsize/8;
constant n_elements_bus    : integer := n_bytes/n_byte_on_databus + integer_ternary_operator((n_bytes mod n_byte_on_databus) = 0, 0, 1);
constant nbit_read_addr    : integer := integer_ternary_operator(n_elements_bus = 1, 1, integer(ceil(log2(real(n_elements_bus)))));
constant max_n_writes      : integer := integer_ternary_operator(PORTSIZE_sel_STORE > PORTSIZE_S_we_ram, PORTSIZE_sel_STORE, PORTSIZE_S_we_ram);
constant max_n_reads       : integer := integer_ternary_operator(PORTSIZE_sel_LOAD > PORTSIZE_S_oe_ram, PORTSIZE_sel_LOAD, PORTSIZE_S_oe_ram);
constant max_n_rw          : integer := integer_ternary_operator(max_n_writes > max_n_reads, max_n_writes, max_n_reads);

signal bram_write : std_logic_vector(max_n_writes-1 downto 0);

signal memory_addr_a : std_logic_vector(nbit_read_addr*max_n_rw-1 downto 0);
signal memory_addr_b : std_logic_vector(nbit_read_addr*max_n_rw-1 downto 0);
signal be_swapped    : std_logic_vector(n_byte_on_databus*max_n_writes-1 downto 0);

signal din_value_aggregated_swapped : std_logic_vector(memory_bitsize*max_n_writes-1 downto 0);
signal dout_a : std_logic_vector((memory_bitsize/2)*max_n_reads-1 downto 0);
signal dout_b : std_logic_vector((memory_bitsize/2)*max_n_reads-1 downto 0);

begin
BRAM_MEMORY_NN_instance_a : BRAM_MEMORY_NN_CORE generic map (PORTSIZE_bram_write=>max_n_writes, BITSIZE_bram_write=>1, BITSIZE_dout_a=>memory_bitsize/2, PORTSIZE_dout_a=>max_n_reads, BITSIZE_memory_addr_a=>nbit_read_addr, PORTSIZE_memory_addr_a=>max_n_rw, BITSIZE_din_value_aggregated_swapped=>memory_bitsize, PORTSIZE_din_value_aggregated_swapped=>max_n_writes, BITSIZE_be_swapped=>n_byte_on_databus, PORTSIZE_be_swapped=>max_n_writes, MEMORY_INIT_file=>MEMORY_INIT_file_a, BRAM_BITSIZE=>BRAM_BITSIZE, READ_ONLY_MEMORY=>READ_ONLY_MEMORY, HIGH_LATENCY=>HIGH_LATENCY, n_bytes=>n_bytes, n_byte_on_databus=>n_byte_on_databus, n_elements=>n_elements_bus, max_n_reads=>max_n_reads, max_n_writes=>max_n_writes, memory_offset=>0, n_byte_on_databus_offset=>0) port map (clock=>clock, bram_write=>bram_write, memory_addr_a=>memory_addr_a, din_value_aggregated_swapped=>din_value_aggregated_swapped, be_swapped=>be_swapped, dout_a=>dout_a);

SECOND_MEMORY : if (n_bytes > BRAM_BITSIZE/8) generate
  BRAM_MEMORY_NN_instance_b : BRAM_MEMORY_NN_CORE generic map (PORTSIZE_bram_write=>max_n_writes, BITSIZE_bram_write=>1, BITSIZE_dout_a=>(memory_bitsize/2), PORTSIZE_dout_a=>max_n_reads, BITSIZE_memory_addr_a=>nbit_read_addr, PORTSIZE_memory_addr_a=>max_n_rw, BITSIZE_din_value_aggregated_swapped=>memory_bitsize, PORTSIZE_din_value_aggregated_swapped=>max_n_writes, BITSIZE_be_swapped=>n_byte_on_databus, PORTSIZE_be_swapped=>max_n_writes, MEMORY_INIT_file=>MEMORY_INIT_file_b, BRAM_BITSIZE=>BRAM_BITSIZE, READ_ONLY_MEMORY=>READ_ONLY_MEMORY, HIGH_LATENCY=>HIGH_LATENCY, n_bytes=>n_bytes, n_byte_on_databus=>n_byte_on_databus, n_elements=>n_elements_bus, max_n_reads=>max_n_reads, max_n_writes=>max_n_writes, memory_offset=>memory_bitsize/2, n_byte_on_databus_offset=>n_byte_on_databus/2) port map (clock=>clock, bram_write=>bram_write, memory_addr_a=>memory_addr_b, din_value_aggregated_swapped=>din_value_aggregated_swapped, be_swapped=>be_swapped, dout_a=>dout_b);
end generate;
SECOND_MEMORY_else : if(n_bytes <= BRAM_BITSIZE/8) generate
  dout_b <= (others => 'X');
end generate;

ADDRESS_DECODING_LOGIC_NN_instance : ADDRESS_DECODING_LOGIC_NN generic map (BITSIZE_in1=>BITSIZE_in1, PORTSIZE_in1=>PORTSIZE_in1, BITSIZE_in2=>BITSIZE_in2, PORTSIZE_in2=>PORTSIZE_in2, BITSIZE_in3=>BITSIZE_in3, PORTSIZE_in3=>PORTSIZE_in3, BITSIZE_sel_LOAD=>BITSIZE_sel_LOAD, PORTSIZE_sel_LOAD=>PORTSIZE_sel_LOAD, BITSIZE_sel_STORE=>BITSIZE_sel_STORE, PORTSIZE_sel_STORE=>PORTSIZE_sel_STORE, BITSIZE_out1=>BITSIZE_out1, PORTSIZE_out1=>PORTSIZE_out1, BITSIZE_S_oe_ram=>BITSIZE_S_oe_ram, PORTSIZE_S_oe_ram=>PORTSIZE_S_oe_ram, BITSIZE_S_we_ram=>BITSIZE_S_we_ram, PORTSIZE_S_we_ram=>PORTSIZE_S_we_ram, BITSIZE_Sin_DataRdy=>BITSIZE_Sin_DataRdy, PORTSIZE_Sin_DataRdy=>PORTSIZE_Sin_DataRdy, BITSIZE_Sout_DataRdy=>BITSIZE_Sout_DataRdy, PORTSIZE_Sout_DataRdy=>PORTSIZE_Sout_DataRdy, BITSIZE_S_addr_ram=>BITSIZE_S_addr_ram, PORTSIZE_S_addr_ram=>PORTSIZE_S_addr_ram, BITSIZE_S_Wdata_ram=>BITSIZE_S_Wdata_ram, PORTSIZE_S_Wdata_ram=>PORTSIZE_S_Wdata_ram, BITSIZE_Sin_Rdata_ram=>BITSIZE_Sin_Rdata_ram, PORTSIZE_Sin_Rdata_ram=>PORTSIZE_Sin_Rdata_ram, BITSIZE_Sout_Rdata_ram=>BITSIZE_Sout_Rdata_ram, PORTSIZE_Sout_Rdata_ram=>PORTSIZE_Sout_Rdata_ram, BITSIZE_S_data_ram_size=>BITSIZE_S_data_ram_size, PORTSIZE_S_data_ram_size=>PORTSIZE_S_data_ram_size, address_space_begin=>address_space_begin, address_space_rangesize=>address_space_rangesize, BUS_PIPELINED=>BUS_PIPELINED, BRAM_BITSIZE=>BRAM_BITSIZE, PRIVATE_MEMORY=>PRIVATE_MEMORY, READ_ONLY_MEMORY=>READ_ONLY_MEMORY, USE_SPARSE_MEMORY=>USE_SPARSE_MEMORY, HIGH_LATENCY=>HIGH_LATENCY, BITSIZE_proxy_in1=>BITSIZE_proxy_in1, PORTSIZE_proxy_in1=>PORTSIZE_proxy_in1, BITSIZE_proxy_in2=>BITSIZE_proxy_in2, PORTSIZE_proxy_in2=>PORTSIZE_proxy_in2, BITSIZE_proxy_in3=>BITSIZE_proxy_in3, PORTSIZE_proxy_in3=>PORTSIZE_proxy_in3, BITSIZE_proxy_sel_LOAD=>BITSIZE_proxy_sel_LOAD, PORTSIZE_proxy_sel_LOAD=>PORTSIZE_proxy_sel_LOAD, BITSIZE_proxy_sel_STORE=>BITSIZE_proxy_sel_STORE, PORTSIZE_proxy_sel_STORE=>PORTSIZE_proxy_sel_STORE, BITSIZE_proxy_out1=>BITSIZE_proxy_out1, PORTSIZE