reg write_enable1;

reg [7:0] data_out_mem;
reg [7:0] data_out1;

wire [7:0] data_in_mem;
reg [7:0] data_in1;
integer index;

reg [BRAM_BITSIZE-1:0] memory [0:n_elements-1]/* synthesis syn_ramstyle =  "no_rw_check" */;

initial
begin
  if (MEMORY_INIT_file != "")
    $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
  else
  begin
    for(index=0; index<n_elements; index=index+1)
    begin
      memory[index] = 0;
    end
  end
end

always @(posedge clock)
begin
  if(READ_ONLY_MEMORY==0)
  begin
    if (write_enable_mem)
      memory[address_inw_mem][(byte_offset+1)*8-1:byte_offset*8] <= data_in_mem;
  end
  data_out_mem <= memory[address_inr_mem][(byte_offset+1)*8-1:byte_offset*8];
end

assign data_out = HIGH_LATENCY==0 ? data_out_mem : data_out1;
always @(posedge clock)
  data_out1 <= data_out_mem;


generate
  if(HIGH_LATENCY==2)
  begin
    always @ (posedge clock)
    begin
       address_inr1 <= address_inr;
       address_inw1 <= address_inw;
       write_enable1 <= write_enable;
       data_in1 <= data_in;
    end
    assign address_inr_mem = address_inr1;
    assign address_inw_mem = address_inw1;
    assign write_enable_mem = write_enable1;
    assign data_in_mem = data_in1;
  end
  else
  begin
    assign address_inr_mem = address_inr;
    assign address_inw_mem = address_inw;
    assign write_enable_mem = write_enable;
    assign data_in_mem = data_in;
  end
endgenerate

wire [BITSIZE_address_inr-1:0] address_inr_mem;
reg [BITSIZE_address_inr-1:0] address_inr1;
reg [BITSIZE_address_inr-1:0] address_inr_mem1;
wire [BITSIZE_address_inw-1:0] address_inw_mem;
reg [BITSIZE_address_inw-1:0] address_inw1;
reg [BITSIZE_address_inw-1:0] address_inw_mem1;

wire write_enable_mem;
reg write_enable1;
reg write_enable_mem1;

reg [7:0] data_out_mem_temp;
reg [7:0] data_out1;
wire [7:0] data_out_mem;

wire [7:0] data_in_mem;
reg [7:0] data_in1;
reg [7:0] data_in_mem1;
integer index;

reg [BRAM_BITSIZE-1:0] memory [0:n_elements-1]/* synthesis syn_ramstyle =  "no_rw_check" */;

initial
begin
  if (MEMORY_INIT_file != "")
    $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
  else
  begin
    for(index=0; index<n_elements; index=index+1)
    begin
      memory[index] = 0;
    end
  end
end

always @(posedge clock)
begin
  if(READ_ONLY_MEMORY==0)
  begin
    if (write_enable_mem)
      memory[address_inw_mem][(byte_offset+1)*8-1:byte_offset*8] <= data_in_mem;
  end
  data_out_mem_temp <= memory[address_inr_mem][(byte_offset+1)*8-1:byte_offset*8];
end

assign data_out_mem = write_enable_mem1 && (address_inr_mem1 == address_inw_mem1) ? data_in_mem1 : data_out_mem_temp;

assign data_out = HIGH_LATENCY==0 ? data_out_mem : data_out1;
always @(posedge clock)
  data_out1 <= data_out_mem;

always @ (posedge clock)
begin
  address_inr_mem1 <= address_inr_mem;
  address_inw_mem1 <= address_inw_mem;
  write_enable_mem1 <= write_enable_mem;
  data_in_mem1 <= data_in_mem;
end

generate
  if(HIGH_LATENCY==2)
  begin
    always @ (posedge clock)
    begin
       address_inr1 <= address_inr;
       address_inw1 <= address_inw;
       write_enable1 <= write_enable;
       data_in1 <= data_in;
    end
    assign address_inr_mem = address_inr1;
    assign address_inw_mem = address_inw1;
    assign write_enable_mem = write_enable1;
    assign data_in_mem = data_in1;
  end
  else
  begin
    assign address_inr_mem = address_inr;
    assign address_inw_mem = address_inw;
    assign write_enable_mem = write_enable;
    assign data_in_mem = data_in;
  end
endgenerate

wire [BITSIZE_dout-1:0] din_array [0:PORTSIZE_din-1];
genvar i1;
generate
  for(i1=0; i1<PORTSIZE_din; i1=i1+1)
  begin: L1
    assign din_array[i1] = din[(i1+1)*BITSIZE_dout-1:i1*BITSIZE_dout];
  end
endgenerate

assign dout = din_array[ctrl];

parameter max_portsize = PORTSIZE_address_inw > PORTSIZE_address_inr ? PORTSIZE_address_inw : PORTSIZE_address_inr;

reg [(PORTSIZE_read_selector*BITSIZE_read_selector)+(-1):0] read_selector_mem;
reg [(PORTSIZE_read_selector*BITSIZE_read_selector)+(-1):0] read_selector1;
wire [PORTSIZE_write_enable-1:0] write_enable_mem;
reg [PORTSIZE_write_enable-1:0] write_enable1;
wire [(PORTSIZE_address_inw*BITSIZE_address_inw)+(-1):0] address_inw_mem;
reg [(PORTSIZE_address_inw*BITSIZE_address_inw)+(-1):0] address_inw1;
wire [(PORTSIZE_address_inr*BITSIZE_address_inr)+(-1):0] address_inr_mem;
reg [(PORTSIZE_address_inr*BITSIZE_address_inr)+(-1):0] address_inr1;

reg [BITSIZE_read_selector-1:0] live_value_table [0:n_elements-1] /* synthesis syn_ramstyle = "registers,no_rw_check" */;
integer index, i1;

initial
begin
  for(index=0; index<n_elements; index=index+1)
  begin
    live_value_table[index] = 0;
  end
end

always @(posedge clock)
begin
  for (i1=0; i1<max_portsize; i1=i1+1)
  begin : L1
    if(i1 < PORTSIZE_address_inw && READ_ONLY_MEMORY==0)
    begin
      if(write_enable_mem[i1])
        live_value_table[address_inw_mem[i1*BITSIZE_address_inw +: BITSIZE_address_inw]] <= i1[BITSIZE_read_selector-1:0];
    end
    if(i1 < PORTSIZE_address_inr)
    begin
      read_selector_mem[i1*BITSIZE_read_selector +: BITSIZE_read_selector] <= live_value_table[address_inr_mem[i1*BITSIZE_address_inr +: BITSIZE_address_inr]];
    end
  end
end

assign read_selector = HIGH_LATENCY==0 ? read_selector_mem : read_selector1;
always @(posedge clock)
  read_selector1 <= read_selector_mem;

generate
  if(HIGH_LATENCY==2)
  begin
    always @ (posedge clock)
    begin
       address_inr1 <= address_inr;
       address_inw1 <= address_inw;
       write_enable1 <= write_enable;
    end
    assign address_inr_mem = address_inr1;
    assign address_inw_mem = address_inw1;
    assign write_enable_mem = write_enable1;
  end
  else
  begin
    assign address_inr_mem = address_inr;
    assign address_inw_mem = address_inw;
    assign write_enable_mem = write_enable;
  end
endgenerate

wire [PORTSIZE_address_inw*BITSIZE_read_selector-1:0] BankSelectorMask;
reg [PORTSIZE_address_inw*BITSIZE_read_selector-1:0] WriteFeedBackData;
wire [BITSIZE_read_selector*(PORTSIZE_address_inw*(PORTSIZE_address_inw-1))-1:0] ReadFeedBackData;
reg [BITSIZE_address_inw*(PORTSIZE_address_inw*(PORTSIZE_address_inw-1))-1:0] ReadFeedBackAddr;
reg [BITSIZE_read_selector*PORTSIZE_address_inr-1:0] ReadSelector;
wire [BITSIZE_read_selector*PORTSIZE_address_inr*PORTSIZE_address_inw-1:0] ReadDataOut;

reg [PORTSIZE_write_enable-1:0] write_enable1;
reg [(PORTSIZE_address_inw*BITSIZE_address_inw)+(-1):0] address_inw1;

assign read_selector = ReadSelector;

genvar indexID;
generate
  for (indexID=0;indexID<PORTSIZE_address_inw;indexID=indexID+1)
  begin
    assign  BankSelectorMask[indexID*BITSIZE_read_selector+:BITSIZE_read_selector]=indexID;
  end
endgenerate

  integer i1,i2,i3;
  always @(*)
  begin
    for(i1=0;i1<PORTSIZE_address_inr;i1=i1+1)
    begin
      ReadSelector[i1*BITSIZE_read_selector+:BITSIZE_read_selector] = ReadDataOut[i1*BITSIZE_read_selector+:BITSIZE_read_selector];
      for(i2=1;i2<PORTSIZE_address_inw;i2=i2+1)
      begin
        ReadSelector[i1*BITSIZE_read_selector+:BITSIZE_read_selector] = ReadSelector[i1*BITSIZE_read_selector+:BITSIZE_read_selector]^ReadDataOut[(i2*PORTSIZE_address_inw+i1)*BITSIZE_read_selector+:BITSIZE_read_selector];
      end
    end
    for(i1=0;i1<PORTSIZE_address_inw;i1=i1+1)
      WriteFeedBackData[i1*BITSIZE_read_selector+:BITSIZE_read_selector] = BankSelectorMask[i1*BITSIZE_read_selector+:BITSIZE_read_selector];
    for(i1=0;i1<PORTSIZE_address_inw;i1=i1+1)
    begin
      i3 = 0;
      for(i2=0;i2<PORTSIZE_address_inw-1;i2=i2+1)
      begin
        i3=i3+(i2==i1);
        ReadFeedBackAddr[(i1*(PORTSIZE_address_inw-1)+i2)*BITSIZE_address_inw+:BITSIZE_address_inw] = address_inw[i3*BITSIZE_address_inw+:BITSIZE_address_inw];
        WriteFeedBackData[i3*BITSIZE_read_selector+:BITSIZE_read_selector] = WriteFeedBackData[i3*BITSIZE_read_selector+:BITSIZE_read_selector]^ReadFeedBackData[(i1*(PORTSIZE_address_inw-1)+i2)*BITSIZE_read_selector+:BITSIZE_read_selector];
        i3=i3+1;
      end
    end
  end

always @(posedge clock)
begin
  write_enable1 <= write_enable;
  address_inw1 <= address_inw;
end

generate
genvar i4;
  for (i4=0; i4<PORTSIZE_address_inw; i4=i4+1)
  begin : L1
    STD_NR_BRAM #(
      .PORTSIZE_address_inr(PORTSIZE_address_inw-1),
      .BITSIZE_address_inr(BITSIZE_address_inr),
      .BITSIZE_address_inw(BITSIZE_address_inw),
      .BITSIZE_data_in(BITSIZE_read_selector),
      .BITSIZE_data_out(BITSIZE_read_selector),
      .PORTSIZE_data_out(PORTSIZE_address_inw-1),
      .MEMORY_INIT_file(""),
      .n_elements(n_elements),
      .forwarding(1),
      .READ_ONLY_MEMORY(READ_ONLY_MEMORY),
      .HIGH_LATENCY(HIGH_LATENCY)
    )
    FeadbackRam (
      .clock(clock),
      .write_enable(write_enable1[i4]),
      .address_inr(ReadFeedBackAddr[i4*(BITSIZE_address_inw*(PORTSIZE_address_inw-1))+:(BITSIZE_address_inw*(PORTSIZE_address_inw-1))]),
      .address_inw(address_inw1[i4*BITSIZE_address_inw+:BITSIZE_address_inw]),
      .data_in(WriteFeedBackData[i4*BITSIZE_read_selector+:BITSIZE_read_selector]),
      .data_out(ReadFeedBackData[i4*BITSIZE_read_selector*(PORTSIZE_address_inw-1)+:BITSIZE_read_selector*(PORTSIZE_address_inw-1)]));

    STD_NR_BRAM #(
      .PORTSIZE_address_inr(PORTSIZE_address_inr),
      .BITSIZE_address_inr(BITSIZE_address_inr),
      .BITSIZE_address_inw(BITSIZE_address_inw),
      .BITSIZE_data_in(BITSIZE_read_selector),
      .BITSIZE_data_out(BITSIZE_read_selector),
      .PORTSIZE_data_out(PORTSIZE_address_inr),
      .MEMORY_INIT_file(""),
      .n_elements(n_elements),
      .forwarding(1),
      .READ_ONLY_MEMORY(READ_ONLY_MEMORY),
      .HIGH_LATENCY(HIGH_LATENCY)
    )
    OutputRam (
      .clock(clock),
      .write_enable(write_enable1[i4]),
      .address_inr(address_inr),
      .address_inw(address_inw1[i4*BITSIZE_address_inw+:BITSIZE_address_inw]),
      .data_in(WriteFeedBackData[i4*BITSIZE_read_selector+:BITSIZE_read_selector]),
      .data_out(ReadDataOut[i4*BITSIZE_read_selector*(PORTSIZE_address_inr)+:BITSIZE_read_selector*(PORTSIZE_address_inr)]));
  end
endgenerate

`ifndef _SIM_HAVE_CLOG2
  function integer log2;
     input integer value;
     integer temp_value;
    begin
      temp_value = value-1;
      for (log2=0; temp_value>0; log2=log2+1)
        temp_value = temp_value>>1;
    end
  endfunction
`endif
`ifdef _SIM_HAVE_CLOG2
  localparam nbit_write = PORTSIZE_address_inw == 1 ? 1 : $clog2(PORTSIZE_address_inw);
`else
  localparam nbit_write = PORTSIZE_address_inw == 1 ? 1 : log2(PORTSIZE_address_inw);
`endif

reg [PORTSIZE_data_in*BITSIZE_data_in-1:0] WriteFeedBackData;
wire [BITSIZE_dout_value*(PORTSIZE_address_inw*(PORTSIZE_address_inw-1))-1:0] ReadFeedBackData;
reg [BITSIZE_address_inw*(PORTSIZE_address_inw*(PORTSIZE_address_inw-1))-1:0] ReadFeedBackAddr;
reg [BITSIZE_dout_value*PORTSIZE_dout_value-1:0] ReadData;
wire [BITSIZE_dout_value*PORTSIZE_dout_value*PORTSIZE_address_inw-1:0] ReadDataOut;

wire [PORTSIZE_write_enable-1:0] write_enable_mem;
wire [PORTSIZE_address_inw*BITSIZE_address_inw-1:0] address_inw_mem;
wire [PORTSIZE_address_inr*BITSIZE_address_inr-1:0] address_inr_mem;
wire [PORTSIZE_data_in*BITSIZE_data_in-1:0] data_in_mem;
wire [PORTSIZE_dout_value*BITSIZE_dout_value-1:0] dout_value_mem;
reg [PORTSIZE_dout_value*BITSIZE_dout_value-1:0] dout_value_mem1;

reg [PORTSIZE_write_enable-1:0] write_enable_mem1;
reg [PORTSIZE_address_inw*BITSIZE_address_inw-1:0] address_inw_mem1;
reg [PORTSIZE_data_in*BITSIZE_data_in-1:0] data_in_mem1;

reg [PORTSIZE_write_enable-1:0] write_enable1;
reg [PORTSIZE_address_inw*BITSIZE_address_inw-1:0] address_inw1;
reg [PORTSIZE_address_inr*BITSIZE_address_inr-1:0] address_inr1;
reg [PORTSIZE_data_in*BITSIZE_data_in-1:0] data_in1;

assign dout_value = HIGH_LATENCY==0 ? dout_value_mem : dout_value_mem1;
always @(posedge clock)
  dout_value_mem1 <= dout_value_mem;


generate
  if(HIGH_LATENCY==2)
  begin
    always @ (posedge clock)
    begin
       address_inr1 <= address_inr;
       address_inw1 <= address_inw;
       write_enable1 <= write_enable;
       data_in1 <= data_in;
    end
    assign address_inr_mem = address_inr1;
    assign address_inw_mem = address_inw1;
    assign write_enable_mem = write_enable1;
    assign data_in_mem = data_in1;
  end
  else
  begin
    assign address_inr_mem = address_inr;
    assign address_inw_mem = address_inw;
    assign write_enable_mem = write_enable;
    assign data_in_mem = data_in;
  end
endgenerate

always @(posedge clock)
begin
  write_enable_mem1 <= write_enable_mem;
  address_inw_mem1 <= address_inw_mem;
  data_in_mem1 <= data_in_mem;
end

assign dout_value_mem = ReadData;

generate
genvar ii1;
  for (ii1=0; ii1<PORTSIZE_address_inw; ii1=ii1+1)
  begin : L1
    STD_NR_BRAM #(
      .PORTSIZE_address_inr(PORTSIZE_address_inw-1),
      .BITSIZE_address_inr(BITSIZE_address_inr),
      .BITSIZE_address_inw(BITSIZE_address_inw),
      .BITSIZE_data_in(BITSIZE_data_in),
      .BITSIZE_data_out(BITSIZE_dout_value),
      .PORTSIZE_data_out(PORTSIZE_address_inw-1),
      .MEMORY_INIT_file(ii1 == 0 ? MEMORY_INIT_file : ""),
      .n_elements(n_elements),
      .forwarding(1),
      .READ_ONLY_MEMORY(READ_ONLY_MEMORY),
      .HIGH_LATENCY(0)
    )
    STD_NR_BRAM_FB_instance (
      .clock(clock),
      .write_enable(write_enable_mem1[ii1]),
      .address_inr(ReadFeedBackAddr[ii1*(BITSIZE_address_inw*(PORTSIZE_address_inw-1))+:(BITSIZE_address_inw*(PORTSIZE_address_inw-1))]),
      .address_inw(address_inw_mem1[ii1*BITSIZE_address_inw+:BITSIZE_address_inw]),
      .data_in(WriteFeedBackData[ii1*BITSIZE_data_in+:BITSIZE_data_in]),
      .data_out(ReadFeedBackData[ii1*BITSIZE_dout_value*(PORTSIZE_address_inw-1)+:BITSIZE_dout_value*(PORTSIZE_address_inw-1)]));

    STD_NR_BRAM #(
      .PORTSIZE_address_inr(PORTSIZE_address_inr),
      .BITSIZE_address_inr(BITSIZE_address_inr),
      .BITSIZE_address_inw(BITSIZE_address_inw),
      .BITSIZE_data_in(BITSIZE_data_in),
      .BITSIZE_data_out(BITSIZE_dout_value),
      .PORTSIZE_data_out(PORTSIZE_address_inr),
      .MEMORY_INIT_file(ii1 == 0 ? MEMORY_INIT_file : ""),
      .n_elements(n_elements),
      .forwarding(1),
      .READ_ONLY_MEMORY(READ_ONLY_MEMORY),
      .HIGH_LATENCY(0)
    )
    STD_NR_BRAM_instance (
      .clock(clock),
      .write_enable(write_enable_mem1[ii1]),
      .address_inr(address_inr_mem),
      .address_inw(address_inw_mem1[ii1*BITSIZE_address_inw+:BITSIZE_address_inw]),
      .data_in(WriteFeedBackData[ii1*BITSIZE_data_in+:BITSIZE_data_in]),
      .data_out(ReadDataOut[ii1*BITSIZE_dout_value*(PORTSIZE_address_inr)+:BITSIZE_dout_value*(PORTSIZE_address_inr)]));
  end
endgenerate
integer i1,i2,i3;
always @(*)
begin
  for(i1=0;i1<PORTSIZE_address_inr;i1=i1+1)
  begin
    ReadData[i1*BITSIZE_dout_value+:BITSIZE_dout_value] = ReadDataOut[i1*BITSIZE_dout_value+:BITSIZE_dout_value];
    for(i2=1;i2<PORTSIZE_address_inw;i2=i2+1)
    begin
      ReadData[i1*BITSIZE_dout_value+:BITSIZE_dout_value] = ReadData[i1*BITSIZE_dout_value+:BITSIZE_dout_value]^ReadDataOut[(i2*PORTSIZE_address_inw+i1)*BITSIZE_dout_value+:BITSIZE_dout_value];
    end
  end
  for(i1=0;i1<PORTSIZE_address_inw;i1=i1+1)
    WriteFeedBackData[i1*BITSIZE_data_in+:BITSIZE_data_in] = data_in_mem1[i1*BITSIZE_data_in+:BITSIZE_data_in];
  for(i1=0;i1<PORTSIZE_address_inw;i1=i1+1)
  begin
    i3 = 0;
    for(i2=0;i2<PORTSIZE_address_inw-1;i2=i2+1)
    begin
      i3=i3+(i2==i1);
      ReadFeedBackAddr[(i1*(PORTSIZE_address_inw-1)+i2)*BITSIZE_address_inw+:BITSIZE_address_inw] = address_inw_mem[i3*BITSIZE_address_inw+:BITSIZE_address_inw];
      WriteFeedBackData[i3*BITSIZE_data_in+:BITSIZE_data_in] = WriteFeedBackData[i3*BITSIZE_data_in+:BITSIZE_data_in]^ReadFeedBackData[(i1*(PORTSIZE_address_inw-1)+i2)*BITSIZE_data_in+:BITSIZE_data_in];
      i3=i3+1;
    end
  end
end

localparam nbytes_per_word = BRAM_BITSIZE / 8;
wire [(PORTSIZE_data_in*BITSIZE_data_in)+(-1):0] din_value_swapped;
wire [(PORTSIZE_dout_value*BITSIZE_dout_value)+(-1):0] dout_value_swapped;
wire [(PORTSIZE_write_enable*BITSIZE_write_enable)+(-1):0] we_swapped;

generate
genvar i1,i2;
  for (i1=0; i1<nbytes_per_word; i1=i1+1)
  begin
    STD_NRNW_BRAM_BYTE #(.BITSIZE_write_enable(1),
  .PORTSIZE_write_enable(PORTSIZE_write_enable),
  .BITSIZE_address_inr(BITSIZE_Addr),
  .PORTSIZE_address_inr(PORTSIZE_Addr),
  .BITSIZE_address_inw(BITSIZE_Addr),
  .PORTSIZE_address_inw(PORTSIZE_Addr),
  .BITSIZE_data_in(8),
  .PORTSIZE_data_in(PORTSIZE_data_in),
  .BITSIZE_dout_value(8),
  .PORTSIZE_dout_value(PORTSIZE_dout_value),
  .MEMORY_INIT_file(MEMORY_INIT_file),
  .n_elements(n_elements),
  .byte_offset(i1),
  .BRAM_BITSIZE(BRAM_BITSIZE),
  .READ_ONLY_MEMORY(READ_ONLY_MEMORY),
  .HIGH_LATENCY(HIGH_LATENCY)) STD_NRNW_BRAM_BYTE_instance (.clock(clock),
  .write_enable(we_swapped[(i1+1)*PORTSIZE_write_enable-1:i1*PORTSIZE_write_enable]),
  .address_inr(Addr),
  .address_inw(Addr),
  .data_in(din_value_swapped[(i1+1)*PORTSIZE_data_in*8-1:i1*PORTSIZE_data_in*8]),
  .dout_value(dout_value_swapped[(i1+1)*PORTSIZE_dout_value*8-1:i1*PORTSIZE_dout_value*8]));
  end
  for (i1=0; i1<PORTSIZE_write_enable; i1=i1+1)
  begin : swapping_i1_we
  for (i2=0; i2<nbytes_per_word; i2=i2+1)
    begin : swapping_i2_we
      assign we_swapped[i2*PORTSIZE_write_enable+i1] = write_enable[i1*nbytes_per_word+i2];
    end
  end
  for (i1=0; i1<PORTSIZE_data_in; i1=i1+1)
  begin : swapping_i1_in
  for (i2=0; i2<nbytes_per_word; i2=i2+1)
    begin : swapping_i2_in
      assign din_value_swapped[i2*PORTSIZE_data_in*8+i1*8+7:i2*PORTSIZE_data_in*8+i1*8] = data_in[i1*nbytes_per_word*8+i2*8+7:i1*nbytes_per_word*8+i2*8];
    end
  end
  for (i1=0; i1<PORTSIZE_dout_value; i1=i1+1)
  begin : swapping_i1_out
  for (i2=0; i2<nbytes_per_word; i2=i2+1)
    begin : swapping_i2_out
      assign dout_value[i1*nbytes_per_word*8+i2*8+7:i1*nbytes_per_word*8+i2*8] = dout_value_swapped[i2*PORTSIZE_dout_value*8+i1*8+7:i2*PORTSIZE_dout_value*8+i1*8];
    end
  end
endgenerate

reg [7:0] dout_value_a1;
reg [7:0] dout_value_b1;
reg [7:0] dout_a_registered;
reg [7:0] dout_b_registered;
reg [7:0] din_value_a1;
reg [7:0] din_value_b1;
reg we_a1;
reg we_b1;
reg [BITSIZE_Addr_a-1:0] Addr_a1;
reg [BITSIZE_Addr_b-1:0] Addr_b1;

reg [BRAM_BITSIZE-1:0] memory [0:n_elements-1] /* synthesis syn_ramstyle = "no_rw_check" */;



initial
begin
  $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
end
assign dout_value_a = dout_value_a1;
assign dout_value_b = dout_value_b1;
always @(posedge clock)
begin
  if(HIGH_LATENCY==0)
  begin
    if(we_a && READ_ONLY_MEMORY==0)
    begin
      memory[Addr_a][(byte_offset+1)*8-1:byte_offset*8] <= din_value_a;
    end
    dout_value_a1 <= memory[Addr_a][(byte_offset+1)*8-1:byte_offset*8];
    if(we_b && READ_ONLY_MEMORY==0)
    begin
      memory[Addr_b][(byte_offset+1)*8-1:byte_offset*8] <= din_value_b;
    end
    dout_value_b1 <= memory[Addr_b][(byte_offset+1)*8-1:byte_offset*8];
  end
  else if(HIGH_LATENCY==1)
  begin
    if(we_a && READ_ONLY_MEMORY==0)
    begin
      memory[Addr_a][(byte_offset+1)*8-1:byte_offset*8] <= din_value_a;
    end
    dout_a_registered <= memory[Addr_a][(byte_offset+1)*8-1:byte_offset*8];
    dout_value_a1 <= dout_a_registered;
    if(we_b && READ_ONLY_MEMORY==0)
    begin
      memory[Addr_b][(byte_offset+1)*8-1:byte_offset*8] <= din_value_b;
    end
    dout_b_registered <= memory[Addr_b][(byte_offset+1)*8-1:byte_offset*8];
    dout_value_b1 <= dout_b_registered;
  end
  else
  begin
    if(we_a1 && READ_ONLY_MEMORY==0)
    begin
      memory[Addr_a1][(byte_offset+1)*8-1:byte_offset*8] <= din_value_a1;
    end
    dout_a_registered <= memory[Addr_a1][(byte_offset+1)*8-1:byte_offset*8];
    dout_value_a1 <= dout_a_registered;
    if(we_b1 && READ_ONLY_MEMORY==0)
    begin
      memory[Addr_b1][(byte_offset+1)*8-1:byte_offset*8] <= din_value_b1;
    end
    dout_b_registered <= memory[Addr_b1][(byte_offset+1)*8-1:byte_offset*8];
    dout_value_b1 <= dout_b_registered;
    Addr_a1 <= Addr_a;
    Addr_b1 <= Addr_b;
    we_a1 <= we_a;
    we_b1 <= we_b;
    din_value_a1 <= din_value_a;
    din_value_b1 <= din_value_b;
  end
end

NP_functionality.VHDL_PROVIDED:
  signal dout_value_a1 : std_logic_vector(7 downto 0);
  signal dout_value_b1 : std_logic_vector(7 downto 0);
  signal dout_a_registered : std_logic_vector(7 downto 0);
  signal dout_b_registered : std_logic_vector(7 downto 0);
  signal din_value_a1 : std_logic_vector(7 downto 0);
  signal din_value_b1 : std_logic_vector(7 downto 0);
  signal we_a1 : std_logic;
  signal we_b1 : std_logic;
  signal Addr_a1 : std_logic_vector(BITSIZE_Addr_a-1 downto 0);
  signal Addr_b1 : std_logic_vector(BITSIZE_Addr_b-1 downto 0);

  type mem_type is array(0 to n_elements-1) of std_logic_vector(7 downto 0);
  impure function InitMemFromFile (MemFileName : in string) return mem_type is
    FILE memfile : text open READ_MODE is MemFileName;
    variable MemFileLine : line;
    variable mem : mem_type;
    variable mem_row: std_logic_vector((BRAM_BITSIZE - 1) downto 0);
    begin
       for i in 0 to n_elements-1 loop
        readline(memfile, MemFileLine);
        read(MemFileLine, mem_row);
        mem(i) := mem_row((byte_offset+1)*8-1 downto byte_offset*8);
      end loop;
      return mem;
  end function;

  signal mem : mem_type := InitMemFromFile(MEMORY_INIT_file);
begin
  dout_value_a <= dout_value_a1;
  dout_value_b <= dout_value_b1;
process (clock)
begin
  if (clock'event and clock='1') then
    if(HIGH_LATENCY = 0) then
      if we_a = '1' and READ_ONLY_MEMORY=0 then
-- synthesis translate_off
        if (to_integer(unsigned(Addr_a)) < n_elements) then
-- synthesis translate_on
          mem(to_integer(unsigned(Addr_a))) <= din_value_a;
-- synthesis translate_off
        end if;
-- synthesis translate_on
      end if;
-- synthesis translate_off
      if (to_integer(unsigned(Addr_a)) >= n_elements) then
        dout_value_a1 <= (others => 'X');
      else
-- synthesis translate_on
        dout_value_a1 <= mem(to_integer(unsigned(Addr_a)));
-- synthesis translate_off
      end if;
-- synthesis translate_on
      if we_b = '1' and READ_ONLY_MEMORY=0 then
-- synthesis translate_off
        if (to_integer(unsigned(Addr_b)) < n_elements) then
-- synthesis translate_on
          mem(to_integer(unsigned(Addr_b))) <= din_value_b;
-- synthesis translate_off
        end if;
-- synthesis translate_on
      end if;
-- synthesis translate_off
      if (to_integer(unsigned(Addr_b)) >= n_elements) then
        dout_value_b1 <= (others => 'X');
      else
-- synthesis translate_on
        dout_value_b1 <= mem(to_integer(unsigned(Addr_b)));
-- synthesis translate_off
      end if;
-- synthesis translate_on
    elsif(HIGH_LATENCY = 1) then
      if we_a = '1' and READ_ONLY_MEMORY=0 then
-- synthesis translate_off
        if (to_integer(unsigned(Addr_a)) < n_elements) then
-- synthesis translate_on
          mem(to_integer(unsigned(Addr_a))) <= din_value_a;
-- synthesis translate_off
        end if;
-- synthesis translate_on
      end if;
-- synthesis translate_off
      if (to_integer(unsigned(Addr_a)) >= n_elements) then
        dout_a_registered <= (others => 'X');
      else
-- synthesis translate_on
        dout_a_registered <= mem(to_integer(unsigned(Addr_a)));
-- synthesis translate_off
      end if;
-- synthesis translate_on
      dout_value_a1 <= dout_a_registered;
      if we_b = '1' and READ_ONLY_MEMORY=0 then
-- synthesis translate_off
        if (to_integer(unsigned(Addr_b)) < n_elements) then
-- synthesis translate_on
          mem(to_integer(unsigned(Addr_b))) <= din_value_b;
-- synthesis translate_off
        end if;
-- synthesis translate_on
      end if;
-- synthesis translate_off
      if (to_integer(unsigned(Addr_b)) >= n_elements) then
        dout_b_registered <= (others => 'X');
      else
-- synthesis translate_on
        dout_b_registered <= mem(to_integer(unsigned(Addr_b)));
-- synthesis translate_off
      end if;
-- synthesis translate_on
      dout_value_b1 <= dout_b_registered;
    else
      if we_a1 = '1' and READ_ONLY_MEMORY=0 then
-- synthesis translate_off
        if (to_integer(unsigned(Addr_a1)) < n_elements) then
-- synthesis translate_on
          mem(to_integer(unsigned(Addr_a1))) <= din_value_a1;
-- synthesis translate_off
        end if;
-- synthesis translate_on
      end if;
-- synthesis translate_off
      if (to_integer(unsigned(Addr_a1)) >= n_elements) then
        dout_a_registered <= (others => 'X');
      else
-- synthesis translate_on
        dout_a_registered <= mem(to_integer(unsigned(Addr_a1)));
-- synthesis translate_off
      end if;
-- synthesis translate_on
      dout_value_a1 <= dout_a_registered;
      if we_b1 = '1' and READ_ONLY_MEMORY=0 then
-- synthesis translate_off
        if (to_integer(unsigned(Addr_b1)) < n_elements) then
-- synthesis translate_on
          mem(to_integer(unsigned(Addr_b1))) <= din_value_b1;
-- synthesis translate_off
        end if;
-- synthesis translate_on
      end if;
-- synthesis translate_off
      if (to_integer(unsigned(Addr_b1)) >= n_elements) then
        dout_b_registered <= (others => 'X');
      else
-- synthesis translate_on
        dout_b_registered <= mem(to_integer(unsigned(Addr_b1)));
-- synthesis translate_off
      end if;
-- synthesis translate_on
      dout_value_b1 <= dout_b_registered;
      Addr_a1 <= Addr_a;
      Addr_b1 <= Addr_b;
      we_a1 <= we_a;
      we_b1 <= we_b;
      din_value_a1 <= din_value_a;
      din_value_b1 <= din_value_b;
    end if;
  end if;
end process;

NP_functionality.VERILOG_PROVIDED:
wire [n_byte_on_databus-1:0] we_a;
wire [n_byte_on_databus-1:0] we_b;

generate
genvar i1;
  for (i1=0; i1<n_byte_on_databus; i1=i1+1)
  begin
    TRUE_DUAL_PORT #(
      .BITSIZE_Addr_a(BITSIZE_memory_addr_a),
      .BITSIZE_Addr_b(BITSIZE_memory_addr_b),
      .MEMORY_INIT_file(MEMORY_INIT_file),
      .n_elements(n_elements),
      .byte_offset(i1),
      .BRAM_BITSIZE(BRAM_BITSIZE),
      .READ_ONLY_MEMORY(READ_ONLY_MEMORY),
      .HIGH_LATENCY(HIGH_LATENCY)
    ) TRUE_DUAL_PORT_instance (.clock(clock),
      .we_a(we_a[i1]),
      .we_b(we_b[i1]),
      .Addr_a(memory_addr_a),
      .Addr_b(memory_addr_b),
      .din_value_a(din_value_aggregated_a[(i1+1)*8-1:i1*8]),
      .din_value_b(din_value_aggregated_b[(i1+1)*8-1:(i1)*8]),
      .dout_value_a(dout_a[(i1+1)*8-1:i1*8]),
      .dout_value_b(dout_b[(i1+1)*8-1:i1*8])
    );
  end
endgenerate

generate
genvar i2_a;
  for (i2_a=0; i2_a<n_byte_on_databus; i2_a=i2_a+1)
  begin  : write_enable_a
    assign we_a[i2_a] = (bram_write0) && be_a[i2_a];
  end
endgenerate

generate
genvar i2_b;
  for (i2_b=0; i2_b<n_byte_on_databus; i2_b=i2_b+1)
  begin  : write_enable_b
    assign we_b[i2_b] = (bram_write1) && be_b[i2_b];
  end
endgenerate

NP_functionality.VHDL_PROVIDED:
signal we_a : std_logic_vector(n_byte_on_databus-1 downto 0);
signal we_b : std_logic_vector(n_byte_on_databus-1 downto 0);
signal dout_a_signal : std_logic_vector(BITSIZE_dout_a-1 downto 0) ;
signal dout_b_signal : std_logic_vector(BITSIZE_dout_b-1 downto 0) ;
begin
  N_TRUE_DUAL_PORT_GEN:
    for i1 in 0 to n_byte_on_databus-1 generate
      TRUE_DUAL_PORT_instance : TRUE_DUAL_PORT generic map (BITSIZE_Addr_a=>BITSIZE_memory_addr_a, BITSIZE_Addr_b=>BITSIZE_memory_addr_b, MEMORY_INIT_file=>MEMORY_INIT_file, n_elements=>n_elements, byte_offset=>i1, BRAM_BITSIZE=>BRAM_BITSIZE, READ_ONLY_MEMORY=>READ_ONLY_MEMORY, HIGH_LATENCY=>HIGH_LATENCY) port map (clock=>clock, we_a=>(we_a(i1)), we_b=>(we_b(i1)), Addr_a=>(memory_addr_a), Addr_b=>(memory_addr_b), din_value_a=>(din_value_aggregated_a((i1+1)*8-1 downto i1*8)), din_value_b=>(din_value_aggregated_b((i1+1)*8-1 downto (i1)*8)), dout_value_a=>(dout_a_signal((i1+1)*8-1 downto i1*8)), dout_value_b=>(dout_b_signal((i1+1)*8-1 downto i1*8)));
    end generate N_TRUE_DUAL_PORT_GEN;
  dout_a <= dout_a_signal;
  dout_b <= dout_b_signal;

  L_write_enable_a : process(bram_write0, be_a)
  begin
    for i2_a in 0 to n_byte_on_databus-1 loop
      we_a(i2_a) <= bram_write0 and be_a(i2_a);
    end loop;
  end process;

  L_write_enable_b : process(bram_write1, be_b)
  begin
    for i2_b in 0 to n_byte_on_databus-1 loop
      we_b(i2_b) <= bram_write1 and be_b(i2_b);
    end loop;
  end process;

NP_functionality.VERILOG_PROVIDED:
wire [n_byte_on_databus-1:0] we_a; wire [n_byte_on_databus-1:0] we_b;
STD_NRNW_BRAM_SB #(.BITSIZE_write_enable(n_byte_on_databus), .PORTSIZE_write_enable(2), .BITSIZE_Addr(BITSIZE_memory_addr_a), .PORTSIZE_Addr(2), .BITSIZE_data_in(BRAM_BITSIZE), .PORTSIZE_data_in(2), .BITSIZE_dout_value(BITSIZE_dout_a), .PORTSIZE_dout_value(2), .MEMORY_INIT_file(MEMORY_INIT_file), .n_elements(n_elements), .BRAM_BITSIZE(BRAM_BITSIZE), .READ_ONLY_MEMORY(READ_ONLY_MEMORY), .HIGH_LATENCY(HIGH_LATENCY)) STD_NRNW_BRAM_instance (.clock(clock), .write_enable({we_b,we_a}), .Addr({memory_addr_b,memory_addr_a}), .data_in(data_in_aggregated), .dout_value({dout_b,dout_a}));
generate genvar i2_a; for (i2_a=0; i2_a<n_byte_on_databus; i2_a=i2_a+1) begin : write_enable_a assign we_a[i2_a] = (bram_write) && be[i2_a]; end endgenerate
generate genvar i2_b; for (i2_b=0; i2_b<n_byte_on_databus; i2_b=i2_b+1) begin : write_enable_b assign we_b[i2_b] = (bram_write) && be[i2_b+n_byte_on_databus]; end endgenerate

reg bram_write1;
reg [BITSIZE_memory_addr_a-1:0] memory_addr_a1;
reg [BITSIZE_memory_addr_b-1:0] memory_addr_b1;
reg [BITSIZE_be-1:0] be1;
reg [BITSIZE_din_value_aggregated-1:0] din_value_aggregated1;

generate
  if(HIGH_LATENCY==2)
  begin
    always @ (posedge clock)
    begin
       memory_addr_a1 <= memory_addr_a;
       memory_addr_b1 <= memory_addr_b;
       bram_write1 <= bram_write;
       be1 <= be;
       din_value_aggregated1 <= din_value_aggregated;
    end
  end
endgenerate

generate
if(BRAM_BITSIZE == 8)
begin
  reg [(n_byte_on_databus)*8-1:0] dout_a_tmp;
  reg [(n_byte_on_databus)*8-1:0] dout_b_tmp;
  reg [(n_byte_on_databus)*8-1:0] dout_a_registered;
  reg [(n_byte_on_databus)*8-1:0] dout_b_registered;
  reg [(n_byte_on_databus)*8-1:0] memory [0:n_elements-1]/* synthesis syn_ramstyle = "no_rw_check" */ ;
  assign dout_a = dout_a_tmp;
  assign dout_b = dout_b_tmp;
  initial
  begin
    $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
  end


  if(n_elements == 1)
  begin
    always @(posedge clock)
    begin
      if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
      begin
        if (bram_write && be[0] && READ_ONLY_MEMORY==0)
          memory[memory_addr_a] <= din_value_aggregated[BRAM_BITSIZE-1:0];
      end
      else
      begin
        if (bram_write1 && be1[0] && READ_ONLY_MEMORY==0)
          memory[memory_addr_a1] <= din_value_aggregated1[BRAM_BITSIZE-1:0];
      end
      if(HIGH_LATENCY == 0)
        dout_a_tmp <= memory[memory_addr_a];
      else if(HIGH_LATENCY == 1)
      begin
        dout_a_registered <= memory[memory_addr_a];
        dout_a_tmp <= dout_a_registered;
      end
      else
      begin
        dout_a_registered <= memory[memory_addr_a1];
        dout_a_tmp <= dout_a_registered;
      end
      if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
      begin
        if (bram_write && be[1] && READ_ONLY_MEMORY==0)
          memory[memory_addr_b] <= din_value_aggregated[2*BRAM_BITSIZE-1:BRAM_BITSIZE];
      end
      else
      begin
        if (bram_write1 && be1[1] && READ_ONLY_MEMORY==0)
          memory[memory_addr_b1] <= din_value_aggregated1[2*BRAM_BITSIZE-1:BRAM_BITSIZE];
      end
      if(HIGH_LATENCY == 0)
        dout_b_tmp <= memory[memory_addr_b];
      else if(HIGH_LATENCY == 1)
      begin
        dout_b_registered <= memory[memory_addr_b];
        dout_b_tmp <= dout_b_registered;
      end
      else
      begin
        dout_b_registered <= memory[memory_addr_b1];
        dout_b_tmp <= dout_b_registered;
      end
    end
  end
  else
  begin
    always @(posedge clock)
    begin
      if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
      begin
        if (bram_write && be[0] && READ_ONLY_MEMORY==0)
          memory[memory_addr_a] <= din_value_aggregated[BRAM_BITSIZE-1:0];
      end
      else
      begin
        if (bram_write1 && be1[0] && READ_ONLY_MEMORY==0)
          memory[memory_addr_a1] <= din_value_aggregated1[BRAM_BITSIZE-1:0];
      end
      if(HIGH_LATENCY == 0)
        dout_a_tmp <= memory[memory_addr_a];
      else if(HIGH_LATENCY == 1)
      begin
        dout_a_registered <= memory[memory_addr_a];
        dout_a_tmp <= dout_a_registered;
      end
      else
      begin
        dout_a_registered <= memory[memory_addr_a1];
        dout_a_tmp <= dout_a_registered;
      end
    end
    always @(posedge clock)
    begin
      if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
      begin
        if (bram_write && be[1] && READ_ONLY_MEMORY==0)
          memory[memory_addr_b] <= din_value_aggregated[2*BRAM_BITSIZE-1:BRAM_BITSIZE];
      end
      else
      begin
        if (bram_write1 && be1[1] && READ_ONLY_MEMORY==0)
          memory[memory_addr_b1] <= din_value_aggregated1[2*BRAM_BITSIZE-1:BRAM_BITSIZE];
      end
      if(HIGH_LATENCY == 0)
        dout_b_tmp <= memory[memory_addr_b];
      else if(HIGH_LATENCY == 1)
      begin
        dout_b_registered <= memory[memory_addr_b];
        dout_b_tmp <= dout_b_registered;
      end
      else
      begin
        dout_b_registered <= memory[memory_addr_b1];
        dout_b_tmp <= dout_b_registered;
      end
    end
  end
end
else
begin
  if(n_elements == 1)
  begin
    reg [(n_byte_on_databus)*8-1:0] dout_a_tmp;
    reg [(n_byte_on_databus)*8-1:0] dout_b_tmp;
    reg [(n_byte_on_databus)*8-1:0] dout_a_registered;
    reg [(n_byte_on_databus)*8-1:0] dout_b_registered;
    reg [(n_byte_on_databus)*8-1:0] memory [0:n_elements-1]/* synthesis syn_ramstyle = "no_rw_check" */ ;
    assign dout_a = dout_a_tmp;
    assign dout_b = dout_b_tmp;
    initial
    begin
      $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
    end
    always @(posedge clock)
    begin
      if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
      begin
        if (bram_write)
        begin : L11_write
          integer i11;
          for (i11=0; i11<n_byte_on_databus; i11=i11+1)
          begin
            if(be[i11] && READ_ONLY_MEMORY==0)
              memory[memory_addr_a][i11*8+:8] <= din_value_aggregated[i11*8+:8];
          end
        end
      end
      else
      begin
        if (bram_write1)
        begin : L11_write1
          integer i11;
          for (i11=0; i11<n_byte_on_databus; i11=i11+1)
          begin
            if(be1[i11] && READ_ONLY_MEMORY==0)
              memory[memory_addr_a1][i11*8+:8] <= din_value_aggregated1[i11*8+:8];
          end
        end
      end
      if(HIGH_LATENCY == 0)
        dout_a_tmp <= memory[memory_addr_a];
      else if(HIGH_LATENCY == 1)
      begin
        dout_a_registered <= memory[memory_addr_a];
        dout_a_tmp <= dout_a_registered;
      end
      else
      begin
        dout_a_registered <= memory[memory_addr_a1];
        dout_a_tmp <= dout_a_registered;
      end
      if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
      begin
        if (bram_write && n_bytes > BRAM_BITSIZE/8)
        begin : L22_write
          integer i22;
          for (i22=0; i22<n_byte_on_databus; i22=i22+1)
          begin
            if(be[i22+n_byte_on_databus] && READ_ONLY_MEMORY==0)
              memory[memory_addr_b][i22*8+:8] <= din_value_aggregated[(i22+n_byte_on_databus)*8+:8];
          end
        end
      end
      else
      begin
        if (bram_write1 && n_bytes > BRAM_BITSIZE/8)
        begin : L22_write1
          integer i22;
          for (i22=0; i22<n_byte_on_databus; i22=i22+1)
          begin
            if(be1[i22+n_byte_on_databus] && READ_ONLY_MEMORY==0)
              memory[memory_addr_b1][i22*8+:8] <= din_value_aggregated1[(i22+n_byte_on_databus)*8+:8];
          end
        end
      end
      if(HIGH_LATENCY == 0)
        dout_b_tmp <= memory[memory_addr_b];
      else if(HIGH_LATENCY == 1)
      begin
        dout_b_registered <= memory[memory_addr_b];
        dout_b_tmp <= dout_b_registered;
      end
      else
      begin
        dout_b_registered <= memory[memory_addr_b1];
        dout_b_tmp <= dout_b_registered;
      end
    end
  end
  else
  begin
    if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
    begin
      pmi_ram_dp_true_be #(.pmi_addr_depth_a(n_elements),
        .pmi_addr_width_a(BITSIZE_memory_addr_a),
        .pmi_data_width_a((n_byte_on_databus)*8),
        .pmi_addr_depth_b(n_elements),
        .pmi_addr_width_b(BITSIZE_memory_addr_a),
        .pmi_data_width_b((n_byte_on_databus)*8),
        .pmi_regmode_a(HIGH_LATENCY== 0 ? "noreg" : "reg"),
        .pmi_regmode_b(HIGH_LATENCY== 0 ? "noreg" : "reg"),
        .pmi_gsr("disable"),
        .pmi_resetmode("sync"),
        .pmi_optimization("speed"),
        .pmi_init_file(MEMORY_INIT_file),
        .pmi_init_file_format("binary"),
        .pmi_write_mode_a("normal"),
        .pmi_write_mode_b("normal"),
        .pmi_byte_size(8),
        .pmi_family("ECP3"),
        .module_type("pmi_ram_dp_true_be")) pmi_ram_dp_true_be_instance
       (.DataInA(din_value_aggregated[BRAM_BITSIZE-1:0]),
        .DataInB(din_value_aggregated[2*BRAM_BITSIZE-1:BRAM_BITSIZE]),
        .AddressA(memory_addr_a),
        .AddressB(memory_addr_b),
        .ClockA(clock),
        .ClockB(clock),
        .ClockEnA(1'b1),
        .ClockEnB(1'b1),
        .WrA(bram_write && READ_ONLY_MEMORY==0),
        .WrB(bram_write && READ_ONLY_MEMORY==0),
        .ResetA(1'b0),
        .ResetB(1'b0),
        .ByteEnA(be[n_byte_on_databus-1:0]),
        .ByteEnB(be[2*n_byte_on_databus-1:n_byte_on_databus]),
        .QA(dout_a),
        .QB(dout_b)) /*synthesis syn_black_box*/;
    end
    else
    begin
      pmi_ram_dp_true_be #(.pmi_addr_depth_a(n_elements),
        .pmi_addr_width_a(BITSIZE_memory_addr_a),
        .pmi_data_width_a((n_byte_on_databus)*8),
        .pmi_addr_depth_b(n_elements),
        .pmi_addr_width_b(BITSIZE_memory_addr_a),
        .pmi_data_width_b((n_byte_on_databus)*8),
        .pmi_regmode_a(HIGH_LATENCY== 0 ? "noreg" : "reg"),
        .pmi_regmode_b(HIGH_LATENCY== 0 ? "noreg" : "reg"),
        .pmi_gsr("disable"),
        .pmi_resetmode("sync"),
        .pmi_optimization("speed"),
        .pmi_init_file(MEMORY_INIT_file),
        .pmi_init_file_format("binary"),
        .pmi_write_mode_a("normal"),
        .pmi_write_mode_b("normal"),
        .pmi_byte_size(8),
        .pmi_family("ECP3"),
        .module_type("pmi_ram_dp_true_be")) pmi_ram_dp_true_be_instance
       (.DataInA(din_value_aggregated1[BRAM_BITSIZE-1:0]),
        .DataInB(din_value_aggregated1[2*BRAM_BITSIZE-1:BRAM_BITSIZE]),
        .AddressA(memory_addr_a1),
        .AddressB(memory_addr_b1),
        .ClockA(clock),
        .ClockB(clock),
        .ClockEnA(1'b1),
        .ClockEnB(1'b1),
        .WrA(bram_write1 && READ_ONLY_MEMORY==0),
        .WrB(bram_write1 && READ_ONLY_MEMORY==0),
        .ResetA(1'b0),
        .ResetB(1'b0),
        .ByteEnA(be1[n_byte_on_databus-1:0]),
        .ByteEnB(be1[2*n_byte_on_databus-1:n_byte_on_databus]),
        .QA(dout_a),
        .QB(dout_b)) /*synthesis syn_black_box*/;
    end
  end
end
endgenerate

reg [PORTSIZE_bram_write-1:0] bram_write1;
reg [(PORTSIZE_memory_addr_a*BITSIZE_memory_addr_a)-1:0] memory_addr_a1;
reg [(PORTSIZE_be*BITSIZE_be)-1:0] be1;
reg [(PORTSIZE_din_value_aggregated*BITSIZE_din_value_aggregated)-1:0] din_value_aggregated1;
reg [(PORTSIZE_dout_a*BITSIZE_dout_a)-1:0] dout_a_tmp;
reg [(PORTSIZE_dout_a*BITSIZE_dout_a)-1:0] dout_a_registered;
reg [(n_byte_on_databus)*8-1:0] memory [0:n_elements-1]/* synthesis syn_ramstyle = "registers,no_rw_check" */ ;
integer p1;

initial
begin
  $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
end

generate
  if(HIGH_LATENCY==2)
  begin
    always @ (posedge clock)
    begin
       memory_addr_a1 <= memory_addr_a;
       bram_write1 <= bram_write;
       be1 <= be;
       din_value_aggregated1 <= din_value_aggregated;
    end
  end
endgenerate

assign dout_a = dout_a_tmp;

  always @(posedge clock)
  begin
    for (p1=0; p1<PORTSIZE_memory_addr_a; p1=p1+1)
    begin
      if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
      begin
        if (bram_write[p1] && READ_ONLY_MEMORY==0)
        begin : L11_write
          integer i11;
          for (i11=0; i11<n_byte_on_databus; i11=i11+1)
          begin
            if(be[i11+p1*n_byte_on_databus])
              memory[memory_addr_a[p1*BITSIZE_memory_addr_a+:BITSIZE_memory_addr_a]][i11*8+:8] <= din_value_aggregated[p1*n_byte_on_databus*8+i11*8+:8];
          end
        end
      end
      else
      begin
        if (bram_write1[p1] && READ_ONLY_MEMORY==0)
        begin : L11_write1
          integer i11;
          for (i11=0; i11<n_byte_on_databus; i11=i11+1)
          begin
            if(be1[i11+p1*n_byte_on_databus])
              memory[memory_addr_a1[p1*BITSIZE_memory_addr_a+:BITSIZE_memory_addr_a]][i11*8+:8] <= din_value_aggregated1[p1*n_byte_on_databus*8+i11*8+:8];
          end
        end
      end
      if(HIGH_LATENCY == 0)
        dout_a_tmp[p1*BITSIZE_dout_a+:BITSIZE_dout_a] <= memory[memory_addr_a[p1*BITSIZE_memory_addr_a+:BITSIZE_memory_addr_a]];
      else if(HIGH_LATENCY == 1)
      begin
        dout_a_registered[p1*BITSIZE_dout_a+:BITSIZE_dout_a] <= memory[memory_addr_a[p1*BITSIZE_memory_addr_a+:BITSIZE_memory_addr_a]];
        dout_a_tmp[p1*BITSIZE_dout_a+:BITSIZE_dout_a] <= dout_a_registered[p1*BITSIZE_dout_a+:BITSIZE_dout_a];
      end
      else
      begin
        dout_a_registered[p1*BITSIZE_dout_a+:BITSIZE_dout_a] <= memory[memory_addr_a1[p1*BITSIZE_memory_addr_a+:BITSIZE_memory_addr_a]];
        dout_a_tmp[p1*BITSIZE_dout_a+:BITSIZE_dout_a] <= dout_a_registered[p1*BITSIZE_dout_a+:BITSIZE_dout_a];
      end
    end
  end

NP_functionality.VHDL_PROVIDED:
  signal bram_write1 : std_logic_vector(PORTSIZE_bram_write-1 downto 0);
  signal memory_addr_a1 : std_logic_vector((PORTSIZE_memory_addr_a*BITSIZE_memory_addr_a)-1 downto 0);
  signal be1 : std_logic_vector((PORTSIZE_be*BITSIZE_be)-1 downto 0);
  signal din_value_aggregated1 : std_logic_vector((PORTSIZE_din_value_aggregated*BITSIZE_din_value_aggregated)-1 downto 0);
  signal dout_a_tmp : std_logic_vector((PORTSIZE_dout_a*BITSIZE_dout_a)-1 downto 0);
  signal dout_a_registered : std_logic_vector((PORTSIZE_dout_a*BITSIZE_dout_a)-1 downto 0);

  type mem_type is array (n_elements-1 downto 0) of std_logic_vector((n_byte_on_databus)*8-1 downto 0);

  impure function InitMemFromFile (MemFileName : in string) return mem_type is
    FILE memfile  :  text open READ_MODE is MemFileName;
    variable MemFileLine  :  line;
    variable mem  :  mem_type;
    begin
      for i in 0 to n_elements-1 loop
        readline(memfile, MemFileLine);
        read(MemFileLine, mem(i));
      end loop;
      return mem;
  end function;
  signal mem : mem_type := InitMemFromFile(MEMORY_INIT_file);
begin

  DelayProcess : process(clock)
  begin
    if (clock'event and clock='1') then
       if(HIGH_LATENCY=2) then
         memory_addr_a1 <= memory_addr_a;
         bram_write1 <= bram_write;
         be1 <= be;
         din_value_aggregated1 <= din_value_aggregated;
       end if;
    end if;
  end process;

  dout_a <= dout_a_tmp;

  L1_single_readwrite : process(clock)
  begin
    if (clock'event and clock='1') then
      for p1 in 0 to PORTSIZE_memory_addr_a-1 loop
        if(HIGH_LATENCY = 0 or HIGH_LATENCY = 1) then
          if(bram_write(p1) ='1' and READ_ONLY_MEMORY=0) then
            for index2 in 0 to n_byte_on_databus-1 loop
              if(be(index2+p1*n_byte_on_databus)='1') then
-- synthesis translate_off
                if (to_integer(unsigned(memory_addr_a((p1+1)*BITSIZE_memory_addr_a-1 downto p1*BITSIZE_memory_addr_a))) < n_elements) then
-- synthesis translate_on
                  mem(to_integer(unsigned(memory_addr_a((p1+1)*BITSIZE_memory_addr_a-1 downto p1*BITSIZE_memory_addr_a))))((index2+1)*8-1 downto index2*8) <= din_value_aggregated((index2+1)*8+p1*n_byte_on_databus*8-1 downto (index2)*8+p1*n_byte_on_databus*8);
-- synthesis translate_off
                end if;
-- synthesis translate_on
              end if;
            end loop;
          end if;
        else
          if(bram_write1(p1) ='1' and READ_ONLY_MEMORY=0) then
            for index2 in 0 to n_byte_on_databus-1 loop
              if(be1(index2+p1*n_byte_on_databus)='1') then
-- synthesis translate_off
                if (to_integer(unsigned(memory_addr_a1((p1+1)*BITSIZE_memory_addr_a-1 downto p1*BITSIZE_memory_addr_a))) < n_elements) then
-- synthesis translate_on
                  mem(to_integer(unsigned(memory_addr_a1((p1+1)*BITSIZE_memory_addr_a-1 downto p1*BITSIZE_memory_addr_a))))((index2+1)*8-1 downto index2*8) <= din_value_aggregated1((index2+1)*8+p1*n_byte_on_databus*8-1 downto (index2)*8+p1*n_byte_on_databus*8);
-- synthesis translate_off
                end if;
-- synthesis translate_on
              end if;
            end loop;
          end if;
        end if;
        if(HIGH_LATENCY = 0) then
-- synthesis translate_off
          if (to_integer(unsigned(memory_addr_a((p1+1)*BITSIZE_memory_addr_a-1 downto p1*BITSIZE_memory_addr_a))) < n_elements) then
-- synthesis translate_on
            dout_a_tmp((p1+1)*BITSIZE_dout_a-1 downto p1*BITSIZE_dout_a)  <= mem(to_integer(unsigned(memory_addr_a((p1+1)*BITSIZE_memory_addr_a-1 downto p1*BITSIZE_memory_addr_a))));
-- synthesis translate_off
          else
            dout_a_tmp((p1+1)*BITSIZE_dout_a-1 downto p1*BITSIZE_dout_a)  <= (others => 'X');
          end if;
-- synthesis translate_on
        elsif(HIGH_LATENCY = 1) then
-- synthesis translate_off
          if (to_integer(unsigned(memory_addr_a((p1+1)*BITSIZE_memory_addr_a-1 downto p1*BITSIZE_memory_addr_a))) < n_elements) then
-- synthesis translate_on
            dout_a_registered((p1+1)*BITSIZE_dout_a-1 downto p1*BITSIZE_dout_a)  <= mem(to_integer(unsigned(memory_addr_a((p1+1)*BITSIZE_memory_addr_a-1 downto p1*BITSIZE_memory_addr_a))));
-- synthesis translate_off
          else
            dout_a_registered((p1+1)*BITSIZE_dout_a-1 downto p1*BITSIZE_dout_a)  <= (others => 'X');
          end if;
-- synthesis translate_on
          dout_a_tmp  <= dout_a_registered;
        else
-- synthesis translate_off
          if (to_integer(unsigned(memory_addr_a1((p1+1)*BITSIZE_memory_addr_a-1 downto p1*BITSIZE_memory_addr_a))) < n_elements) then
-- synthesis translate_on
            dout_a_registered((p1+1)*BITSIZE_dout_a-1 downto p1*BITSIZE_dout_a) <= mem(to_integer(unsigned(memory_addr_a1((p1+1)*BITSIZE_memory_addr_a-1 downto p1*BITSIZE_memory_addr_a))));
-- synthesis translate_off
          else
            dout_a_registered((p1+1)*BITSIZE_dout_a-1 downto p1*BITSIZE_dout_a) <= (others => 'X');
          end if;
-- synthesis translate_on
          dout_a_tmp <= dout_a_registered;
        end if;
      end loop;
    end if;
  end process;

NP_functionality.VERILOG_PROVIDED:
wire [n_byte_on_databus-1:0] we_a;
wire [n_byte_on_databus-1:0] we_b;
reg [n_byte_on_databus-1:0] we_a1;
reg [n_byte_on_databus-1:0] we_b1;
reg [BITSIZE_din_value_aggregated_a-1:0] din_value_aggregated_a1;
reg [BITSIZE_din_value_aggregated_b-1:0] din_value_aggregated_b1;

reg [BITSIZE_dout_a-1:0] dout_a;
reg [BITSIZE_dout_a-1:0] dout_a_registered;
reg [BITSIZE_dout_b-1:0] dout_b;
reg [BITSIZE_dout_b-1:0] dout_b_registered;
reg [BITSIZE_memory_addr_a-1:0] memory_addr_a1;
reg [BITSIZE_memory_addr_b-1:0] memory_addr_b1;
reg [BRAM_BITSIZE-1:0] memory [0:n_elements-1] /* synthesis syn_ramstyle = "no_rw_check" */;
integer i11, i12;

initial
begin
  $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
end

always @(posedge clock)
begin
  if(READ_ONLY_MEMORY==0)
  begin
    for (i11=0; i11<n_byte_on_databus; i11=i11+1)
    begin : L11_write_a
      if(HIGH_LATENCY==0||HIGH_LATENCY==1)
      begin
        if(we_a[i11])
          memory[memory_addr_a][i11*8+:8] <= din_value_aggregated_a[i11*8+:8];
      end
      else
      begin
        if(we_a1[i11])
          memory[memory_addr_a1][i11*8+:8] <= din_value_aggregated_a1[i11*8+:8];
      end
    end
  end
  if(HIGH_LATENCY==0)
  begin
    dout_a <= memory[memory_addr_a];
  end
  else if(HIGH_LATENCY==1)
  begin
    dout_a_registered <= memory[memory_addr_a];
    dout_a <= dout_a_registered;
  end
  else
  begin
    memory_addr_a1 <= memory_addr_a;
    we_a1 <= we_a;
    din_value_aggregated_a1 <= din_value_aggregated_a;
    dout_a_registered <= memory[memory_addr_a1];
    dout_a <= dout_a_registered;
  end
end

always @(posedge clock)
begin
  if(READ_ONLY_MEMORY==0)
  begin
    for (i12=0; i12<n_byte_on_databus; i12=i12+1)
    begin : L12_write_b
      if(HIGH_LATENCY==0||HIGH_LATENCY==1)
      begin
        if(we_b[i12])
          memory[memory_addr_b][i12*8+:8] <= din_value_aggregated_b[i12*8+:8];
      end
      else
      begin
        if(we_b1[i12])
          memory[memory_addr_b1][i12*8+:8] <= din_value_aggregated_b1[i12*8+:8];
      end
    end
  end
  if(HIGH_LATENCY==0)
  begin
    dout_b <= memory[memory_addr_b];
  end
  else if(HIGH_LATENCY==1)
  begin
    dout_b_registered <= memory[memory_addr_b];
    dout_b <= dout_b_registered;
  end
  else
  begin
    memory_addr_b1 <= memory_addr_b;
    we_b1 <= we_b;
    din_value_aggregated_b1 <= din_value_aggregated_b;
    dout_b_registered <= memory[memory_addr_b1];
    dout_b <= dout_b_registered;
  end
end

generate
genvar i2_a;
  for (i2_a=0; i2_a<n_byte_on_databus; i2_a=i2_a+1)
  begin  : write_enable_a
    assign we_a[i2_a] = (bram_write0) && be_a[i2_a];
  end
endgenerate

generate
genvar i2_b;
  for (i2_b=0; i2_b<n_byte_on_databus; i2_b=i2_b+1)
  begin  : write_enable_b
    assign we_b[i2_b] = (bram_write1) && be_b[i2_b];
  end
endgenerate

NP_functionality.VHDL_PROVIDED:
signal we_a : std_logic_vector(n_byte_on_databus-1 downto 0);
signal we_b : std_logic_vector(n_byte_on_databus-1 downto 0);
signal we_a1 : std_logic_vector(n_byte_on_databus-1 downto 0);
signal we_b1 : std_logic_vector(n_byte_on_databus-1 downto 0);
signal din_value_aggregated_a1 : std_logic_vector(BITSIZE_din_value_aggregated_a-1 downto 0);
signal din_value_aggregated_b1 : std_logic_vector(BITSIZE_din_value_aggregated_b-1 downto 0);

signal dout_a_0 : std_logic_vector(BITSIZE_dout_a-1 downto 0);
signal dout_a_registered : std_logic_vector(BITSIZE_dout_a-1 downto 0);
signal dout_b_0 : std_logic_vector(BITSIZE_dout_a-1 downto 0);
signal dout_b_registered : std_logic_vector(BITSIZE_dout_a-1 downto 0);
signal memory_addr_a1 : std_logic_vector(BITSIZE_memory_addr_a-1 downto 0);
signal memory_addr_b1 : std_logic_vector(BITSIZE_memory_addr_b-1 downto 0);

type mem_type is array (n_elements-1 downto 0) of std_logic_vector(BRAM_BITSIZE-1 downto 0);

impure function InitMemFromFile (MemFileName : in string) return mem_type is
  FILE memfile  :  text open READ_MODE is MemFileName;
  variable MemFileLine  :  line;
  variable mem  :  mem_type;
  begin
     for i in 0 to n_elements-1 loop
      readline(memfile, MemFileLine);
      read(MemFileLine, mem(i));
    end loop;
    return mem;
end function;

shared variable memory : mem_type := InitMemFromFile(MEMORY_INIT_file);
begin

dout_a <= dout_a_0;
dout_b <= dout_b_0;

L1_PROC : process(clock)
begin
   if (clock'event and clock='1') then
      if(HIGH_LATENCY = 0) then
-- synthesis translate_off
         if (to_integer(unsigned(memory_addr_a)) < n_elements) then
-- synthesis translate_on
            dout_a_0  <= memory(to_integer(unsigned(memory_addr_a)));
-- synthesis translate_off
         else
            dout_a_0  <= (others => 'X');
         end if;
-- synthesis translate_on
      elsif(HIGH_LATENCY = 1) then
-- synthesis translate_off
         if (to_integer(unsigned(memory_addr_a)) < n_elements) then
-- synthesis translate_on
            dout_a_registered  <= memory(to_integer(unsigned(memory_addr_a)));
-- synthesis translate_off
         else
            dout_a_registered  <= (others => 'X');
         end if;
-- synthesis translate_on
         dout_a_0  <= dout_a_registered;
      else
-- synthesis translate_off
         if (to_integer(unsigned(memory_addr_a1)) < n_elements) then
-- synthesis translate_on
            dout_a_registered <= memory(to_integer(unsigned(memory_addr_a1)));
-- synthesis translate_off
         else
            dout_a_registered  <= (others => 'X');
         end if;
-- synthesis translate_on
         memory_addr_a1 <= memory_addr_a;
         we_a1 <= we_a;
         din_value_aggregated_a1 <= din_value_aggregated_a;
         dout_a_0 <= dout_a_registered;
      end if;
      if (READ_ONLY_MEMORY=0) then
        for index1 in 0 to n_byte_on_databus-1 loop
           if(HIGH_LATENCY= 0 or HIGH_LATENCY = 1) then
              if(we_a(index1)='1') then
-- synthesis translate_off
                 if (to_integer(unsigned(memory_addr_a)) < n_elements) then
-- synthesis translate_on
                    memory(to_integer(unsigned(memory_addr_a)))((index1+1)*8-1 downto index1*8) := din_value_aggregated_a((index1+1)*8-1 downto index1*8);
-- synthesis translate_off
                 end if;
-- synthesis translate_on
              end if;
           else
              if(we_a1(index1)='1') then
-- synthesis translate_off
                 if (to_integer(unsigned(memory_addr_a1)) < n_elements) then
-- synthesis translate_on
                    memory(to_integer(unsigned(memory_addr_a1)))((index1+1)*8-1 downto index1*8) := din_value_aggregated_a1((index1+1)*8-1 downto index1*8);
-- synthesis translate_off
                 end if;
-- synthesis translate_on
              end if;
           end if;
        end loop;
     end if;
   end if;
end process;


L2_PROC : process(clock)
begin
  if (clock'event and clock='1') then
    if(HIGH_LATENCY = 0) then
-- synthesis translate_off
       if (to_integer(unsigned(memory_addr_b)) < n_elements) then
-- synthesis translate_on
          dout_b_0  <= memory(to_integer(unsigned(memory_addr_b)));
-- synthesis translate_off
       else
          dout_b_0  <= (others => 'X');
       end if;
  -- synthesis translate_on
    elsif(HIGH_LATENCY = 1) then
-- synthesis translate_off
      if (to_integer(unsigned(memory_addr_b)) < n_elements) then
-- synthesis translate_on
          dout_b_registered  <= memory(to_integer(unsigned(memory_addr_b)));
-- synthesis translate_off
      else
         dout_b_registered  <= (others => 'X');
      end if;
-- synthesis translate_on
      dout_b_0  <= dout_b_registered;
    else
-- synthesis translate_off
      if (to_integer(unsigned(memory_addr_b1)) < n_elements) then
-- synthesis translate_on
        dout_b_registered <= memory(to_integer(unsigned(memory_addr_b1)));
-- synthesis translate_off
      else
        dout_b_registered  <= (others => 'X');
      end if;
-- synthesis translate_on
      memory_addr_b1 <= memory_addr_b;
      we_b1 <= we_b;
      din_value_aggregated_b1 <= din_value_aggregated_b;
      dout_b_0 <= dout_b_registered;
    end if;
    if (READ_ONLY_MEMORY=0) then
      for index2 in 0 to n_byte_on_databus-1 loop
        if(HIGH_LATENCY= 0 or HIGH_LATENCY = 1) then
          if(we_b(index2)='1') then
-- synthesis translate_off
            if (to_integer(unsigned(memory_addr_b)) < n_elements) then
-- synthesis translate_on
              memory(to_integer(unsigned(memory_addr_b)))((index2+1)*8-1 downto index2*8) := din_value_aggregated_b((index2+1)*8-1 downto (index2)*8);
-- synthesis translate_off
            end if;
-- synthesis translate_on
          end if;
          else
          if(we_b1(index2)='1') then
-- synthesis translate_off
            if (to_integer(unsigned(memory_addr_b1)) < n_elements) then
-- synthesis translate_on
              memory(to_integer(unsigned(memory_addr_b1)))((index2+1)*8-1 downto index2*8) := din_value_aggregated_b1((index2+1)*8-1 downto (index2)*8);
-- synthesis translate_off
            end if;
-- synthesis translate_on
          end if;
        end if;
      end loop;
    end if;
  end if;
end process;

L_write_enable_a : process(bram_write0, be_a)
begin
   for i2_a in 0 to n_byte_on_databus-1 loop
      we_a(i2_a) <= bram_write0 and be_a(i2_a);
   end loop;
end process;

L_write_enable_b : process(bram_write1, be_b)
begin
   for i2_b in 0 to n_byte_on_databus-1 loop
      we_b(i2_b) <= bram_write1 and be_b(i2_b);
   end loop;
end process;

NP_functionality.VERILOG_PROVIDED:
wire [(n_byte_on_databus/2)*max_n_writes-1:0] we_a; wire [max_n_writes*BRAM_BITSIZE-1:0] din_value_swapped;
STD_NRNW_BRAM_SB #(.BITSIZE_write_enable(n_byte_on_databus/2), .PORTSIZE_write_enable(max_n_writes), .BITSIZE_Addr(BITSIZE_memory_addr_a), .PORTSIZE_Addr(PORTSIZE_memory_addr_a), .BITSIZE_data_in(BITSIZE_din_value_aggregated_swapped/2), .PORTSIZE_data_in(max_n_writes), .BITSIZE_dout_value(BITSIZE_dout_a), .PORTSIZE_dout_value(PORTSIZE_dout_a), .MEMORY_INIT_file(MEMORY_INIT_file), .n_elements(n_elements), .READ_ONLY_MEMORY(READ_ONLY_MEMORY), .BRAM_BITSIZE(BRAM_BITSIZE), .HIGH_LATENCY(HIGH_LATENCY)) STD_NRNW_BRAM_SB_instance (.clock(clock), .write_enable(we_a), .Addr(memory_addr_a), .data_in(din_value_swapped), .dout_value(dout_a[BRAM_BITSIZE*max_n_reads-1:0]));
generate genvar i1,i2; for (i2=0; i2<max_n_writes; i2=i2+1) begin : we2 for (i1=0; i1<n_byte_on_databus/2; i1=i1+1) begin : we1 assign we_a[i1+(n_byte_on_databus/2)*i2] = bram_write[i2] && be_swapped[i1+n_byte_on_databus*i2+n_byte_on_databus_offset]; end end for (i2=0; i2<max_n_writes; i2=i2+1) begin : din2 for (i1=0; i1<n_byte_on_databus/2; i1=i1+1) begin : din1 assign din_value_swapped[i2*n_byte_on_databus*4+i1*8+7:i2*(n_byte_on_databus)*4+i1*8] = din_value_aggregated_swapped[i2*2*BRAM_BITSIZE+i1*8+memory_offset+7:i2*2*BRAM_BITSIZE+i1*8+memory_offset]; end end endgenerate

wire [(n_byte_on_databus/2)*8-1:0] dina; wire [(n_byte_on_databus/2)*8-1:0] dinb; wire [n_byte_on_databus*8-1:0] din;
assign dina = din_value_aggregated_swapped[memory_offset+:(n_byte_on_databus/2)*8]; assign dinb = din_value_aggregated_swapped[2*BRAM_BITSIZE+memory_offset+:(n_byte_on_databus/2)*8]; assign din = {dinb,dina};
generate if(n_elements == 1) begin BRAM_MEMORY_CORE_SMALL #(.PORTSIZE_bram_write(PORTSIZE_bram_write), .BITSIZE_bram_write(BITSIZE_bram_write), .PORTSIZE_memory_addr_a(PORTSIZE_memory_addr_a), .BITSIZE_memory_addr_a(BITSIZE_memory_addr_a), .PORTSIZE_din_value_aggregated(PORTSIZE_din_value_aggregated_swapped), .BITSIZE_din_value_aggregated((n_byte_on_databus/2)*8), .PORTSIZE_be(PORTSIZE_be_swapped), .BITSIZE_be(n_byte_on_databus/PORTSIZE_be_swapped), .PORTSIZE_dout_a(PORTSIZE_dout_a), .BITSIZE_dout_a(BITSIZE_dout_a), .MEMORY_INIT_file(MEMORY_INIT_file), .n_byte_on_databus(n_byte_on_databus/2), .n_elements(n_elements), .n_bytes(n_bytes), .READ_ONLY_MEMORY(READ_ONLY_MEMORY), .HIGH_LATENCY(HIGH_LATENCY)) BRAM_MEMORY_instance_small (.clock(clock), .bram_write(bram_write), .memory_addr_a(memory_addr_a), .din_value_aggregated(din), .be({be_swapped[n_byte_on_databus+n_byte_on_databus_offset+:n_byte_on_databus/2],be_swapped[n_byte_on_databus_offset+:n_byte_on_databus/2]}), .dout_a(dout_a)); end else begin TRUE_DUAL_PORT_BYTE_ENABLING_RAM #(.BITSIZE_memory_addr_a(BITSIZE_memory_addr_a), .BITSIZE_memory_addr_b(BITSIZE_memory_addr_a), .BITSIZE_din_value_aggregated_a((n_byte_on_databus/2)*8), .BITSIZE_din_value_aggregated_b((n_byte_on_databus/2)*8), .BITSIZE_be_a(n_byte_on_databus/2), .BITSIZE_be_b(n_byte_on_databus/2), .BITSIZE_dout_a((n_byte_on_databus/2)*8), .BITSIZE_dout_b((n_byte_on_databus/2)*8), .MEMORY_INIT_file(MEMORY_INIT_file), .BRAM_BITSIZE(BRAM_BITSIZE), .n_byte_on_databus(n_byte_on_databus/2), .n_elements(n_elements), .READ_ONLY_MEMORY(READ_ONLY_MEMORY), .HIGH_LATENCY(HIGH_LATENCY) ) TRUE_DUAL_PORT_BYTE_ENABLING_RAM_instance (.clock(clock), .bram_write0(bram_write[0]), .bram_write1(bram_write[1]), .memory_addr_a(memory_addr_a[BITSIZE_memory_addr_a-1:0]), .memory_addr_b(memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]), .din_value_aggregated_a(dina), .din_value_aggregated_b(dinb), .be_a(be_swapped[n_byte_on_databus_offset+:n_byte_on_databus/2]), .be_b(be_swapped[n_byte_on_databus+n_byte_on_databus_offset+:n_byte_on_databus/2]), .dout_a(dout_a[BRAM_BITSIZE-1:0]), .dout_b(dout_a[2*BRAM_BITSIZE-1:BRAM_BITSIZE]) ); end endgenerate

reg [PORTSIZE_bram_write-1:0] bram_write1;
reg [(PORTSIZE_memory_addr_a*BITSIZE_memory_addr_a)+(-1):0] memory_addr_a1;
reg [(PORTSIZE_din_value_aggregated_swapped*BITSIZE_din_value_aggregated_swapped)+(-1):0] din_value_aggregated_swapped1;
reg [(PORTSIZE_be_swapped*BITSIZE_be_swapped)+(-1):0] be_swapped1;

generate
  if(HIGH_LATENCY==2)
  begin
    always @ (posedge clock)
    begin
       memory_addr_a1 <= memory_addr_a;
       bram_write1 <= bram_write;
       be_swapped1 <= be_swapped;
       din_value_aggregated_swapped1 <= din_value_aggregated_swapped;
    end
  end
endgenerate

generate
if(BRAM_BITSIZE == 8)
begin
  reg [(n_byte_on_databus/2)*8-1:0] dout_a_tmp;
  reg [(n_byte_on_databus/2)*8-1:0] dout_b_tmp;
  reg [(n_byte_on_databus/2)*8-1:0] dout_a_registered;
  reg [(n_byte_on_databus/2)*8-1:0] dout_b_registered;
  reg [(n_byte_on_databus/2)*8-1:0] memory [0:n_elements-1]/* synthesis syn_ramstyle = "no_rw_check" */ ;
  assign dout_a[BRAM_BITSIZE-1:0] = dout_a_tmp;
  assign dout_a[2*BRAM_BITSIZE-1:BRAM_BITSIZE] = dout_b_tmp;
  initial
  begin
    $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
  end
  if(n_elements == 1)
  begin
    always @(posedge clock)
    begin
      if(READ_ONLY_MEMORY==0)
      begin
        if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
        begin
          if (bram_write[0] && be_swapped[n_byte_on_databus_offset])
            memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]] <= din_value_aggregated_swapped[(n_byte_on_databus/2)*8+memory_offset-1:memory_offset];
        end
        else
        begin
          if (bram_write1[0] && be_swapped1[n_byte_on_databus_offset])
            memory[memory_addr_a1[BITSIZE_memory_addr_a-1:0]] <= din_value_aggregated_swapped1[(n_byte_on_databus/2)*8+memory_offset-1:memory_offset];
        end
      end
      if(HIGH_LATENCY == 0)
        dout_a_tmp <= memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]];
      else if(HIGH_LATENCY == 1)
      begin
        dout_a_registered <= memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]];
        dout_a_tmp <= dout_a_registered;
      end
      else
      begin
        dout_a_registered <= memory[memory_addr_a1[BITSIZE_memory_addr_a-1:0]];
        dout_a_tmp <= dout_a_registered;
      end
      if(READ_ONLY_MEMORY==0)
      begin
        if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
        begin
          if (bram_write[1] && be_swapped[n_byte_on_databus+n_byte_on_databus_offset])
            memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]] <= din_value_aggregated_swapped[2*BRAM_BITSIZE+(n_byte_on_databus/2)*8+memory_offset-1:2*BRAM_BITSIZE+memory_offset];
        end
        else
        begin
          if (bram_write1[1] && be_swapped1[n_byte_on_databus+n_byte_on_databus_offset])
            memory[memory_addr_a1[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]] <= din_value_aggregated_swapped1[2*BRAM_BITSIZE+(n_byte_on_databus/2)*8+memory_offset-1:2*BRAM_BITSIZE+memory_offset];
        end
      end
      if(HIGH_LATENCY == 0)
        dout_b_tmp <= memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
      else if(HIGH_LATENCY == 1)
      begin
        dout_b_registered <= memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
        dout_b_tmp <= dout_b_registered;
      end
      else
      begin
        dout_b_registered <= memory[memory_addr_a1[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
        dout_b_tmp <= dout_b_registered;
      end
    end
  end
  else
  begin
    always @(posedge clock)
    begin
      if(READ_ONLY_MEMORY==0)
      begin
        if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
        begin
          if (bram_write[0] && be_swapped[n_byte_on_databus_offset])
            memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]] <= din_value_aggregated_swapped[(n_byte_on_databus/2)*8+memory_offset-1:memory_offset];
        end
        else
        begin
          if (bram_write1[0] && be_swapped1[n_byte_on_databus_offset])
            memory[memory_addr_a1[BITSIZE_memory_addr_a-1:0]] <= din_value_aggregated_swapped1[(n_byte_on_databus/2)*8+memory_offset-1:memory_offset];
        end
      end
      if(HIGH_LATENCY == 0)
        dout_a_tmp <= memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]];
      else if(HIGH_LATENCY == 1)
      begin
        dout_a_registered <= memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]];
        dout_a_tmp <= dout_a_registered;
      end
      else
      begin
        dout_a_registered <= memory[memory_addr_a1[BITSIZE_memory_addr_a-1:0]];
        dout_a_tmp <= dout_a_registered;
      end
    end
    always @(posedge clock)
    begin
      if(READ_ONLY_MEMORY==0)
      begin
        if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
        begin
          if (bram_write[1] && be_swapped[n_byte_on_databus+n_byte_on_databus_offset])
            memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]] <= din_value_aggregated_swapped[2*BRAM_BITSIZE+(n_byte_on_databus/2)*8+memory_offset-1:2*BRAM_BITSIZE+memory_offset];
        end
        else
        begin
          if (bram_write1[1] && be_swapped1[n_byte_on_databus+n_byte_on_databus_offset])
            memory[memory_addr_a1[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]] <= din_value_aggregated_swapped1[2*BRAM_BITSIZE+(n_byte_on_databus/2)*8+memory_offset-1:2*BRAM_BITSIZE+memory_offset];
        end
      end
      if(HIGH_LATENCY == 0)
        dout_b_tmp <= memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
      else if(HIGH_LATENCY == 1)
      begin
        dout_b_registered <= memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
        dout_b_tmp <= dout_b_registered;
      end
      else
      begin
        dout_b_registered <= memory[memory_addr_a1[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
        dout_b_tmp <= dout_b_registered;
      end
    end
  end
end
else
begin
  if(n_elements == 1)
  begin
    reg [(n_byte_on_databus/2)*8-1:0] dout_a_tmp;
    reg [(n_byte_on_databus/2)*8-1:0] dout_b_tmp;
    reg [(n_byte_on_databus/2)*8-1:0] dout_a_registered;
    reg [(n_byte_on_databus/2)*8-1:0] dout_b_registered;
    reg [(n_byte_on_databus/2)*8-1:0] memory [0:n_elements-1]/* synthesis syn_ramstyle = "no_rw_check" */ ;
    assign dout_a[BRAM_BITSIZE-1:0] = dout_a_tmp;
    assign dout_a[2*BRAM_BITSIZE-1:BRAM_BITSIZE] = dout_b_tmp;
    initial
    begin
      $readmemb(MEMORY_INIT_file, memory, 0, n_elements-1);
    end
    always @(posedge clock)
    begin
      if(READ_ONLY_MEMORY==0)
      begin
        if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
        begin
          if (bram_write[0])
          begin : L11_write
            integer i11;
            for (i11=0; i11<n_byte_on_databus/2; i11=i11+1)
            begin
              if(be_swapped[i11+n_byte_on_databus_offset])
                memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]][i11*8+:8] <= din_value_aggregated_swapped[i11*8+memory_offset+:8];
            end
          end
        end
        else
        begin
          if (bram_write1[0])
          begin : L11_write1
            integer i11;
            for (i11=0; i11<n_byte_on_databus/2; i11=i11+1)
            begin
              if(be_swapped1[i11+n_byte_on_databus_offset])
                memory[memory_addr_a1[BITSIZE_memory_addr_a-1:0]][i11*8+:8] <= din_value_aggregated_swapped1[i11*8+memory_offset+:8];
            end
          end
        end
      end
      if(HIGH_LATENCY == 0)
        dout_a_tmp <= memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]];
      else if(HIGH_LATENCY == 1)
      begin
        dout_a_registered <= memory[memory_addr_a[BITSIZE_memory_addr_a-1:0]];
        dout_a_tmp <= dout_a_registered;
      end
      else
      begin
        dout_a_registered <= memory[memory_addr_a1[BITSIZE_memory_addr_a-1:0]];
        dout_a_tmp <= dout_a_registered;
      end
      if(READ_ONLY_MEMORY==0)
      begin
        if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
        begin
          if (bram_write[1])
          begin : L22_write
            integer i22;
            for (i22=0; i22<n_byte_on_databus/2; i22=i22+1)
            begin
              if(be_swapped[i22+n_byte_on_databus+n_byte_on_databus_offset])
                memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]][i22*8+:8] <= din_value_aggregated_swapped[2*BRAM_BITSIZE+memory_offset+i22*8+:8];
            end
          end
        end
        else
        begin
          if (bram_write1[1])
          begin : L22_write1
            integer i22;
            for (i22=0; i22<n_byte_on_databus/2; i22=i22+1)
            begin
              if(be_swapped1[i22+n_byte_on_databus+n_byte_on_databus_offset])
                memory[memory_addr_a1[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]][i22*8+:8] <= din_value_aggregated_swapped1[2*BRAM_BITSIZE+memory_offset+i22*8+:8];
            end
          end
        end
      end
      if(HIGH_LATENCY == 0)
        dout_b_tmp <= memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
      else if(HIGH_LATENCY == 1)
      begin
        dout_b_registered <= memory[memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
        dout_b_tmp <= dout_b_registered;
      end
      else
      begin
        dout_b_registered <= memory[memory_addr_a1[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]];
        dout_b_tmp <= dout_b_registered;
      end
    end
  end
  else
    if(HIGH_LATENCY == 0||HIGH_LATENCY == 1)
    begin
      pmi_ram_dp_true_be #(.pmi_addr_depth_a(n_elements),
        .pmi_addr_width_a(BITSIZE_memory_addr_a),
        .pmi_data_width_a((n_byte_on_databus/2)*8),
        .pmi_addr_depth_b(n_elements),
        .pmi_addr_width_b(BITSIZE_memory_addr_a),
        .pmi_data_width_b((n_byte_on_databus/2)*8),
        .pmi_regmode_a(HIGH_LATENCY== 0 ? "noreg" : "reg"),
        .pmi_regmode_b(HIGH_LATENCY== 0 ? "noreg" : "reg"),
        .pmi_gsr("disable"),
        .pmi_resetmode("sync"),
        .pmi_optimization("speed"),
        .pmi_init_file(MEMORY_INIT_file),
        .pmi_init_file_format("binary"),
        .pmi_write_mode_a("normal"),
        .pmi_write_mode_b("normal"),
        .pmi_byte_size(8),
        .pmi_family("ECP3"),
        .module_type("pmi_ram_dp_true_be")) pmi_ram_dp_true_be_instance
       (.DataInA(din_value_aggregated_swapped[(n_byte_on_databus/2)*8+memory_offset-1:memory_offset]),
        .DataInB(din_value_aggregated_swapped[2*BRAM_BITSIZE+(n_byte_on_databus/2)*8+memory_offset-1:2*BRAM_BITSIZE+memory_offset]),
        .AddressA(memory_addr_a[BITSIZE_memory_addr_a-1:0]),
        .AddressB(memory_addr_a[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]),
        .ClockA(clock),
        .ClockB(clock),
        .ClockEnA(1'b1),
        .ClockEnB(1'b1),
        .WrA(bram_write[0] && READ_ONLY_MEMORY==0),
        .WrB(bram_write[1] && READ_ONLY_MEMORY==0),
        .ResetA(1'b0),
        .ResetB(1'b0),
        .ByteEnA(be_swapped[n_byte_on_databus/2+n_byte_on_databus_offset-1:n_byte_on_databus_offset]),
        .ByteEnB(be_swapped[n_byte_on_databus/2+n_byte_on_databus+n_byte_on_databus_offset-1:n_byte_on_databus+n_byte_on_databus_offset]),
        .QA(dout_a[BRAM_BITSIZE-1:0]),
        .QB(dout_a[2*BRAM_BITSIZE-1:BRAM_BITSIZE])) /*synthesis syn_black_box*/;
    end
    else
    begin
      pmi_ram_dp_true_be #(.pmi_addr_depth_a(n_elements),
        .pmi_addr_width_a(BITSIZE_memory_addr_a),
        .pmi_data_width_a((n_byte_on_databus/2)*8),
        .pmi_addr_depth_b(n_elements),
        .pmi_addr_width_b(BITSIZE_memory_addr_a),
        .pmi_data_width_b((n_byte_on_databus/2)*8),
        .pmi_regmode_a(HIGH_LATENCY== 0 ? "noreg" : "reg"),
        .pmi_regmode_b(HIGH_LATENCY== 0 ? "noreg" : "reg"),
        .pmi_gsr("disable"),
        .pmi_resetmode("sync"),
        .pmi_optimization("speed"),
        .pmi_init_file(MEMORY_INIT_file),
        .pmi_init_file_format("binary"),
        .pmi_write_mode_a("normal"),
        .pmi_write_mode_b("normal"),
        .pmi_byte_size(8),
        .pmi_family("ECP3"),
        .module_type("pmi_ram_dp_true_be")) pmi_ram_dp_true_be_instance
       (.DataInA(din_value_aggregated_swapped1[(n_byte_on_databus/2)*8+memory_offset-1:memory_offset]),
        .DataInB(din_value_aggregated_swapped1[2*BRAM_BITSIZE+(n_byte_on_databus/2)*8+memory_offset-1:2*BRAM_BITSIZE+memory_offset]),
        .AddressA(memory_addr_a1[BITSIZE_memory_addr_a-1:0]),
        .AddressB(memory_addr_a1[2*BITSIZE_memory_addr_a-1:BITSIZE_memory_addr_a]),
        .ClockA(clock),
        .ClockB(clock),
        .ClockEnA(1'b1),
        .ClockEnB(1'b1),
        .WrA(bram_write1[0] && READ_ONLY_MEMORY==0),
        .WrB(bram_write1[1] && READ_ONLY_MEMORY==0),
        .ResetA(1'b0),
        .ResetB(1'b0),
        .ByteEnA(be_swapped1[n_byte_on_databus/2+n_byte_on_databus_offset-1:n_byte_on_databus_offset]),
        .ByteEnB(be_swapped1[n_byte_on_databus/2+n_byte_on_databus+n_byte_on_databus_offset-1:n_byte_on_databus+n_byte_on_databus_offset]),
        .QA(dout_a[BRAM_BITSIZE-1:0]),
        .QB(dout_a[2*BRAM_BITSIZE-1:BRAM_BITSIZE])) /*synthesis syn_black_box*/;
    end
end
endgenerate

`ifndef _SIM_HAVE_CLOG2 function integer log2; input integer value; integer temp_value; begin temp_value = value-1; for (log2=0; temp_value>0; log2=log2+1) temp_value = temp_value>>1; end endfunction `endif parameter n_bytes = (n_elements*data_size)/8; parameter n_byte_on_databus = BRAM_BITSIZE/8; parameter n_elements_bus = n_bytes/(n_byte_on_databus) + (n_bytes%(n_byte_on_databus) == 0 ? 0 : 1); `ifdef _SIM_HAVE_CLOG2 localparam nbit_read_addr = n_elements_bus == 1 ? 1 : $clog2(n_elements_bus); `else localparam nbit_read_addr = n_elements_bus == 1 ? 1 : log2(n_elements_bus); `endif
wire [nbit_read_addr-1:0] memory_addr_a; wire [nbit_read_addr-1:0] memory_addr_b; wire [n_byte_on_databus*2-1:0] be;
