`ifndef _SIM_HAVE_CLOG2
  function integer log2;
     input integer value;
     integer temp_value;
    begin
      temp_value = value-1;
      for (log2=0; temp_value>0; log2=log2+1)
        temp_value = temp_value>>1;
    end
  endfunction
`endif
parameter n_byte_on_databus = ALIGNMENT/8;
parameter nbit_addr_r = BITSIZE_in2r > BITSIZE_proxy_in2r ? BITSIZE_in2r : BITSIZE_proxy_in2r;
parameter nbit_addr_w = BITSIZE_in2w > BITSIZE_proxy_in2w ? BITSIZE_in2w : BITSIZE_proxy_in2w;

`ifdef _SIM_HAVE_CLOG2
  localparam nbit_read_addr = n_elements == 1 ? 1 : $clog2(n_elements);
  localparam nbits_byte_offset = n_byte_on_databus<=1 ? 0 : $clog2(n_byte_on_databus);
  localparam nbits_address_space_rangesize = $clog2(address_space_rangesize);
`else
  localparam nbit_read_addr = n_elements == 1 ? 1 : log2(n_elements);
  localparam nbits_byte_offset = n_byte_on_databus<=1 ? 0 : log2(n_byte_on_databus);
  localparam nbits_address_space_rangesize = log2(address_space_rangesize);
`endif

wire [nbit_read_addr-1:0] memory_addr_a_r;
wire [nbit_read_addr-1:0] memory_addr_a_w;
wire bram_write;
wire [data_size-1:0] dout_a;
wire [data_size-1:0] din_a;

wire [nbit_addr_r-1:0] relative_addr_r;
wire [nbit_addr_w-1:0] relative_addr_w;

wire [nbit_addr_r-1:0] tmp_addr_r;
wire [nbit_addr_w-1:0] tmp_addr_w;

STD_SP_BRAM #(
  .BITSIZE_data_in(data_size),
  .BITSIZE_data_out(data_size),
  .BITSIZE_address_inr(nbit_read_addr),
  .BITSIZE_address_inw(nbit_read_addr),
  .n_elements(n_elements),
  .MEMORY_INIT_file(MEMORY_INIT_file),
  .READ_ONLY_MEMORY(READ_ONLY_MEMORY),
  .HIGH_LATENCY(HIGH_LATENCY)
) STD_SP_BRAM_instance (
  .clock(clock),
  .write_enable(bram_write),
  .data_in(din_a),
  .address_inr(memory_addr_a_r),
  .address_inw(memory_addr_a_w),
  .data_out(dout_a)
);

assign din_a = (proxy_sel_STORE && proxy_in4w) ? proxy_in1 : in1;
assign bram_write = (sel_STORE && in4w) || (proxy_sel_STORE && proxy_in4w);

assign tmp_addr_r = (proxy_sel_LOAD && proxy_in4r) ? proxy_in2r : in2r;
assign tmp_addr_w = (proxy_sel_STORE && proxy_in4w) ? proxy_in2w : in2w;

generate
  if(USE_SPARSE_MEMORY==1)
    assign relative_addr_r = tmp_addr_r[nbits_address_space_rangesize-1:0];
  else
    assign relative_addr_r = tmp_addr_r-address_space_begin[nbit_addr_r-1:0];
endgenerate

generate
  if(USE_SPARSE_MEMORY==1)
    assign relative_addr_w = tmp_addr_w[nbits_address_space_rangesize-1:0];
  else
    assign relative_addr_w = tmp_addr_w-address_space_begin[nbit_addr_w-1:0];
endgenerate

generate
  if (n_elements==1)
    assign memory_addr_a_r = {nbit_read_addr{1'b0}};
  else
    assign memory_addr_a_r = relative_addr_r[nbit_read_addr+nbits_byte_offset-1:nbits_byte_offset];
endgenerate

generate
  if (n_elements==1)
    assign memory_addr_a_w = {nbit_read_addr{1'b0}};
  else
    assign memory_addr_a_w = relative_addr_w[nbit_read_addr+nbits_byte_offset-1:nbits_byte_offset];
endgenerate


assign out1 = dout_a;
assign proxy_out1 = dout_a;
assign Sout_Rdata_ram =Sin_Rdata_ram;
assign Sout_DataRdy = Sin_DataRdy;
// Add assertion here
// psl default clock = (posedge clock);
// psl ERROR_SDS_data_ram_size: assert never {((data_size != in3r && sel_LOAD && in4r) || (data_size != in3w && sel_STORE & in4w)) || ((data_size != proxy_in3r && proxy_sel_LOAD && proxy_in4r) || (data_size != proxy_in3w && proxy_sel_STORE && proxy_in4w))};
// psl ERROR_SDS_alignment: assert never {(((in2r-address_space_begin) %(ALIGNMENT/8) != 0) && sel_LOAD && in4r) || (((in2r-address_space_begin) %(ALIGNMENT/8) != 0) && sel_STORE && in4w) || (((proxy_in2r-address_space_begin) %(ALIGNMENT/8) != 0) && proxy_sel_LOAD  && proxy_in4r) || (((proxy_in2w-address_space_begin) %(ALIGNMENT/8) != 0) && proxy_sel_STORE && proxy_in4w)};

NP_functionality.VHDL_PROVIDED:
  function integer_ternary_operator(cond : boolean; op1 : integer; op2 : integer) return integer is
  begin
    if cond then
      return op1;
    else
      return op2;
    end if;
  end integer_ternary_operator;
  constant n_byte_on_databus : integer := ALIGNMENT/8;
  constant nbit_addr_r         : integer := integer_ternary_operator(BITSIZE_in2r > BITSIZE_proxy_in2r, BITSIZE_in2r, BITSIZE_proxy_in2r);
  constant nbit_addr_w         : integer := integer_ternary_operator(BITSIZE_in2w > BITSIZE_proxy_in2w, BITSIZE_in2w, BITSIZE_proxy_in2w);
  constant nbit_read_addr    : integer := integer_ternary_operator(n_elements = 1, 1, integer(ceil(log2(real(n_elements)))));
  constant nbits_byte_offset : integer := integer_ternary_operator(n_byte_on_databus <= 1, 0, integer(ceil(log2(real(n_byte_on_databus)))));

  constant nbits_address_space_rangesize  : integer := integer(ceil(log2(real(address_space_rangesize))));

  signal memory_addr_a_r : std_logic_vector(nbit_read_addr-1 downto 0);
  signal memory_addr_a_w : std_logic_vector(nbit_read_addr-1 downto 0);

  signal bram_write : std_logic;
  signal dout_a : std_logic_vector(data_size-1 downto 0);
  signal din_a : std_logic_vector(data_size-1 downto 0);

  signal relative_addr_r : std_logic_vector(nbit_addr_r-1 downto 0);
  signal relative_addr_w : std_logic_vector(nbit_addr_w-1 downto 0);

  signal tmp_addr_r : std_logic_vector(nbit_addr_r-1 downto 0);
  signal tmp_addr_w : std_logic_vector(nbit_addr_w-1 downto 0);

begin

  STD_SP_BRAM_instance : STD_SP_BRAM generic map(
    BITSIZE_data_in=>data_size,
    BITSIZE_data_out=>data_size,
    BITSIZE_address_inr=>nbit_read_addr,
    BITSIZE_address_inw=>nbit_read_addr,
    n_elements=>n_elements,
    MEMORY_INIT_file=>MEMORY_INIT_file,
    READ_ONLY_MEMORY=>READ_ONLY_MEMORY,
    HIGH_LATENCY=>HIGH_LATENCY
  ) port map (
    data_out => dout_a,
    write_enable => bram_write,
    clock => clock,
    data_in => din_a,
    address_inr => memory_addr_a_r,
    address_inw => memory_addr_a_w);

  din_a <= std_logic_vector(resize(unsigned(proxy_in1), data_size))  when ((proxy_sel_STORE = '1') and (proxy_in4w = '1')) else std_logic_vector(resize(unsigned(in1), data_size));
  bram_write <= (sel_STORE and in4w) or (proxy_sel_STORE and proxy_in4w);

  tmp_addr_r <= proxy_in2r when ((proxy_sel_LOAD  = '1') and (proxy_in4r = '1')) else in2r;
  tmp_addr_w <= proxy_in2w when ((proxy_sel_STORE = '1') and (proxy_in4w = '1')) else in2w;

  L2_r : process(relative_addr_r)
  begin
    if(n_elements=1) then
      memory_addr_a_r <= (others => '0');
    else
      memory_addr_a_r <= relative_addr_r(nbit_read_addr+nbits_byte_offset-1 downto nbits_byte_offset);
    end if;
  end process;

  L2_w : process(relative_addr_w)
  begin
    if(n_elements=1) then
      memory_addr_a_w <= (others => '0');
    else
      memory_addr_a_w <= relative_addr_w(nbit_read_addr+nbits_byte_offset-1 downto nbits_byte_offset);
    end if;
  end process;

  L3_r : process(tmp_addr_r)
  begin
      if(USE_SPARSE_MEMORY=1) then
        relative_addr_r <= std_logic_vector(resize(unsigned(tmp_addr_r(nbits_address_space_rangesize-1 downto 0)), nbit_addr_r));
      else
        relative_addr_r <= std_logic_vector(resize(unsigned(tmp_addr_r), nbit_addr_r) - to_unsigned(address_space_begin, nbit_addr_r));
      end if;
  end process;

  L3_w : process(tmp_addr_w)
  begin
      if(USE_SPARSE_MEMORY=1) then
        relative_addr_w <= std_logic_vector(resize(unsigned(tmp_addr_w(nbits_address_space_rangesize-1 downto 0)), nbit_addr_w));
      else
        relative_addr_w <= std_logic_vector(resize(unsigned(tmp_addr_w), nbit_addr_w) - to_unsigned(address_space_begin, nbit_addr_w));
      end if;
  end process;

  out1 <= dout_a;
  proxy_out1 <= std_logic_vector(resize(unsigned(dout_a), BITSIZE_proxy_out1));
  Sout_Rdata_ram <=Sin_Rdata_ram;
  Sout_DataRdy <= Sin_DataRdy;

NP_functionality.VERILOG_PROVIDED:
`ifndef _SIM_HAVE_CLOG2
  function integer log2;
     input integer value;
     integer temp_value;
    begin
      temp_value = value-1;
      for (log2=0; temp_value>0; log2=log2+1)
        temp_value = temp_value>>1;
    end
  endfunction
`endif
parameter n_byte_on_databus = ALIGNMENT/8;
parameter nbit_addr = BITSIZE_in2 > BITSIZE_proxy_in2 ? BITSIZE_in2 : BITSIZE_proxy_in2;

`ifdef _SIM_HAVE_CLOG2
  localparam nbit_read_addr = n_elements == 1 ? 1 : $clog2(n_elements);
  localparam nbits_byte_offset = n_byte_on_databus<=1 ? 0 : $clog2(n_byte_on_databus);
  localparam nbits_address_space_rangesize = $clog2(address_space_rangesize);
`else
  localparam nbit_read_addr = n_elements == 1 ? 1 : log2(n_elements);
  localparam nbits_byte_offset = n_byte_on_databus<=1 ? 0 : log2(n_byte_on_databus);
  localparam nbits_address_space_rangesize = log2(address_space_rangesize);
`endif

wire [nbit_read_addr-1:0] memory_addr_a;
wire bram_write;
wire [data_size-1:0] dout_a;
wire [data_size-1:0] din_a;

wire cs, oe_ram_cs, we_ram_cs;
reg we_ram_cs_delayed;
reg oe_ram_cs_delayed;
reg oe_ram_cs_delayed_registered;
reg oe_ram_cs_delayed_registered1;
wire [nbit_addr-1:0] relative_addr;
wire [nbit_addr-1:0] tmp_addr;

STD_SP_BRAM #(
  .BITSIZE_data_in(data_size),
  .BITSIZE_data_out(data_size),
  .BITSIZE_address_inr(nbit_read_addr),
  .BITSIZE_address_inw(nbit_read_addr),
  .n_elements(n_elements),
  .MEMORY_INIT_file(MEMORY_INIT_file),
  .READ_ONLY_MEMORY(READ_ONLY_MEMORY),
  .HIGH_LATENCY(HIGH_LATENCY)
) STD_SP_BRAM_instance (
  .clock(clock),
  .write_enable(bram_write),
  .data_in(din_a),
  .address_inr(memory_addr_a),
  .address_inw(memory_addr_a),
  .data_out(dout_a)
);

assign din_a = proxy_sel_STORE ? proxy_in1 : sel_STORE ? in1 : S_Wdata_ram;
assign bram_write = sel_STORE || proxy_sel_STORE || we_ram_cs;

assign tmp_addr = (proxy_sel_LOAD||proxy_sel_STORE) ? proxy_in2 : in2;

generate
  if(PRIVATE_MEMORY==0 && USE_SPARSE_MEMORY==0)
    assign cs = (S_addr_ram >= (address_space_begin)) && (S_addr_ram < (address_space_begin+address_space_rangesize));
  else if(PRIVATE_MEMORY==0 && nbits_address_space_rangesize < 32)
    assign cs = S_addr_ram[nbit_addr-1:nbits_address_space_rangesize] == address_space_begin[((nbit_addr-1) < 32 ? (nbit_addr-1) : 31):nbits_address_space_rangesize];
  else
    assign cs = 1'b0;
endgenerate

assign oe_ram_cs = S_oe_ram && cs;
assign we_ram_cs = S_we_ram && cs;

generate
  if(PRIVATE_MEMORY==0 && USE_SPARSE_MEMORY==0)
    assign relative_addr = (sel_STORE===1'b1 || sel_LOAD===1'b1 || proxy_sel_STORE===1'b1 || proxy_sel_LOAD===1'b1) ? tmp_addr-address_space_begin[((nbit_addr-1) < 32 ? (nbit_addr-1) : 31):0] : S_addr_ram-address_space_begin[((nbit_addr-1) < 32 ? (nbit_addr-1) : 31):0];
  else if(PRIVATE_MEMORY==0)
    assign relative_addr = (sel_STORE===1'b1 || sel_LOAD===1'b1 || proxy_sel_STORE===1'b1 || proxy_sel_LOAD===1'b1) ? tmp_addr[nbits_address_space_rangesize-1:0] : S_addr_ram[nbits_address_space_rangesize-1:0];
  else if(USE_SPARSE_MEMORY==1)
    assign relative_addr = tmp_addr[nbits_address_space_rangesize-1:0];
  else
    assign relative_addr = tmp_addr-address_space_begin[((nbit_addr-1) < 32 ? (nbit_addr-1) : 31):0];
endgenerate

generate
  if (n_elements==1)
    assign memory_addr_a = {nbit_read_addr{1'b0}};
  else
    assign memory_addr_a = relative_addr[nbit_read_addr+nbits_byte_offset-1:nbits_byte_offset];
endgenerate

assign out1 = dout_a;
assign proxy_out1 = dout_a;
always @(posedge clock 1RESET_EDGE)
begin
  if(1RESET_VALUE)
    begin
      oe_ram_cs_delayed <= 1'b0;
      if(HIGH_LATENCY != 0) oe_ram_cs_delayed_registered <= 1'b0;
      if(HIGH_LATENCY == 2) oe_ram_cs_delayed_registered1 <= 1'b0;
    end
  else
    begin
      if(HIGH_LATENCY==0)
        oe_ram_cs_delayed <= oe_ram_cs & (!oe_ram_cs_delayed | BUS_PIPELINED);
      else if(HIGH_LATENCY==1)
      begin
        oe_ram_cs_delayed_registered <= oe_ram_cs & ((!oe_ram_cs_delayed_registered & !oe_ram_cs_delayed) | BUS_PIPELINED);
        oe_ram_cs_delayed <= oe_ram_cs_delayed_registered;
      end
      else
      begin
        oe_ram_cs_delayed_registered1 <= oe_ram_cs & ((!oe_ram_cs_delayed_registered1 & !oe_ram_cs_delayed_registered) | BUS_PIPELINED);
        oe_ram_cs_delayed_registered <= oe_ram_cs_delayed_registered1;
        oe_ram_cs_delayed <= oe_ram_cs_delayed_registered;
      end
    end
end

always @(posedge clock 1RESET_EDGE)
begin
  if(1RESET_VALUE)
    we_ram_cs_delayed <= 1'b0;
  else
    we_ram_cs_delayed <= we_ram_cs & !we_ram_cs_delayed;
end

generate
  if(PRIVATE_MEMORY==1)
    assign Sout_Rdata_ram =Sin_Rdata_ram;
  else if (BITSIZE_Sout_Rdata_ram <= data_size)
    assign Sout_Rdata_ram = oe_ram_cs_delayed ? dout_a[BITSIZE_Sout_Rdata_ram-1:0] : Sin_Rdata_ram;
  else
    assign Sout_Rdata_ram = oe_ram_cs_delayed ? {{BITSIZE_Sout_Rdata_ram-data_size{1'b0}}, dout_a} : Sin_Rdata_ram;
endgenerate

generate
  if(PRIVATE_MEMORY==1)
    assign Sout_DataRdy = Sin_DataRdy;
  else
    assign Sout_DataRdy = oe_ram_cs_delayed | Sin_DataRdy | we_ram_cs_delayed;
endgenerate

// Add assertion here
// psl default clock = (posedge clock);
// psl ERROR_SDS_data_ram_size: assert never {(data_size != in3 && (sel_LOAD || sel_STORE)) || (data_size != proxy_in3 && (proxy_sel_LOAD || proxy_sel_STORE))};
// psl ERROR_SDS_alignment: assert never {(((in2-address_space_begin) %(ALIGNMENT/8) != 0) && (sel_LOAD || sel_STORE)) || (((proxy_in2-address_space_begin) %(ALIGNMENT/8) != 0) && (proxy_sel_LOAD || proxy_sel_STORE))};

NP_functionality.VHDL_PROVIDED:
  function integer_ternary_operator(cond : boolean; op1 : integer; op2 : integer) return integer is
  begin
    if cond then
      return op1;
    else
      return op2;
    end if;
  end integer_ternary_operator;
  constant n_byte_on_databus : integer := ALIGNMENT/8;
  constant nbit_addr         : integer := integer_ternary_operator(BITSIZE_in2 > BITSIZE_proxy_in2, BITSIZE_in2, BITSIZE_proxy_in2);
  constant nbit_read_addr    : integer := integer_ternary_operator(n_elements = 1, 1, integer(ceil(log2(real(n_elements)))));
  constant nbits_byte_offset : integer := integer_ternary_operator(n_byte_on_databus <= 1, 0, integer(ceil(log2(real(n_byte_on_databus)))));

  constant nbits_address_space_rangesize  : integer := integer(ceil(log2(real(address_space_rangesize))));
  constant address_space_begin_u         : unsigned(nbit_addr-1 downto 0) := to_unsigned(address_space_begin, nbit_addr);
  signal memory_addr_a : std_logic_vector(nbit_read_addr-1 downto 0);
  signal memory_addr_a_mem : std_logic_vector(nbit_read_addr-1 downto 0);
  signal memory_addr_a1 : std_logic_vector(nbit_read_addr-1 downto 0);

  signal bram_write : std_logic;
  signal bram_write_mem : std_logic;
  signal bram_write1 : std_logic;

  signal dout_a_tmp : std_logic_vector(data_size-1 downto 0) ;
  signal dout_a : std_logic_vector(data_size-1 downto 0);
  signal dout_a_registered : std_logic_vector(data_size-1 downto 0);
  signal cs, oe_ram_cs, we_ram_cs: std_logic;
  signal we_ram_cs_delayed : std_logic;
  signal oe_ram_cs_delayed : std_logic;
  signal oe_ram_cs_delayed_registered : std_logic;
  signal oe_ram_cs_delayed_registered1 : std_logic;

  signal relative_addr : std_logic_vector(nbit_addr-1 downto 0);
  signal tmp_addr : std_logic_vector(nbit_addr-1 downto 0);
  signal din_a : std_logic_vector(data_size-1 downto 0);
  signal din_a_mem : std_logic_vector(data_size-1 downto 0);
  signal din_a1 : std_logic_vector(data_size-1 downto 0);

begin

  STD_SP_BRAM_instance : STD_SP_BRAM generic map(
    BITSIZE_data_in=>data_size,
    BITSIZE_data_out=>data_size,
    BITSIZE_address_inr=>nbit_read_addr,
    BITSIZE_address_inw=>nbit_read_addr,
    n_elements=>n_elements,
    MEMORY_INIT_file=>MEMORY_INIT_file,
    READ_ONLY_MEMORY=>READ_ONLY_MEMORY,
    HIGH_LATENCY=>HIGH_LATENCY
  ) port map (
    data_out => dout_a,
    write_enable => bram_write,
    clock => clock,
    data_in => din_a,
    address_inr => memory_addr_a,
    address_inw => memory_addr_a
  );

  din_a <= std_logic_vector(resize(unsigned(proxy_in1), data_size))  when (proxy_sel_STORE = '1') else std_logic_vector(resize(unsigned(in1), data_size)) when  (sel_STORE = '1') else std_logic_vector(resize(unsigned(S_Wdata_ram), data_size));
  bram_write <= sel_STORE or proxy_sel_STORE or we_ram_cs;

  tmp_addr <= proxy_in2 when ((proxy_sel_LOAD or proxy_sel_STORE)='1') else in2;

  L3 : process(S_addr_ram)
  begin
    if(PRIVATE_MEMORY = 0 and USE_SPARSE_MEMORY = 0) then
       if(to_integer(unsigned(S_addr_ram)) >= (address_space_begin)) and (to_integer(unsigned(S_addr_ram)) < (address_space_begin+address_space_rangesize)) then
          cs <= '1';
       else
          cs <= '0';
       end if;
    else
       if(PRIVATE_MEMORY=0) then
          if S_addr_ram(nbit_addr-1 downto nbits_address_space_rangesize) = std_logic_vector(address_space_begin_u(nbit_addr-1 downto nbits_address_space_rangesize)) then
             cs <= '1';
          else
             cs <= '0';
          end if;
       else
          cs <= '0';
       end if;
    end if;
  end process;

  oe_ram_cs <= S_oe_ram and cs;
  we_ram_cs <= S_we_ram and cs;

  L6 : process(sel_STORE, proxy_sel_STORE, sel_LOAD, proxy_sel_LOAD, tmp_addr, S_addr_ram)
  begin
      if(PRIVATE_MEMORY=0 and USE_SPARSE_MEMORY=0) then
         if(sel_STORE = '1' or sel_LOAD = '1' or proxy_sel_STORE = '1' or proxy_sel_LOAD = '1') then
            relative_addr <= std_logic_vector(unsigned(tmp_addr)-(to_unsigned(address_space_begin, nbit_addr)));
         else
            relative_addr <= std_logic_vector(unsigned(S_addr_ram) - (to_unsigned(address_space_begin, nbit_addr)));
         end if;
      else
         if(PRIVATE_MEMORY=0) then
            if(sel_STORE = '1' or sel_LOAD = '1' or proxy_sel_STORE = '1' or proxy_sel_LOAD = '1') then
               relative_addr <= std_logic_vector(resize(unsigned(tmp_addr(nbits_address_space_rangesize-1 downto 0)), nbit_addr));
            else
               relative_addr <= std_logic_vector(resize(unsigned(S_addr_ram(nbits_address_space_rangesize-1 downto 0)), nbit_addr));
            end if;
         else
            if(USE_SPARSE_MEMORY=1) then
               relative_addr <= std_logic_vector(resize(unsigned(tmp_addr(nbits_address_space_rangesize-1 downto 0)), nbit_addr));
            else
               relative_addr <= std_logic_vector(unsigned(tmp_addr) - to_unsigned(address_space_begin, nbit_addr));
            end if;
         end if;
      end if;
   end process;

   L7_A : process(relative_addr)
   begin
      if (n_elements=1) then
         memory_addr_a <= (others => '0');
      elsif (n_byte_on_databus<=1) then
         memory_addr_a <= relative_addr(nbit_read_addr-1 downto 0);
      else
         memory_addr_a <= relative_addr(nbit_read_addr+nbits_byte_offset-1 downto nbits_byte_offset);
      end if;
   end process;

   out1 <= std_logic_vector(resize(unsigned(dout_a), BITSIZE_out1));
   proxy_out1 <= std_logic_vector(resize(unsigned(dout_a), BITSIZE_proxy_out1));

   L16 : process(reset, clock)
   begin
      if(1RESET_VALUE) then
         oe_ram_cs_delayed <= '0';
         if(HIGH_LATENCY /= 0) then
            oe_ram_cs_delayed_registered <= '0';
         end if;
         if(HIGH_LATENCY = 2) then
            oe_ram_cs_delayed_registered1 <= '0';
         end if;
      else
         if (clock'event and clock = '1') then
            if(HIGH_LATENCY = 0) then
               if(BUS_PIPELINED /= 0) then
                  oe_ram_cs_delayed <= oe_ram_cs;
               else
                  oe_ram_cs_delayed <= oe_ram_cs and (not oe_ram_cs_delayed);
               end if;
            elsif(HIGH_LATENCY = 1) then
               if(BUS_PIPELINED /= 0) then
                  oe_ram_cs_delayed_registered <= oe_ram_cs;
               else
                  oe_ram_cs_delayed_registered <= oe_ram_cs and ((not oe_ram_cs_delayed_registered and not oe_ram_cs_delayed));
               end if;
               oe_ram_cs_delayed <= oe_ram_cs_delayed_registered;
            else
               if(BUS_PIPELINED /= 0) then
                  oe_ram_cs_delayed_registered1 <= oe_ram_cs;
               else
                  oe_ram_cs_delayed_registered1 <= oe_ram_cs and ((not oe_ram_cs_delayed_registered1 and not oe_ram_cs_delayed_registered and not oe_ram_cs_delayed));
               end if;
               oe_ram_cs_delayed_registered <= oe_ram_cs_delayed_registered1;
               oe_ram_cs_delayed <= oe_ram_cs_delayed_registered;
            end if;
         end if;
      end if;
   end process;

   process(clock, reset)
   begin
      if(1RESET_VALUE) then
         we_ram_cs_delayed <= '0';
      else
         if (clock'event and clock = '1') then
            we_ram_cs_delayed <= we_ram_cs and not we_ram_cs_delayed;
         end if;
      end if;
   end process;

   L18 : process(Sin_Rdata_ram, dout_a, oe_ram_cs_delayed)
   begin
      if(PRIVATE_MEMORY=1) then
         Sout_Rdata_ram(BITSIZE_Sout_Rdata_ram-1 downto 0) <= Sin_Rdata_ram(BITSIZE_Sin_Rdata_ram-1 downto 0);
      else
         if (BITSIZE_Sout_Rdata_ram <= data_size) then
            if(oe_ram_cs_delayed /= '0') then
               Sout_Rdata_ram <= dout_a(BITSIZE_Sout_Rdata_ram-1 downto 0);
            else
               Sout_Rdata_ram <= Sin_Rdata_ram;
            end if;
         else
            if(oe_ram_cs_delayed /= '0') then
               Sout_Rdata_ram <= std_logic_vector(resize(unsigned(dout_a), BITSIZE_Sout_Rdata_ram));
            else
               Sout_Rdata_ram <= Sin_Rdata_ram;
            end if;
         end if;
      end if;
   end process;

   L19 : process(oe_ram_cs_delayed, Sin_DataRdy, we_ram_cs_delayed)
   begin
      if(PRIVATE_MEMORY=0) then
         Sout_DataRdy <= oe_ram_cs_delayed or Sin_DataRdy or we_ram_cs_delayed;
      else
         Sout_DataRdy <= Sin_DataRdy;
      end if;
   end process;

NP_functionality.VERILOG_PROVIDED:
`ifndef _SIM_HAVE_CLOG2
  function integer log2;
     input integer value;
     integer temp_value;
    begin
      temp_value = value-1;
      for (log2=0; temp_value>0; log2=log2+1)
        temp_value = temp_value>>1;
    end
  endfunction
`endif
parameter n_byte_on_databus = ALIGNMENT/8;
parameter nbit_addr_r = BITSIZE_in2r > BITSIZE_proxy_in2r ? BITSIZE_in2r : BITSIZE_proxy_in2r;
parameter nbit_addr_w = BITSIZE_in2w > BITSIZE_proxy_in2w ? BITSIZE_in2w : BITSIZE_proxy_in2w;
`ifdef _SIM_HAVE_CLOG2
  localparam nbit_read_addr = n_elements == 1 ? 1 : $clog2(n_elements);
  localparam nbits_byte_offset = n_byte_on_databus<=1 ? 0 : $clog2(n_byte_on_databus);
`else
  localparam nbit_read_addr = n_elements == 1 ? 1 : log2(n_elements);
  localparam nbits_byte_offset = n_byte_on_databus<=1 ? 0 : log2(n_byte_on_databus);
`endif
parameter max_n_writes = READ_ONLY_MEMORY ? 1 : PORTSIZE_sel_STORE;
parameter max_n_reads = PORTSIZE_sel_LOAD;

wire [nbit_read_addr*max_n_reads-1:0] memory_addr_a_r;
wire [nbit_read_addr*max_n_writes-1:0] memory_addr_a_w;

wire [max_n_writes-1:0] bram_write;

wire [data_size*max_n_reads-1:0] dout_a;
wire [nbit_addr_r*max_n_reads-1:0] relative_addr_r;
wire [nbit_addr_w*max_n_writes-1:0] relative_addr_w;
wire [nbit_addr_r*max_n_reads-1:0] tmp_addr_r;
wire [nbit_addr_w*max_n_writes-1:0] tmp_addr_w;
wire [data_size*max_n_writes-1:0] din_a;
wire [data_size*max_n_writes-1:0] din_a_mem;
reg [data_size*max_n_writes-1:0] din_a1;

STD_NRNW_BRAM_GEN #(
  .PORTSIZE_write_enable(max_n_writes),
  .BITSIZE_write_enable(1),
  .PORTSIZE_data_in(max_n_writes),
  .BITSIZE_data_in(data_size),
  .PORTSIZE_dout_value(max_n_reads),
  .BITSIZE_dout_value(data_size),
  .PORTSIZE_address_inr(max_n_reads),
  .BITSIZE_address_inr(nbit_read_addr),
  .PORTSIZE_address_inw(max_n_writes),
  .BITSIZE_address_inw(nbit_read_addr),
  .n_elements(n_elements),
  .MEMORY_INIT_file(MEMORY_INIT_file),
  .READ_ONLY_MEMORY(READ_ONLY_MEMORY),
  .HIGH_LATENCY(HIGH_LATENCY)
) STD_NRNW_BRAM_GEN_instance (
  .clock(clock),
  .write_enable(bram_write),
  .data_in(din_a),
  .address_inr(memory_addr_a_r),
  .address_inw(memory_addr_a_w),
  .dout_value(dout_a)
);

generate
genvar i14;
  for (i14=0; i14<max_n_writes; i14=i14+1)
  begin : L14
    assign din_a[(i14+1)*data_size-1:i14*data_size] = (proxy_sel_STORE[i14] && proxy_in4w[i14]) ? proxy_in1[(i14+1)*BITSIZE_proxy_in1-1:i14*BITSIZE_proxy_in1] : in1[(i14+1)*BITSIZE_in1-1:i14*BITSIZE_in1];
  end
endgenerate

generate
genvar i21;
  for (i21=0; i21<max_n_writes; i21=i21+1)
  begin : L21
      assign bram_write[i21] = (sel_STORE[i21] && in4w[i21]) || (proxy_sel_STORE[i21] && proxy_in4w[i21]);
  end
endgenerate

generate
genvar ind2r;
for (ind2r=0; ind2r<max_n_reads; ind2r=ind2r+1)
  begin : Lind2r
    assign tmp_addr_r[(ind2r+1)*nbit_addr_r-1:ind2r*nbit_addr_r] = (proxy_sel_LOAD[ind2r] && proxy_in4r[ind2r]) ? proxy_in2r[(ind2r+1)*BITSIZE_proxy_in2r-1:ind2r*BITSIZE_proxy_in2r] : in2r[(ind2r+1)*BITSIZE_in2r-1:ind2r*BITSIZE_in2r];
  end
endgenerate

generate
genvar ind2w;
for (ind2w=0; ind2w<max_n_writes; ind2w=ind2w+1)
  begin : Lind2w
    assign tmp_addr_w[(ind2w+1)*nbit_addr_w-1:ind2w*nbit_addr_w] = (proxy_sel_STORE[ind2w] && proxy_in4w[ind2w]) ? proxy_in2w[(ind2w+1)*BITSIZE_proxy_in2w-1:ind2w*BITSIZE_proxy_in2w] : in2w[(ind2w+1)*BITSIZE_in2w-1:ind2w*BITSIZE_in2w];
  end
endgenerate

generate
genvar i6r;
  for (i6r=0; i6r<max_n_reads; i6r=i6r+1)
  begin : L6r
    if(USE_SPARSE_MEMORY==1)
      assign relative_addr_r[(i6r+1)*nbit_addr_r-1:i6r*nbit_addr_r] = tmp_addr_r[(i6r+1)*nbit_addr_r-1:i6r*nbit_addr_r];
    else
      assign relative_addr_r[(i6r+1)*nbit_addr_r-1:i6r*nbit_addr_r] = tmp_addr_r[(i6r+1)*nbit_addr_r-1:i6r*nbit_addr_r]-address_space_begin;
  end
endgenerate

generate
genvar i6w;
  for (i6w=0; i6w<max_n_writes; i6w=i6w+1)
  begin : L6w
    if(USE_SPARSE_MEMORY==1)
      assign relative_addr_w[(i6w+1)*nbit_addr_w-1:i6w*nbit_addr_w] = tmp_addr_w[(i6w+1)*nbit_addr_w-1:i6w*nbit_addr_w];
    else
      assign relative_addr_w[(i6w+1)*nbit_addr_w-1:i6w*nbit_addr_w] = tmp_addr_w[(i6w+1)*nbit_addr_w-1:i6w*nbit_addr_w]-address_space_begin;
  end
endgenerate

generate
genvar i7r;
  for (i7r=0; i7r<max_n_reads; i7r=i7r+1)
  begin : L7_Ar
    if (n_elements==1)
      assign memory_addr_a_r[(i7r+1)*nbit_read_addr-1:i7r*nbit_read_addr] = {nbit_read_addr{1'b0}};
    else
      assign memory_addr_a_r[(i7r+1)*nbit_read_addr-1:i7r*nbit_read_addr] = relative_addr_r[nbit_read_addr+nbits_byte_offset-1+i7r*nbit_addr_r:nbits_byte_offset+i7r*nbit_addr_r];
  end
endgenerate

generate
genvar i7w;
  for (i7w=0; i7w<max_n_writes; i7w=i7w+1)
  begin : L7_Aw
    if (n_elements==1)
      assign memory_addr_a_w[(i7w+1)*nbit_read_addr-1:i7w*nbit_read_addr] = {nbit_read_addr{1'b0}};
    else
      assign memory_addr_a_w[(i7w+1)*nbit_read_addr-1:i7w*nbit_read_addr] = relative_addr_w[nbit_read_addr+nbits_byte_offset-1+i7w*nbit_addr_w:nbits_byte_offset+i7w*nbit_addr_w];
  end
endgenerate

assign out1 = dout_a;
assign proxy_out1 = dout_a;
assign Sout_Rdata_ram =Sin_Rdata_ram;
assign Sout_DataRdy = Sin_DataRdy;

`ifndef _SIM_HAVE_CLOG2
  function integer log2;
     input integer value;
     integer temp_value;
    begin
      temp_value = value-1;
      for (log2=0; temp_value>0; log2=log2+1)
        temp_value = temp_value>>1;
    end
  endfunction
`endif
parameter n_byte_on_databus = ALIGNMENT/8;
parameter nbit_addr = BITSIZE_in2 > BITSIZE_proxy_in2 ? BITSIZE_in2 : BITSIZE_proxy_in2;
`ifdef _SIM_HAVE_CLOG2
  localparam nbit_read_addr = n_elements == 1 ? 1 : $clog2(n_elements);
  localparam nbits_address_space_rangesize = $clog2(address_space_rangesize);
  localparam nbits_byte_offset = n_byte_on_databus<=1 ? 0 : $clog2(n_byte_on_databus);
`else
  localparam nbit_read_addr = n_elements == 1 ? 1 : log2(n_elements);
  localparam nbits_address_space_rangesize = log2(address_space_rangesize);
  localparam nbits_byte_offset = n_byte_on_databus<=1 ? 0 : log2(n_byte_on_databus);
`endif
parameter max_n_writes = PORTSIZE_sel_STORE;
parameter max_n_reads = PORTSIZE_sel_LOAD;
parameter max_n_rw = max_n_writes > max_n_reads ? max_n_writes : max_n_reads;
parameter n_bus_ports = PORTSIZE_S_addr_ram;

wire [nbit_read_addr*max_n_rw-1:0] memory_addr_a;

wire [max_n_writes-1:0] bram_write;
wire [data_size*max_n_reads-1:0] dout_a;
wire [n_bus_ports-1:0] cs;
wire [n_bus_ports-1:0] oe_ram_cs;
wire [n_bus_ports-1:0] we_ram_cs;
reg [n_bus_ports-1:0] we_ram_cs_delayed;
reg [n_bus_ports-1:0] oe_ram_cs_delayed;
reg [n_bus_ports-1:0] oe_ram_cs_delayed_registered;
reg [n_bus_ports-1:0] oe_ram_cs_delayed_registered1;
wire [nbit_addr*max_n_rw-1:0] relative_addr;
wire [nbit_addr*max_n_rw-1:0] tmp_addr;
wire [data_size*max_n_writes-1:0] din_a;
wire [data_size*max_n_writes-1:0] din_a_mem;
reg [data_size*max_n_writes-1:0] din_a1;
wire [data_size*PORTSIZE_S_Wdata_ram-1:0] S_Wdata_ram_int;

STD_NRNW_BRAM_GEN #(
  .PORTSIZE_write_enable(max_n_writes),
  .BITSIZE_write_enable(1),
  .PORTSIZE_data_in(max_n_writes),
  .BITSIZE_data_in(data_size),
  .PORTSIZE_dout_value(max_n_reads),
  .BITSIZE_dout_value(data_size),
  .PORTSIZE_address_inr(max_n_rw),
  .BITSIZE_address_inr(nbit_read_addr),
  .PORTSIZE_address_inw(max_n_rw),
  .BITSIZE_address_inw(nbit_read_addr),
  .n_elements(n_elements),
  .MEMORY_INIT_file(MEMORY_INIT_file),
  .READ_ONLY_MEMORY(READ_ONLY_MEMORY),
  .HIGH_LATENCY(HIGH_LATENCY)
) STD_NRNW_BRAM_GEN_instance (
  .clock(clock),
  .write_enable(bram_write),
  .data_in(din_a),
  .address_inr(memory_addr_a),
  .address_inw(memory_addr_a),
  .dout_value(dout_a)
);

generate
genvar ind2;
for (ind2=0; ind2<max_n_rw; ind2=ind2+1)
  begin : Lind2
    assign tmp_addr[(ind2+1)*nbit_addr-1:ind2*nbit_addr] = (proxy_sel_LOAD[ind2]||proxy_sel_STORE[ind2]) ? proxy_in2[(ind2+1)*BITSIZE_proxy_in2-1:ind2*BITSIZE_proxy_in2] : in2[(ind2+1)*BITSIZE_in2-1:ind2*BITSIZE_in2];
  end
endgenerate

generate
genvar i3;
  for (i3=0; i3<n_bus_ports; i3=i3+1)
  begin : L3
    if(PRIVATE_MEMORY==0 && USE_SPARSE_MEMORY==0)
      assign cs[i3] = (S_addr_ram[(i3+1)*BITSIZE_S_addr_ram-1:i3*BITSIZE_S_addr_ram] >= (address_space_begin)) && (S_addr_ram[(i3+1)*BITSIZE_S_addr_ram-1:i3*BITSIZE_S_addr_ram] < (address_space_begin+address_space_rangesize));
    else if(PRIVATE_MEMORY==0 && nbits_address_space_rangesize < 32)
      assign cs[i3] = S_addr_ram[(i3+1)*BITSIZE_S_addr_ram-1:i3*BITSIZE_S_addr_ram+nbits_address_space_rangesize] == address_space_begin[((nbit_addr-1) < 32 ? (nbit_addr-1) : 31):nbits_address_space_rangesize];
    else
      assign cs[i3] = 1'b0;
  end
endgenerate

generate
genvar i4;
  for (i4=0; i4<n_bus_ports; i4=i4+1)
  begin : L4
    assign oe_ram_cs[i4] = S_oe_ram[i4] & cs[i4];
  end
endgenerate

generate
genvar i5;
  for (i5=0; i5<n_bus_ports; i5=i5+1)
  begin : L5
    assign we_ram_cs[i5] = S_we_ram[i5] & cs[i5];
  end
endgenerate

generate
genvar i6;
  for (i6=0; i6<max_n_rw; i6=i6+1)
  begin : L6
    if(PRIVATE_MEMORY==0 && USE_SPARSE_MEMORY==0 && i6< n_bus_ports)
      assign relative_addr[(i6+1)*nbit_addr-1:i6*nbit_addr] = ((i6 < max_n_writes && (sel_STORE[i6]==1'b1 || proxy_sel_STORE[i6]==1'b1)) || (i6 < max_n_reads && (sel_LOAD[i6]==1'b1 || proxy_sel_LOAD[i6]==1'b1))) ? tmp_addr[(i6+1)*BITSIZE_in2-1:i6*BITSIZE_in2]-address_space_begin: S_addr_ram[(i6+1)*BITSIZE_S_addr_ram-1:i6*BITSIZE_S_addr_ram]-address_space_begin;
    else if(PRIVATE_MEMORY==0 && i6< n_bus_ports)
      assign relative_addr[(i6)*nbit_addr+nbits_address_space_rangesize-1:i6*nbit_addr] = ((i6 < max_n_writes && (sel_STORE[i6]==1'b1 || proxy_sel_STORE[i6]==1'b1)) || (i6 < max_n_reads && (sel_LOAD[i6]==1'b1 || proxy_sel_LOAD[i6]==1'b1))) ? tmp_addr[(i6)*BITSIZE_in2+nbits_address_space_rangesize-1:i6*BITSIZE_in2] : S_addr_ram[(i6)*BITSIZE_S_addr_ram+nbits_address_space_rangesize-1:i6*BITSIZE_S_addr_ram];
    else if(USE_SPARSE_MEMORY==1)
      assign relative_addr[(i6)*nbit_addr+nbits_address_space_rangesize-1:i6*nbit_addr] = tmp_addr[(i6)*BITSIZE_in2+nbits_address_space_rangesize-1:i6*BITSIZE_in2];
    else
      assign relative_addr[(i6+1)*nbit_addr-1:i6*nbit_addr] = tmp_addr[(i6+1)*BITSIZE_in2-1:i6*BITSIZE_in2]-address_space_begin;
  end
endgenerate

generate
genvar i7;
  for (i7=0; i7<max_n_rw; i7=i7+1)
  begin : L7_A
    if (n_elements==1)
      assign memory_addr_a[(i7+1)*nbit_read_addr-1:i7*nbit_read_addr] = {nbit_read_addr{1'b0}};
    else
      assign memory_addr_a[(i7+1)*nbit_read_addr-1:i7*nbit_read_addr] = relative_addr[nbit_read_addr+nbits_byte_offset-1+i7*nbit_addr:nbits_byte_offset+i7*nbit_addr];
  end
endgenerate

generate
genvar i13;
  for (i13=0; i13<n_bus_ports; i13=i13+1)
  begin : L13
    if (BITSIZE_S_Wdata_ram < data_size)
      assign S_Wdata_ram_int[data_size*(i13+1)-1:data_size*i13] = {{data_size-BITSIZE_S_Wdata_ram{1'b0}}, S_Wdata_ram[(i13+1)*BITSIZE_S_Wdata_ram-1:BITSIZE_S_Wdata_ram*i13]};
    else
      assign S_Wdata_ram_int[data_size*(i13+1)-1:data_size*i13] = S_Wdata_ram[data_size+BITSIZE_S_Wdata_ram*i13-1:BITSIZE_S_Wdata_ram*i13];
  end
endgenerate

generate
genvar i14;
  for (i14=0; i14<max_n_writes; i14=i14+1)
  begin : L14
    if(PRIVATE_MEMORY==0 && i14 < n_bus_ports)
      assign din_a[(i14+1)*data_size-1:i14*data_size] = proxy_sel_STORE[i14] ? proxy_in1[(i14+1)*BITSIZE_proxy_in1-1:i14*BITSIZE_proxy_in1] : (sel_STORE[i14] ? in1[(i14+1)*BITSIZE_in1-1:i14*BITSIZE_in1] : S_Wdata_ram_int[data_size*(i14+1)-1:data_size*i14]);
    else
      assign din_a[(i14+1)*data_size-1:i14*data_size] = proxy_sel_STORE[i14] ? proxy_in1[(i14+1)*BITSIZE_proxy_in1-1:i14*BITSIZE_proxy_in1] : in1[(i14+1)*BITSIZE_in1-1:i14*BITSIZE_in1];
  end
endgenerate

assign out1 = dout_a;
assign proxy_out1 = dout_a;

generate
genvar i16;
  for (i16=0; i16<n_bus_ports; i16=i16+1)
  begin : L16
    always @(posedge clock 1RESET_EDGE)
    begin
      if(1RESET_VALUE)
        begin
          oe_ram_cs_delayed[i16] <= 1'b0;
          if(HIGH_LATENCY!=0) oe_ram_cs_delayed_registered[i16] <= 1'b0;
          if(HIGH_LATENCY == 2) oe_ram_cs_delayed_registered1[i16] <= 1'b0;
        end
      else
        begin
          if(HIGH_LATENCY==0)
            oe_ram_cs_delayed[i16] <= oe_ram_cs[i16] & (!oe_ram_cs_delayed[i16] | BUS_PIPELINED);
          else if(HIGH_LATENCY==1)
          begin
            oe_ram_cs_delayed_registered[i16] <= oe_ram_cs[i16] & ((!oe_ram_cs_delayed_registered[i16] & !oe_ram_cs_delayed[i16]) | BUS_PIPELINED);
            oe_ram_cs_delayed[i16] <= oe_ram_cs_delayed_registered[i16];
          end
          else
          begin
            oe_ram_cs_delayed_registered1[i16] <= oe_ram_cs[i16] & ((!oe_ram_cs_delayed_registered1[i16] & !oe_ram_cs_delayed_registered[i16]) | BUS_PIPELINED);
            oe_ram_cs_delayed_registered[i16] <= oe_ram_cs_delayed_registered1[i16];
            oe_ram_cs_delayed[i16] <= oe_ram_cs_delayed_registered[i16];
          end
        end
    end
  end
endgenerate

generate
genvar i17;
  for (i17=0; i17<n_bus_ports; i17=i17+1)
  begin : L17
    always @(posedge clock 1RESET_EDGE)
    begin
      if(1RESET_VALUE)
        we_ram_cs_delayed[i17] <= 1'b0;
      else
        we_ram_cs_delayed[i17] <= we_ram_cs[i17] & !we_ram_cs_delayed[i17];
    end
  end
endgenerate

generate
genvar i18;
  for (i18=0; i18<n_bus_ports; i18=i18+1)
  begin : L18
    if(PRIVATE_MEMORY==1)
      assign Sout_Rdata_ram[(i18+1)*BITSIZE_Sout_Rdata_ram-1:i18*BITSIZE_Sout_Rdata_ram] = Sin_Rdata_ram[(i18+1)*BITSIZE_Sin_Rdata_ram-1:i18*BITSIZE_Sin_Rdata_ram];
    else if (BITSIZE_Sout_Rdata_ram <= data_size)
      assign Sout_Rdata_ram[(i18+1)*BITSIZE_Sout_Rdata_ram-1:i18*BITSIZE_Sout_Rdata_ram] = oe_ram_cs_delayed[i18] ? dout_a[BITSIZE_Sout_Rdata_ram+i18*data_size-1:i18*data_size] : Sin_Rdata_ram[(i18+1)*BITSIZE_Sin_Rdata_ram-1:i18*BITSIZE_Sin_Rdata_ram];
    else
      assign Sout_Rdata_ram[(i18+1)*BITSIZE_Sout_Rdata_ram-1:i18*BITSIZE_Sout_Rdata_ram] = oe_ram_cs_delayed[i18] ? {{BITSIZE_S_Wdata_ram-data_size{1'b0}}, dout_a[(i18+1)*data_size-1:i18*data_size]} : Sin_Rdata_ram[(i18+1)*BITSIZE_Sin_Rdata_ram-1:i18*BITSIZE_Sin_Rdata_ram];
  end
endgenerate

generate
genvar i19;
  for (i19=0; i19<n_bus_ports; i19=i19+1)
  begin : L19
    if(PRIVATE_MEMORY==0)
      assign Sout_DataRdy[i19] = (i19 < n_bus_ports && oe_ram_cs_delayed[i19]) | Sin_DataRdy[i19] | (i19 < n_bus_ports && we_ram_cs_delayed[i19]);
    else
      assign Sout_DataRdy[i19] = Sin_DataRdy[i19];
  end
endgenerate

generate
genvar i21;
  for (i21=0; i21<max_n_writes; i21=i21+1)
  begin : L21
    if(i21 < n_bus_ports)
      assign bram_write[i21] = (sel_STORE[i21] || proxy_sel_STORE[i21] || we_ram_cs[i21]);
    else
      assign bram_write[i21] = (sel_STORE[i21] || proxy_sel_STORE[i21]);
  end
endgenerate

wire  [BITSIZE_in2-1:0] tmp_addr;
wire int_sel_LOAD;
wire int_sel_STORE;
assign tmp_addr = in2;
assign Mout_addr_ram = (int_sel_LOAD || int_sel_STORE) ? tmp_addr : Min_addr_ram;
assign Mout_oe_ram = int_sel_LOAD ? 1'b1 : Min_oe_ram;
assign Mout_we_ram = int_sel_STORE ? 1'b1 : Min_we_ram;
assign out1 = M_Rdata_ram[BITSIZE_out1-1:0];
assign Mout_Wdata_ram = int_sel_STORE ? in1 : Min_Wdata_ram;
assign Mout_data_ram_size = int_sel_STORE || int_sel_LOAD ? in3[BITSIZE_in3-1:0] : Min_data_ram_size;
assign int_sel_LOAD = sel_LOAD & in4;
assign int_sel_STORE = sel_STORE & in4;
// Add assertion here
// psl default clock = (posedge clock);
// psl ERROR_LOAD_Min_oe_ram: assert never {sel_LOAD && Min_oe_ram};
// psl ERROR_STORE_Min_we_ram: assert never {sel_STORE && Min_we_ram};
// psl ERROR_STORE_LOAD: assert never {sel_STORE && sel_LOAD};

NP_functionality.VHDL_PROVIDED:
  signal tmp_addr : std_logic_vector(BITSIZE_in2-1 downto 0);
  signal int_sel_LOAD : std_logic;
  signal int_sel_STORE : std_logic;

begin
  tmp_addr <= in2;
  Mout_addr_ram <=  tmp_addr when ((int_sel_LOAD or int_sel_STORE) = '1') else Min_addr_ram;
  Mout_oe_ram  <= '1' when (int_sel_LOAD = '1') else  Min_oe_ram;
  Mout_we_ram  <= '1' when (int_sel_STORE = '1') else  Min_we_ram;
  out1  <= M_Rdata_ram(BITSIZE_out1-1 downto 0);
  Mout_Wdata_ram  <= std_logic_vector(resize(unsigned(in1), BITSIZE_Mout_Wdata_ram)) when (int_sel_STORE = '1') else Min_Wdata_ram;
  Mout_data_ram_size <= in3(BITSIZE_in3-1 downto 0) when ((int_sel_STORE or int_sel_LOAD) = '1') else Min_data_ram_size;
  int_sel_LOAD <= sel_LOAD and in4;
  int_sel_STORE <= sel_STORE and in4;

NP_functionality.VERILOG_PROVIDED:
parameter max_n_writes = PORTSIZE_sel_STORE > PORTSIZE_Mout_we_ram ? PORTSIZE_sel_STORE : PORTSIZE_Mout_we_ram;
parameter max_n_reads = PORTSIZE_sel_LOAD > PORTSIZE_Mout_oe_ram ? PORTSIZE_sel_STORE : PORTSIZE_Mout_oe_ram;
parameter max_n_rw = max_n_writes > max_n_reads ? max_n_writes : max_n_reads;
wire  [(PORTSIZE_in2*BITSIZE_in2)-1:0] tmp_addr;
wire [PORTSIZE_sel_LOAD-1:0] int_sel_LOAD;
wire [PORTSIZE_sel_STORE-1:0] int_sel_STORE;
assign int_sel_LOAD = sel_LOAD & in4;
assign int_sel_STORE = sel_STORE & in4;
assign tmp_addr = in2;
generate
genvar i;
  for (i=0; i<max_n_rw; i=i+1)
  begin : L0
    assign Mout_addr_ram[(i+1)*BITSIZE_Mout_addr_ram-1:i*BITSIZE_Mout_addr_ram] = ((i < PORTSIZE_sel_LOAD && int_sel_LOAD[i]) || (i < PORTSIZE_sel_STORE && int_sel_STORE[i])) ? (tmp_addr[(i+1)*BITSIZE_in2-1:i*BITSIZE_in2]) : Min_addr_ram[(i+1)*BITSIZE_Min_addr_ram-1:i*BITSIZE_Min_addr_ram];
  end
  endgenerate
assign Mout_oe_ram = int_sel_LOAD | Min_oe_ram;
assign Mout_we_ram = int_sel_STORE | Min_we_ram;
generate
  for (i=0; i<max_n_reads; i=i+1)
  begin : L1
    assign out1[(i+1)*BITSIZE_out1-1:i*BITSIZE_out1] = M_Rdata_ram[i*BITSIZE_M_Rdata_ram+BITSIZE_out1-1:i*BITSIZE_M_Rdata_ram];
end
endgenerate
generate
  for (i=0; i<max_n_rw; i=i+1)
  begin : L2
    assign Mout_Wdata_ram[(i+1)*BITSIZE_Mout_Wdata_ram-1:i*BITSIZE_Mout_Wdata_ram] = int_sel_STORE[i] ? in1[(i+1)*BITSIZE_in1-1:i*BITSIZE_in1] : Min_Wdata_ram[(i+1)*BITSIZE_Min_Wdata_ram-1:i*BITSIZE_Min_Wdata_ram];
end
endgenerate
generate
  for (i=0; i<max_n_rw; i=i+1)
  begin : L3
    assign Mout_data_ram_size[(i+1)*BITSIZE_Mout_data_ram_size-1:i*BITSIZE_Mout_data_ram_size] = ((i < PORTSIZE_sel_LOAD && int_sel_LOAD[i]) || (i < PORTSIZE_sel_STORE && int_sel_STORE[i])) ? (in3[(i+1)*BITSIZE_in3-1:i*BITSIZE_in3]) : Min_data_ram_size[(i+1)*BITSIZE_Min_data_ram_size-1:i*BITSIZE_Min_data_ram_size];
  end
  endgenerate

NP_functionality.VHDL_PROVIDED:
  function integer_ternary_operator(cond : boolean; op1 : integer; op2 : integer) return integer is
  begin
    if cond then
      return op1;
    else
      return op2;
    end if;
  end integer_ternary_operator;

  constant max_n_writes : integer := integer_ternary_operator(PORTSIZE_sel_STORE > PORTSIZE_Mout_we_ram, PORTSIZE_sel_STORE, PORTSIZE_Mout_we_ram);
  constant max_n_reads  : integer := integer_ternary_operator(PORTSIZE_sel_LOAD > PORTSIZE_Mout_oe_ram, PORTSIZE_sel_STORE, PORTSIZE_Mout_oe_ram);
  constant max_n_rw     : integer := integer_ternary_operator(max_n_writes > max_n_reads, max_n_writes, max_n_reads);

  signal tmp_addr : std_logic_vector((PORTSIZE_in2*BITSIZE_in2)-1 downto 0);
  signal int_sel_LOAD : std_logic_vector(PORTSIZE_sel_LOAD-1 downto 0);
  signal int_sel_STORE : std_logic_vector(PORTSIZE_sel_STORE-1 downto 0);

begin
  tmp_addr <= in2;
  int_sel_LOAD <= sel_LOAD and in4;
  int_sel_STORE <= sel_STORE and in4;

  L0 : process(tmp_addr, Min_addr_ram, int_sel_LOAD, int_sel_STORE)
  begin
    for i in 0 to max_n_rw-1 loop
      if((i < PORTSIZE_sel_LOAD and int_sel_LOAD(i)='1') or (i < PORTSIZE_sel_STORE and int_sel_STORE(i)='1')) then
        Mout_addr_ram((i+1)*BITSIZE_Mout_addr_ram-1 downto i*BITSIZE_Mout_addr_ram) <= tmp_addr((i+1)*BITSIZE_in2-1 downto i*BITSIZE_in2);
      else
        Mout_addr_ram((i+1)*BITSIZE_Mout_addr_ram-1 downto i*BITSIZE_Mout_addr_ram) <= Min_addr_ram((i+1)*BITSIZE_Min_addr_ram-1 downto i*BITSIZE_Min_addr_ram);
      end if;
    end loop;
  end process;

  Mout_oe_ram <= int_sel_LOAD or Min_oe_ram;
  Mout_we_ram <= int_sel_STORE or Min_we_ram;

  L1 : process(M_Rdata_ram)
  begin
    for i in 0 to max_n_reads-1 loop
       out1((i+1)*BITSIZE_out1-1 downto i*BITSIZE_out1) <= M_Rdata_ram(i*BITSIZE_M_Rdata_ram+BITSIZE_out1-1 downto i*BITSIZE_M_Rdata_ram);
    end loop;
  end process;

  L2 : process(int_sel_STORE, in1,Min_Wdata_ram)
  begin
     for i in 0 to max_n_rw-1 loop
       if(int_sel_STORE(i)='1') then
         Mout_Wdata_ram((i+1)*BITSIZE_Mout_Wdata_ram-1 downto i*BITSIZE_Mout_Wdata_ram) <= std_logic_vector(resize(unsigned(in1((i+1)*BITSIZE_in1-1 downto i*BITSIZE_in1)), BITSIZE_Mout_Wdata_ram));
       else
         Mout_Wdata_ram((i+1)*BITSIZE_Mout_Wdata_ram-1 downto i*BITSIZE_Mout_Wdata_ram) <= std_logic_vector(resize(unsigned(Min_Wdata_ram((i+1)*BITSIZE_Min_Wdata_ram-1 downto i*BITSIZE_Min_Wdata_ram)), BITSIZE_Mout_Wdata_ram));
       end if;
     end loop;
  end process;

  L3 : process(int_sel_LOAD, int_sel_STORE, in3, Min_data_ram_size)
  begin
    for i in 0 to max_n_rw-1 loop
      if((i < PORTSIZE_sel_LOAD and int_sel_LOAD(i)='1') or (i < PORTSIZE_sel_STORE and int_sel_STORE(i)='1')) then
        Mout_data_ram_size((i+1)*BITSIZE_Mout_data_ram_size-1 downto i*BITSIZE_Mout_data_ram_size) <= in3((i+1)*BITSIZE_in3-1 downto i*BITSIZE_in3);
      else
        Mout_data_ram_size((i+1)*BITSIZE_Mout_data_ram_size-1 downto i*BITSIZE_Mout_data_ram_size) <= Min_data_ram_size((i+1)*BITSIZE_Min_data_ram_size-1 downto i*BITSIZE_Min_data_ram_size);
      end if;
    end loop;
  end process;

NP_functionality.VERILOG_PROVIDED:
parameter [1:0] S_0 = 2'd0,
  S_1 = 2'd1,
  S_2 = 2'd2;
reg [1:0] _present_state=S_0, _next_state;
reg done_port;
reg Mout_oe_ram;
reg Mout_we_ram;
reg [BITSIZE_Mout_addr_ram-1:0] Mout_addr_ram;
reg [BITSIZE_Mout_Wdata_ram-1:0] Mout_Wdata_ram;
reg [BITSIZE_Mout_data_ram_size-1:0] Mout_data_ram_size;
always @(posedge clock 1RESET_EDGE)
  if (1RESET_VALUE)
    _present_state <= S_0;
  else
    _present_state <= _next_state;

always @(*)
begin
  _next_state=S_0;
  done_port=1'b0;
  Mout_oe_ram=Min_oe_ram;
  Mout_we_ram=Min_we_ram;
  Mout_data_ram_size=Min_data_ram_size;
  Mout_Wdata_ram=Min_Wdata_ram;
  Mout_addr_ram=Min_addr_ram;
  case (_present_state)
    S_0 :
      if(start_port != 1'b1)
      begin
        _next_state=S_0;
      end
      else
      begin
        _next_state=S_1;
        Mout_we_ram=1'b1;
        Mout_addr_ram=addr;
        Mout_Wdata_ram=data;
        Mout_data_ram_size=size;
        if(M_DataRdy)
        begin
          _next_state=S_2;
        end
      end
    S_1 :
    begin
      _next_state=S_1;
      Mout_we_ram=1'b1;
      Mout_addr_ram=addr;
      Mout_Wdata_ram=data;
      Mout_data_ram_size=size;
      if(M_DataRdy)
      begin
        _next_state=S_2;
      end
    end
    S_2 :
    begin
      _next_state=S_0;
      done_port=1'b1;
    end
  endcase
end

parameter [1:0] S_0 = 2'd0,
  S_1 = 2'd1,
  S_2 = 2'd2;
reg [1:0] _present_state=S_0, _next_state;
reg done_port;
reg [PORTSIZE_Mout_oe_ram-1:0] Mout_oe_ram;
reg [PORTSIZE_Mout_we_ram-1:0] Mout_we_ram;
reg [(PORTSIZE_Mout_addr_ram*BITSIZE_Mout_addr_ram)+(-1):0] Mout_addr_ram;
reg [(PORTSIZE_Mout_Wdata_ram*BITSIZE_Mout_Wdata_ram)+(-1):0] Mout_Wdata_ram;
reg [(PORTSIZE_Mout_data_ram_size*BITSIZE_Mout_data_ram_size)+(-1):0] Mout_data_ram_size;
always @(posedge clock 1RESET_EDGE)
  if (1RESET_VALUE)
    _present_state <= S_0;
  else
    _present_state <= _next_state;

always @(*)
begin
  _next_state=S_0;
  done_port=1'b0;
  Mout_oe_ram=Min_oe_ram;
  Mout_we_ram=Min_we_ram;
  Mout_data_ram_size=Min_data_ram_size;
  Mout_Wdata_ram=Min_Wdata_ram;
  Mout_addr_ram=Min_addr_ram;
  case (_present_state)
    S_0 :
      if(start_port != 1'b1)
      begin
        _next_state=S_0;
      end
      else
      begin
        _next_state=S_1;
        Mout_we_ram[0]=1'b1;
        Mout_addr_ram[BITSIZE_Mout_addr_ram-1:0]=addr;
        Mout_Wdata_ram[BITSIZE_Mout_Wdata_ram-1:0]=data;
        Mout_data_ram_size[BITSIZE_Mout_data_ram_size-1:0]=size;
        if(M_DataRdy[0])
        begin
          _next_state=S_2;
        end
      end
    S_1 :
    begin
      _next_state=S_1;
      Mout_we_ram[0]=1'b1;
      Mout_addr_ram[BITSIZE_Mout_addr_ram-1:0]=addr;
      Mout_Wdata_ram[BITSIZE_Mout_Wdata_ram-1:0]=data;
      Mout_data_ram_size[BITSIZE_Mout_data_ram_size-1:0]=size;
      if(M_DataRdy[0])
      begin
        _next_state=S_2;
      end
    end
    S_2 :
    begin
      _next_state=S_0;
      done_port=1'b1;
    end
  endcase
end

NP_functionality.VHDL_PROVIDED:
signal present_state : std_logic_vector(1 downto 0) := "00";
signal next_state : std_logic_vector(1 downto 0);
begin
process(clock,reset)
begin
  if(1RESET_VALUE) then
    present_state <= "00";
  elsif (clock'event and clock = '1') then
    present_state <= next_state;
  end if;
end process;
process(present_state, start_port, Min_oe_ram, Min_we_ram, Min_data_ram_size,Min_Wdata_ram,Min_addr_ram, M_DataRdy(0), M_Rdata_ram,data)
begin
  next_state <= "00";
  done_port <= '0';
  Mout_oe_ram <= Min_oe_ram;
  Mout_we_ram <=Min_we_ram;
  Mout_data_ram_size <= Min_data_ram_size;
  Mout_Wdata_ram <= Min_Wdata_ram;
  Mout_addr_ram <= Min_addr_ram;
  case present_state is
    when "00" =>
     if(start_port = '0') then
       next_state <= "00";
    else
      next_state <= "01";
      Mout_we_ram(0) <= '1';
      Mout_addr_ram(BITSIZE_Mout_addr_ram-1 downto 0) <= std_logic_vector(resize(unsigned(addr), BITSIZE_Mout_addr_ram));
      Mout_Wdata_ram(BITSIZE_data-1 downto 0) <= data;
      Mout_data_ram_size(BITSIZE_Mout_data_ram_size-1 downto 0) <= std_logic_vector(resize(unsigned(size), BITSIZE_Mout_data_ram_size));
      if(M_DataRdy(0) = '1') then
        next_state <= "10";
      end if;
    end if;
  when "01" =>
    next_state <= "01";
    Mout_we_ram(0) <= '1';
    Mout_addr_ram(BITSIZE_Mout_addr_ram-1 downto 0) <= std_logic_vector(resize(unsigned(addr), BITSIZE_Mout_addr_ram));
    Mout_Wdata_ram(BITSIZE_data-1 downto 0) <= data;
    Mout_data_ram_size(BITSIZE_Mout_data_ram_size-1 downto 0) <= std_logic_vector(resize(unsigned(size), BITSIZE_Mout_data_ram_size));
    if(M_DataRdy(0) = '1') then
       next_state <= "10";
    end if;
  when "10" =>
    next_state <= "00";
     done_port <= '1';
     when others =>
    end case;
end process;

NP_functionality.VERILOG_PROVIDED:
parameter idle=0,taglookup=1,readdata=3,waitmemread=4,write=6;
//Control Signals
reg tagwrite;
reg datawrite;
reg countup;
reg cachereadswitch,writefrommem;  // mux controls
reg M_wait;
reg S_read;
reg S_write;

//Control inputs
wire cachehit;

reg [3:0] state,nextstate;

wire [DATAWIDTH-1:0] Cache_rddata,Cache_wrdata;
wire [ADDRESSWIDTH-1:LOG2CACHEDEPTH] oldtag;
wire valid,tagsmatch;

assign M_rddata=(cachereadswitch) ? Cache_rddata : S_rddata;
assign S_address=M_address;
assign S_wrdata=M_wrdata;
assign Cache_wrdata = (writefrommem) ? S_rddata : M_wrdata;

  _comparator #(.BITSIZE_dataa(ADDRESSWIDTH-LOG2CACHEDEPTH),
  .BITSIZE_datab(ADDRESSWIDTH-LOG2CACHEDEPTH))
            comparator(
              .dataa(M_address[ADDRESSWIDTH-1:LOG2CACHEDEPTH]),
              .datab(oldtag),
              .aeb(tagsmatch)
            );
  assign cachehit=tagsmatch&valid;

  // Tag and valid bit cache
  STD_SP_BRAM #(
    .BITSIZE_data_out(ADDRESSWIDTH - LOG2CACHEDEPTH + 1),
    .BITSIZE_data_in(ADDRESSWIDTH - LOG2CACHEDEPTH + 1),
    .BITSIZE_address_inr(LOG2CACHEDEPTH),
    .BITSIZE_address_inw(LOG2CACHEDEPTH),
    .MEMORY_INIT_file(""),
    .n_elements(CACHEDEPTH),
    .READ_ONLY_MEMORY(0),
    .HIGH_LATENCY(0)
	) tagcache (
      .clock (clock),
      .write_enable(tagwrite),
      .address_inr (M_address[LOG2CACHEDEPTH + LOG2DATAWIDTH -1:LOG2DATAWIDTH]),
      .address_inw (M_address[LOG2CACHEDEPTH + LOG2DATAWIDTH -1:LOG2DATAWIDTH]),
      .data_in ({1'b1,M_address[ADDRESSWIDTH-1:LOG2CACHEDEPTH]}),
      .data_out ({valid,oldtag})
    );

  //Ram used to store data
  STD_SP_BRAM #(
    .BITSIZE_data_out(DATAWIDTH),
    .BITSIZE_data_in(DATAWIDTH),
    .BITSIZE_address_inr(LOG2CACHEDEPTH),
    .BITSIZE_address_inw(LOG2CACHEDEPTH),
    .MEMORY_INIT_file(""),
    .n_elements(CACHEDEPTH),
    .READ_ONLY_MEMORY(0),
    .HIGH_LATENCY(0)
  ) data_cache (
    .clock (clock),
    .write_enable(datawrite),
    .address_inr(M_address[LOG2CACHEDEPTH + LOG2DATAWIDTH -1: LOG2DATAWIDTH]),
    .address_inw(M_address[LOG2CACHEDEPTH + LOG2DATAWIDTH -1: LOG2DATAWIDTH]),
    .data_in (Cache_wrdata),
    .data_out (Cache_rddata)
    );

  // ******************* Cache Controller **********************//
  always @(posedge clock)
    state<=nextstate;

  always @(M_read or M_write or state or S_wait or cachehit)
  begin
     tagwrite<=0;
    datawrite<=0;
    cachereadswitch<=0; writefrommem<=0;
    M_wait<=0;
    S_read<=0;
    S_write<=0;
    case(state)
    idle:
      if (M_read==0 && M_write==0)
        nextstate<=idle;
      else if (M_read==1 && M_write==0)
      begin
        M_wait<=1;
        nextstate<=readdata;
      end
      else //is a write request
      begin
        M_wait<=1;
        datawrite<=1;   //write to data cache
        tagwrite<=1;
        S_write<=1;
        nextstate<=write;
      end
    write:
      if (S_wait)
      begin
        S_write<=1;
        M_wait<=1;
        nextstate<=write;
      end
      else
        nextstate<=idle;
    readdata:
      if (cachehit)
      begin
        cachereadswitch<=1;
        nextstate<=idle;
      end
      else
      begin
        M_wait<=1;
        S_read<=1;
        nextstate<=waitmemread;
      end
    waitmemread:
      if (S_wait)
      begin
        S_read<=1;
        M_wait<=1;
        nextstate<=waitmemread;
      end
      else
      begin
        tagwrite<=1;
        datawrite<=1;
        writefrommem<=1;
        nextstate<=idle;
      end
    default:
      nextstate<=idle;
    endcase
  end
