NP_functionality.VERILOG_PROVIDED:
// synthesis attribute use_sync_reset of current is no;
reg  [PORTSIZE_ops-1:0] current 1INIT_ZERO_VALUE;
wire [PORTSIZE_ops-1:0] or_temp;
wire sop_temp;
function reduce_INs;
input [PORTSIZE_ops-1 : 0] instring;
integer i;
begin
   reduce_INs = 1'b1;
   for(i = 0; i < PORTSIZE_ops; i = i + 1)
   begin
      reduce_INs = reduce_INs & instring[i];
   end
end
endfunction
function [PORTSIZE_ops-1:0] NEXT_CURR ;
input sop_temp1;
input [PORTSIZE_ops-1 : 0] ops1;
input [PORTSIZE_ops-1 : 0] current1;
integer i;
begin
   for(i = 0; i < PORTSIZE_ops; i = i + 1)
   begin
      NEXT_CURR[i] = ~sop_temp1 & ((current1[i]) | ops1[i]);
   end
end
endfunction
assign or_temp = current | ops;
assign sop_temp = reduce_INs(or_temp);
assign sop = sop_temp;
always @ (posedge clock 1RESET_EDGE)
begin
if(1RESET_VALUE)
  begin
    current <= {PORTSIZE_ops{1'b0}};
  end
else
  begin
    current <= NEXT_CURR(sop_temp,ops,current);
  end
end
