function [BITSIZE_out1-1:0] merge;
  input [BITSIZE_in1*PORTSIZE_in1-1:0] m;
  reg [BITSIZE_out1-1:0] res;
  integer i1;
begin
  res={BITSIZE_in1{1'b0}};
  for(i1 = 0; i1 < PORTSIZE_in1; i1 = i1 + 1)
  begin
    res = res | m[i1*BITSIZE_in1 +:BITSIZE_in1];
  end
  merge = res;
end
endfunction

assign out1 = merge(in1);

NP_functionality.VHDL_PROVIDED:
begin
process(in1)
variable temp_out1 : std_logic_vector(BITSIZE_out1-1 downto 0);
begin
  temp_out1 := (others => '0');
  for I in 0 to PORTSIZE_in1-1 loop
    temp_out1 := temp_out1 or in1(((I+1)*BITSIZE_in1)-1 downto (I*BITSIZE_in1));
  end loop;  out1 <= temp_out1;
end process;

NP_functionality.VERILOG_PROVIDED:
reg [BITSIZE_out1-1:0] out1;
reg [BITSIZE_out2-1:0] out2;
always @ (sel or in1)
  case (sel)
    1'b1: {out1, out2} = {{BITSIZE_out1{1'b0}}, in1};
    default: {out1, out2} = {in1, {BITSIZE_out2{1'b0}}};
  endcase

NP_functionality.VHDL_PROVIDED:

 type STATE_TYPE is (IDLE, READDATA, READADDR, WAITING, RUNNING);

 signal STATE : STATE_TYPE := IDLE;
 signal n_reads : natural range 1 to in_words := 1;
 signal n_addr : natural range 1 to addr_words := 1;
 signal temp_addr : std_logic_vector(31 downto 0);
 signal stop : std_logic := '0';

begin

 S_Read <= S_Exists when STATE = READDATA or STATE = READADDR else '0';
 M_Write <= '0';
 M_Control <= stop;

 handler : process (FSLCLK) is     
 begin
  if (FSLCLK'event and FSLCLK = '1') then
   core_reset <= '0';
   if (FSLRST = '1') then
    STATE <= IDLE;
   else
    case STATE is                        

     when IDLE =>
      if (S_Exists = '1' and stop = '0') then
       for i in 0 to in_words-1 loop
        IN_DATA(BITSIZE_S_Data*(i+1)-1 downto i*BITSIZE_S_Data) <= (others => 'Z');
       end loop;
       for i in 0 to addr_words-1 loop
        IN_ADDR(BITSIZE_S_Data*(i+1)-1 downto i*BITSIZE_S_Data) <= (others => 'Z');
       end loop;

       temp_addr <= (others => 'Z');
       STATE <= READDATA;
       n_reads <= 1;
      end if;

     when READDATA =>
      if (S_Exists = '1') then
       IN_DATA(BITSIZE_S_Data*n_reads-1 downto (n_reads-1)*BITSIZE_S_Data) <= S_Data;
       if (n_reads = in_words) then
        STATE <= READADDR;
        n_addr <= 1;
       else
        n_reads <= n_reads + 1;
       end if;
      end if;

     when READADDR =>
      if (S_Exists = '1') then
       IN_ADDR(BITSIZE_S_Data*n_addr-1 downto (n_addr-1)*BITSIZE_S_Data) <= S_Data;
       if (n_addr = addr_words) then
        core_reset <= '1';
        STATE <= WAITING;
       else
        n_addr <= n_addr + 1;
       end if;
      end if;

     when WAITING =>
      if (core_done = '1') then
       STATE <= RUNNING;
      end if;

     when RUNNING =>
      if (core_done = '1') then
       stop <= '1';
       STATE <= IDLE;
      end if;

    end case;
   end if;
  end if;
 end process handler;

