NP_functionality.VERILOG_PROVIDED:
function automatic integer log2;
  input integer value;
  `ifdef _SIM_HAVE_CLOG2
    log2 = $clog2(value);
  `else
    automatic integer temp_value = value-1;
    for (log2=0; temp_value > 0; log2=log2+1)
      temp_value = temp_value >> 1;
  `endif
endfunction

import "DPI-C" function void m_getarg(inout logic [127:0] data, input int unsigned index);
import "DPI-C" function void m_setarg(input logic [127:0] data, input int unsigned index);
import "DPI-C" function int unsigned m_getptrargsize(input int unsigned index);

function automatic [BITSIZE_data-1:0] getarg(input int unsigned index);
  automatic reg [127:0] data = 0;
  m_getarg(data, index);
  return data[BITSIZE_data-1:0];
endfunction

function automatic ptr_t getptrarg(input int unsigned index);
  automatic reg [127:0] data = 0;
  m_getarg(data, index);
  return data[$bits(ptr_t)-1:0];
endfunction

function automatic int unsigned getptrargsize(input int unsigned index);
  return m_getptrargsize(index);
endfunction

function automatic void setarg(input logic [BITSIZE_data-1:0] data, input int unsigned index);
  automatic reg [127:0] _data = 0;
  _data[BITSIZE_data-1:0] = data;
  m_setarg(_data, index);
endfunction

function automatic integer log2;
  input integer value;
  `ifdef _SIM_HAVE_CLOG2
    log2 = $clog2(value);
  `else
    automatic integer temp_value = value-1;
    for (log2=0; temp_value > 0; log2=log2+1)
      temp_value = temp_value >> 1;
  `endif
endfunction

parameter BITSIZE_mem_bus=1<<(log2(BITSIZE_data) > 3 ? log2(BITSIZE_data) : 3),
  BITSIZE_strobe=BITSIZE_mem_bus/8;

import "DPI-C" function void m_read8    (output logic    [7:0] data, input ptr_t addr);
import "DPI-C" function void m_read16   (output logic   [15:0] data, input ptr_t addr);
import "DPI-C" function void m_read32   (output logic   [31:0] data, input ptr_t addr);
import "DPI-C" function void m_read64   (output logic   [63:0] data, input ptr_t addr);
import "DPI-C" function void m_read128  (output logic  [127:0] data, input ptr_t addr);
import "DPI-C" function void m_read256  (output logic  [255:0] data, input ptr_t addr);
import "DPI-C" function void m_read512  (output logic  [511:0] data, input ptr_t addr);
import "DPI-C" function void m_read1024 (output logic [1023:0] data, input ptr_t addr);
import "DPI-C" function void m_read2048 (output logic [2047:0] data, input ptr_t addr);
import "DPI-C" function void m_read4096 (output logic [4095:0] data, input ptr_t addr);

import "DPI-C" function void m_write8    (input shortint unsigned size, input logic    [7:0] data, input ptr_t addr);
import "DPI-C" function void m_write16   (input shortint unsigned size, input logic   [15:0] data, input ptr_t addr);
import "DPI-C" function void m_write32   (input shortint unsigned size, input logic   [31:0] data, input ptr_t addr);
import "DPI-C" function void m_write64   (input shortint unsigned size, input logic   [63:0] data, input ptr_t addr);
import "DPI-C" function void m_write128  (input shortint unsigned size, input logic  [127:0] data, input ptr_t addr);
import "DPI-C" function void m_write256  (input shortint unsigned size, input logic  [255:0] data, input ptr_t addr);
import "DPI-C" function void m_write512  (input shortint unsigned size, input logic  [511:0] data, input ptr_t addr);
import "DPI-C" function void m_write1024 (input shortint unsigned size, input logic [1023:0] data, input ptr_t addr);
import "DPI-C" function void m_write2048 (input shortint unsigned size, input logic [2047:0] data, input ptr_t addr);
import "DPI-C" function void m_write4096 (input shortint unsigned size, input logic [4095:0] data, input ptr_t addr);

function automatic [BITSIZE_data-1:0] read(input ptr_t addr);
  automatic reg [4095:0] wtmp = 0;

  if(BITSIZE_mem_bus == 8)
    m_read8(wtmp[7:0], addr);
  else if(BITSIZE_mem_bus == 16)
    m_read16(wtmp[15:0], addr);
  else if(BITSIZE_mem_bus == 32)
    m_read32(wtmp[31:0], addr);
  else if(BITSIZE_mem_bus == 64)
    m_read64(wtmp[63:0], addr);
  else if(BITSIZE_mem_bus == 128)
    m_read128(wtmp[127:0], addr);
  else if(BITSIZE_mem_bus == 256)
    m_read256(wtmp[255:0], addr);
  else if(BITSIZE_mem_bus == 512)
    m_read512(wtmp[511:0], addr);
  else if(BITSIZE_mem_bus == 1024)
    m_read1024(wtmp[1023:0], addr);
  else if(BITSIZE_mem_bus == 2048)
    m_read2048(wtmp[2047:0], addr);
  else if(BITSIZE_mem_bus == 4096)
    m_read4096(wtmp[4095:0], addr);
  else
  begin
    $display("Unsupported memory bus size: %0d", BITSIZE_mem_bus);
    $finish;
  end
  return wtmp[BITSIZE_data-1:0];
endfunction

function automatic void write(
  input shortint size,
  input logic [BITSIZE_data-1:0] data,
  input ptr_t addr);
  automatic reg [4095:0] wtmp = 0;

  wtmp[BITSIZE_data-1:0] = data[BITSIZE_data-1:0];
  if(BITSIZE_mem_bus == 8)
    m_write8(size, wtmp[7:0], addr);
  else if(BITSIZE_mem_bus == 16)
    m_write16(size, wtmp[15:0], addr);
  else if(BITSIZE_mem_bus == 32)
    m_write32(size, wtmp[31:0], addr);
  else if(BITSIZE_mem_bus == 64)
    m_write64(size, wtmp[63:0], addr);
  else if(BITSIZE_mem_bus == 128)
    m_write128(size, wtmp[127:0], addr);
  else if(BITSIZE_mem_bus == 256)
    m_write256(size, wtmp[255:0], addr);
  else if(BITSIZE_mem_bus == 512)
    m_write512(size, wtmp[511:0], addr);
  else if(BITSIZE_mem_bus == 1024)
    m_write1024(size, wtmp[1023:0], addr);
  else if(BITSIZE_mem_bus == 2048)
    m_write2048(size, wtmp[2047:0], addr);
  else if(BITSIZE_mem_bus == 4096)
    m_write4096(size, wtmp[4095:0], addr);
  else
  begin
    $display("Unsupported memory bus size: %0d", BITSIZE_mem_bus);
    $finish;
  end
endfunction

function automatic void write_strobe(
  input logic [BITSIZE_strobe-1:0] strobe,
  input logic [BITSIZE_data-1:0] data,
  input ptr_t addr);
  automatic shortint unsigned size = 0;

  while(strobe != 0 && !strobe[0])
  begin
    addr = addr + 1;
    strobe = strobe >> 1;
  end
  while(strobe[0])
  begin
    size = size + 8;
    strobe = strobe >> 1;
  end
  if(strobe != 0)
  begin
    $display("Scattered strobe write operations not supported");
    $finish;
  end

  write(size, data, addr);
endfunction

`ifdef VERILATOR
timeunit 1ps;
timeprecision 1ps;
`endif

import "DPI-C" function void m_init();
import "DPI-C" function int unsigned m_next(input int unsigned state);
import "DPI-C" function int m_fini();

parameter [6:0] 
  SIM_READY   =7'd1,
  COSIM_INIT  =7'd2,
  SIM_SETUP   =7'd4,
  SIM_RUNNING =7'd8,
  SIM_END     =7'd16,
  COSIM_END   =7'd32,
  COSIM_ABORT =7'd64;
reg [$bits(SIM_READY)-1:0] state, state_next, state_succ, state_succ_next;

reg rst, rst_next, setup, setup_next, start, start_next;
integer rst_count, rst_count_next;
time over_time;

initial
begin
  // Open file results will be written
  automatic integer res_file;
  res_file = $fopen(RESFILE, "w");
  if (res_file == 0)
  begin
    $display("ERROR - Error opening the res_file");
    $finish;// Terminate
  end
  $fwrite(res_file, "");
  $fclose(res_file);

  state = SIM_READY;
  state_next = SIM_READY;
  state_succ = SIM_READY;
  state_succ_next = SIM_READY;
  rst = !RESET_ACTIVE;
  rst_next = !RESET_ACTIVE;
  rst_count = RESET_CYCLES - 1;
  rst_count_next = RESET_CYCLES - 1;
  setup = 0;
  setup_next = 0;
  start = 0;
  start_next = 0;
  over_time = 0;

  $display("Results file: %s", RESFILE);
  $display("Reset active: %0s", rst ? "LOW" : "HIGH");

  m_init();
end

assign reset = rst;
assign setup_port = setup;
assign start_port = start;

always @(posedge clock)
begin
  state <= state_next;
  state_succ <= state_succ_next;
  rst <= rst_next;
  rst_count <= rst_count_next;
  setup <= setup_next;
  start <= start_next;
  case(state_next)
  SIM_READY:
    begin
      automatic integer unsigned next_state = m_next(COSIM_INIT);
      `ifndef NDEBUG
      $display("Sim: next state: %0d", next_state);
      `endif
      state_succ <= next_state[$bits(state_succ)-1:0];
    end
  SIM_SETUP:
    begin
      automatic time start_time = $time + CLOCK_PERIOD;
      automatic time start_cycle = $rtoi(start_time/CLOCK_PERIOD);
      automatic integer res_file;
      res_file = $fopen(RESFILE, "a");
      $fwrite(res_file, "%0d|", start_time);
      $fclose(res_file);
      `ifndef NDEBUG
      $display("Sim: Argument setup\\nSim: Simulation started at cycle %0d", start_cycle);
      `endif
      over_time <= start_cycle + MAX_SIM_CYCLES;
    end
  SIM_RUNNING:
    begin
      automatic time curr_cycle = $rtoi($time/CLOCK_PERIOD);
      if(curr_cycle >= over_time)
      begin
        automatic integer res_file;
        res_file = $fopen(RESFILE, "a");
        $fwrite(res_file, "X");
        $fclose(res_file);
        $display("Sim: Simulation exceeds %0d cycles", MAX_SIM_CYCLES);
        $finish;
      end
    end
  SIM_END:
    begin
      automatic time curr_time = $time;
      automatic time curr_cycle = $rtoi(curr_time/CLOCK_PERIOD);
      automatic integer res_file;
      res_file = $fopen(RESFILE, "a");
      $fwrite(res_file, "%0d,", curr_time);
      $fclose(res_file);
      `ifndef NDEBUG
      $display("Sim: DUT port writeback\\nSim: Simulation ended at cycle %0d", curr_cycle);
      `endif
    end
  COSIM_END:
    begin
      automatic integer r = m_fini();
      automatic integer res_file;
      res_file = $fopen(RESFILE, "a");
      if(r[$bits(SIM_READY)-1:0] == COSIM_ABORT)
      begin
        $fwrite(res_file, "\\nA\\n");
        $display("Sim: Testbench aborted");
      end
      else
      begin
        $fwrite(res_file, "\\n%0d\\n", r[15:8]);
        $display("Sim: Testbench returned: %0d", r[15:8]);
      end
      $fclose(res_file);
      $finish;
    end
  default:
    begin
    end
  endcase
end

always @(*)
begin
  rst_next = rst;
  rst_count_next = rst_count;
  setup_next = setup;
  start_next = start;
  state_next = state;
  state_succ_next = state_succ;
  case(state)
  SIM_READY:
    begin
      state_next = state_succ;
      if(state_next == SIM_SETUP)
      begin
        rst_next = RESET_ACTIVE;
        setup_next = RESET_CYCLES == 1;
        rst_count_next = RESET_CYCLES - 1;
      end
    end
  SIM_SETUP:
    begin
      if(rst_count > 0)
      begin
        setup_next = rst_count == 1;
        rst_count_next = rst_count - 1;
      end
      else
      begin
        state_next = SIM_RUNNING;
        rst_next = !RESET_ACTIVE;
        setup_next = 0;
        start_next = 1;
      end
    end
  SIM_RUNNING:
    begin
      start_next = 0;
      if(done_port)
      begin
        state_next = SIM_END;
      end
    end
  SIM_END:
    begin
      // A clock cycle must pass to allow interface modules 
      // finalization operations before m_next(SIM_END) is called
      state_next = SIM_READY;
    end
  COSIM_END:
    begin
    end
  default:
    begin
      state_next = SIM_READY;
    end
  endcase
end

parameter MEM_DELAY_MAX= MEM_DELAY_READ > MEM_DELAY_WRITE ? MEM_DELAY_READ : MEM_DELAY_WRITE,
  CHANNELS_NUMBER=BITSIZE_Mout_oe_ram,
  BITSIZE_oe=1,
  BITSIZE_we=1,
  BITSIZE_addr=BITSIZE_Mout_addr_ram/CHANNELS_NUMBER,
  BITSIZE_Wsize=BITSIZE_Mout_data_ram_size/CHANNELS_NUMBER,
  BITSIZE_Wdata=BITSIZE_Mout_Wdata_ram/CHANNELS_NUMBER,
  BITSIZE_ready=1,
  BITSIZE_Rdata=BITSIZE_M_Rdata_ram/CHANNELS_NUMBER,
  BITSIZE_item=BITSIZE_Wdata+BITSIZE_Wsize+BITSIZE_addr+BITSIZE_we+BITSIZE_oe,
  OFFSET_oe=0,
  OFFSET_we=OFFSET_oe+BITSIZE_oe,
  OFFSET_addr=OFFSET_we+BITSIZE_we,
  OFFSET_Wsize=OFFSET_addr+BITSIZE_addr,
  OFFSET_Wdata=OFFSET_Wsize+BITSIZE_Wsize;

genvar i;
integer dump_file;
reg [BITSIZE_M_DataRdy-1:0] _M_DataRdy;
reg [BITSIZE_M_Rdata_ram-1:0] _M_Rdata_ram;

assign S_oe_ram = Mout_oe_ram;
assign S_we_ram = Mout_we_ram;
assign S_addr_ram = Mout_addr_ram;
assign S_data_ram_size = Mout_data_ram_size;
assign S_Wdata_ram = Mout_Wdata_ram;
assign M_DataRdy = _M_DataRdy | (Sout_DataRdy === 1'b1);
assign M_Rdata_ram = _M_Rdata_ram | (Sout_Rdata_ram & {BITSIZE_M_Rdata_ram{Sout_DataRdy === 1'b1}});

generate
  if(MEM_DUMP)
  begin
    initial
    begin
      dump_file = $fopen(MEM_DUMP_FILE, "w");
      $fwrite(dump_file, "Channel,Operation,Address,Bitwidth,Data\\n");
    end

    always@(posedge clock)
    begin
      if(done_port)
      begin
        $fflush(dump_file);
      end
    end
  end
endgenerate

mem_utils #(BITSIZE_Rdata) m_utils();

generate
  for(i = 0; i < CHANNELS_NUMBER; i = i + 1)
  begin : channel
    reg [MEM_DELAY_MAX*BITSIZE_item-1:0] queue, queue_next = 0;
    reg [BITSIZE_oe-1:0] oe;
    reg [BITSIZE_we-1:0] we;
    ptr_t Waddr, Raddr;
    shortint unsigned Wsize;
    reg [BITSIZE_Wdata-1:0] Wdata;
    reg [BITSIZE_ready-1:0] Wready, Rready;
    reg [BITSIZE_Rdata-1:0] Rdata;
    assign oe = queue_next[(MEM_DELAY_READ-1)*BITSIZE_item+OFFSET_oe+:BITSIZE_oe];
    assign we = queue_next[(MEM_DELAY_WRITE-1)*BITSIZE_item+OFFSET_we+:BITSIZE_we];
    assign Raddr = queue_next[(MEM_DELAY_READ-1)*BITSIZE_item+OFFSET_addr+:BITSIZE_addr];
    assign Waddr = queue_next[(MEM_DELAY_WRITE-1)*BITSIZE_item+OFFSET_addr+:BITSIZE_addr];
    assign Wsize = {{16-BITSIZE_Wsize{1'b0}}, queue_next[(MEM_DELAY_WRITE-1)*BITSIZE_item+OFFSET_Wsize+:BITSIZE_Wsize]};
    assign Wdata = queue_next[(MEM_DELAY_WRITE-1)*BITSIZE_item+OFFSET_Wdata+:BITSIZE_Wdata];
    assign _M_DataRdy[BITSIZE_ready*i+:BITSIZE_ready] = Wready | Rready;
    assign _M_Rdata_ram[BITSIZE_Rdata*i+:BITSIZE_Rdata] = Rdata;

    assign queue[BITSIZE_item-1:0] = {
      Mout_Wdata_ram[BITSIZE_Wdata*i+:BITSIZE_Wdata], 
      Mout_data_ram_size[BITSIZE_Wsize*i+:BITSIZE_Wsize],
      Mout_addr_ram[BITSIZE_addr*i+:BITSIZE_addr],
      Mout_we_ram[BITSIZE_we*i+:BITSIZE_we],
      Mout_oe_ram[BITSIZE_oe*i+:BITSIZE_oe]};

    if(MEM_DELAY_MAX > 1)
    begin : requests_queue
      if(MEM_PIPELINED)
      begin : pipelined_handler
        always@(*)
        begin
          queue_next = {queue[(MEM_DELAY_MAX-1)*BITSIZE_item-1:0], queue[BITSIZE_item-1:0]};
        end
        always@(posedge clock)
        begin
          if(1RESET_VALUE)
          begin
            queue[MEM_DELAY_MAX*BITSIZE_item-1:BITSIZE_item] <= 0;
          end
          else
          begin
            queue[MEM_DELAY_MAX*BITSIZE_item-1:BITSIZE_item] <= queue_next[MEM_DELAY_MAX*BITSIZE_item-1:BITSIZE_item];
          end
        end
      end
      else
      begin : sequential_handler
        reg tx_active, tx_active_next = 0;
        always@(*)
        begin
          tx_active_next = queue[OFFSET_oe+:BITSIZE_we] | queue[OFFSET_we+:BITSIZE_we];
          queue_next = {queue[(MEM_DELAY_MAX-1)*BITSIZE_item-1:0], queue[BITSIZE_item-1:0]} & {{(MEM_DELAY_MAX-2)*BITSIZE_item{1'b1}}, {BITSIZE_item*2{~tx_active}}};
        end
        always@(posedge clock)
        begin
          if(1RESET_VALUE)
          begin
            tx_active <= 0;
            queue[MEM_DELAY_MAX*BITSIZE_item-1:BITSIZE_item] <= 0;
          end
          else
          begin
            tx_active <= tx_active_next;
            queue[MEM_DELAY_MAX*BITSIZE_item-1:BITSIZE_item] <= queue_next[MEM_DELAY_MAX*BITSIZE_item-1:BITSIZE_item];
          end
        end
      end
    end
    else
    begin
      always@(*)
      begin
        queue_next = queue;
      end
    end

    if(MEM_DELAY_READ > 1)
    begin : read_channel
      always@(posedge clock)
      begin : read_channel
        automatic reg [BITSIZE_Rdata-1:0] data;
        Rready <= 0;
        Rdata <= 0;
        if(oe && base_addr <= Raddr)
        begin
          data = m_utils.read(Raddr);
          Rready <= 1;
          Rdata <= data;
          if(MEM_DUMP)
          begin
            $fwrite(dump_file, "%0d,r,%0X,%0d,%0X\\n", i, Raddr, BITSIZE_Rdata, data);
          end
        end
      end
    end
    else
    begin
      assign Rready = oe && base_addr <= Raddr;
      always@(negedge clock)
      begin : read_channel
        Rdata = 0;
        if(oe && base_addr <= Raddr)
        begin
          Rdata = m_utils.read(Raddr);
          if(MEM_DUMP)
          begin
            $fwrite(dump_file, "%0d,r,%0X,%0d,%0X\\n", i, Raddr, BITSIZE_Rdata, Rdata);
          end
        end
      end
    end

    if(MEM_DELAY_WRITE > 1)
    begin : write_channel
      always@(posedge clock)
      begin : write_channel
        Wready <= 0;
        if(we && base_addr <= Waddr)
        begin
          Wready <= 1;
          m_utils.write(Wsize, Wdata, Waddr);
          if(MEM_DUMP)
          begin
            $fwrite(dump_file, "%0d,w,%0X,%0d,%0X\\n", i, Waddr, Wsize, Wdata);
          end
        end
      end
    end
    else
    begin
      assign Wready = we && base_addr <= Waddr;
      always@(negedge clock)
      begin : write_channel
        if(we && base_addr <= Waddr)
        begin
          m_utils.write(Wsize, Wdata, Waddr);
          if(MEM_DUMP)
          begin
            $fwrite(dump_file, "%0d,w,%0X,%0d,%0X\\n", i, Waddr, Wsize, Wdata);
          end
        end
      end
    end

    always @(posedge clock)
    begin
      if (queue[OFFSET_oe+:BITSIZE_we] & queue[OFFSET_we+:BITSIZE_we])
      begin
        $display("ERROR - Mout_we_ram and Mout_oe_ram both enabled on channel %0d!", i);
        $finish;
      end
    end
  end
endgenerate

parameter MEM_DELAY_MAX= MEM_DELAY_READ > MEM_DELAY_WRITE ? MEM_DELAY_READ : MEM_DELAY_WRITE,
  BITSIZE_item=BITSIZE_dat_im+1+1;
reg cyc, stb, we, stall, ack;
reg [BITSIZE_addr_om-1:0] addr;
reg [BITSIZE_dat_om-1:0] Wdata;
reg [7:0] size;
reg [BITSIZE_dat_im-1:0] Rdata;

reg [MEM_DELAY_MAX*BITSIZE_item-1:0] queue, queue_next;

mem_utils #(BITSIZE_dat_im) m_utils();

initial
begin
  stall = 0;
  ack = 0;
  Rdata = 0;
  queue = 0;
  queue_next = 0;
end

assign cyc = cyc_om;
assign stb = stb_om;
assign we = we_om;
assign addr = addr_om;
assign Wdata = dat_om;
assign size = sel_om;
assign stall_im = stall; // & cyc
assign ack_im = ack; // & cyc
assign dat_im = Rdata; // & {BITSIZE_dat_im{cyc}};

always @(posedge clock)
begin
  automatic reg w_ack = queue_next[(MEM_DELAY_WRITE-1)*BITSIZE_item] & queue_next[(MEM_DELAY_WRITE-1)*BITSIZE_item+1];
  automatic reg r_ack = queue_next[(MEM_DELAY_READ-1)*BITSIZE_item] & !queue_next[(MEM_DELAY_READ-1)*BITSIZE_item+1];
  ack <= w_ack | r_ack;
  Rdata <= queue_next[(MEM_DELAY_READ-1)*BITSIZE_item+2 +:BITSIZE_dat_im];
  queue <= queue_next;
end

always @(*)
begin
  queue_next = {queue[(MEM_DELAY_MAX-1)*BITSIZE_item-1:0], {BITSIZE_dat_im{1'b0}}, 1'b0, 1'b0};
  if(cyc & stb)
  begin
    queue_next[1:0] = {we, 1'b1};
  end
end

always @(negedge clock)
begin
  if(queue_next[0])
  begin
    automatic ptr_t ptr_addr = addr;
    if(queue_next[1])
    begin
      m_utils.write_strobe(sel_om, Wdata, ptr_addr);
    end
    else
    begin
      queue_next[BITSIZE_item-1:2] <= m_utils.read(ptr_addr);
    end
  end
end

arg_utils #(BITSIZE_val_port) m_utils();
reg [BITSIZE_val_port-1:0] val, val_next;
assign val_port = val;

initial
begin
  val = 0;
  val_next = 0;
end

always @(posedge clock) 
begin
  val <= val_next;
  if(setup_port)
  begin
    val <= m_utils.getarg(index);
  end
end

always @(*)
begin
  val_next = val;
end

reg [bitsize-1:0] val, val_next;
reg we, we_next, rdy, start;
reg [BITSIZE_S_addr_ram-1:0] addr, addr_next;
reg [BITSIZE_Sout_Rdata_ram-1:0] data, data_next;
reg [BITSIZE_S_data_ram_size-1:0] data_size, data_size_next;

arg_utils #(bitsize) a_utils();

initial
begin
  val = 0;
  val_next = 0;
  we = 0;
  we_next = 0;
  addr = 0;
  addr_next = 0;
  data = 0;
  data_next = 0;
  data_size = 0;
  data_size_next = 0;
  start = 0;
end

assign S_oe_ram = 0;
assign S_we_ram = we_next;
assign S_addr_ram = addr_next;
assign S_Wdata_ram = data_next;
assign S_data_ram_size = data_size_next;
assign rdy = Sout_DataRdy;
assign start_port = start;

always @(posedge clock) 
begin
  automatic reg active_tx = (we_next) & !rdy;
  val <= val_next;
  we <= active_tx;
  addr <= addr_next & {BITSIZE_S_addr_ram{active_tx}};
  data <= data_next & {BITSIZE_Sout_Rdata_ram{active_tx}};
  data_size <= data_size_next & {BITSIZE_S_data_ram_size{active_tx}};
  start <= rdy & we_next;
  if(setup_port)
  begin
    val <= a_utils.getarg(index);
  end
end

always @(*)
begin
  val_next = val;
  we_next = we;
  addr_next = addr;
  data_next = data;
  data_size_next = data_size;
  if(i_start_port)
  begin
    we_next = 1;
    addr_next = tgt_addr;
    data_next[bitsize-1:0] = val;
    data_size_next = bitsize;
  end
end

reg oe, oe_next, rdy, done;
reg [BITSIZE_S_addr_ram-1:0] addr, addr_next;

arg_utils #(bitsize) a_utils();

initial
begin
  oe = 0;
  oe_next = 0;
  addr = 0;
  addr_next = 0;
  start = 0;
end

assign S_oe_ram = oe_next;
assign S_we_ram = 0;
assign S_addr_ram = addr_next;
assign S_Wdata_ram = 0;
assign S_data_ram_size = 0;
assign rdy = Sout_DataRdy;
assign start_port = i_start_port;
assign done_port = done;

always @(posedge clock) 
begin
  automatic reg active_tx = (oe_next) & !rdy;
  oe <= active_tx;
  addr <= addr_next & {BITSIZE_S_addr_ram{active_tx}};
  done <= rdy & oe_next;
  if(rdy & oe_next)
  begin
    a_utils.setarg(Sout_Rdata_ram, index);
  end
end

always @(*)
begin
  oe_next = oe;
  addr_next = addr;
  if(i_done_port)
  begin
    oe_next = 1;
    addr_next = tgt_addr;
  end
end

reg we, we_next, rdy, start;
reg [BITSIZE_S_addr_ram-1:0] addr, addr_next;
reg [BITSIZE_Sout_Rdata_ram-1:0] data, data_next;
reg [BITSIZE_S_data_ram_size-1:0] data_size, data_size_next;

initial
begin
  we = 0;
  we_next = 0;
  addr = 0;
  addr_next = 0;
  data = 0;
  data_next = 0;
  data_size = 0;
  data_size_next = 0;
  start = 0;
end

assign S_oe_ram = 0;
assign S_we_ram = we_next;
assign S_addr_ram = addr_next;
assign S_Wdata_ram = data_next;
assign S_data_ram_size = data_size_next;
assign rdy = Sout_DataRdy;
assign start_port = start;

always @(posedge clock) 
begin
  automatic reg active_tx = (we_next) & !rdy;
  we <= active_tx;
  addr <= addr_next & {BITSIZE_S_addr_ram{active_tx}};
  data <= data_next & {BITSIZE_Sout_Rdata_ram{active_tx}};
  data_size <= data_size_next & {BITSIZE_S_data_ram_size{active_tx}};
  start <= rdy & we_next;
end

always @(*)
begin
  we_next = we;
  addr_next = addr;
  data_next = data;
  data_size_next = data_size;
  if(i_start_port)
  begin
    we_next = 1;
    addr_next = tgt_addr;
    data_next = 1;
    data_size_next = 1;
  end
end

parameter bytesize=((bitsize>>3)<<3) == bitsize ? (bitsize>>3) : ((bitsize>>3) + 1);
reg [bitsize-1:0] val, val_next;
reg cyc, cyc_next, stb, stb_next, we, we_next, stall, ack, start;
reg [BITSIZE_addr_is-1:0] addr, addr_next;
reg [BITSIZE_dat_is-1:0] data, data_next;
reg [BITSIZE_sel_is-1:0] sel, sel_next;

arg_utils #(bitsize) a_utils();

initial
begin
  val = 0;
  val_next = 0;
  cyc = 0;
  cyc_next = 0;
  stb = 0;
  stb_next = 0;
  we = 0;
  we_next = 0;
  addr = 0;
  addr_next = 0;
  data = 0;
  data_next = 0;
  sel = 0;
  sel_next = 0;
  start = 0;
end

assign cyc_is = cyc_next;
assign stb_is = stb_next;
assign we_is = we_next;
assign addr_is = addr_next;
assign dat_is = data_next;
assign sel_is = sel_next;
assign stall = !ack_os; // TODO: this should be stall_os
assign ack = ack_os;
assign start_port = start;

always @(posedge clock) 
begin
  automatic reg active_tx = cyc_next & !ack;
  automatic reg wait_tx = active_tx & stall;
  val <= val_next;
  cyc <= active_tx;
  stb <= stb_next & wait_tx;
  we <= we_next & wait_tx;
  addr <= addr_next & {BITSIZE_addr_is{wait_tx}};
  data <= data_next & {BITSIZE_dat_is{wait_tx}};
  sel <= sel_next & {BITSIZE_sel_is{wait_tx}};
  start <= ack & cyc_next;
  if(setup_port)
  begin
    val <= a_utils.getarg(index);
  end
end

always @(*)
begin
  val_next = val;
  cyc_next = cyc;
  stb_next = stb;
  we_next = we;
  addr_next = addr;
  data_next = data;
  sel_next = sel;
  if(i_start_port)
  begin
    cyc_next = 1;
    stb_next = 1;
    we_next = 1;
    addr_next = tgt_addr;
    data_next[bitsize-1:0] = val;
    sel_next = {bytesize{1'b1}};
  end
end

parameter bytesize=((bitsize>>3)<<3) == bitsize ? (bitsize>>3) : ((bitsize>>3) + 1);
reg cyc, cyc_next, stb, stb_next, stall, ack, done;
reg [BITSIZE_addr_is-1:0] addr, addr_next;

arg_utils #(bitsize) a_utils();

initial
begin
  cyc = 0;
  cyc_next = 0;
  stb = 0;
  stb_next = 0;
  addr = 0;
  addr_next = 0;
  done = 0;
end

assign cyc_is = cyc_next;
assign stb_is = stb_next;
assign we_is = 0;
assign addr_is = addr_next;
assign dat_is = 0;
assign sel_is = 0;
assign stall = !ack_os; // TODO: this should be stall_os
assign ack = ack_os;
assign start_port = i_start_port;
assign done_port = done;

always @(posedge clock) 
begin
  automatic reg active_tx = cyc_next & !ack;
  automatic reg wait_tx = active_tx & stall;
  cyc <= active_tx;
  stb <= stb_next & wait_tx;
  addr <= addr_next & {BITSIZE_addr_is{wait_tx}};
  done <= ack & cyc_next;
  if(ack & cyc_next)
  begin
    a_utils.setarg(dat_os, index);
  end
end

always @(*)
begin
  cyc_next = cyc;
  stb_next = stb;
  addr_next = addr;
  if(i_done_port)
  begin
    cyc_next = 1;
    stb_next = 1;
    addr_next = tgt_addr;
  end
end

parameter bytesize=1;
reg cyc, cyc_next, stb, stb_next, we, we_next, stall, ack, start;
reg [BITSIZE_addr_is-1:0] addr, addr_next;
reg [BITSIZE_dat_is-1:0] data, data_next;
reg [BITSIZE_sel_is-1:0] sel, sel_next;

initial
begin
  cyc = 0;
  cyc_next = 0;
  stb = 0;
  stb_next = 0;
  we = 0;
  we_next = 0;
  addr = 0;
  addr_next = 0;
  data = 0;
  data_next = 0;
