NP_functionality.VERILOG_PROVIDED:
function automatic integer log2;
  input integer value;
  `ifdef _SIM_HAVE_CLOG2
    log2 = $clog2(value);
  `else
    automatic integer temp_value = value-1;
    for (log2=0; temp_value > 0; log2=log2+1)
      temp_value = temp_value >> 1;
  `endif
endfunction

import "DPI-C" function void m_getarg(inout logic [127:0] data, input int unsigned index);
import "DPI-C" function void m_setarg(input logic [127:0] data, input int unsigned index);
import "DPI-C" function int unsigned m_getptrargsize(input int unsigned index);

function automatic [BITSIZE_data-1:0] getarg(input int unsigned index);
  automatic reg [127:0] data = 0;
  m_getarg(data, index);
  return data[BITSIZE_data-1:0];
endfunction

function automatic ptr_t getptrarg(input int unsigned index);
  automatic reg [127:0] data = 0;
  m_getarg(data, index);
  return data[$bits(ptr_t)-1:0];
endfunction

function automatic int unsigned getptrargsize(input int unsigned index);
  return m_getptrargsize(index);
endfunction

function automatic void setarg(input logic [BITSIZE_data-1:0] data, input int unsigned index);
  automatic reg [127:0] _data = 0;
  _data[BITSIZE_data-1:0] = data;
  m_setarg(_data, index);
endfunction

function automatic integer log2;
  input integer value;
  `ifdef _SIM_HAVE_CLOG2
    log2 = $clog2(value);
  `else
    automatic integer temp_value = value-1;
    for (log2=0; temp_value > 0; log2=log2+1)
      temp_value = temp_value >> 1;
  `endif
endfunction

parameter BITSIZE_mem_bus=1<<(log2(BITSIZE_data) > 3 ? log2(BITSIZE_data) : 3),
  BITSIZE_strobe=BITSIZE_mem_bus/8;

import "DPI-C" function void m_read8    (output logic    [7:0] data, input ptr_t addr);
import "DPI-C" function void m_read16   (output logic   [15:0] data, input ptr_t addr);
import "DPI-C" function void m_read32   (output logic   [31:0] data, input ptr_t addr);
import "DPI-C" function void m_read64   (output logic   [63:0] data, input ptr_t addr);
import "DPI-C" function void m_read128  (output logic  [127:0] data, input ptr_t addr);
import "DPI-C" function void m_read256  (output logic  [255:0] data, input ptr_t addr);
import "DPI-C" function void m_read512  (output logic  [511:0] data, input ptr_t addr);
import "DPI-C" function void m_read1024 (output logic [1023:0] data, input ptr_t addr);
import "DPI-C" function void m_read2048 (output logic [2047:0] data, input ptr_t addr);
import "DPI-C" function void m_read4096 (output logic [4095:0] data, input ptr_t addr);

import "DPI-C" function void m_write8    (input shortint unsigned size, input logic    [7:0] data, input ptr_t addr);
import "DPI-C" function void m_write16   (input shortint unsigned size, input logic   [15:0] data, input ptr_t addr);
import "DPI-C" function void m_write32   (input shortint unsigned size, input logic   [31:0] data, input ptr_t addr);
import "DPI-C" function void m_write64   (input shortint unsigned size, input logic   [63:0] data, input ptr_t addr);
import "DPI-C" function void m_write128  (input shortint unsigned size, input logic  [127:0] data, input ptr_t addr);
import "DPI-C" function void m_write256  (input shortint unsigned size, input logic  [255:0] data, input ptr_t addr);
import "DPI-C" function void m_write512  (input shortint unsigned size, input logic  [511:0] data, input ptr_t addr);
import "DPI-C" function void m_write1024 (input shortint unsigned size, input logic [1023:0] data, input ptr_t addr);
import "DPI-C" function void m_write2048 (input shortint unsigned size, input logic [2047:0] data, input ptr_t addr);
import "DPI-C" function void m_write4096 (input shortint unsigned size, input logic [4095:0] data, input ptr_t addr);

function automatic [BITSIZE_data-1:0] read(input ptr_t addr);
  automatic reg [4095:0] wtmp = 0;

  if(BITSIZE_mem_bus == 8)
    m_read8(wtmp[7:0], addr);
  else if(BITSIZE_mem_bus == 16)
    m_read16(wtmp[15:0], addr);
  else if(BITSIZE_mem_bus == 32)
    m_read32(wtmp[31:0], addr);
  else if(BITSIZE_mem_bus == 64)
    m_read64(wtmp[63:0], addr);
  else if(BITSIZE_mem_bus == 128)
    m_read128(wtmp[127:0], addr);
  else if(BITSIZE_mem_bus == 256)
    m_read256(wtmp[255:0], addr);
  else if(BITSIZE_mem_bus == 512)
    m_read512(wtmp[511:0], addr);
  else if(BITSIZE_mem_bus == 1024)
    m_read1024(wtmp[1023:0], addr);
  else if(BITSIZE_mem_bus == 2048)
    m_read2048(wtmp[2047:0], addr);
  else if(BITSIZE_mem_bus == 4096)
    m_read4096(wtmp[4095:0], addr);
  else
  begin
    $display("Unsupported memory bus size: %0d", BITSIZE_mem_bus);
    $finish;
  end
  return wtmp[BITSIZE_data-1:0];
endfunction

function automatic void write(
  input shortint size,
  input logic [BITSIZE_data-1:0] data,
  input ptr_t addr);
  automatic reg [BITSIZE_mem_bus-1:0] tmp = 0;

  tmp = data[BITSIZE_data-1:0];
  if(BITSIZE_mem_bus == 8)
    m_write8(size, tmp, addr);
  else if(BITSIZE_mem_bus == 16)
    m_write16(size, tmp, addr);
  else if(BITSIZE_mem_bus == 32)
    m_write32(size, tmp, addr);
  else if(BITSIZE_mem_bus == 64)
    m_write64(size, tmp, addr);
  else if(BITSIZE_mem_bus == 128)
    m_write128(size, tmp, addr);
  else if(BITSIZE_mem_bus == 256)
    m_write256(size, tmp, addr);
  else if(BITSIZE_mem_bus == 512)
    m_write512(size, tmp, addr);
  else if(BITSIZE_mem_bus == 1024)
    m_write1024(size, tmp, addr);
  else if(BITSIZE_mem_bus == 2048)
    m_write2048(size, tmp, addr);
  else if(BITSIZE_mem_bus == 4096)
    m_write4096(size, tmp, addr);
  else
  begin
    $display("Unsupported memory bus size: %0d", BITSIZE_mem_bus);
    $finish;
  end
endfunction

function automatic void write_strobe(
  input logic [BITSIZE_strobe-1:0] strobe,
  input logic [BITSIZE_data-1:0] data,
  input ptr_t addr);
  automatic shortint unsigned size = 0;

  while(strobe != 0 && !strobe[0])
  begin
    addr = addr + 1;
    strobe = strobe >> 1;
  end
  while(strobe[0])
  begin
    size = size + 8;
    strobe = strobe >> 1;
  end
  if(strobe != 0)
  begin
    $display("Scattered strobe write operations not supported");
    $finish;
  end

  write(size, data, addr);
endfunction

import "DPI-C" function void m_init();
import "DPI-C" function int unsigned m_next(input int unsigned state);
import "DPI-C" function int m_fini();

enum bit[2:0] {
  SIM_READY = 1,
  COSIM_INIT,
  SIM_SETUP,
  SIM_RUNNING,
  SIM_END,
  COSIM_END,
  COSIM_ABORT
} mdpi_state;
reg [2:0] state, state_next, state_succ, state_succ_next;

reg rst, rst_next, start, start_next;
time over_time;

initial
begin
  // Open file results will be written
  automatic integer res_file;
  res_file = $fopen(RESFILE, "w");
  if (res_file == 0)
  begin
    $display("ERROR - Error opening the res_file");
    $finish;// Terminate
  end
  $fwrite(res_file, "");
  $fclose(res_file);

  state = SIM_READY;
  state_next = SIM_READY;
  state_succ = SIM_READY;
  state_succ_next = SIM_READY;
  rst = !RESET_ACTIVE;
  rst_next = !RESET_ACTIVE;
  start = 1'b0;
  start_next = 1'b0;
  over_time = 0;

  $display("Results file: %s", RESFILE);
  $display("Reset active: %0s", rst ? "LOW" : "HIGH");

  m_init();
end

assign reset = rst;
assign start_port = start;

always @(posedge clock)
begin
  state <= state_next;
  state_succ <= state_succ_next;
  rst <= rst_next;
  start <= start_next;
  case(state_next)
  SIM_READY:
    begin
      automatic integer unsigned next_state = m_next(int'(COSIM_INIT));
      `ifndef NDEBUG
      $display("Sim: next state: %0d", next_state);
      `endif
      state_succ <= next_state[2:0];
    end
  SIM_SETUP:
    begin
      automatic time start_time = $time + CLOCK_PERIOD;
      automatic time start_cycle = $rtoi(start_time/CLOCK_PERIOD);
      automatic integer res_file;
      res_file = $fopen(RESFILE, "a");
      $fwrite(res_file, "%0d|", start_time);
      $fclose(res_file);
      `ifndef NDEBUG
      $display("Sim: Argument setup\\nSim: Simulation started at cycle %0d", start_cycle);
      `endif
      over_time <= start_cycle + MAX_SIM_CYCLES;
    end
  SIM_RUNNING:
    begin
      automatic time curr_cycle = $rtoi($time/CLOCK_PERIOD);
      if(curr_cycle >= over_time)
      begin
        automatic integer res_file;
        res_file = $fopen(RESFILE, "a");
        $fwrite(res_file, "X");
        $fclose(res_file);
        $display("Sim: Simulation exceeds %0d cycles", MAX_SIM_CYCLES);
        $finish;
      end
    end
  SIM_END:
    begin
      automatic time curr_time = $time;
      automatic time curr_cycle = $rtoi(curr_time/CLOCK_PERIOD);
      automatic integer res_file;
      res_file = $fopen(RESFILE, "a");
      $fwrite(res_file, "%0d,", curr_time);
      $fclose(res_file);
      `ifndef NDEBUG
      $display("Sim: DUT port writeback\\nSim: Simulation ended at cycle %0d", curr_cycle);
      `endif
    end
  COSIM_END:
    begin
      automatic integer r = m_fini();
      automatic integer res_file;
      res_file = $fopen(RESFILE, "a");
      if(r[2:0] == int'(COSIM_ABORT))
      begin
        $fwrite(res_file, "\\nA\\n");
        $display("Sim: Testbench aborted");
      end
      else
      begin
        $fwrite(res_file, "\\n%0d\\n", r[15:8]);
        $display("Sim: Testbench returned: %0d", r[15:8]);
      end
      $fclose(res_file);
      $finish;
    end
  default:
    begin
    end
  endcase
end

always @(*)
begin
  rst_next = rst;
  start_next = start;
  state_next = state;
  state_succ_next = state_succ;
  case(state)
  SIM_READY:
    begin
      state_next = state_succ;
      if(state_next == int'(SIM_SETUP))
      begin
        rst_next = RESET_ACTIVE;
      end
    end
  SIM_SETUP:
    begin
      state_next = SIM_RUNNING;
      rst_next = !RESET_ACTIVE;
      start_next = 1'b1;
    end
  SIM_RUNNING:
    begin
      start_next = 1'b0;
      if(done_port)
      begin
        state_next = SIM_END;
      end
    end
  SIM_END:
    begin
      // A clock cycle must pass to allow interface modules 
      // finalization operations before m_next(SIM_END) is called
      state_next = SIM_READY;
    end
  COSIM_END:
    begin
    end
  default:
    begin
      state_next = SIM_READY;
    end
  endcase
end

parameter MEM_DELAY_MAX= MEM_DELAY_READ > MEM_DELAY_WRITE ? MEM_DELAY_READ : MEM_DELAY_WRITE,
  CHANNELS_NUMBER= BITSIZE_Mout_oe_ram,
  elm_BITSIZE_Mout_addr_ram= BITSIZE_Mout_addr_ram / CHANNELS_NUMBER,
  elm_BITSIZE_Mout_data_ram_size= BITSIZE_Mout_data_ram_size / CHANNELS_NUMBER,
  elm_BITSIZE_Mout_Wdata_ram= BITSIZE_Mout_Wdata_ram / CHANNELS_NUMBER,
  elm_BITSIZE_M_Rdata_ram= BITSIZE_M_Rdata_ram / CHANNELS_NUMBER;

reg [BITSIZE_Mout_Wdata_ram-1:0] mask;
reg [BITSIZE_M_Rdata_ram-1:0] M_Rdata_ram_temp;
reg [CHANNELS_NUMBER-1:0] M_DataRdy_temp;

reg [CHANNELS_NUMBER*MEM_DELAY_READ-1:0] Mout_oe_ram_queue_next;
reg [CHANNELS_NUMBER*MEM_DELAY_READ-1:0] Mout_oe_ram_queue_curr;
reg [CHANNELS_NUMBER*MEM_DELAY_WRITE-1:0] Mout_we_ram_queue_next;
reg [CHANNELS_NUMBER*MEM_DELAY_WRITE-1:0] Mout_we_ram_queue_curr;
reg [BITSIZE_Mout_addr_ram*MEM_DELAY_MAX-1:0] Mout_addr_ram_queue_next;
reg [BITSIZE_Mout_addr_ram*MEM_DELAY_MAX-1:0] Mout_addr_ram_queue_curr;
reg [BITSIZE_Mout_data_ram_size*MEM_DELAY_MAX-1:0] Mout_data_ram_size_queue_next;
reg [BITSIZE_Mout_data_ram_size*MEM_DELAY_MAX-1:0] Mout_data_ram_size_queue_curr;
reg [BITSIZE_Mout_Wdata_ram*MEM_DELAY_WRITE-1:0] Mout_Wdata_ram_queue_next;
reg [BITSIZE_Mout_Wdata_ram*MEM_DELAY_WRITE-1:0] Mout_Wdata_ram_queue_curr;

genvar i;

mem_utils #(elm_BITSIZE_M_Rdata_ram) m_utils();

initial
begin
  Mout_oe_ram_queue_next = 0;
  Mout_oe_ram_queue_curr = 0;
  Mout_we_ram_queue_next = 0;
  Mout_we_ram_queue_curr = 0;
  Mout_addr_ram_queue_next = 0;
  Mout_addr_ram_queue_curr = 0;
  Mout_Wdata_ram_queue_next = 0;
  Mout_Wdata_ram_queue_curr = 0;
  Mout_data_ram_size_queue_next = 0;
  Mout_data_ram_size_queue_curr = 0;
  M_DataRdy_temp = 0;
  M_Rdata_ram_temp = 0;
end

// Memory queue update
generate
  if(MEM_DELAY_READ != 1)
  begin
    always @(posedge clock)
    begin
      Mout_oe_ram_queue_next[CHANNELS_NUMBER*MEM_DELAY_READ-1:1] <= 
        Mout_oe_ram_queue_curr[CHANNELS_NUMBER*(MEM_DELAY_READ-1)-1:0];
    end
    always @(*)
    begin
      Mout_oe_ram_queue_curr[CHANNELS_NUMBER*MEM_DELAY_READ-1:1] = 
        Mout_oe_ram_queue_next[CHANNELS_NUMBER*MEM_DELAY_READ-1:1];
      Mout_oe_ram_queue_curr[CHANNELS_NUMBER-1:0] = Mout_oe_ram;
    end
  end
  else
  begin
    always @(*)
    begin
      Mout_oe_ram_queue_curr[CHANNELS_NUMBER-1:0] = Mout_oe_ram;
    end
  end
endgenerate

generate
  if(MEM_DELAY_WRITE != 1)
  begin
    always @(posedge clock)
    begin
      Mout_we_ram_queue_next[CHANNELS_NUMBER*MEM_DELAY_WRITE-1:CHANNELS_NUMBER] <= 
        Mout_we_ram_queue_curr[CHANNELS_NUMBER*(MEM_DELAY_WRITE-1)-1:0];
      Mout_Wdata_ram_queue_next[MEM_DELAY_WRITE*BITSIZE_Mout_Wdata_ram-1:BITSIZE_Mout_Wdata_ram] <= 
        Mout_Wdata_ram_queue_curr[CHANNELS_NUMBER*(MEM_DELAY_WRITE-1)*BITSIZE_Mout_Wdata_ram-1:0];
    end
    always @(*)
    begin
      Mout_we_ram_queue_curr[CHANNELS_NUMBER*MEM_DELAY_WRITE-1:CHANNELS_NUMBER] = 
        Mout_we_ram_queue_next[CHANNELS_NUMBER*MEM_DELAY_WRITE-1:CHANNELS_NUMBER];
      Mout_we_ram_queue_curr[CHANNELS_NUMBER-1:0] = Mout_we_ram;
      Mout_Wdata_ram_queue_curr[MEM_DELAY_WRITE*BITSIZE_Mout_Wdata_ram-1:BITSIZE_Mout_Wdata_ram] = 
        Mout_Wdata_ram_queue_next[MEM_DELAY_WRITE*BITSIZE_Mout_Wdata_ram-1:BITSIZE_Mout_Wdata_ram];
      Mout_Wdata_ram_queue_curr[BITSIZE_Mout_Wdata_ram-1:0] = Mout_Wdata_ram;
    end
  end
  else
  begin
    always @(*)
    begin
      Mout_we_ram_queue_curr[CHANNELS_NUMBER-1:0] = Mout_we_ram;
      Mout_Wdata_ram_queue_curr[BITSIZE_Mout_Wdata_ram-1:0] = Mout_Wdata_ram;
    end
  end
endgenerate

generate
  if(MEM_DELAY_MAX != 1)
  begin
    always @(posedge clock)
    begin
      Mout_addr_ram_queue_next[MEM_DELAY_MAX*BITSIZE_Mout_addr_ram-1:BITSIZE_Mout_addr_ram] <= 
        Mout_addr_ram_queue_curr[(MEM_DELAY_MAX-1)*BITSIZE_Mout_addr_ram-1:0];
      Mout_data_ram_size_queue_next[MEM_DELAY_MAX*BITSIZE_Mout_data_ram_size-1:BITSIZE_Mout_data_ram_size] <= 
        Mout_data_ram_size_queue_curr[(MEM_DELAY_MAX-1)*BITSIZE_Mout_data_ram_size-1:0];
    end
    always @(*)
    begin
      Mout_addr_ram_queue_curr[MEM_DELAY_MAX*BITSIZE_Mout_addr_ram-1:BITSIZE_Mout_addr_ram] = 
        Mout_addr_ram_queue_next[MEM_DELAY_MAX*BITSIZE_Mout_addr_ram-1:BITSIZE_Mout_addr_ram];
      Mout_addr_ram_queue_curr[BITSIZE_Mout_addr_ram-1:0] = Mout_addr_ram;
      Mout_data_ram_size_queue_curr[MEM_DELAY_MAX*BITSIZE_Mout_data_ram_size-1:BITSIZE_Mout_data_ram_size] = 
        Mout_data_ram_size_queue_next[MEM_DELAY_MAX*BITSIZE_Mout_data_ram_size-1:BITSIZE_Mout_data_ram_size];
      Mout_data_ram_size_queue_curr[BITSIZE_Mout_data_ram_size-1:0] = Mout_data_ram_size;
    end
  end
  else
  begin
    always @(*)
    begin
      Mout_addr_ram_queue_curr[BITSIZE_Mout_addr_ram-1:0] = Mout_addr_ram;
      Mout_data_ram_size_queue_curr[BITSIZE_Mout_data_ram_size-1:0] = Mout_data_ram_size;
    end
  end
endgenerate

// OffChip Memory write
generate
  for(i = 0; i < CHANNELS_NUMBER; i = i + 1)
  begin : memory_port
    always @(negedge clock)
    begin : write_channel
      automatic shortint unsigned size =
        Mout_data_ram_size_queue_curr[(elm_BITSIZE_Mout_data_ram_size*(i+1))-1+(MEM_DELAY_WRITE-1)*BITSIZE_Mout_data_ram_size:
                                      elm_BITSIZE_Mout_data_ram_size*i+(MEM_DELAY_WRITE-1)*BITSIZE_Mout_data_ram_size];
      automatic ptr_t addr = Mout_addr_ram_queue_curr[(elm_BITSIZE_Mout_addr_ram*(i+1))-1+(MEM_DELAY_WRITE-1)*BITSIZE_Mout_addr_ram:
                                                      elm_BITSIZE_Mout_addr_ram*i+(MEM_DELAY_WRITE-1)*BITSIZE_Mout_addr_ram];
      automatic reg [elm_BITSIZE_Mout_Wdata_ram-1:0] data =
        Mout_Wdata_ram_queue_curr[(elm_BITSIZE_Mout_Wdata_ram*(i+1))-1+(MEM_DELAY_WRITE-1)*BITSIZE_Mout_Wdata_ram:
                                   elm_BITSIZE_Mout_Wdata_ram*i+(MEM_DELAY_WRITE-1)*BITSIZE_Mout_Wdata_ram];
      if (Mout_we_ram_queue_curr[CHANNELS_NUMBER*(MEM_DELAY_WRITE-1)+i] === 1'b1 
          && base_addr <= Mout_addr_ram_queue_curr[(elm_BITSIZE_Mout_addr_ram*(i+1))-1+(MEM_DELAY_WRITE-1)*BITSIZE_Mout_addr_ram:
                                                   elm_BITSIZE_Mout_addr_ram*i+(MEM_DELAY_WRITE-1)*BITSIZE_Mout_addr_ram])
      begin
        m_utils.write(size, data, addr);
      end
    end
    always @(*)
    begin : read_channel
      automatic ptr_t addr = Mout_addr_ram_queue_curr[(elm_BITSIZE_Mout_addr_ram*(i+1))-1+(MEM_DELAY_READ-1)*BITSIZE_Mout_addr_ram:
                                                      elm_BITSIZE_Mout_addr_ram*i+(MEM_DELAY_READ-1)*BITSIZE_Mout_addr_ram];
      automatic reg [elm_BITSIZE_M_Rdata_ram-1:0] data = 0;
      M_Rdata_ram_temp[(elm_BITSIZE_M_Rdata_ram*(i+1))-1:elm_BITSIZE_M_Rdata_ram*i] = 0;
      if(base_addr <= Mout_addr_ram_queue_curr[(elm_BITSIZE_Mout_addr_ram*(i+1))-1+(MEM_DELAY_READ-1)*BITSIZE_Mout_addr_ram:
                                               elm_BITSIZE_Mout_addr_ram*i+(MEM_DELAY_READ-1)*BITSIZE_Mout_addr_ram])
      begin
        M_Rdata_ram_temp[(elm_BITSIZE_M_Rdata_ram*(i+1))-1:elm_BITSIZE_M_Rdata_ram*i] = m_utils.read(addr);
      end
    end
    always @(*)
    begin
      M_DataRdy_temp[i] = 0;
      if(Mout_we_ram_queue_curr[CHANNELS_NUMBER*(MEM_DELAY_WRITE-1) + i] === 1'b1)
      begin
        M_DataRdy_temp[i] = 
          base_addr <= Mout_addr_ram_queue_curr[(elm_BITSIZE_Mout_addr_ram*(i+1))-1+(MEM_DELAY_WRITE-1)*BITSIZE_Mout_addr_ram:
                                                elm_BITSIZE_Mout_addr_ram*i+(MEM_DELAY_WRITE-1)*BITSIZE_Mout_addr_ram];
      end
      else if(Mout_oe_ram_queue_curr[CHANNELS_NUMBER*(MEM_DELAY_READ-1) + i] === 1'b1)
      begin
        M_DataRdy_temp[i] = 
          base_addr <= Mout_addr_ram_queue_curr[(elm_BITSIZE_Mout_addr_ram*(i+1))-1+(MEM_DELAY_READ-1)*BITSIZE_Mout_addr_ram:
                                                elm_BITSIZE_Mout_addr_ram*i+(MEM_DELAY_READ-1)*BITSIZE_Mout_addr_ram];
      end
    end
  end
endgenerate
assign M_Rdata_ram = M_Rdata_ram_temp;
assign M_DataRdy = M_DataRdy_temp;

always @(posedge clock)
begin
  if ((Mout_we_ram & Mout_oe_ram) != 0)
  begin
    $display("ERROR - Mout_we_ram and Mout_oe_ram both enabled");
    $finish;
  end
end

parameter MEM_DELAY_MAX= MEM_DELAY_READ > MEM_DELAY_WRITE ? MEM_DELAY_READ : MEM_DELAY_WRITE,
  BITSIZE_item=BITSIZE_dat_im+1+1;
reg cyc, stb, we, stall, ack;
reg [BITSIZE_addr_om-1:0] addr;
reg [BITSIZE_dat_om-1:0] Wdata;
reg [7:0] size;
reg [BITSIZE_dat_im-1:0] Rdata;

reg [MEM_DELAY_MAX*BITSIZE_item-1:0] queue, queue_next;

mem_utils #(BITSIZE_dat_im) m_utils();

initial
begin
  stall = 0;
  ack = 0;
  Rdata = 0;
  queue = 0;
  queue_next = 0;
end

assign cyc = cyc_om;
assign stb = stb_om;
assign we = we_om;
assign addr = addr_om;
assign Wdata = dat_om;
assign size = sel_om;
assign stall_im = stall; // & cyc
assign ack_im = ack; // & cyc
assign dat_im = Rdata; // & {BITSIZE_dat_im{cyc}};

always @(posedge clock)
begin
  automatic reg w_ack = queue_next[(MEM_DELAY_WRITE-1)*BITSIZE_item] & queue_next[(MEM_DELAY_WRITE-1)*BITSIZE_item+1];
  automatic reg r_ack = queue_next[(MEM_DELAY_READ-1)*BITSIZE_item] & !queue_next[(MEM_DELAY_READ-1)*BITSIZE_item+1];
  ack <= w_ack | r_ack;
  Rdata <= queue_next[(MEM_DELAY_READ-1)*BITSIZE_item+2 +:BITSIZE_dat_im];
  queue <= queue_next;
end

always @(*)
begin
  queue_next = {queue[(MEM_DELAY_MAX-1)*BITSIZE_item-1:0], {BITSIZE_dat_im{1'b0}}, 1'b0, 1'b0};
  if(cyc & stb)
  begin
    queue_next[1:0] = {we, 1'b1};
  end
end

always @(negedge clock)
begin
  if(queue_next[0])
  begin
    automatic ptr_t ptr_addr = addr;
    if(queue_next[1])
    begin
      m_utils.write_strobe(sel_om, Wdata, ptr_addr);
    end
    else
    begin
      queue_next[BITSIZE_item-1:2] <= m_utils.read(ptr_addr);
    end
  end
end

arg_utils #(BITSIZE_val_port) m_utils();
reg [BITSIZE_val_port-1:0] val, val_next;
assign val_port = val;

initial
begin
  val = 0;
  val_next = 0;
end

always @(posedge clock) 
begin
  val <= val_next;
  if(1RESET_VALUE)
  begin
    val <= m_utils.getarg(index);
  end
end

always @(*)
begin
  val_next = val;
end

reg [bitsize-1:0] val, val_next;
reg oe, oe_next, we, we_next, rdy, start;
reg [BITSIZE_S_addr_ram-1:0] addr, addr_next;
reg [BITSIZE_S_data_ram-1:0] data, data_next;
reg [BITSIZE_S_data_ram_size-1:0] data_size, data_size_next;

arg_utils #(bitsize) a_utils();

initial
begin
  val = 0;
  val_next = 0;
  oe = 0;
  oe_next = 0;
  we = 0;
  we_next = 0;
  addr = 0;
  addr_next = 0;
  data = 0;
  data_next = 0;
  data_size = 0;
  data_size_next = 0;
  start = 0;
end

assign S_oe_ram = oe_next;
assign S_we_ram = we_next;
assign S_addr_ram = addr_next;
assign S_Wdata_ram = data_next;
assign S_data_ram_size = data_size_next;
assign rdy = Sout_DataRdy;
assign start_port = start;

always @(posedge clock) 
begin
  automatic reg active_tx = (oe_next) & !rdy;
  val <= val_next;
  oe <= active_tx;
  we <= we_next & active_tx;
  addr <= addr_next & {BITSIZE_S_addr_ram{active_tx}};
  data <= data_next & {BITSIZE_S_data_ram{active_tx}};
  data_size <= data_size_next & {BITSIZE_S_data_ram_size{active_tx}};
  start <= rdy & oe_next;
  if(1RESET_VALUE)
  begin
    val <= a_utils.getarg(index);
  end
end

always @(*)
begin
  val_next = val;
  oe_next = oe;
  we_next = we;
  addr_next = addr;
  data_next = data;
  data_size_next = data_size;
  if(i_start_port)
  begin
    oe_next = 1;
    we_next = 1;
    addr_next = tgt_addr;
    data_next[bitsize-1:0] = val;
    data_size_next = bitsize;
  end
end

