  cyc = 0;
  cyc_next = 0;
  stb = 0;
  stb_next = 0;
  we = 0;
  we_next = 0;
  addr = 0;
  addr_next = 0;
  data = 0;
  data_next = 0;
  sel = 0;
  sel_next = 0;
  start = 0;
end

