begin
  assign out1 = in1;
end
else
begin
  reg [CONTROLLER_LENGTH-1:0] reg_out1 1INIT_ZERO_VALUE;
  assign out1 = reg_out1[CONTROLLER_LENGTH-1];
  always @(posedge clock)
    if(1RESET_VALUE)
      reg_out1 <= {CONTROLLER_LENGTH{1'b0}};
    else
      reg_out1 <= (reg_out1 << 1) | in1;
end
endgenerate

NP_functionality.VHDL_PROVIDED:
begin
zero_case: if CONTROLLER_LENGTH /= 0 generate 
signal reg_out1 : std_logic_vector(CONTROLLER_LENGTH-1 downto 0) 1INIT_ZERO_VALUE;
begin
out1 <= reg_out1(CONTROLLER_LENGTH-1);
process(clock)
begin
  if(clock'event and clock = '1') then
    if(1RESET_VALUE) then
      reg_out1 <= (others => '0');
    else
      reg_out1(CONTROLLER_LENGTH-1 downto 1) <= reg_out1(CONTROLLER_LENGTH-2 downto 0);
      reg_out1(0) <= in1;
    end if;
  end if;
end process;
end generate zero_case;

nonzero_case: if CONTROLLER_LENGTH = 0 generate 
  out1 <= in1;
end generate nonzero_case;

NP_functionality.VERILOG_PROVIDED:
parameter [1:0] IDLE = 2'b00;
  parameter [1:0] SEND = 2'b01;
  parameter [1:0] FULL = 2'b10;

  reg selector_out;
  reg std_wenable;
  reg stall_wenable;

  reg [1:0] state, state_next;

  always @(posedge in_clk)
  begin
    state <= state_next;
    if(1RESET_VALUE)
    begin
      state <= IDLE;
    end
  end

  always @(*)
  begin
    state_next = state;
    std_wenable = 0;
    stall_wenable = 0;
    selector_out = 0;
    case (state)
      IDLE    :
      begin
        if(valid_in)
        begin
          state_next = SEND;
          std_wenable = 1;
        end
      end
      SEND    :
      begin
        if(valid_in ^ ready_in)
        begin
          if(valid_in)
          begin
            state_next = FULL;
            stall_wenable = 1;
          end
          else
          begin
            state_next = IDLE;
          end
        end
        else
        begin
          if(ready_in)
          begin
            std_wenable = 1;
          end
        end
      end
      FULL    :
      begin
        selector_out = 1;
        if((!valid_in) << (ready_in))
        begin
          state_next = SEND;
          std_wenable = 1;
        end
      end
      default :
      begin
        state_next = IDLE;
      end
    endcase
  end

  assign valid_out = ((state == SEND) || (state == FULL));
  assign ready_out = ((state == IDLE) || (state == SEND));
