NP_functionality.VERILOG_PROVIDED:
`ifndef _SIM_HAVE_CLOG2
  function integer log2;
     input integer value;
     integer temp_value;
    begin
      temp_value = value-1;
      for (log2=0; temp_value>0; log2=log2+1)
        temp_value = temp_value>>1;
    end
  endfunction
`endif
`ifdef _SIM_HAVE_CLOG2
  parameter arg2_bitsize = $clog2(BITSIZE_in1*NUM_ELEM_in1);
`else
  parameter arg2_bitsize = log2(BITSIZE_in1*NUM_ELEM_in1);
`endif
generate
  if(BITSIZE_in3 > arg2_bitsize)
    assign out1 = in1 >>> (in3[arg2_bitsize-1:0]);
  else
    assign out1 = in1 >>> in3;
endgenerate

NP_functionality.VHDL_PROVIDED:
begin
   process(in1, in3)
   begin
      if(BITSIZE_in3 > integer(log2(real(BITSIZE_in1*NUM_ELEM_in1)))) then
         out1 <= resize(signed(in1((to_integer(in3(integer(log2(real(BITSIZE_in1*NUM_ELEM_in1)))-1 downto 0) + BITSIZE_out1 - 1)) downto to_integer(in3(integer(log2(real(BITSIZE_in1*NUM_ELEM_in1)))-1 downto 0)))), BITSIZE_out1);
      else
         out1 <= resize(signed(in1((to_integer(in3) + BITSIZE_out1 -1) downto to_integer(in3))), BITSIZE_out1);
      end if;
   end process;

NP_functionality.VERILOG_PROVIDED:
`ifndef _SIM_HAVE_CLOG2
  function integer log2;
     input integer value;
     integer temp_value;
    begin
      temp_value = value-1;
      for (log2=0; temp_value>0; log2=log2+1)
        temp_value = temp_value>>1;
    end
  endfunction
`endif
`ifdef _SIM_HAVE_CLOG2
  parameter arg2_bitsize = $clog2(BITSIZE_in1*NUM_ELEM_in1);
`else
  parameter arg2_bitsize = log2(BITSIZE_in1*NUM_ELEM_in1);
`endif
generate
  if(BITSIZE_in3 > arg2_bitsize)
    assign out1 = $unsigned(in1 >> (in3[arg2_bitsize-1:0]));
  else
    assign out1 = $unsigned(in1 >> in3);
endgenerate

generate
genvar o1;
  for (o1=0; o1<NUM_ELEM_out1; o1=o1+1)
  begin  : vec_expr
  assign out1[o1*BITSIZE_out1+BITSIZE_out1-1:o1*BITSIZE_out1] = {{{BITSIZE_out1-1}{1'b0}},in1[o1]};
  end
endgenerate

NP_functionality.VHDL_PROVIDED:
process(in1)
begin
   for index in 0 to NUM_ELEM_out1-1 loop
       out1((index+1)*BITSIZE_out1-1 downto index*BITSIZE_out1) <= std_logic_vector(resize(unsigned(in1(index), BITSIZE_out1)));
   end loop;
end process;

NP_functionality.VERILOG_PROVIDED:
generate
genvar o1;
  for (o1=0; o1<NUM_ELEM_out1; o1=o1+1)
  begin  : vec_expr
    assign out1[o1*BITSIZE_out1+BITSIZE_out1-1:o1*BITSIZE_out1] = in1[o1*BITSIZE_in1+BITSIZE_in1-1:o1*BITSIZE_in1];
  end
endgenerate

NP_functionality.VHDL_PROVIDED:
process(in1)
begin
   for index in 0 to NUM_ELEM_out1-1 loop
       out1((index+1)*BITSIZE_out1-1 downto index*BITSIZE_out1) <= std_logic_vector(resize(unsigned(in1((index+1)*BITSIZE_in1-1 downto index*BITSIZE_in1)), BITSIZE_out1));
   end loop;
end process;
