NP_functionality.VERILOG_PROVIDED:
reg isInRange;
always @ (address or base or max_address)
   if (address >= base && address <= max_address)
     isInRange = 1'b1;
   else
     isInRange = 1'b0;

`ifndef _SIM_HAVE_CLOG2
  function integer log2;
     input integer value;
     integer temp_value;
    begin
      temp_value = value-1;
      for (log2=0; temp_value>0; log2=log2+1)
        temp_value = temp_value>>1;
    end
  endfunction
`endif
`ifdef _SIM_HAVE_CLOG2
  parameter nbit_addr = MAX_ADDRESS == 1 ? 1 : $clog2(MAX_ADDRESS);
`else
  parameter nbit_addr = MAX_ADDRESS == 1 ? 1 : log2(MAX_ADDRESS);
`endif

generate
if (BITSIZE_out1 <= nbit_addr)
begin
  assign out1 = in1[BITSIZE_out1-1:0];
end
else
begin
  assign out1 = {{(BITSIZE_out1-nbit_addr-1){1'b0}},in1[nbit_addr:0]};
end
endgenerate

reg [BITSIZE_dataRamSize - 1:0]    dataRamSize;

generate
   if (BITSIZE_sel == 1)
     begin
        always @ (sel)
            dataRamSize = { {BITSIZE_dataRamSize-4{1'b0}}, 4'd8};
     end // if (BITSIZE_sel == 4)
   if (BITSIZE_sel == 2)
     begin
        always @ (sel)
          case (sel)
            2'h1: dataRamSize = { {BITSIZE_dataRamSize-4{1'b0}}, 4'd8};
            default: dataRamSize = { 1'b1, {BITSIZE_dataRamSize-1{1'b0}}};
          endcase // case (sel)
     end
   if (BITSIZE_sel == 4)
     begin
        if (BITSIZE_dataRamSize > 4)
          begin
             always @ (sel)
               case (sel)
                 4'h1: dataRamSize = { {BITSIZE_dataRamSize-4{1'b0}}, 4'd8};
                 4'h3: dataRamSize = { {BITSIZE_dataRamSize-5{1'b0}}, 5'd16};
                 default: dataRamSize = { 1'b1, {BITSIZE_dataRamSize-1{1'b0}}};
               endcase // case (sel)
          end
        else
          begin
             always @ (sel)
            dataRamSize = { 1'b1, {BITSIZE_dataRamSize-1{1'b0}}};
          end
     end
   if (BITSIZE_sel == 8)
     begin
        if (BITSIZE_dataRamSize > 5)
          begin
             always @ (sel)
               case (sel)
                 8'h1: dataRamSize = { {BITSIZE_dataRamSize-4{1'b0}}, 4'd8};
                 8'h3: dataRamSize = { {BITSIZE_dataRamSize-5{1'b0}}, 5'd16};
                 8'hf: dataRamSize = { {BITSIZE_dataRamSize-6{1'b0}}, 6'd32};
                 default: dataRamSize = { 1'b1, {BITSIZE_dataRamSize-1{1'b0}}};
               endcase // case (sel)
          end
        else if (BITSIZE_dataRamSize == 5)
          begin
             always @ (sel)
               case (sel)
                 8'h1: dataRamSize = { {BITSIZE_dataRamSize-4{1'b0}}, 4'd8};
                 8'h3: dataRamSize = { {BITSIZE_dataRamSize-5{1'b0}}, 5'd16};
                 default: dataRamSize = { 1'b1, {BITSIZE_dataRamSize-1{1'b0}}};
               endcase // case (sel)
          end
        else
          begin
             always @ (sel)
            dataRamSize = { 1'b1, {BITSIZE_dataRamSize-1{1'b0}}};
          end
     end
   if (BITSIZE_sel == 16)
     begin
        if (BITSIZE_dataRamSize > 6)
          begin
             always @ (sel)
               case (sel)
                 16'h1: dataRamSize = { {BITSIZE_dataRamSize-4{1'b0}}, 4'd8};
                 16'h3: dataRamSize = { {BITSIZE_dataRamSize-5{1'b0}}, 5'd16};
                 16'hf: dataRamSize = { {BITSIZE_dataRamSize-6{1'b0}}, 6'd32};
                 16'hff: dataRamSize = { {BITSIZE_dataRamSize-7{1'b0}}, 7'd64};
                 default: dataRamSize = { 1'b1, {BITSIZE_dataRamSize-1{1'b0}}};
               endcase // case (sel)
          end
        else if (BITSIZE_dataRamSize == 6)
          begin
             always @ (sel)
               case (sel)
                 16'h1: dataRamSize = { {BITSIZE_dataRamSize-4{1'b0}}, 4'd8};
                 16'h3: dataRamSize = { {BITSIZE_dataRamSize-5{1'b0}}, 5'd16};
                 16'hf: dataRamSize = { {BITSIZE_dataRamSize-6{1'b0}}, 6'd32};
                 default: dataRamSize = { 1'b1, {BITSIZE_dataRamSize-1{1'b0}}};
               endcase // case (sel)
          end
        else if (BITSIZE_dataRamSize == 5)
          begin
             always @ (sel)
               case (sel)
                 16'h1: dataRamSize = { {BITSIZE_dataRamSize-4{1'b0}}, 4'd8};
                 16'h3: dataRamSize = { {BITSIZE_dataRamSize-5{1'b0}}, 5'd16};
                 default: dataRamSize = { 1'b1, {BITSIZE_dataRamSize-1{1'b0}}};
               endcase // case (sel)
          end
        else
          begin
             always @ (sel)
            dataRamSize = { 1'b1, {BITSIZE_dataRamSize-1{1'b0}}};
          end
     end
endgenerate

reg [BITSIZE_sel - 1:0]    sel;

generate
   if (BITSIZE_dataRamSize == 4)
     begin
        always @ (dataRamSize)
          case (dataRamSize)
            4'd8: sel = {{BITSIZE_sel-1{1'b0}}, 1'h1};
            default: sel = {BITSIZE_sel{1'b1}};
          endcase
     end
   if (BITSIZE_dataRamSize == 5)
     begin
        always @ (dataRamSize)
          case (dataRamSize)
            5'd8: sel = {{BITSIZE_sel-1{1'b0}}, 1'h1};
            5'd16: sel = {{BITSIZE_sel-2{1'b0}}, 2'h3};
            default: sel = {BITSIZE_sel{1'b1}};
          endcase
     end
   if (BITSIZE_dataRamSize == 6)
     begin
        always @ (dataRamSize)
          case (dataRamSize)
            6'd8: sel = {{BITSIZE_sel-1{1'b0}}, 1'h1};
            6'd16: sel = {{BITSIZE_sel-2{1'b0}}, 2'h3};
            6'd32: sel = {{BITSIZE_sel-4{1'b0}}, 4'hf};
            default: sel = {BITSIZE_sel{1'b1}};
          endcase
     end
   if (BITSIZE_dataRamSize == 7)
     begin
        always @ (dataRamSize)
          case (dataRamSize)
            7'd8: sel = {{BITSIZE_sel-1{1'b0}}, 1'h1};
            7'd16: sel = {{BITSIZE_sel-2{1'b0}}, 2'h3};
            7'd32: sel = {{BITSIZE_sel-4{1'b0}}, 4'hf};
            7'd64: sel = {{BITSIZE_sel-8{1'b0}}, 8'hff};
            default: sel = {BITSIZE_sel{1'b1}};
          endcase
     end
   if (BITSIZE_dataRamSize == 8)
     begin
        always @ (dataRamSize)
          case (dataRamSize)
            8'd8: sel = {{BITSIZE_sel-1{1'b0}}, 1'h1};
            8'd16: sel = {{BITSIZE_sel-2{1'b0}}, 2'h3};
            8'd32: sel = {{BITSIZE_sel-4{1'b0}}, 4'hf};
            8'd64: sel = {{BITSIZE_sel-8{1'b0}}, 8'hff};
            8'd128: sel = {{BITSIZE_sel-16{1'b0}}, 16'hffff};
            default: sel = {BITSIZE_sel{1'b1}};
          endcase
     end
endgenerate

wire ack_temp;
reg [BITSIZE_out1-1:0] reg_out1 1INIT_ZERO_VALUE;
assign out1 = reg_out1;
assign ack_temp = ALLOCATED_ADDRESS == address;
assign ack = ack_temp;
always @(posedge clock 1RESET_EDGE)
  if (1RESET_VALUE)
    reg_out1 <= {BITSIZE_out1{1'b0}};
  else if (wenable && ack_temp)
    reg_out1 <= in1;

reg startAndWaitRead;
reg [1:0]                  startAndWait 1INIT_ZERO_VALUE;
reg [1:0]                  startAndWait_next;
reg [BITSIZE_dat_is-1:0]  ackAddress 1INIT_ZERO_VALUE;
reg [BITSIZE_dat_is-1:0]  ackAddress_next;

assign startRegValue = {ackAddress[BITSIZE_dat_is-1:2], startAndWait};

always @ (weI_CS or addr_is_f)
if (!weI_CS &&
addr_is_f == START_REGISTER_ADDRESS)
startAndWaitRead = 1'b1;
else
startAndWaitRead = 1'b0;

assign start_port = startAndWait[0];
assign ack_startAndWait = addr_is_f == START_REGISTER_ADDRESS & cyc_is;

always @ (posedge clock 1RESET_EDGE)
  if (1RESET_VALUE)
  begin
    startAndWait = 2'b00;
    ackAddress = {BITSIZE_dat_is{1'b0}};
  end
else
begin
  startAndWait = startAndWait_next;
  ackAddress = ackAddress_next;
end

always @ (done_port, ack_startAndWait, startAndWait, startAndWaitRead)
if (startAndWait == 2'b00) begin
if (ack_startAndWait & !startAndWaitRead) begin
startAndWait_next = 2'b01;ackAddress_next = dat_is;
end
else begin
startAndWait_next = 2'b00;
ackAddress_next = ackAddress;
end
end else if (startAndWait == 2'b01) begin
ackAddress_next = ackAddress;
if (done_port)
startAndWait_next = 2'b10;
else
startAndWait_next = 2'b01;
end else if (startAndWait == 2'b10) begin
if (ack_startAndWait & startAndWaitRead) begin
startAndWait_next = 2'b00;
ackAddress_next = {BITSIZE_dat_is{1'b0}};
end
else begin
startAndWait_next = 2'b10;
ackAddress_next = ackAddress;
end
end
else begin
startAndWait_next = 2'b00;
ackAddress_next = {BITSIZE_dat_is{1'b0}};
end

reg [BITSIZE_addr_om-1:0] addr_om;
reg cyc_om;
reg stb_om;
reg we_om;
reg outctrl;
reg state 1INIT_ZERO_VALUE;
reg state_next;
wire [BITSIZE_status_reg-1:0] notify_addr;

assign notify_addr = {status_reg[BITSIZE_status_reg-1:2], {2{1'b0}}};

always @ (posedge clock 1RESET_EDGE)
  if (1RESET_VALUE)
    begin
       state <= 1'b0;
    end
  else
    begin
       state <= state_next;
    end

always @ (*)
  begin
     state_next = 1'b0;
     cyc_om  = 1'b0;
     stb_om  = 1'b0;
     addr_om = {BITSIZE_addr_om{1'b0}};
     we_om   = 1'b0;
     outctrl = 1'b0;
     if (state == 1'b0)
       begin
          if (done_port == 1'b1 && (| notify_addr))
            begin
               state_next = 1'b1;
               cyc_om  = 1'b1;
               stb_om  = 1'b1;
               addr_om = notify_addr;
               we_om   = 1'b1;
               outctrl = 1'b1;
            end
          else
            state_next = 1'b0;
       end
     else
       begin
          cyc_om  = 1'b1;
          stb_om  = 1'b1;
          addr_om = notify_addr;
          we_om   = 1'b1;
          outctrl = 1'b1;
          if (ack_im == 1'b1)
            state_next = 1'b0;
          else
            state_next = 1'b1;
       end
  end

reg oe_int_delayed 1INIT_ZERO_VALUE;
assign out1 = !((we1 & internal1) | oe_int_delayed);
always @(posedge clock)
  oe_int_delayed <= oe1 & internal1;

reg  [PORTSIZE_arb_grant-1:0] arb_grant 1INIT_ZERO_VALUE;
reg  [PORTSIZE_arb_grant-1:0] arb_grant_next;

always @ (arb_requests, arb_grant)
  begin : ARBITER
     integer i, busy, stop;

     busy = 0;
     stop = 0;

     for (i = 0; i < PORTSIZE_arb_requests; i = i + 1)
       if (arb_requests[i] == arb_grant[i] && arb_grant[i] == 1'b1)
         busy = 1;

     if (busy == 1)
       // I don't want to interrupt ongoing cycles.
       arb_grant_next = arb_grant;
     else
       for (i = 0; i < PORTSIZE_arb_requests; i = i + 1)
         if (stop == 0 && arb_requests[i] == 1'b1) begin
            arb_grant_next[i] = 1'b1;
            stop = 1;
         end else
           arb_grant_next[i] = 1'b0;
  end

always @ (posedge clock 1RESET_EDGE)
  if (1RESET_VALUE) begin
     arb_grant = {PORTSIZE_arb_grant{1'b0}};
  end
  else begin
     arb_grant = arb_grant_next;
   end

// Internal wires
wire [MASTERS-1:0]                 arb_grant;
reg [SLAVES-1:0]                  requested_slave;

// Common Master bus
reg                               mst_comm_cyc_o, mst_comm_stb_o, mst_comm_we_o;
reg [BITSIZE_sel_om-1:0]            mst_comm_sel_o;
reg [BITSIZE_addr_om-1:0]           mst_comm_addr_o;
reg [BITSIZE_dat_om-1:0]           mst_comm_dat_o;

// Common Slave bus
reg [BITSIZE_dat_om-1:0]           slv_comm_dat_o;
reg                               slv_comm_ack_o;

// Address rom
reg [BITSIZE_addr_om-1:0]           slave_addresses [2*SLAVES-1:0];

reg [(PORTSIZE_cyc_om*BITSIZE_cyc_om)+(-1):0] cyc_om_next 1INIT_ZERO_VALUE;
reg [(PORTSIZE_stb_om*BITSIZE_stb_om)+(-1):0] stb_om_next 1INIT_ZERO_VALUE;
reg [(PORTSIZE_we_om*BITSIZE_we_om)+(-1):0]   we_om_next 1INIT_ZERO_VALUE;
reg [(PORTSIZE_sel_om*BITSIZE_sel_om)+(-1):0] sel_om_next 1INIT_ZERO_VALUE;
reg [(PORTSIZE_addr_om*BITSIZE_addr_om)+(-1):0] addr_om_next 1INIT_ZERO_VALUE;
reg [(PORTSIZE_dat_om*BITSIZE_dat_om)+(-1):0]   dat_om_next 1INIT_ZERO_VALUE;

always @ (posedge clock 1RESET_EDGE)
  begin
     if (1RESET_VALUE)
       begin
          cyc_om_next  = {(PORTSIZE_ack_im*BITSIZE_ack_im){1'b0}};  
          stb_om_next  = {(PORTSIZE_stb_om*BITSIZE_stb_om){1'b0}};  
          we_om_next   = {(PORTSIZE_we_om*BITSIZE_we_om){1'b0}};   
          sel_om_next  = {(PORTSIZE_sel_om*BITSIZE_sel_om){1'b0}};  
          addr_om_next = {(PORTSIZE_addr_om*BITSIZE_addr_om){1'b0}};
          dat_om_next  = {(PORTSIZE_dat_om*BITSIZE_dat_om){1'b0}};
       end
     else
       begin
          cyc_om_next  = cyc_om;  
          stb_om_next  = stb_om;  
          we_om_next   = we_om;   
          sel_om_next  = sel_om;  
          addr_om_next = addr_om;
          dat_om_next  = dat_om;
       end
  end

initial
  begin
     slave_addresses[0] = {BITSIZE_addr_om{1'b0}};
     slave_addresses[1] = {BITSIZE_addr_om{1'b0}};
     $readmemb(MEMORY_INIT_file, slave_addresses, 2, 2*SLAVES-1);
  end

generate
genvar                            idx;
   for (idx = 0; idx < SLAVES; idx = idx + 1)
     begin: mst_to_slv_common_bus
        assign cyc_is  [idx] = requested_slave[idx] ? mst_comm_cyc_o  : 1'b0;
        assign stb_is  [idx] = requested_slave[idx] ? mst_comm_stb_o  : 1'b0;
        assign we_is   [idx] = requested_slave[idx] ? mst_comm_we_o   : 1'b0;
        assign sel_is  [idx * BITSIZE_sel_om+:BITSIZE_sel_om]   = requested_slave[idx] ? mst_comm_sel_o  : {BITSIZE_sel_om{1'b0}};
        assign addr_is [idx * BITSIZE_addr_om+:BITSIZE_addr_om] = requested_slave[idx] ? mst_comm_addr_o : {BITSIZE_addr_om{1'b0}};
        assign dat_is  [idx * BITSIZE_dat_om+:BITSIZE_dat_om] = requested_slave[idx] ? mst_comm_dat_o  : {BITSIZE_dat_om{1'b0}};
     end
endgenerate

generate
   for (idx = 0; idx < MASTERS; idx = idx + 1)
     begin: slv_to_mst_common_bus
        assign ack_im [idx] = arb_grant[idx] ? slv_comm_ack_o : 1'b0;
        assign dat_im [idx * BITSIZE_dat_om+:BITSIZE_dat_om] = arb_grant[idx] ? slv_comm_dat_o : {BITSIZE_dat_om{1'b0}};
     end
endgenerate

// Master Muxes
always @ (*)
  begin : MST_MUX
     integer i;

     mst_comm_cyc_o = 1'b0;
     mst_comm_stb_o = 1'b0;
     mst_comm_we_o = 1'b0;
     mst_comm_sel_o = {BITSIZE_sel_om{1'b0}};
     mst_comm_addr_o = {BITSIZE_addr_om{1'b0}};
     mst_comm_dat_o = {BITSIZE_dat_om{1'b0}};

     for (i = 0; i < MASTERS; i = i + 1) begin
        // Grant the master
        if (arb_grant[i] == 1) begin
           mst_comm_cyc_o  = cyc_om_next[i];
           mst_comm_stb_o  = stb_om_next[i];
           mst_comm_we_o   = we_om_next[i];
           mst_comm_sel_o  = sel_om_next[(i*BITSIZE_sel_om)+:BITSIZE_sel_om];
           mst_comm_addr_o = addr_om_next[(i*BITSIZE_addr_om)+:BITSIZE_addr_om];
           mst_comm_dat_o  = dat_om_next[(i*BITSIZE_dat_om)+:BITSIZE_dat_om];
        end
     end
  end

always @ (*)
  begin : SLV_MUX
     integer j;

     slv_comm_ack_o = 1'b0;
     slv_comm_dat_o = {BITSIZE_dat_om{1'b0}};

     for (j = 0; j < SLAVES; j = j + 1) begin
        if (requested_slave[j] == 1'b1) begin
           slv_comm_ack_o = ack_os[j];
           slv_comm_dat_o = dat_os[j*BITSIZE_dat_om+:BITSIZE_dat_om];
        end
     end
  end

always @ (*) begin : RQS_SLAVE
     integer j;
   for (j = 1; j < SLAVES; j = j + 1)
     begin
        if (mst_comm_addr_o[BITSIZE_addr_om-1:0] >= slave_addresses[2 * j] &&
            mst_comm_addr_o[BITSIZE_addr_om-1:0] <= slave_addresses[2 * j + 1])
          requested_slave[j] = 1'b1;
        else
          requested_slave[j] = 1'b0;
     end
   requested_slave[0] = (~| requested_slave[SLAVES - 1:1]);
end

intercon_arbiter #(.PORTSIZE_arb_requests(MASTERS), .PORTSIZE_arb_grant(MASTERS)) wb_arbiter(clock, reset, cyc_om, arb_grant);
